import{_ as l}from"./plugin-vue_export-helper-c27b6911.js";import{r as e,o as p,c as g,a as o,b as a,e as i,d as r,f as s}from"./app-9ec59630.js";const n={},h=a("p",null,[a("strong",null,"蛇頭山"),i("，又名風櫃尾，海拔20公尺，位於澎湖縣馬公市風櫃里，原有三等三角點陸補40號已遺失。蛇頭山昔日為軍事要地，荷蘭人、法國人及日本人曾在此築城、登陸及駐軍，目前已由澎湖國家風景區管理處規劃為蛇頭山遊憩區。該山名列台灣小百岳No.100。")],-1),c=a("p",null,"蛇頭山雖然名列台灣小百岳，交通工具便可直接到達，不同於其他需要徒步登臨的小百岳，但是蛇頭山視野極佳，向北可眺望馬公港及測天島，向西則可遠眺四角嶼、西嶼等，景緻獨特值得一訪。在結婚紀念日當天，終於完成百座小百岳，為自己留下美好的回憶。",-1),d=s('<p>走走日期：2014/07/08</p><h2 id="行車路線" tabindex="-1"><a class="header-anchor" href="#行車路線" aria-hidden="true">#</a> 行車路線</h2><p>馬公市出發，沿204縣道再轉201縣道，於201縣道10.1Km處(進入風櫃社區前)右轉往蛇頭山(路口有座紅色小廟及蛇頭山指標)，經過風櫃北漁港後，依指標即可抵達蛇頭山。</p><h2 id="走走路線" tabindex="-1"><a class="header-anchor" href="#走走路線" aria-hidden="true">#</a> 走走路線</h2><p>→蛇頭山遊憩區停車場<br><img src="https://1013399.github.io/image-2/102/1036352509_l.jpg" alt="澎湖馬公 蛇頭山遊憩區停車場" loading="lazy"></p><p>→蛇頭山及荷蘭城堡遺址解說牌<br><img src="https://1013399.github.io/image-2/102/1036353014_l.jpg" alt="澎湖馬公 蛇頭山及荷蘭城堡遺址解說牌" loading="lazy"></p><p>→日本松島艦沉船紀念碑<br><img src="https://1013399.github.io/image-2/102/1036356269_l.jpg" alt="澎湖馬公 蛇頭山 日本松島艦沉船紀念碑" loading="lazy"></p><p>→眺望對岸的馬公港及西瀛虹橋<br><img src="https://1013399.github.io/image-2/102/1036356572_l.jpg" alt="澎湖馬公 蛇頭山 眺望對岸的馬公港及西瀛虹橋" loading="lazy"></p><p>→小廣場，取左上階梯<br><img src="https://1013399.github.io/image-2/102/1036358732_l.jpg" alt="澎湖馬公 蛇頭山" loading="lazy"></p><p>→日晷儀，地處高點視野佳<br><img src="https://1013399.github.io/image-2/102/1036359226_l.jpg" alt="澎湖馬公 蛇頭山 日晷儀" loading="lazy"></p><p>→眺望四角嶼及遠方的西嶼鄉<br><img src="https://1013399.github.io/image-2/102/1036358733_l.jpg" alt="澎湖馬公 蛇頭山 眺望四角嶼及遠方的西嶼鄉" loading="lazy"></p><p>→通往七美、望安的遊艇，船班很多<br><img src="https://1013399.github.io/image-2/102/1036358051_l.jpg" alt="澎湖馬公 蛇頭山" loading="lazy"></p><p>→續往前行，步道往下<br><img src="https://1013399.github.io/image-2/102/1036359027_l.jpg" alt="澎湖馬公 蛇頭山" loading="lazy"></p><p>→法軍殉職紀念碑，紀念1885在澎湖犧牲之法軍將士<br><img src="https://1013399.github.io/image-2/102/1036355663_l.jpg" alt="澎湖馬公 蛇頭山 法軍殉職紀念碑" loading="lazy"></p><p>→澎湖縣縣花－天人菊，天性耐旱害、風害、鹽害<br><img src="https://1013399.github.io/image-2/102/1036353168_l.jpg" alt="澎湖縣縣花 天人菊" loading="lazy"></p><p>→續循步道往荷蘭城堡遺址<br><img src="https://1013399.github.io/image-2/102/1036358140_l.jpg" alt="澎湖馬公 蛇頭山" loading="lazy"></p><p>→路旁有一口古井<br><img src="https://1013399.github.io/image-2/102/1036358142_l.jpg" alt="澎湖馬公 蛇頭山" loading="lazy"></p><p>→頂天立地、繼往開來，後方有棟營舍<br><img src="https://1013399.github.io/image-2/102/1036359028_l.jpg" alt="澎湖馬公 蛇頭山" loading="lazy"></p><p>→荷軍城堡遺址<br><img src="https://1013399.github.io/image-2/102/1036352513_l.jpg" alt="澎湖馬公 蛇頭山 荷軍城堡遺址" loading="lazy"></p><p>→荷軍城堡遺址解說牌，原路折返<br><img src="https://1013399.github.io/image-2/102/1036355093_l.jpg" alt="澎湖馬公 蛇頭山 荷軍城堡遺址解說牌" loading="lazy"></p><p>→循法軍紀念碑下方步道回<br><img src="https://1013399.github.io/image-2/102/1036358056_l.jpg" alt="澎湖馬公 蛇頭山 法軍紀念碑" loading="lazy"></p><p>→此處為1622年荷蘭軍登陸處<br><img src="https://1013399.github.io/image-2/102/1036353087_l.jpg" alt="澎湖馬公 蛇頭山 1622年荷蘭軍登陸處" loading="lazy"></p><p>→續行步道，回到小廣場<br><img src="https://1013399.github.io/image-2/102/1036359228_l.jpg" alt="澎湖馬公 蛇頭山 小廣場" loading="lazy"></p><p>→回到停車場</p><h2 id="走走資訊" tabindex="-1"><a class="header-anchor" href="#走走資訊" aria-hidden="true">#</a> 走走資訊</h2><div class="hint-container info"><p class="hint-container-title">相關信息</p><p>走走時間：約20分鐘(含休息)</p></div><h2 id="參考地圖" tabindex="-1"><a class="header-anchor" href="#參考地圖" aria-hidden="true">#</a> 參考地圖</h2>',27),m={href:"http://www.keepon.com.tw/DiscussLoad.aspx?code=314B5CF9AEC3A19113F6CAA6F539A6620D493705A3BE6A04",target:"_blank",rel:"noopener noreferrer"},b=a("figure",null,[a("img",{src:"https://1013399.github.io/image-2/102/1036355105_l.jpg",alt:"澎湖馬公 蛇頭山 參考地圖",tabindex:"0",loading:"lazy"}),a("figcaption",null,"澎湖馬公 蛇頭山 參考地圖")],-1);function _(u,j){const t=e("ExternalLinkIcon");return p(),g("div",null,[h,c,o(" more "),d,a("p",null,[i("原圖連結→"),a("a",m,[i("登山補給站-shiou2shiou2(小佑)"),r(t)])]),b])}const f=l(n,[["render",_],["__file","post-102-2014-07-23.html.vue"]]);export{f as default};
