import{_ as l}from"./plugin-vue_export-helper-c27b6911.js";import{r as p,o as e,c as r,a as o,b as a,f as i,d as g,e as s}from"./app-a9847bbb.js";const h={},n=a("h2",{id:"走走行程",tabindex:"-1"},[a("a",{class:"header-anchor",href:"#走走行程","aria-hidden":"true"},"#"),i(" 走走行程")],-1),m=a("p",null,"澎湖開拓館→中央街→四眼井→施公祠、萬軍井→天后宮→水仙宮→順承門→篤行十村→張雨生紀念館→潘安邦故居→觀音亭遊憩區→西瀛虹橋賞夕陽→來福海鮮餐廳晚餐→菊島之星→玉冠嫩仙草",-1),c=s('<p>走走日期：2014/07/07</p><h2 id="走走路線" tabindex="-1"><a class="header-anchor" href="#走走路線" aria-hidden="true">#</a> 走走路線</h2><p>→會館休息約40分鐘後，16:00開始機車遊馬公<br> 　首先來到澎湖開拓館，日治時期縣長官邸，和洋混合式的建築，今日星期一，休館中</p><figure><img src="https://1013399.github.io/image-2/103/1036356354_l.jpg" alt="澎湖馬公 馬公市區 澎湖開拓館" tabindex="0" loading="lazy"><figcaption>澎湖馬公 馬公市區 澎湖開拓館</figcaption></figure><p>→前往中央老街，停妥機車步行，依指標往四眼井<br><img src="https://1013399.github.io/image-2/103/1036349256_l.jpg" alt="澎湖馬公 馬公市區" loading="lazy"></p><p>→中央老街，中央旅社、澎湖第一印刷廠，前方即為四眼井<br><img src="https://1013399.github.io/image-2/103/1036351051_l.jpg" alt="澎湖馬公 馬公市區 中央老街，中央旅社、澎湖第一印刷廠 四眼井" loading="lazy"></p><p>→四眼井，400年澎湖老古井，一個井四個孔，又名四孔井<br><img src="https://1013399.github.io/image-2/103/1036353986_l.jpg" alt="澎湖馬公 馬公市區 四眼井" loading="lazy"></p><p>→乾益藥膳蛋，四眼井旁，一顆蛋加一塊豆干20元<br><img src="https://1013399.github.io/image-2/103/1036354641_l.jpg" alt="澎湖馬公 馬公市區" loading="lazy"></p><p>→中央老街，舊稱大井街，為台灣第一街，為昔日的商業中心<br><img src="https://1013399.github.io/image-2/103/1036353571_l.jpg" alt="澎湖馬公 馬公市區" loading="lazy"></p><p>→施公祠，主要祭祀清朝施琅將軍；萬軍井，為施琅將軍以皇帝賜劍鑿地成井<br><img src="https://1013399.github.io/image-2/103/1036354642_l.jpg" alt="澎湖馬公 馬公市區" loading="lazy"></p><p>→澎湖天后宮，全台最早的廟宇，祀奉媽祖，目前被指定為國家一級古蹟<br><img src="https://1013399.github.io/image-2/103/1036355350_l.jpg" alt="澎湖天后宮" loading="lazy"></p><p>→中央老街、摸乳巷<br><img src="https://1013399.github.io/image-2/103/1036355234_l.jpg" alt="澎湖馬公 馬公市區 中央老街、摸乳巷" loading="lazy"></p><p>→水仙宮，供奉大禹等五位水仙尊王，也是昔日的台夏郊會館，目前為三級古蹟<br><img src="https://1013399.github.io/image-2/103/1036354644_l.jpg" alt="澎湖馬公 馬公市區 水仙宮" loading="lazy"></p><p>→取車，前往媽宮城順承門<br><img src="https://1013399.github.io/image-2/103/1036355353_l.jpg" alt="澎湖馬公 馬公市區 媽宮城順承門" loading="lazy"></p><p>→順承門旁邊港口停靠著一艘麗星郵輪寶瓶星號<br><img src="https://1013399.github.io/image-2/103/1036355041_l.jpg" alt="澎湖馬公 馬公市區 麗星郵輪寶瓶星號" loading="lazy"></p><p>→順承門為城牆式防禦型古城遺址，目前整修中，無法登上城門參觀<br><img src="https://1013399.github.io/image-2/103/1036353057_l.jpg" alt="澎湖馬公 馬公市區 順承門" loading="lazy"></p><p>→穿越城門，即來到篤行十村<br><img src="https://1013399.github.io/image-2/103/1036349401_l.jpg" alt="澎湖馬公 馬公市區 篤行十村" loading="lazy"></p><p>→先參觀張雨生紀念館，4個主題區回憶張雨生小時候在澎湖求學與居住的生活<br><img src="https://1013399.github.io/image-2/103/1036354548_l.jpg" alt="澎湖馬公 馬公市區 張雨生紀念館" loading="lazy"></p><p>→再參觀潘安邦故居，也可稱外婆的澎湖灣故事館<br><img src="https://1013399.github.io/image-2/103/1036353991_l.jpg" alt="澎湖馬公 馬公市區 潘安邦故居" loading="lazy"></p><p>→當年潘安邦先生作詞作曲手稿<br><img src="https://1013399.github.io/image-2/103/1036353858_l.jpg" alt="澎湖馬公 潘安邦先生作詞作曲手稿" loading="lazy"></p><p>→縣政府將潘安邦故居重新整理成庭園景觀，並設置潘安邦外婆塑像<br><img src="https://1013399.github.io/image-2/103/1036354846_l.jpg" alt="澎湖馬公 馬公市區 潘安邦故居" loading="lazy"></p><p>→續行，來到觀音亭，觀音亭目前為縣定三級古蹟<br><img src="https://1013399.github.io/image-2/103/1036351679_l.jpg" alt="澎湖馬公 馬公市區 觀音亭" loading="lazy"></p><p>→觀音亭簡介，觀音亭建於康熙35年，為澎湖最早的佛教寺院<br><img src="https://1013399.github.io/image-2/103/1036351681_l.jpg" alt="澎湖馬公 馬公市區 觀音亭" loading="lazy"></p><p>→觀音亭為兩進式的寺廟建築，大殿兩側有龍王廟與養慈堂，另有鐘鼓樓各一座<br><img src="https://1013399.github.io/image-2/103/1036349069_l.jpg" alt="澎湖馬公 馬公市區 觀音亭" loading="lazy"></p><p>→觀音亭遊憩區設有親水游憩區、風帆比賽中心、兒童遊樂場、藍球場、溜冰場等公共設施<br><img src="https://1013399.github.io/image-2/103/1036352966_l.jpg" alt="澎湖馬公 馬公市區 觀音亭遊憩區" loading="lazy"></p><p>→西瀛虹橋，欣賞夕陽西下<br><img src="https://1013399.github.io/image-2/103/1036351052_l.jpg" alt="澎湖馬公 馬公市區 西瀛虹橋" loading="lazy"></p><p>→來福海鮮餐廳享用晚餐</p><p>→參觀菊島之星，三層樓仿船型巨大建築的新漁產直銷中心<br><img src="https://1013399.github.io/image-2/103/1036350098_l.jpg" alt="澎湖馬公 馬公市區" loading="lazy"></p><p>→著名的玉冠嫩仙草，翡翠嫩仙草刨冰已售完，買碗綜合嫩仙草凍回會館享用<br><img src="https://1013399.github.io/image-2/103/1036352970_l.jpg" alt="澎湖馬公 菊島自由行 馬公市區" loading="lazy"></p><h2 id="參考地圖" tabindex="-1"><a class="header-anchor" href="#參考地圖" aria-hidden="true">#</a> 參考地圖</h2>',30),d={href:"http://www.phsea.com.tw/travel/index.php/Main_Page",target:"_blank",rel:"noopener noreferrer"},b={href:"http://www.phsea.com.tw/travel/index.php/%E9%A6%AC%E5%85%AC%E5%B8%82%E5%8D%80%E7%BE%8E%E9%A3%9F%E5%9C%B0%E5%9C%96",target:"_blank",rel:"noopener noreferrer"};function _(u,j){const t=p("ExternalLinkIcon");return e(),r("div",null,[n,m,o(" more "),c,a("p",null,[i("("),a("a",d,[i("沿著菊島旅行--澎湖資訊網"),g(t)]),i(")")]),a("ul",null,[a("li",null,[i("原圖連結→"),a("a",b,[i("馬公市中心街道散步地圖"),g(t)])])])])}const f=l(h,[["render",_],["__file","post-103-2014-07-22.html.vue"]]);export{f as default};
