import{_ as p}from"./plugin-vue_export-helper-c27b6911.js";import{r as e,o as g,c as r,a as o,b as t,f as a,d as l,e as h}from"./app-7f62a6ff.js";const s={},n=t("h2",{id:"走走行程",tabindex:"-1"},[t("a",{class:"header-anchor",href:"#走走行程","aria-hidden":"true"},"#"),a(" 走走行程")],-1),c=t("p",null,"今日行程為南海二島(望安鄉、七美鄉)",-1),m=t("ul",null,[t("li",null,"南海遊客中心→望安鄉潭門港→網垵口沙灘→西安社區→花宅聚落(中社社區)→天台山、呂洞賓腳印→望安鄉潭門港"),t("li",null,"望安鄉潭門港→七美鄉南滬港→雙心石滬→白馬公園→小台灣、牛姆坪→望夫石→七美人塚→七美鄉南滬港→桶盤嶼(停船欣賞玄武岩)→南海遊客中心")],-1),d=h('<h2 id="交通路線" tabindex="-1"><a class="header-anchor" href="#交通路線" aria-hidden="true">#</a> 交通路線</h2><p>會館(信義路)出發，先至利樂拉早餐店用餐(會館提供)，再騎車前往南海遊客中心，停車於機車停車場，搭乘海安遊艇公司的07:40船班。</p><p>走走日期：2014/07/07</p><h2 id="走走路線" tabindex="-1"><a class="header-anchor" href="#走走路線" aria-hidden="true">#</a> 走走路線</h2><p>→南海遊客中心，前往海安遊艇公司櫃檯報到<br><img src="https://1013399.github.io/image-2/104/1036354051_l.jpg" alt="澎湖望安、七美 南海遊客中心" loading="lazy"></p><p>→今日搭乘的海有一號(延誤了10分鐘才出發)<br><img src="https://1013399.github.io/image-2/104/1036351447_l.jpg" alt="澎湖望安、七美 海有一號" loading="lazy"></p><p>→途經桶盤嶼，欣賞柱狀玄武岩<br><img src="https://1013399.github.io/image-2/104/1036355340_l.jpg" alt="澎湖望安、七美 桶盤嶼 柱狀玄武岩" loading="lazy"></p><p>→行船約45分鐘，08:40到達望安鄉潭門港，船家說10:30開船往七美，必須於10:20前上船<br> 　領機車，因綠蠵龜保育中心尚未開門，循澎34鄉道順時針方向行</p><p>→網垵口沙灘，貝殼砂灘潔淨無汙染，弧形海灣綿延將近1公里長<br><img src="https://1013399.github.io/image-2/104/1036349795_l.jpg" alt="澎湖望安、七美" loading="lazy"></p><p>→澎34鄉道因道路施工，必須繞道西安社區<br><img src="https://1013399.github.io/image-2/104/1036354159_l.jpg" alt="澎湖 西安社區" loading="lazy"></p><p>→與當地居民閒聊，這就是風茹草<br><img src="https://1013399.github.io/image-2/104/1036353981_l.jpg" alt="澎湖 風茹草" loading="lazy"></p><p>→續行，來到花宅聚落(中社社區)，是澎湖地區保存較完整的澎湖傳統聚落之一<br><img src="https://1013399.github.io/image-2/104/1036350085_l.jpg" alt="澎湖 花宅聚落(中社社區)" loading="lazy"></p><p>→張家公井，為花宅數座公井之一(另有顏家、陳家、龍泉甘露井)<br><img src="https://1013399.github.io/image-2/104/1036351045_l.jpg" alt="澎湖 張家公井" loading="lazy"></p><p>→曾家古厝，為花宅保存最完整、規模最宏偉華麗的建築<br><img src="https://1013399.github.io/image-2/104/1036351047_l.jpg" alt="澎湖 曾家古厝" loading="lazy"></p><p>→花宅聚落，傳統閩南式建築的窗、門樓、壁磚及生活空間的配置<br><img src="https://1013399.github.io/image-2/104/1036350193_l.jpg" alt="澎湖 花宅聚落" loading="lazy"></p><p>→花宅碼頭，位於天后宮前，參觀休息完，續往天台山<br><img src="https://1013399.github.io/image-2/104/1036354449_l.jpg" alt="澎湖 花宅碼頭" loading="lazy"></p><p>→天台山，海拔53公尺，建有木棧道跟涼亭<br><img src="https://1013399.github.io/image-2/104/1036349797_l.jpg" alt="澎湖 天台山" loading="lazy"></p><p>→天台山視野遼闊，可以遠眺台灣海峽、虎井島、花嶼<br><img src="https://1013399.github.io/image-2/104/1036354376_l.jpg" alt="澎湖 天台山" loading="lazy"></p><p>→天台山西方海崖秀麗，海崖頂上有因風化而成的一處足形凹陷面，又名「仙跡岩」<br> 　相傳是呂洞賓出遊經過天台山，一腳踏在望安島，另一腳則踩在花嶼上「方便」，留下的痕跡<br><img src="https://1013399.github.io/image-2/104/1036351450_l.jpg" alt="澎湖望安、七美 天台山" loading="lazy"></p><p>→回到潭門港，等候上船，結果又延誤了約30分鐘，才出發往七美<br> 　行船也大約45分鐘，11:45到達七美南滬港，先吃午餐再領機車，需於13:40前回來登船回馬公</p><p>→採逆時針方向行，首先經過七美人塚<br> 　由於停留時間與望安島差不多，且含午餐時間，時間有限，所以直奔雙心石滬<br><img src="https://1013399.github.io/image-2/104/1036355036_l.jpg" alt="澎湖七美人塚" loading="lazy"></p><p>→到達雙心石滬前有一座圓形九層混凝土塔，名為金龍寶塔大將軍<br><img src="https://1013399.github.io/image-2/104/1036350501_l.jpg" alt="澎湖 金龍寶塔大將軍" loading="lazy"></p><p>→雙心石滬意象，石滬就在圍牆外下方<br><img src="https://1013399.github.io/image-2/104/1036352959_l.jpg" alt="澎湖 雙心石滬 頂隙滬" loading="lazy"></p><p>→雙心石滬又稱頂隙滬，石滬就是捕魚的陷阱，金龍寶塔旁有石板路可以步行下石滬<br><img src="https://1013399.github.io/image-2/104/1036354547_l.jpg" alt="澎湖 雙心石滬 頂隙滬" loading="lazy"></p><p>→回望牛姆坪<br><img src="https://1013399.github.io/image-2/104/1036351752_l.jpg" alt="澎湖 牛姆坪" loading="lazy"></p><p>→在此品嚐石花冰、烤小卷，休息後折返<br><img src="https://1013399.github.io/image-2/104/1036350087_l.jpg" alt="澎湖 石花冰、烤小卷" loading="lazy"></p><p>→白馬公園，有座風水牆、紅色大龍珠，一座裹著紅布的白馬雕像以及一個葫蘆<br><img src="https://1013399.github.io/image-2/104/1036354164_l.jpg" alt="澎湖 白馬公園" loading="lazy"></p><p>→小台灣，為牛姆坪附近的許多海蝕平台之一<br><img src="https://1013399.github.io/image-2/104/1036352960_l.jpg" alt="澎湖 小台灣" loading="lazy"></p><p>→酷似台灣圖形海蝕地形的小台灣<br><img src="https://1013399.github.io/image-2/104/1036354047_l.jpg" alt="澎湖 小台灣" loading="lazy"></p><p>→狀似牛形的牛姆坪<br><img src="https://1013399.github.io/image-2/104/1036352961_l.jpg" alt="澎湖 牛姆坪" loading="lazy"></p><p>→望夫石，玄武岩流所形成的岩塊，形似仰臥海面的孕婦<br><img src="https://1013399.github.io/image-2/104/1036354839_l.jpg" alt="澎湖 望夫石" loading="lazy"></p><p>→七美人塚，明朝嘉靖年間，七個貞烈女子投井自盡，將井填平成塚，以追念其德行<br><img src="https://1013399.github.io/image-2/104/1036353983_l.jpg" alt="澎湖 七美人塚" loading="lazy"></p><p>→回到南滬港，等候上船，返回馬公港</p><p>→返航途中經過桶盤嶼，船家停船介紹玄武岩，並讓遊客拍照<br><img src="https://1013399.github.io/image-2/104/1036355346_l.jpg" alt="澎湖 桶盤嶼 玄武岩" loading="lazy"></p><p>→行船約1.5小時，回到馬公港，取機車回會館休息</p><h2 id="參考地圖" tabindex="-1"><a class="header-anchor" href="#參考地圖" aria-hidden="true">#</a> 參考地圖</h2>',36),b={href:"http://www.phsea.com.tw/travel/index.php/Main_Page",target:"_blank",rel:"noopener noreferrer"},_=t("p",null,"原圖連結→",-1),u={href:"http://www.phsea.com.tw/travel/index.php/%E6%BE%8E%E6%B9%96%E5%9C%B0%E5%9C%96-%E6%9C%9B%E5%AE%89%E5%9C%B0%E5%9C%96",target:"_blank",rel:"noopener noreferrer"},E={href:"http://www.phsea.com.tw/travel/index.php/%E6%BE%8E%E6%B9%96%E5%9C%B0%E5%9C%96-%E4%B8%83%E7%BE%8E%E5%9C%B0%E5%9C%96",target:"_blank",rel:"noopener noreferrer"},j={href:"http://www.phsea.com.tw/travel/index.php/%E9%A6%AC%E5%85%AC%E5%B8%82%E5%8D%80%E7%BE%8E%E9%A3%9F%E5%9C%B0%E5%9C%96",target:"_blank",rel:"noopener noreferrer"};function y(z,f){const i=e("ExternalLinkIcon");return g(),r("div",null,[n,c,m,o(" more "),d,t("p",null,[a("("),t("a",b,[a("沿著菊島旅行--澎湖資訊網"),l(i)]),a(")")]),_,t("ul",null,[t("li",null,[t("a",u,[a("澎湖地圖-望安旅遊地圖"),l(i)])]),t("li",null,[t("a",E,[a("澎湖地圖-七美旅遊地圖"),l(i)])]),t("li",null,[t("a",j,[a("馬公市中心街道散步地圖"),l(i)])])])])}const C=p(s,[["render",y],["__file","post-104-2014-07-21.html.vue"]]);export{C as default};
