import{_ as g}from"./plugin-vue_export-helper-c27b6911.js";import{r as p,o as e,c as o,a as r,b as t,e as a,d as l,f as s}from"./app-e90ae243.js";const h={},n=t("p",null,[t("strong",null,"羅東林業文化園區"),a("，位於宜蘭縣羅東鎮中正北路118號，佔地面積約16公頃，原為太平山林場之製材、貯木作業以及營林辦公廳舍原址。為保存傳統林業文化，並賦予林業新的生命，從93年起積極進行區內整建工作，至98年6月30日園區正式開放，101年依『文化資產保存法』公告為文化景觀，為百年林業留下見證。園區有古樸的日式房舍、蒼鬱綠林與貯木池，為都會中難得一見蘊含人文與自然風貌的水綠祕境。")],-1),m={href:"http://luodong.forest.gov.tw/ct.asp?xItem=54135&CtNode=2449&mp=360",target:"_blank",rel:"noopener noreferrer"},c=s('<p>走走日期：2014/05/23</p><h2 id="行車路線" tabindex="-1"><a class="header-anchor" href="#行車路線" aria-hidden="true">#</a> 行車路線</h2><p>參加公司活動，走完福山植物園後，直接前往羅東林業文化園區(羅東鎮中正北路118號)參觀，於園區南側入口停車場附近下車。</p><h2 id="走走路線" tabindex="-1"><a class="header-anchor" href="#走走路線" aria-hidden="true">#</a> 走走路線</h2><p>→羅東林業文化園區南側停車場<br><img src="https://1013399.github.io/image-2/107/1028948201_l.jpg" alt="羅東林業文化園區" loading="lazy"></p><p>→轉個彎後，往入口方向<br><img src="https://1013399.github.io/image-2/107/1028948208_l.jpg" alt="羅東林業文化園區" loading="lazy"></p><p>→羅東林業文化園區入口<br><img src="https://1013399.github.io/image-2/107/1028948215_l.jpg" alt="羅東林業文化園區" loading="lazy"></p><p>→進入不久遇叉路，左往竹林車站，取直行，右側為陸上貯木場<br><img src="https://1013399.github.io/image-2/107/1028948220_l.jpg" alt="羅東林業文化園區" loading="lazy"></p><p>→來到「竹探館」叉路，左往竹林車站，取右木棧道，沿著貯木池環繞一圈<br><img src="https://1013399.github.io/image-2/107/1028948224_l.jpg" alt="羅東林業文化園區" loading="lazy"></p><p>→竹探館是以竹材及輕型鋼構設計而成的建築，為竹材及相關應用展示推廣的場所<br><img src="https://1013399.github.io/image-2/107/1028948228_l.jpg" alt="羅東林業文化園區" loading="lazy"></p><p>→竹探館建築解說圖<br><img src="https://1013399.github.io/image-2/107/1028948231_l.jpg" alt="羅東林業文化園區" loading="lazy"></p><p>→貯木池，如今已是鳥類棲息樂園<br><img src="https://1013399.github.io/image-2/107/1028948235_l.jpg" alt="羅東林業文化園區" loading="lazy"></p><p>→經過水生植物池，池內水生植物豐富，是生態觀察、自然教育的最佳場域<br><img src="https://1013399.github.io/image-2/107/1028948240_l.jpg" alt="羅東林業文化園區" loading="lazy"></p><p>→進入木屑步道<br><img src="https://1013399.github.io/image-2/107/1028948246_l.jpg" alt="羅東林業文化園區" loading="lazy"></p><p>→轉個彎後經過涼亭，沿著鐵路續行<br><img src="https://1013399.github.io/image-2/107/1028948251_l.jpg" alt="羅東林業文化園區" loading="lazy"></p><p>→水濱平台，可供遊客駐足小憩，欣賞池中的水鳥嬉戲覓食<br><img src="https://1013399.github.io/image-2/107/1028948258_l.jpg" alt="羅東林業文化園區" loading="lazy"></p><p>→白鷺鷥與夜鷺盤據池中小島，數量眾多<br><img src="https://1013399.github.io/image-2/107/1028948263_l.jpg" alt="羅東林業文化園區" loading="lazy"></p><p>→進入環池步道，叉路，左可往出口，續直行<br><img src="https://1013399.github.io/image-2/107/1028948267_l.jpg" alt="羅東林業文化園區" loading="lazy"></p><p>→池中堆放入漂流木，遙想昔日貯木池盛景<br><img src="https://1013399.github.io/image-2/107/1028948274_l.jpg" alt="羅東林業文化園區" loading="lazy"></p><p>→過去伐木時期原為貯放高級木材的水域場所，隨著林業發展轉型，貯木功能已不再<br><img src="https://1013399.github.io/image-2/107/1028948280_l.jpg" alt="羅東林業文化園區" loading="lazy"></p><p>→經過水閘門後左彎，來到藝文區與宜蘭縣文化創意中心<br><img src="https://1013399.github.io/image-2/107/1028948286_l.jpg" alt="羅東林業文化園區" loading="lazy"></p><p>→藝文區是以日式老宿舍整修而成，將林業文化與生活美學創意結合<br><img src="https://1013399.github.io/image-2/107/1028948293_l.jpg" alt="羅東林業文化園區" loading="lazy"></p><p>→羅東林業文化園區北側入口，直行往中正北路，取左往竹林車站<br><img src="https://1013399.github.io/image-2/107/1028948300_l.jpg" alt="羅東林業文化園區" loading="lazy"></p><p>→昔日的太平山森林鐵道，取右前方往竹林車站<br><img src="https://1013399.github.io/image-2/107/1028948309_l.jpg" alt="羅東林業文化園區" loading="lazy"></p><p>→森趣館，原為維修火車的檢車室，目前暫停開放<br><img src="https://1013399.github.io/image-2/107/1028948316_l.jpg" alt="羅東林業文化園區" loading="lazy"></p><p>→蒸氣火車頭、小火車展示區，位於竹林車站後方<br><img src="https://1013399.github.io/image-2/107/1028948325_l.jpg" alt="羅東林業文化園區" loading="lazy"></p><p>→竹林車站為昔日太平山森林鐵路的起終點，現已為民眾休憩空間<br><img src="https://1013399.github.io/image-2/107/1028948331_l.jpg" alt="羅東林業文化園區" loading="lazy"></p><p>→森林鐵道的復舊重現，是見證林業運材史的最佳歷史地景<br><img src="https://1013399.github.io/image-2/107/1028948339_l.jpg" alt="羅東林業文化園區" loading="lazy"></p><p>→木雕廣場<br><img src="https://1013399.github.io/image-2/107/1028948346_l.jpg" alt="羅東林業文化園區" loading="lazy"></p><p>→不同型式的蒸氣火車頭展示<br><img src="https://1013399.github.io/image-2/107/1028948353_l.jpg" alt="羅東林業文化園區" loading="lazy"></p><p>→回到南側停車場，上車，前往南方澳豆腐岬17餐廳晚餐<br><img src="https://1013399.github.io/image-2/107/1028948360_l.jpg" alt="羅東林業文化園區" loading="lazy"></p><p>→餐畢，夜宿中華電信蘇澳會館</p><h2 id="走走資訊" tabindex="-1"><a class="header-anchor" href="#走走資訊" aria-hidden="true">#</a> 走走資訊</h2><div class="hint-container info"><p class="hint-container-title">相關信息</p><p>走走時間：漫步行約1小時(含休息)</p></div><h2 id="參考地圖" tabindex="-1"><a class="header-anchor" href="#參考地圖" aria-hidden="true">#</a> 參考地圖</h2>',35),d={href:"http://blog.xuite.net/yang5757/blog/62969590",target:"_blank",rel:"noopener noreferrer"},b=t("br",null,null,-1),_=t("img",{src:"https://1013399.github.io/image-2/107/1028948422_l.jpg",alt:"羅東林業文化園區 參考地圖",loading:"lazy"},null,-1);function u(y,j){const i=p("ExternalLinkIcon");return e(),o("div",null,[n,r(" more "),t("p",null,[a("(資料來源："),t("a",m,[a("林務局羅東林區管理處"),l(i)]),a(")")]),c,t("p",null,[a("原圖連結→"),t("a",d,[a("重新站起＆一步一腳印"),l(i)]),b,_])])}const x=g(h,[["render",u],["__file","post-107-2014-06-06.html.vue"]]);export{x as default};
