import{_ as l}from"./plugin-vue_export-helper-c27b6911.js";import{r as p,o,c as r,a as e,b as i,e as a,d as g,f as s}from"./app-455fc4b3.js";const h={},m=i("p",null,[i("strong",null,"里龍山"),a("，又稱里瀧山，位於屏東縣牡丹鄉、獅子鄉境內，為中央山脈南端的延伸，山勢雄偉，標高1062公尺，為一等三角點，為恆春半島唯一海拔超過1000公尺的高山；天候良好時視野極佳，可東眺獅子、牡丹群山、太平洋；西望楓港社區與台灣海峽；南瞧牡丹水庫及整個墾丁國家公園；北邊是層層疊疊中央山脈山巒，是屏東縣一座優良的展望台。該山名列台灣小百岳No.080。")],-1),n={href:"http://www.forest.gov.tw/ct.asp?xItem=40200&ctNode=1787&mp=1",target:"_blank",rel:"noopener noreferrer"},b=s('<p>里龍山有兩處登山口，北登山口位於台26線3.6Km轉入龍山路(入口有圓東庵牌樓)，由鯉龍山人文紀念館或龍峰寺起登；南登山口則由台26線6.6Km大陸羊肉店招牌旁進入竹坑登山口，南北兩線在里龍山步道2.55Km涼亭休息區會合。</p><p>登里龍山可順訪下里龍山或南里龍山，今日參加登山隊行程，由鯉龍山人文紀念館起登，因時間受限，下里龍山、南里龍山皆未造訪。</p><p>走走日期： 2014/03/30</p><h2 id="行車路線" tabindex="-1"><a class="header-anchor" href="#行車路線" aria-hidden="true">#</a> 行車路線</h2><p>由恆春出發，走台26線北上，於台26線3.6Km轉入龍山路(入口有圓東庵牌樓)，約行2Km停車於鯉龍山人文紀念館。</p><h2 id="走走路線" tabindex="-1"><a class="header-anchor" href="#走走路線" aria-hidden="true">#</a> 走走路線</h2><p>→鯉龍山人文紀念館(海拔約250m)<br><img src="https://1013399.github.io/image-2/110/1024247569_l.jpg" alt="鯉龍山人文紀念館" loading="lazy"></p><p>→循人文紀念館後方產業道路往上行<br><img src="https://1013399.github.io/image-2/110/1024247571_l.jpg" alt="鯉龍山人文紀念館 後方產業道路" loading="lazy"></p><p>→叉路，左往龍峰寺，取右續行<br><img src="https://1013399.github.io/image-2/110/1024247574_l.jpg" alt="里龍山步道 左往龍峰寺" loading="lazy"></p><p>→里龍山步道入口，設有導覽圖及0K里程樁(海拔350m)<br><img src="https://1013399.github.io/image-2/110/1024247576_l.jpg" alt="里龍山步道入口" loading="lazy"></p><p>→續行產道，來到0.5K里程樁(海拔407m)<br><img src="https://1013399.github.io/image-2/110/1024247579_l.jpg" alt="里龍山步道、里龍山" loading="lazy"></p><p>→產道終止，取左上<br><img src="https://1013399.github.io/image-2/110/1024247581_l.jpg" alt="里龍山步道、里龍山" loading="lazy"></p><p>→上下起伏，經過大石壁<br><img src="https://1013399.github.io/image-2/110/1024247583_l.jpg" alt="里龍山步道 大石壁" loading="lazy"></p><p>→石塊山徑，像是走在溪谷中<br><img src="https://1013399.github.io/image-2/110/1024247585_l.jpg" alt="里龍山步道" loading="lazy"></p><p>→往右望雄偉山容<br><img src="https://1013399.github.io/image-2/110/1024247588_l.jpg" alt="里龍山步道" loading="lazy"></p><p>→過小橋來到1K里程樁(海拔469m)<br><img src="https://1013399.github.io/image-2/110/1024247590_l.jpg" alt="里龍山步道" loading="lazy"></p><p>→依舊上上下下<br><img src="https://1013399.github.io/image-2/110/1024247592_l.jpg" alt="里龍山步道" loading="lazy"></p><p>→經過1.5K里程樁(海拔475m)<br><img src="https://1013399.github.io/image-2/110/1024247598_l.jpg" alt="里龍山步道" loading="lazy"></p><p>→溪谷，休息、吃點心<br><img src="https://1013399.github.io/image-2/110/1024247604_l.jpg" alt="里龍山步道 溪谷" loading="lazy"></p><p>→休息後續行，之字形陡上，經過2K里程樁(海拔574m)及休息桌椅<br><img src="https://1013399.github.io/image-2/110/1024247608_l.jpg" alt="里龍山步道" loading="lazy"></p><p>→繼續陡上<br><img src="https://1013399.github.io/image-2/110/1024247611_l.jpg" alt="里龍山步道" loading="lazy"></p><p>→叉路，右上往「下里龍山」，續直行<br><img src="https://1013399.github.io/image-2/110/1024247616_l.jpg" alt="里龍山步道 右上往下里龍山" loading="lazy"></p><p>→2.5K里程樁(海拔668m)<br><img src="https://1013399.github.io/image-2/110/1024247618_l.jpg" alt="里龍山步道、里龍山" loading="lazy"></p><p>→2.55K涼亭休息區，大空地，設有許多桌椅，在此午餐<br><img src="https://1013399.github.io/image-2/110/1024247621_l.jpg" alt="里龍山步道、里龍山" loading="lazy"></p><p>→餐畢續行往里龍山<br><img src="https://1013399.github.io/image-2/110/1024247629_l.jpg" alt="里龍山步道、里龍山" loading="lazy"></p><p>→叉路，直行往竹坑登山口，取左續行<br><img src="https://1013399.github.io/image-2/110/1024247634_l.jpg" alt="竹坑登山口" loading="lazy"></p><p>→依指標往里龍山三角點<br><img src="https://1013399.github.io/image-2/110/1024247640_l.jpg" alt="里龍山三角點" loading="lazy"></p><p>→開始陡上，比前段步道更陡<br><img src="https://1013399.github.io/image-2/110/1024247645_l.jpg" alt="里龍山步道、里龍山" loading="lazy"></p><p>→經過3K里程樁(海拔754m)，繼續陡上<br><img src="https://1013399.github.io/image-2/110/1024247646_l.jpg" alt="里龍山步道、里龍山" loading="lazy"></p><p>→平緩坡，但只有數十公尺<br><img src="https://1013399.github.io/image-2/110/1024247647_l.jpg" alt="里龍山步道、里龍山" loading="lazy"></p><p>→繼續陡上，山徑越來越濕滑<br><img src="https://1013399.github.io/image-2/110/1024247649_l.jpg" alt="里龍山步道、里龍山" loading="lazy"></p><p>→一路陡上，經過3.5K里程樁(海拔941m)<br><img src="https://1013399.github.io/image-2/110/1024247651_l.jpg" alt="里龍山步道、里龍山" loading="lazy"></p><p>→陡上再陡上<br><img src="https://1013399.github.io/image-2/110/1024247653_l.jpg" alt="里龍山步道、里龍山" loading="lazy"></p><p>→濕滑陡上的山徑，和北插天山的路徑相似<br><img src="https://1013399.github.io/image-2/110/1024247654_l.jpg" alt="里龍山步道、里龍山" loading="lazy"></p><p>→上至平緩地，左邊叉路不知去向，續直行<br><img src="https://1013399.github.io/image-2/110/1024247655_l.jpg" alt="里龍山步道、里龍山" loading="lazy"></p><p>→到達里龍山山頂，一片霧茫茫，無緣欣賞傳說中的無敵視野<br><img src="https://1013399.github.io/image-2/110/1024247658_l.jpg" alt="達里龍山山頂" loading="lazy"></p><p>→里龍山一等三角點<br><img src="https://1013399.github.io/image-2/110/1024247662_l.jpg" alt="里龍山一等三角點" loading="lazy"></p><p>→里龍山步道終點3.85K<br><img src="https://1013399.github.io/image-2/110/1024247663_l.jpg" alt="里龍山步道終點" loading="lazy"></p><p>→續行可往「南里龍山」<br><img src="https://1013399.github.io/image-2/110/1024247670_l.jpg" alt="南里龍山" loading="lazy"></p><p>→里龍山山頂大石<br><img src="https://1013399.github.io/image-2/110/1024247672_l.jpg" alt="里龍山山頂大石" loading="lazy"></p><p>→拍張攻頂合照後，原路下山<br><img src="https://1013399.github.io/image-2/110/1024247668_l.jpg" alt="里龍山步道、里龍山" loading="lazy"></p><p>→回到2.55K涼亭休息區，休息、泡茶(有水源)<br><img src="https://1013399.github.io/image-2/110/1024247673_l.jpg" alt="里龍山步道、里龍山" loading="lazy"></p><p>→休息後續行，回到里龍山步道入口<br><img src="https://1013399.github.io/image-2/110/1024247677_l.jpg" alt="里龍山步道入口" loading="lazy"></p><p>→回到鯉龍山人文紀念館<br><img src="https://1013399.github.io/image-2/110/1024247680_l.jpg" alt="鯉龍山人文紀念館" loading="lazy"></p><h2 id="走走資訊" tabindex="-1"><a class="header-anchor" href="#走走資訊" aria-hidden="true">#</a> 走走資訊</h2><div class="hint-container info"><p class="hint-container-title">相關信息</p><p>走走時間：約6小時(含午餐、休息、泡茶)</p><p>高低落差：約820公尺</p></div><h2 id="參考地圖" tabindex="-1"><a class="header-anchor" href="#參考地圖" aria-hidden="true">#</a> 參考地圖</h2><p>(裁剪過原圖)</p>',48),c={href:"http://www.keepon.com.tw/DiscussLoad.aspx?code=314B5CF9AEC3A19113F6CAA6F539A662A943A92CADF95654",target:"_blank",rel:"noopener noreferrer"},d=i("figure",null,[i("img",{src:"https://1013399.github.io/image-2/110/1024247731_l.jpg",alt:"里龍山步道、里龍山 參考地圖",tabindex:"0",loading:"lazy"}),i("figcaption",null,"里龍山步道、里龍山 參考地圖")],-1);function _(u,j){const t=p("ExternalLinkIcon");return o(),r("div",null,[m,e(" more "),i("p",null,[a("(資料摘自："),i("a",n,[a("林務局全球資訊網"),g(t)]),a(")")]),b,i("p",null,[a("原圖連結→"),i("a",c,[a("登山補給站-brownlee"),g(t)])]),d])}const f=l(h,[["render",_],["__file","post-110-2014-04-03.html.vue"]]);export{f as default};
