import{_ as l}from"./plugin-vue_export-helper-c27b6911.js";import{r as g,o as p,c as o,a as r,b as i,e as t,d as s,f as e}from"./app-157980c9.js";const h={},n=i("p",null,[i("strong",null,"女仍山"),t("，又稱女乃山，海拔804公尺，位於屏東縣獅子鄉雙流國家森林遊樂區附近，有顆三等三角點No.4243，山頂可俯瞰楓港溪出海口。登山路徑需數度過溪，以穿著雨鞋為宜，雨季造訪需注意螞蝗、硬蜱及蛇類出沒，該山名列台灣小百岳No.079。")],-1),m=i("p",null,"今日參加大台中登山隊，屏東附近3座小百岳(女仍、萬里得、里龍)2天行程，遇見久違的老山友及新朋友，走來輕鬆愉快、忘記途中的疲累。",-1),c=e('<p>走走日期： 2014/03/29</p><h2 id="行車路線" tabindex="-1"><a class="header-anchor" href="#行車路線" aria-hidden="true">#</a> 行車路線</h2><p>國道3號南下接屏鵝公路續行，在楓港前轉台9線(南迴公路)往台東方向，於台9線463.5Km抵達伊屯橋，於橋旁路邊停車。</p><h2 id="走走路線" tabindex="-1"><a class="header-anchor" href="#走走路線" aria-hidden="true">#</a> 走走路線</h2><p>→女仍山登山口，伊屯橋旁<br><img src="https://1013399.github.io/image-2/111/1024286386_l.jpg" alt="女仍山登山口，伊屯橋旁" loading="lazy"></p><p>→循水泥路緩上，經過民宅<br><img src="https://1013399.github.io/image-2/111/1024247116_l.jpg" alt="女仍山" loading="lazy"></p><p>→水泥路終止，接石板步道<br><img src="https://1013399.github.io/image-2/111/1024247124_l.jpg" alt="女仍山" loading="lazy"></p><p>→山蘇工寮，右彎續行<br><img src="https://1013399.github.io/image-2/111/1024247129_l.jpg" alt="女仍山" loading="lazy"></p><p>→拉繩上陡坡<br><img src="https://1013399.github.io/image-2/111/1024247134_l.jpg" alt="女仍山" loading="lazy"></p><p>→接土石產道，取右續行<br><img src="https://1013399.github.io/image-2/111/1024247138_l.jpg" alt="女仍山" loading="lazy"></p><p>→經過數段崩塌路段<br><img src="https://1013399.github.io/image-2/111/1024247151_l.jpg" alt="女仍山" loading="lazy"></p><p>→叉路，右下往溪谷，取左續行<br><img src="https://1013399.github.io/image-2/111/1024247154_l.jpg" alt="女仍山" loading="lazy"></p><p>→下切第一處溪谷，在此午餐、休息<br><img src="https://1013399.github.io/image-2/111/1024247158_l.jpg" alt="女仍山" loading="lazy"></p><p>→餐畢續行，拉繩陡上<br><img src="https://1013399.github.io/image-2/111/1024247164_l.jpg" alt="女仍山" loading="lazy"></p><p>→下切第二處溪谷<br><img src="https://1013399.github.io/image-2/111/1024247167_l.jpg" alt="女仍山" loading="lazy"></p><p>→沿著溪谷，踏石溯溪而上<br><img src="https://1013399.github.io/image-2/111/1024247172_l.jpg" alt="女仍山" loading="lazy"></p><p>→溪旁大榕樹<br><img src="https://1013399.github.io/image-2/111/1024247175_l.jpg" alt="女仍山" loading="lazy"></p><p>→於大榕樹附近，取左離開溪谷，進入山徑<br><img src="https://1013399.github.io/image-2/111/1024247179_l.jpg" alt="女仍山" loading="lazy"></p><p>→再度溯溪而上<br><img src="https://1013399.github.io/image-2/111/1024247183_l.jpg" alt="女仍山" loading="lazy"></p><p>→溪谷盡頭，取右上<br><img src="https://1013399.github.io/image-2/111/1024247190_l.jpg" alt="女仍山" loading="lazy"></p><p>→拉繩陡上<br><img src="https://1013399.github.io/image-2/111/1024247195_l.jpg" alt="女仍山" loading="lazy"></p><p>→一路陡上<br><img src="https://1013399.github.io/image-2/111/1024247200_l.jpg" alt="女仍山" loading="lazy"></p><p>→上至平坦空地，休息<br><img src="https://1013399.github.io/image-2/111/1024247205_l.jpg" alt="女仍山" loading="lazy"></p><p>→休息後續行<br><img src="https://1013399.github.io/image-2/111/1024247209_l.jpg" alt="女仍山" loading="lazy"></p><p>→繼續拉繩陡上<br><img src="https://1013399.github.io/image-2/111/1024247212_l.jpg" alt="女仍山" loading="lazy"></p><p>→上至尾稜<br><img src="https://1013399.github.io/image-2/111/1024247215_l.jpg" alt="女仍山" loading="lazy"></p><p>→驚蟄過後，蛇類已經出來活動<br><img src="https://1013399.github.io/image-2/111/1024247222_l.jpg" alt="女仍山" loading="lazy"></p><p>→鑽行於平緩、草密的路徑<br><img src="https://1013399.github.io/image-2/111/1024247228_l.jpg" alt="女仍山" loading="lazy"></p><p>→已可見前方的廢棄反射板<br><img src="https://1013399.github.io/image-2/111/1024247237_l.jpg" alt="女仍山" loading="lazy"></p><p>→路徑又再趨陡<br><img src="https://1013399.github.io/image-2/111/1024247252_l.jpg" alt="女仍山" loading="lazy"></p><p>→到達廢棄反射板<br><img src="https://1013399.github.io/image-2/111/1024247255_l.jpg" alt="女仍山" loading="lazy"></p><p>→繞過反射板續行<br><img src="https://1013399.github.io/image-2/111/1024247263_l.jpg" alt="女仍山" loading="lazy"></p><p>→到達女仍山山頂<br><img src="https://1013399.github.io/image-2/111/1024247269_l.jpg" alt="女仍山" loading="lazy"></p><p>→女仍山，三等三角點No.4243<br><img src="https://1013399.github.io/image-2/111/1024247276_l.jpg" alt="女仍山" loading="lazy"></p><p>→拍張攻頂合照後，原路下山<br><img src="https://1013399.github.io/image-2/111/1024247282_l.jpg" alt="女仍山" loading="lazy"></p><p>→回到第一處溪谷<br><img src="https://1013399.github.io/image-2/111/1024247286_l.jpg" alt="女仍山" loading="lazy"></p><p>→回到土石產道叉路，記得取左回，勿直行<br><img src="https://1013399.github.io/image-2/111/1024247289_l.jpg" alt="女仍山" loading="lazy"></p><p>→回到登山口停車處<br><img src="https://1013399.github.io/image-2/111/1024247293_l.jpg" alt="女仍山 登山口停車處" loading="lazy"></p><h2 id="走走資訊" tabindex="-1"><a class="header-anchor" href="#走走資訊" aria-hidden="true">#</a> 走走資訊</h2><div class="hint-container info"><p class="hint-container-title">相關信息</p><p>走走時間：約6小時(含午餐、休息)</p><p>高低落差：約660公尺</p></div><h2 id="參考地圖" tabindex="-1"><a class="header-anchor" href="#參考地圖" aria-hidden="true">#</a> 參考地圖</h2><p>(裁剪過原圖)</p>',42),b={href:"http://www.keepon.com.tw/DiscussLoad.aspx?code=314B5CF9AEC3A19113F6CAA6F539A6621F69A753B64927D9",target:"_blank",rel:"noopener noreferrer"},d=i("br",null,null,-1),_=i("img",{src:"https://1013399.github.io/image-2/111/1024247357_l.jpg",alt:"女仍山 參考地圖",loading:"lazy"},null,-1),u=i("p",null,[t("→上車前往墾丁逛大街"),i("br"),t(" →到達墾丁大街，剛好晚間6點，隊長帶我們去摸一顆飯店內的基石"),i("br"),t("   統茂高山青飯店，位於墾丁國家公園大門對面，入內與櫃檯人員打聲招呼即可"),i("br"),i("img",{src:"https://1013399.github.io/image-2/111/1024247302_l.jpg",alt:"墾丁 統茂高山青飯店",loading:"lazy"})],-1),j=i("p",null,[t("→墾丁省政府圖根補點"),i("br"),i("img",{src:"https://1013399.github.io/image-2/111/1024247304_l.jpg",alt:"墾丁省政府圖根補點",loading:"lazy"})],-1),y=i("p",null,"→逛完墾丁大街及晚餐，夜宿出火民宿",-1);function z(f,x){const a=g("ExternalLinkIcon");return p(),o("div",null,[n,m,r(" more "),c,i("p",null,[t("原圖連結→"),i("a",b,[t("登山補給站-brownlee"),s(a)]),d,_]),u,j,y])}const A=l(h,[["render",z],["__file","post-111-2014-04-02.html.vue"]]);export{A as default};
