import{_ as l}from"./plugin-vue_export-helper-c27b6911.js";import{r as p,o,c as r,a as e,b as i,f as t,d as g,e as s}from"./app-6d4d6805.js";const h={},n=i("p",null,[i("strong",null,"北得拉曼步道"),t("，位於新竹尖石水田部落的北得拉曼山區，目前有台灣罕見的中低海拔巨木，多為紅檜與扁柏，大約僅存一、二百棵，其中四棵巨木已被林務局公開讓人參觀，這些巨木因為樹形分叉、扭曲，未被砍伐而得以倖存。這4棵巨木並非台灣最大、最高或最古老的檜木，但因地處海拔不高讓人們極易親近，顯得格外可貴。")],-1),m={href:"http://slaq.hoto.com.tw/photo.php",target:"_blank",rel:"noopener noreferrer"},b=s('<p>從水田林道支線登山口通往這些參天大樹的步道，全長約2.6公里，路程適中，每隔100公尺設置里程指示牌，指標清楚；一趟來回大概得花上3至4小時，沿途植物、景觀豐富，上坡路段雖多卻不難走。</p><p>2008/10/18曾造訪過北得拉曼步道，水田林道支線後段約3.3公里的土石路崎嶇難行，以徒步方式花了約一小時才到達登山口，今日參加公司登山社活動，四部中巴直接開到登山口，省了體力與時間，走來較為輕鬆。北得拉曼步道，也是登尖石鳥嘴山的路線之一，兩次來訪，都未上鳥嘴山。</p><p>走走日期：2014/03/15</p><h2 id="行車路線" tabindex="-1"><a class="header-anchor" href="#行車路線" aria-hidden="true">#</a> 行車路線</h2><p>竹東走台3線往橫山，過67K後右轉120縣道往內灣，經過內灣之後續往尖石鄉，至120縣道35.4K時，左轉新樂大橋，進入水田道路(竹58鄉道)，前行至約竹58鄉道1.1Km附近，再右後轉水田林道支線，沿主線約行4Km轉土石路(低底盤轎車不宜再往前開)，續行土石路到登山口前中巴迴轉處下車。</p><h2 id="走走路線" tabindex="-1"><a class="header-anchor" href="#走走路線" aria-hidden="true">#</a> 走走路線</h2><p>→中巴迴轉處，距離登山口已不遠了<br><img src="https://1013399.github.io/image-2/112/1022674557_l.jpg" alt="北得拉曼步道" loading="lazy"></p><p>→北得拉曼步道登山口<br><img src="https://1013399.github.io/image-2/112/1022674577_l.jpg" alt="北得拉曼步道" loading="lazy"></p><p>→登山口旁路線導覽圖<br><img src="https://1013399.github.io/image-2/112/1022674584_l.jpg" alt="北得拉曼步道" loading="lazy"></p><p>→依慣例，出發前拍張大合照<br><img src="https://1013399.github.io/image-2/112/1022674591_l.jpg" alt="北得拉曼步道" loading="lazy"></p><p>→出發，循序進入步道往上行<br><img src="https://1013399.github.io/image-2/112/1022674600_l.jpg" alt="北得拉曼步道" loading="lazy"></p><p>→0.1K里程木樁，每100公尺設一根<br><img src="https://1013399.github.io/image-2/112/1022674606_l.jpg" alt="北得拉曼步道" loading="lazy"></p><p>→0.6K里程木樁，好漢坡開始，步道轉陡<br><img src="https://1013399.github.io/image-2/112/1022674611_l.jpg" alt="北得拉曼步道" loading="lazy"></p><p>→之字形陡上<br><img src="https://1013399.github.io/image-2/112/1022674619_l.jpg" alt="北得拉曼步道" loading="lazy"></p><p>→0.935K休息區，休息<br><img src="https://1013399.github.io/image-2/112/1022674628_l.jpg" alt="北得拉曼步道" loading="lazy"></p><p>→1.0K叉路，右側山徑應可往拉庫山，續直上<br><img src="https://1013399.github.io/image-2/112/1022674635_l.jpg" alt="北得拉曼步道" loading="lazy"></p><p>→大石叉路，右側山徑不知去向，直行跨石而過<br><img src="https://1013399.github.io/image-2/112/1022674642_l.jpg" alt="北得拉曼步道" loading="lazy"></p><p>→抵達1.2K回音谷岩稜，右往拉庫山(水田山)，取左行<br><img src="https://1013399.github.io/image-2/112/1022674658_l.jpg" alt="北得拉曼步道" loading="lazy"></p><p>→於岩稜上俯瞰煤源部落<br><img src="https://1013399.github.io/image-2/112/1022674664_l.jpg" alt="北得拉曼步道" loading="lazy"></p><p>→仰望鳥嘴山<br><img src="https://1013399.github.io/image-2/112/1022674671_l.jpg" alt="北得拉曼步道" loading="lazy"></p><p>→過岩稜後，踩簡易木梯陡下<br><img src="https://1013399.github.io/image-2/112/1022674682_l.jpg" alt="北得拉曼步道" loading="lazy"></p><p>→下到低鞍<br><img src="https://1013399.github.io/image-2/112/1022674690_l.jpg" alt="北得拉曼步道" loading="lazy"></p><p>→過低鞍後，繼續陡上，經過1.3K里程木樁<br><img src="https://1013399.github.io/image-2/112/1022674707_l.jpg" alt="北得拉曼步道" loading="lazy"></p><p>→叉路，左右皆可，直上會經過1442峰(田中山)，今日取右繞腰路<br><img src="https://1013399.github.io/image-2/112/1022674727_l.jpg" alt="北得拉曼步道" loading="lazy"></p><p>→叉路在1.4K會合，進入杜鵑林<br><img src="https://1013399.github.io/image-2/112/1022674756_l.jpg" alt="北得拉曼步道" loading="lazy"></p><p>→經過箭竹、杜鵑林，一路踩在平緩柔軟的地毯路，非常舒服<br><img src="https://1013399.github.io/image-2/112/1022674767_l.jpg" alt="北得拉曼步道" loading="lazy"></p><p>→到達1.6K環形叉路，設有導覽圖，右下往2、3、4號神木，取直上<br><img src="https://1013399.github.io/image-2/112/1022674775_l.jpg" alt="北得拉曼步道" loading="lazy"></p><p>→叉路，直上往鳥嘴山，取右往1號神木<br><img src="https://1013399.github.io/image-2/112/1022674787_l.jpg" alt="北得拉曼步道" loading="lazy"></p><p>→大石，石頭下有祭拜痕跡，續直行<br><img src="https://1013399.github.io/image-2/112/1022674795_l.jpg" alt="北得拉曼步道" loading="lazy"></p><p>→開始出現巨木<br><img src="https://1013399.github.io/image-2/112/1022674801_l.jpg" alt="北得拉曼步道" loading="lazy"></p><p>→1.8K巨木，此處約為步道最高點<br><img src="https://1013399.github.io/image-2/112/1022674809_l.jpg" alt="北得拉曼步道" loading="lazy"></p><p>→過了1.8K，步道開始往下<br><img src="https://1013399.github.io/image-2/112/1022674817_l.jpg" alt="北得拉曼步道" loading="lazy"></p><p>→北得拉曼瀑布<br><img src="https://1013399.github.io/image-2/112/1022674827_l.jpg" alt="北得拉曼步道" loading="lazy"></p><p>→1號神木，又稱飛鼠神木，位於步道2.1K處<br><img src="https://1013399.github.io/image-2/112/1022674834_l.jpg" alt="北得拉曼步道" loading="lazy"></p><p>→2.4K叉路，右回1.6K環形叉路，取左往2、3、4號神木<br><img src="https://1013399.github.io/image-2/112/1022674847_l.jpg" alt="北得拉曼步道" loading="lazy"></p><p>→越過溪谷亂石<br><img src="https://1013399.github.io/image-2/112/1022674852_l.jpg" alt="北得拉曼步道" loading="lazy"></p><p>→4號神木，位於木棧道起點旁<br><img src="https://1013399.github.io/image-2/112/1022674861_l.jpg" alt="北得拉曼步道" loading="lazy"></p><p>→3號神木，續行木棧道往上<br><img src="https://1013399.github.io/image-2/112/1022674871_l.jpg" alt="北得拉曼步道" loading="lazy"></p><p>→2號神木<br><img src="https://1013399.github.io/image-2/112/1022674879_l.jpg" alt="北得拉曼步道" loading="lazy"></p><p>→2號神木附近發現闊葉根結蘭<br><img src="https://1013399.github.io/image-2/112/1022674893_l.jpg" alt="北得拉曼步道" loading="lazy"></p><p>→原路退回，回到2.4K叉路，續直行，經過崩塌處<br><img src="https://1013399.github.io/image-2/112/1022674910_l.jpg" alt="北得拉曼步道" loading="lazy"></p><p>→又見漂亮的闊葉根結蘭<br><img src="https://1013399.github.io/image-2/112/1022674922_l.jpg" alt="北得拉曼步道" loading="lazy"></p><p>→拉繩踩木梯<br><img src="https://1013399.github.io/image-2/112/1022674930_l.jpg" alt="北得拉曼步道" loading="lazy"></p><p>→過木橋<br><img src="https://1013399.github.io/image-2/112/1022674941_l.jpg" alt="北得拉曼步道" loading="lazy"></p><p>→回到1.6K環形叉路，休息、午餐<br><img src="https://1013399.github.io/image-2/112/1022674948_l.jpg" alt="北得拉曼步道" loading="lazy"></p><p>→餐畢，原路回，三遇根結蘭<br><img src="https://1013399.github.io/image-2/112/1022674958_l.jpg" alt="北得拉曼步道" loading="lazy"></p><p>→回到登山口<br><img src="https://1013399.github.io/image-2/112/1022674966_l.jpg" alt="北得拉曼步道" loading="lazy"></p><p>→回到中巴停車處</p><h2 id="走走資訊" tabindex="-1"><a class="header-anchor" href="#走走資訊" aria-hidden="true">#</a> 走走資訊</h2><div class="hint-container info"><p class="hint-container-title">相關信息</p><p>走走時間：約4小時(含休息、午餐)</p><p>高低落差：約400公尺</p></div><h2 id="參考地圖" tabindex="-1"><a class="header-anchor" href="#參考地圖" aria-hidden="true">#</a> 參考地圖</h2>',51),c={href:"http://www.tonyhuang39.com/tony0605/tony0605.html",target:"_blank",rel:"noopener noreferrer"},d=i("br",null,null,-1),_=i("img",{src:"https://1013399.github.io/image-2/112/1022675288_l.jpg",alt:"北得拉曼步道 參考地圖",loading:"lazy"},null,-1);function u(y,j){const a=p("ExternalLinkIcon");return o(),r("div",null,[n,e(" more "),i("p",null,[t("(資料摘自："),i("a",m,[t("尖石鄉甕碧潭生態文化保護協會"),g(a)]),t(")")]),b,i("p",null,[t("原圖連結→"),i("a",c,[t("Tony的自然人文旅記"),g(a)]),d,_])])}const f=l(h,[["render",u],["__file","post-112-2014-03-19.html.vue"]]);export{f as default};
