import{_ as g}from"./plugin-vue_export-helper-c27b6911.js";import{r as l,o as p,c as o,a as r,b as i,e as a,d as e,f as s}from"./app-e90ae243.js";const h={},n=i("p",null,[i("strong",null,"紅頭山"),a("，海拔552公尺，位於台東縣蘭嶼鄉椰油村，山頂立有一等三角點及三等三角點No.696，因山林投、白背芒草密生，視野欠佳，紅頭山擁有近似熱帶雨林的獨特生態，是蘭嶼的最高峰，該山名列台灣小百岳No.097。")],-1),m=s('<p>走走日期：2014/03/02</p><h2 id="行車路線" tabindex="-1"><a class="header-anchor" href="#行車路線" aria-hidden="true">#</a> 行車路線</h2><p>從椰油灣民宿騎乘機車，行駛環島公路往東行，到達椰油國小路旁停車，登山口就在椰油國小後方。</p><h2 id="走走路線" tabindex="-1"><a class="header-anchor" href="#走走路線" aria-hidden="true">#</a> 走走路線</h2><p>→紅頭山登山口，穿雨鞋出發<br><img src="https://1013399.github.io/image-2/114/1021719601_l.jpg" alt="蘭嶼 紅頭山" loading="lazy"></p><p>→行約十餘公尺，經過水泥水塔<br><img src="https://1013399.github.io/image-2/114/1021719611_l.jpg" alt="蘭嶼 紅頭山" loading="lazy"></p><p>→第一處水芋田，沿著芋田右邊走<br><img src="https://1013399.github.io/image-2/114/1021719621_l.jpg" alt="蘭嶼 紅頭山" loading="lazy"></p><p>回望饅頭岩<br><img src="https://1013399.github.io/image-2/114/1021719631_l.jpg" alt="蘭嶼 紅頭山" loading="lazy"></p><p>→過了水芋田遇工寮，取右側進入山徑<br><img src="https://1013399.github.io/image-2/114/1021719640_l.jpg" alt="蘭嶼 紅頭山" loading="lazy"></p><p>→進入芒草原<br><img src="https://1013399.github.io/image-2/114/1021719646_l.jpg" alt="蘭嶼 紅頭山" loading="lazy"></p><p>→往左望蘭嶼第二高峰－相愛山(海拔511公尺)<br><img src="https://1013399.github.io/image-2/114/1021719655_l.jpg" alt="蘭嶼 紅頭山" loading="lazy"></p><p>→再往回望饅頭岩<br><img src="https://1013399.github.io/image-2/114/1021719659_l.jpg" alt="蘭嶼 紅頭山" loading="lazy"></p><p>→經過大片矮芒草區<br><img src="https://1013399.github.io/image-2/114/1021719668_l.jpg" alt="蘭嶼 紅頭山" loading="lazy"></p><p>→第二處水芋田<br><img src="https://1013399.github.io/image-2/114/1021719676_l.jpg" alt="蘭嶼 紅頭山" loading="lazy"></p><p>→沿途可見野百合<br><img src="https://1013399.github.io/image-2/114/1021719679_l.jpg" alt="蘭嶼 紅頭山" loading="lazy"></p><p>→第三處水芋田(最後一處)，自此進入樹林<br><img src="https://1013399.github.io/image-2/114/1021719691_l.jpg" alt="蘭嶼 紅頭山" loading="lazy"></p><p>→叉路，右側是不好走的溪谷路徑，不建議走<br><img src="https://1013399.github.io/image-2/114/1021719702_l.jpg" alt="蘭嶼 紅頭山" loading="lazy"></p><p>→取左陡上<br><img src="https://1013399.github.io/image-2/114/1021719708_l.jpg" alt="蘭嶼 紅頭山" loading="lazy"></p><p>→拉繩陡上亂石溪溝<br><img src="https://1013399.github.io/image-2/114/1021719715_l.jpg" alt="蘭嶼 紅頭山" loading="lazy"></p><p>→大板根，是蘭嶼原住民打造拼板舟的材料<br><img src="https://1013399.github.io/image-2/114/1021719733_l.jpg" alt="蘭嶼 紅頭山" loading="lazy"></p><p>→已遭切割的大板根<br><img src="https://1013399.github.io/image-2/114/1021719741_l.jpg" alt="蘭嶼 紅頭山" loading="lazy"></p><p>→大榕樹<br><img src="https://1013399.github.io/image-2/114/1021719748_l.jpg" alt="蘭嶼 紅頭山" loading="lazy"></p><p>→再陡上<br><img src="https://1013399.github.io/image-2/114/1021719756_l.jpg" alt="蘭嶼 紅頭山" loading="lazy"></p><p>→叉路，之前的溪谷路徑在此右側接回，取左續行<br><img src="https://1013399.github.io/image-2/114/1021719761_l.jpg" alt="蘭嶼 紅頭山" loading="lazy"></p><p>→上稜線，取右續行<br><img src="https://1013399.github.io/image-2/114/1021719767_l.jpg" alt="蘭嶼 紅頭山" loading="lazy"></p><p>→繼續陡上<br><img src="https://1013399.github.io/image-2/114/1021719772_l.jpg" alt="蘭嶼 紅頭山" loading="lazy"></p><p>→進入山林投區<br><img src="https://1013399.github.io/image-2/114/1021719783_l.jpg" alt="蘭嶼 紅頭山" loading="lazy"></p><p>→路徑愈來愈難行<br><img src="https://1013399.github.io/image-2/114/1021719792_l.jpg" alt="蘭嶼 紅頭山" loading="lazy"></p><p>→在濃密的山林投中鑽行<br><img src="https://1013399.github.io/image-2/114/1021719799_l.jpg" alt="蘭嶼 紅頭山" loading="lazy"></p><p>→最後陡上<br><img src="https://1013399.github.io/image-2/114/1021719805_l.jpg" alt="蘭嶼 紅頭山" loading="lazy"></p><p>→抵達紅頭山<br><img src="https://1013399.github.io/image-2/114/1021719814_l.jpg" alt="蘭嶼 紅頭山" loading="lazy"></p><p>→紅頭山，一等三角點<br><img src="https://1013399.github.io/image-2/114/1021719824_l.jpg" alt="蘭嶼 紅頭山" loading="lazy"></p><p>→紅頭山，三等三角點No.696<br><img src="https://1013399.github.io/image-2/114/1021719828_l.jpg" alt="蘭嶼 紅頭山" loading="lazy"></p><p>→紅頭山山頂展望，休息後原路下山，途中下起大雨<br><img src="https://1013399.github.io/image-2/114/1021720089_l.jpg" alt="蘭嶼 紅頭山" loading="lazy"></p><p>→回到登山口，全身濕透，騎車回民宿沐浴更衣<br><img src="https://1013399.github.io/image-2/114/1021720098_l.jpg" alt="蘭嶼 紅頭山" loading="lazy"></p><h2 id="走走資訊" tabindex="-1"><a class="header-anchor" href="#走走資訊" aria-hidden="true">#</a> 走走資訊</h2><div class="hint-container info"><p class="hint-container-title">相關信息</p><p>走走時間：約4小時</p><p>高低落差：約540公尺</p></div><h2 id="參考地圖" tabindex="-1"><a class="header-anchor" href="#參考地圖" aria-hidden="true">#</a> 參考地圖</h2>',38),b={href:"http://blog.xuite.net/lin6151/blog/166578765",target:"_blank",rel:"noopener noreferrer"},c=i("br",null,null,-1),d=i("img",{src:"https://1013399.github.io/image-2/114/1021720118_l.jpg",alt:"蘭嶼 紅頭山 參考地圖",loading:"lazy"},null,-1);function _(u,j){const t=l("ExternalLinkIcon");return p(),o("div",null,[n,r(" more "),m,i("p",null,[a("原圖連結→"),i("a",b,[a("黃家大嫂部落格"),e(t)]),c,d])])}const f=g(h,[["render",_],["__file","post-114-2014-03-13.html.vue"]]);export{f as default};
