import{_ as g}from"./plugin-vue_export-helper-c27b6911.js";import{r as l,o as p,c as o,a as r,b as i,f as a,d as e,e as s}from"./app-7f62a6ff.js";const h={},n=i("p",null,[i("strong",null,"棚集山"),a("，海拔899公尺，位於屏東縣來義鄉丹林村，有顆三等三角點No.6646，山頂腹地寬廣，沿途山友休息的布帆帳棚群集，為屏東地區熱門的登山路線，與里龍山、笠頂山合稱屏東三大名山，該山名列台灣小百岳No.078。")],-1),m=i("p",null,[i("strong",null,"棚集下山"),a("，海拔835公尺，有顆省政府圖根補點No.342，基點不在山頭，而是位於登山路徑旁，林中無展望。從多叉路口經由棚集下山登棚集山，據山友稱，此段登山路線為中鋼員工愛走的路徑，稱為「中鋼路」。")],-1),b=s('<p>走走日期： 2014/02/28</p><h2 id="行車路線" tabindex="-1"><a class="header-anchor" href="#行車路線" aria-hidden="true">#</a> 行車路線</h2><p>國道3號於竹田系統交流道轉88快速道路往潮州方向，接台1線南下，於通潮路(屏110鄉道)左轉往來義鄉，經過來義高中、來義國小丹林分班至多叉路，取右二叉路(右一叉路往沿山公路)直行到底為第一登山口，續行至第二登山口停車場停車(計次20元)。</p><h2 id="走走路線" tabindex="-1"><a class="header-anchor" href="#走走路線" aria-hidden="true">#</a> 走走路線</h2><p>→丹林第二登山口<br><img src="https://1013399.github.io/image-2/117/1021716491_l.jpg" alt="棚集下山、棚集山" loading="lazy"></p><p>→第一座涼亭<br><img src="https://1013399.github.io/image-2/117/1021716522_l.jpg" alt="棚集下山、棚集山" loading="lazy"></p><p>→經過高壓電塔<br><img src="https://1013399.github.io/image-2/117/1021716529_l.jpg" alt="棚集下山、棚集山" loading="lazy"></p><p>→接土石產道，右往「取火石的故事」，取左行<br><img src="https://1013399.github.io/image-2/117/1021716536_l.jpg" alt="棚集下山、棚集山" loading="lazy"></p><p>→叉路，直行不知去向，依指標取右上<br><img src="https://1013399.github.io/image-2/117/1021716540_l.jpg" alt="棚集下山、棚集山" loading="lazy"></p><p>→第二座涼亭，接土石產道，右下應往丹林第一登山口，取左行<br><img src="https://1013399.github.io/image-2/117/1021716544_l.jpg" alt="棚集下山、棚集山" loading="lazy"></p><p>→緩上土石產道<br><img src="https://1013399.github.io/image-2/117/1021716550_l.jpg" alt="棚集下山、棚集山" loading="lazy"></p><p>→叉路，左小徑不知去向，續直走土石產道<br><img src="https://1013399.github.io/image-2/117/1021716557_l.jpg" alt="棚集下山、棚集山" loading="lazy"></p><p>→叉路，右小徑應是田園停車場登山口上來的路，續直行土石產道<br><img src="https://1013399.github.io/image-2/117/1021716576_l.jpg" alt="棚集下山、棚集山" loading="lazy"></p><p>→休息棚，右也應可往田園停車場登山口，續直行土石產道<br><img src="https://1013399.github.io/image-2/117/1021716581_l.jpg" alt="棚集下山、棚集山" loading="lazy"></p><p>→多叉路口，取左中鋼路，經由棚集下山登棚集山</p><figure><img src="https://1013399.github.io/image-2/117/1021716588_l.jpg" alt="棚集下山、棚集山" tabindex="0" loading="lazy"><figcaption>棚集下山、棚集山</figcaption></figure><p>→叉路，直行不知去向，取右上<br><img src="https://1013399.github.io/image-2/117/1021716597_l.jpg" alt="棚集下山、棚集山" loading="lazy"></p><p>→開始陡上<br><img src="https://1013399.github.io/image-2/117/1021716607_l.jpg" alt="棚集下山、棚集山" loading="lazy"></p><p>→棚集山和笠頂山一樣，休息棚到處林立<br><img src="https://1013399.github.io/image-2/117/1021716616_l.jpg" alt="棚集山" loading="lazy"></p><p>→繼續陡上，出現拉繩<br><img src="https://1013399.github.io/image-2/117/1021716629_l.jpg" alt="棚集下山、棚集山" loading="lazy"></p><p>→休息棚，在此休息、午餐<br><img src="https://1013399.github.io/image-2/117/1021716639_l.jpg" alt="棚集下山、棚集山" loading="lazy"></p><p>→餐畢，繼續拉繩陡上<br><img src="https://1013399.github.io/image-2/117/1021716645_l.jpg" alt="棚集下山、棚集山" loading="lazy"></p><p>→叉路，旁有休息棚，右下往「靜心園」，續直行<br><img src="https://1013399.github.io/image-2/117/1021716654_l.jpg" alt="棚集下山、棚集山" loading="lazy"></p><p>→過簡易木橋<br><img src="https://1013399.github.io/image-2/117/1021716674_l.jpg" alt="棚集下山、棚集山" loading="lazy"></p><p>→一路長陡上<br><img src="https://1013399.github.io/image-2/117/1021716680_l.jpg" alt="棚集下山、棚集山" loading="lazy"><br><img src="https://1013399.github.io/image-2/117/1021716689_l.jpg" alt="棚集下山、棚集山" loading="lazy"><br><img src="https://1013399.github.io/image-2/117/1021716694_l.jpg" alt="棚集下山、棚集山" loading="lazy"></p><p>→到達棚集下山<br><img src="https://1013399.github.io/image-2/117/1021716702_l.jpg" alt="棚集下山" loading="lazy"></p><p>→棚集下山，省政府圖根補點No.342<br><img src="https://1013399.github.io/image-2/117/1021716707_l.jpg" alt="棚集下山" loading="lazy"></p><p>→叉路，左往石板屋，續直行<br><img src="https://1013399.github.io/image-2/117/1021716715_l.jpg" alt="棚集下山" loading="lazy"></p><p>→到達棚集山<br><img src="https://1013399.github.io/image-2/117/1021716720_l.jpg" alt="棚集下山" loading="lazy"></p><p>→棚集山，三等三角點No.6646<br><img src="https://1013399.github.io/image-2/117/1021716733_l.jpg" alt="棚集山" loading="lazy"></p><p>→山頂寬廣，往左續行可往久保山，二條右下山徑皆可回登山口(右側較陡)<br><img src="https://1013399.github.io/image-2/117/1021716741_l.jpg" alt="棚集山" loading="lazy"></p><p>→休息後，取右一叉路陡下，非常陡<br><img src="https://1013399.github.io/image-2/117/1021716753_l.jpg" alt="棚集山" loading="lazy"></p><p>→一路長陡下<br><img src="https://1013399.github.io/image-2/117/1021716758_l.jpg" alt="棚集山" loading="lazy"></p><p>→休息棚，名為「開心亭」，休息後續行<br><img src="https://1013399.github.io/image-2/117/1021716763_l.jpg" alt="棚集山" loading="lazy"></p><p>→接土石產道，左往棚集山(山頂右二叉路下來的路)，取右下<br><img src="https://1013399.github.io/image-2/117/1021716769_l.jpg" alt="棚集山" loading="lazy"></p><p>→叉路，左往山蘇園，取右下<br><img src="https://1013399.github.io/image-2/117/1021716781_l.jpg" alt="棚集山" loading="lazy"></p><p>→沿途偶有展望<br><img src="https://1013399.github.io/image-2/117/1021716785_l.jpg" alt="棚集山" loading="lazy"></p><p>→叉路，左不知去向，續直行<br><img src="https://1013399.github.io/image-2/117/1021716793_l.jpg" alt="棚集山" loading="lazy"></p><p>→途中展望<br><img src="https://1013399.github.io/image-2/117/1021716800_l.jpg" alt="棚集山" loading="lazy"></p><p>→回到多叉路口，續直行<br><img src="https://1013399.github.io/image-2/117/1021716811_l.jpg" alt="棚集山" loading="lazy"></p><p>→回到第二座涼亭，取右下<br><img src="https://1013399.github.io/image-2/117/1021716818_l.jpg" alt="棚集山" loading="lazy"></p><p>→回到第一座涼亭<br><img src="https://1013399.github.io/image-2/117/1021716831_l.jpg" alt="棚集山" loading="lazy"></p><p>→回到登山口停車場<br><img src="https://1013399.github.io/image-2/117/1021716840_l.jpg" alt="棚集下山、棚集山" loading="lazy"></p><h2 id="走走資訊" tabindex="-1"><a class="header-anchor" href="#走走資訊" aria-hidden="true">#</a> 走走資訊</h2><div class="hint-container info"><p class="hint-container-title">相關信息</p><p>走走時間：約4小時(含午餐、休息)</p><p>高低落差：約730公尺</p></div><h2 id="參考地圖" tabindex="-1"><a class="header-anchor" href="#參考地圖" aria-hidden="true">#</a> 參考地圖</h2><p>(裁剪過原圖)</p>',47),c={href:"http://www.keepon.com.tw/DiscussLoad.aspx?code=314B5CF9AEC3A19113F6CAA6F539A66297274B6A30BCE1A2",target:"_blank",rel:"noopener noreferrer"},d=i("figure",null,[i("img",{src:"https://1013399.github.io/image-2/117/1021717017_l.jpg",alt:"棚集下山、棚集山 參考地圖",tabindex:"0",loading:"lazy"}),i("figcaption",null,"棚集下山、棚集山 參考地圖")],-1);function _(u,j){const t=l("ExternalLinkIcon");return p(),o("div",null,[n,m,r(" more "),b,i("p",null,[a("原圖連結→"),i("a",c,[a("登山補給站-brownlee"),e(t)])]),d])}const f=g(h,[["render",_],["__file","post-117-2014-03-08.html.vue"]]);export{f as default};
