import{_ as g}from"./plugin-vue_export-helper-c27b6911.js";import{r as l,o as p,c as o,a as r,b as i,e as a,d as e,f as s}from"./app-cb5d936b.js";const h={},n=i("p",null,[i("strong",null,"八里灣山"),a("，海拔924公尺，位於花蓮縣豐濱鄉與瑞穗鄉交界，原名貓公山，又名貓公富士山，阿美族人稱其為吉拉雅山，是海岸山脈北段的最高峰，擁有一等三角點，山頂祭祀著來自大港口、太巴塱、奇美、馬太鞍等部落的祖靈，每年阿美族人均會上山尋根朝拜，是阿美族人的聖山，也是海岸山脈三雄之一(老大新港山、老二八里灣山、老三都蘭山)，該山名列台灣小百岳No.091。")],-1),m=s('<p>走走日期： 2014/02/23</p><h2 id="行車路線" tabindex="-1"><a class="header-anchor" href="#行車路線" aria-hidden="true">#</a> 行車路線</h2><p>花蓮縣豐濱鄉行駛台11線往南，於台11線50.5Km右轉花51鄉道往八里灣，行約7.8Km抵達八里灣分校(已廢棄)操場停車。</p><h2 id="走走路線" tabindex="-1"><a class="header-anchor" href="#走走路線" aria-hidden="true">#</a> 走走路線</h2><p>→八里灣分校操場停車，著裝(必須穿雨鞋)後出發<br><img src="https://1013399.github.io/image-2/118/1020708369_l.jpg" alt="花蓮 八里灣山" loading="lazy"></p><p>→往堤防方向走<br><img src="https://1013399.github.io/image-2/118/1020708374_l.jpg" alt="花蓮 八里灣山" loading="lazy"></p><p>→以為右前方山頭為八里灣山，當地原住民說八里灣山在更後方<br><img src="https://1013399.github.io/image-2/118/1020708379_l.jpg" alt="花蓮 八里灣山" loading="lazy"></p><p>→下切堤防走攔沙壩至對岸，再沿擋土牆往左行<br><img src="https://1013399.github.io/image-2/118/1020708381_l.jpg" alt="花蓮 八里灣山" loading="lazy"></p><p>→八里灣山登山口<br><img src="https://1013399.github.io/image-2/118/1020708391_l.jpg" alt="花蓮 八里灣山" loading="lazy"></p><p>→經過廢棄崗哨<br><img src="https://1013399.github.io/image-2/118/1020708398_l.jpg" alt="花蓮 八里灣山" loading="lazy"></p><p>→叉路，取直行<br><img src="https://1013399.github.io/image-2/118/1020708403_l.jpg" alt="花蓮 八里灣山" loading="lazy"></p><p>→沿著廢耕地右側山徑行<br><img src="https://1013399.github.io/image-2/118/1020708411_l.jpg" alt="花蓮 八里灣山" loading="lazy"></p><p>→叉路，往右不知去向，續直行<br><img src="https://1013399.github.io/image-2/118/1020708419_l.jpg" alt="花蓮 八里灣山" loading="lazy"></p><p>→叉路，左右皆可，都有路條，取左穿越廢耕地<br><img src="https://1013399.github.io/image-2/118/1020708423_l.jpg" alt="花蓮 八里灣山" loading="lazy"></p><p>→廢耕地雜草多且泥濘(穿雨鞋沒影響)<br><img src="https://1013399.github.io/image-2/118/1020708427_l.jpg" alt="花蓮 八里灣山" loading="lazy"></p><p>→經過三棵大榕樹<br><img src="https://1013399.github.io/image-2/118/1020708430_l.jpg" alt="花蓮 八里灣山" loading="lazy"></p><p>→經過幾處小坍方<br><img src="https://1013399.github.io/image-2/118/1020708435_l.jpg" alt="花蓮 八里灣山" loading="lazy"></p><p>→陡上亂石溪澗<br><img src="https://1013399.github.io/image-2/118/1020708443_l.jpg" alt="花蓮 八里灣山" loading="lazy"></p><p>→離開亂石區接土徑<br><img src="https://1013399.github.io/image-2/118/1020708446_l.jpg" alt="花蓮 八里灣山" loading="lazy"></p><p>→水泥柱界樁<br><img src="https://1013399.github.io/image-2/118/1020708451_l.jpg" alt="花蓮 八里灣山" loading="lazy"></p><p>→接廢棄產道，取右續行<br><img src="https://1013399.github.io/image-2/118/1020708457_l.jpg" alt="花蓮 八里灣山" loading="lazy"></p><p>→越過產道崩塌<br><img src="https://1013399.github.io/image-2/118/1020708462_l.jpg" alt="花蓮 八里灣山" loading="lazy"></p><p>→產道終點，取右下過溪<br><img src="https://1013399.github.io/image-2/118/1020708471_l.jpg" alt="花蓮 八里灣山" loading="lazy"></p><p>→越過兩處小溪<br><img src="https://1013399.github.io/image-2/118/1020708476_l.jpg" alt="花蓮 八里灣山" loading="lazy"></p><p>→過溪後取右上，即達第一處指標、解說牌<br><img src="https://1013399.github.io/image-2/118/1020708480_l.jpg" alt="花蓮 八里灣山" loading="lazy"></p><p>→再過小溪<br><img src="https://1013399.github.io/image-2/118/1020708492_l.jpg" alt="花蓮 八里灣山" loading="lazy"></p><p>→第二處指標、解說牌<br><img src="https://1013399.github.io/image-2/118/1020708506_l.jpg" alt="花蓮 八里灣山" loading="lazy"></p><p>→步道崩塌，沿著坍方右側往上接回步道<br><img src="https://1013399.github.io/image-2/118/1020708509_l.jpg" alt="花蓮 八里灣山" loading="lazy"></p><p>→陡上山溝路徑<br><img src="https://1013399.github.io/image-2/118/1020708515_l.jpg" alt="花蓮 八里灣山" loading="lazy"></p><p>→休息空地，有個茶壺，取左彎續行(需注意，別直行)<br><img src="https://1013399.github.io/image-2/118/1020708518_l.jpg" alt="花蓮 八里灣山" loading="lazy"></p><p>→再經過休息空地<br><img src="https://1013399.github.io/image-2/118/1020708523_l.jpg" alt="花蓮 八里灣山" loading="lazy"></p><p>→第三處指標、解說牌，左往舊部落，取右行<br><img src="https://1013399.github.io/image-2/118/1020708528_l.jpg" alt="花蓮 八里灣山" loading="lazy"></p><p>→此處解說牌較為乾淨、清楚<br><img src="https://1013399.github.io/image-2/118/1020708531_l.jpg" alt="花蓮 八里灣山" loading="lazy"></p><p>→仰望前方的八里灣山<br><img src="https://1013399.github.io/image-2/118/1020708535_l.jpg" alt="花蓮 八里灣山" loading="lazy"></p><p>→高角度陡上<br><img src="https://1013399.github.io/image-2/118/1020708539_l.jpg" alt="花蓮 八里灣山" loading="lazy"></p><p>→上至平緩空地<br><img src="https://1013399.github.io/image-2/118/1020708544_l.jpg" alt="花蓮 八里灣山" loading="lazy"></p><p>→最後攻頂<br><img src="https://1013399.github.io/image-2/118/1020708548_l.jpg" alt="花蓮 八里灣山" loading="lazy"></p><p>→到達八里灣山山頂<br><img src="https://1013399.github.io/image-2/118/1020708551_l.jpg" alt="花蓮 八里灣山" loading="lazy"></p><p>→八里灣山是阿美族人的發祥地<br><img src="https://1013399.github.io/image-2/118/1020708556_l.jpg" alt="花蓮 八里灣山" loading="lazy"></p><p>→山頂祭祀著阿美族各部落的祖靈<br><img src="https://1013399.github.io/image-2/118/1020708563_l.jpg" alt="花蓮 八里灣山" loading="lazy"></p><p>→八里灣山，一等三角點，位於倒塌的鐵塔下，休息後，原路折返<br><img src="https://1013399.github.io/image-2/118/1020708567_l.jpg" alt="花蓮 八里灣山" loading="lazy"></p><p>→回到登山口<br><img src="https://1013399.github.io/image-2/118/1020708572_l.jpg" alt="花蓮 八里灣山" loading="lazy"></p><p>→過攔沙壩後，清洗雨鞋<br> →回到八里灣分校</p><h2 id="走走資訊" tabindex="-1"><a class="header-anchor" href="#走走資訊" aria-hidden="true">#</a> 走走資訊</h2><div class="hint-container info"><p class="hint-container-title">相關信息</p><p>走走時間：約7.5小時(含午餐、休息)</p><p>高低落差：約850公尺</p></div><h2 id="參考地圖" tabindex="-1"><a class="header-anchor" href="#參考地圖" aria-hidden="true">#</a> 參考地圖</h2><p>(裁剪過原圖)</p>',47),b={href:"http://www.keepon.com.tw/DiscussLoad.aspx?code=314B5CF9AEC3A19113F6CAA6F539A662ACA1E2B351E8E754",target:"_blank",rel:"noopener noreferrer"},c=i("br",null,null,-1),d=i("img",{src:"https://1013399.github.io/image-2/118/1020708739_l.jpg",alt:"花蓮 八里灣山",loading:"lazy"},null,-1);function _(u,j){const t=l("ExternalLinkIcon");return p(),o("div",null,[n,r(" more "),m,i("p",null,[a("原圖連結→"),i("a",b,[a("登山補給站-brownlee"),e(t)]),c,d])])}const f=g(h,[["render",_],["__file","post-118-2014-03-05.html.vue"]]);export{f as default};
