import{_ as l}from"./plugin-vue_export-helper-c27b6911.js";import{r as g,o as p,c as o,a as e,b as i,e as t,d as r,f as s}from"./app-39f3f472.js";const n={},h=i("p",null,[i("strong",null,"月眉山"),t("，又名米棧山，海拔614公尺，位於花蓮縣壽豐鄉月眉山步道中間點，屬於海岸山脈北段低海拔森林，是海岸山脈第一座擁有一等三角點的山頭。該山名列台灣小百岳No.090。")],-1),m=i("p",null,[i("strong",null,"月眉山步道"),t("，全長4.8公里，分別有南北兩處入口，步道依循山脈稜線而建，平緩寬廣、視野遼闊、生態資源豐富，沿途可瞭望花東縱谷美景，適合全家健行旅遊。")],-1),c=s('<p>走走日期： 2014/02/22</p><h2 id="行車路線" tabindex="-1"><a class="header-anchor" href="#行車路線" aria-hidden="true">#</a> 行車路線</h2><p>花蓮市行駛台9線往南，於台9線222.5Km左轉，穿越台11線及過了米棧大橋後左轉193縣道(月眉路)，約200公尺(193縣道37.4Km)右轉花38-1鄉道，約行5.1Km後抵達月眉山步道南側入口，於路旁較寬處停車。</p><p>(<strong>需注意</strong>：花38-1鄉道里程是從2.5Km起算，故月眉山步道南側入口約在里程7.6Km處)</p><h2 id="走走路線" tabindex="-1"><a class="header-anchor" href="#走走路線" aria-hidden="true">#</a> 走走路線</h2><p>→月眉山南側步道入口<br><img src="https://1013399.github.io/image-2/119/1020707870_l.jpg" alt="月眉山步道、月眉山" loading="lazy"></p><p>→入口設有解說導覽圖<br><img src="https://1013399.github.io/image-2/119/1020707874_l.jpg" alt="月眉山步道、月眉山" loading="lazy"></p><p>→月眉山步道4.8K木樁，每隔250公尺設一根<br><img src="https://1013399.github.io/image-2/119/1020707876_l.jpg" alt="月眉山步道、月眉山" loading="lazy"></p><p>→過小橋後，一路緩上<br><img src="https://1013399.github.io/image-2/119/1020707881_l.jpg" alt="月眉山步道、月眉山" loading="lazy"></p><p>→步道以土石、枕木鋪設而成，經過4.5K木樁<br><img src="https://1013399.github.io/image-2/119/1020707883_l.jpg" alt="月眉山步道、月眉山" loading="lazy"></p><p>→觀景平台，約在步道4.4K處<br><img src="https://1013399.github.io/image-2/119/1020707887_l.jpg" alt="月眉山步道、月眉山" loading="lazy"></p><p>→往西俯瞰壽豐鄉及壽豐溪<br><img src="https://1013399.github.io/image-2/119/1020707889_l.jpg" alt="月眉山步道、月眉山" loading="lazy"></p><p>→往東遠眺太平洋及水璉村<br><img src="https://1013399.github.io/image-2/119/1020707894_l.jpg" alt="月眉山步道、月眉山" loading="lazy"></p><p>→步道寬大平緩，經過4.25K木樁<br><img src="https://1013399.github.io/image-2/119/1020707899_l.jpg" alt="月眉山步道、月眉山" loading="lazy"></p><p>→依序再經過4K、3.75K、3.5K木樁<br><img src="https://1013399.github.io/image-2/119/1020707902_l.jpg" alt="月眉山步道、月眉山" loading="lazy"></p><p>→觀景平台叉路，位於步道3.4K處，先取左觀景再回步道<br><img src="https://1013399.github.io/image-2/119/1020707906_l.jpg" alt="月眉山步道、月眉山" loading="lazy"></p><p>→叉路口解說牌，月眉山屬脆弱的安山岩，結構鬆散<br><img src="https://1013399.github.io/image-2/119/1020707912_l.jpg" alt="月眉山步道、月眉山" loading="lazy"></p><p>→觀景平台，寬大可供休憩<br><img src="https://1013399.github.io/image-2/119/1020707916_l.jpg" alt="月眉山步道、月眉山" loading="lazy"></p><p>→月眉山是海岸山脈第一座擁有一等三角點的山頭<br><img src="https://1013399.github.io/image-2/119/1020707920_l.jpg" alt="月眉山步道、月眉山" loading="lazy"></p><p>→往西瞭望花東縱谷<br><img src="https://1013399.github.io/image-2/119/1020707924_l.jpg" alt="月眉山步道、月眉山" loading="lazy"></p><p>→往東視野稍差<br><img src="https://1013399.github.io/image-2/119/1020707928_l.jpg" alt="月眉山步道、月眉山" loading="lazy"></p><p>→退回步道續行，經過3.25K木樁<br><img src="https://1013399.github.io/image-2/119/1020707932_l.jpg" alt="月眉山步道、月眉山" loading="lazy"></p><p>→空地，有塊「綠色宣言」告示牌<br><img src="https://1013399.github.io/image-2/119/1020707942_l.jpg" alt="月眉山步道、月眉山" loading="lazy"></p><p>→經過此空地後，步道轉為傳統山路<br><img src="https://1013399.github.io/image-2/119/1020707951_l.jpg" alt="月眉山步道、月眉山" loading="lazy"></p><p>→經過3K木樁<br><img src="https://1013399.github.io/image-2/119/1020707956_l.jpg" alt="月眉山步道、月眉山" loading="lazy"></p><p>→步道轉陡下<br><img src="https://1013399.github.io/image-2/119/1020707962_l.jpg" alt="月眉山步道、月眉山" loading="lazy"></p><p>→可見前方的月眉山山頭<br><img src="https://1013399.github.io/image-2/119/1020707966_l.jpg" alt="月眉山步道、月眉山" loading="lazy"></p><p>→下至低鞍，經過2.75K、2.5K木樁，即到達月眉山山頂<br><img src="https://1013399.github.io/image-2/119/1020707974_l.jpg" alt="月眉山步道、月眉山" loading="lazy"></p><p>→月眉山，一等三角點、一等衛星控制點及解說牌<br><img src="https://1013399.github.io/image-2/119/1020707977_l.jpg" alt="月眉山步道、月眉山" loading="lazy"></p><p>→續行可往北側步道入口<br><img src="https://1013399.github.io/image-2/119/1020707981_l.jpg" alt="月眉山步道、月眉山" loading="lazy"></p><p>→山頂特殊的植物，休息後，原路折返<br><img src="https://1013399.github.io/image-2/119/1020707987_l.jpg" alt="月眉山步道、月眉山" loading="lazy"></p><p>→回到月眉山南側步道入口<br><img src="https://1013399.github.io/image-2/119/1020707991_l.jpg" alt="月眉山步道、月眉山" loading="lazy"></p><h2 id="走走資訊" tabindex="-1"><a class="header-anchor" href="#走走資訊" aria-hidden="true">#</a> 走走資訊</h2><div class="hint-container info"><p class="hint-container-title">相關信息</p><p>走走時間：約1小時40分鐘(含休息)</p><p>高低落差：約150公尺</p></div><h2 id="參考地圖" tabindex="-1"><a class="header-anchor" href="#參考地圖" aria-hidden="true">#</a> 參考地圖</h2><p>(裁剪過原圖)</p>',36),d={href:"http://www.yougoipay.com/kenny/w616/index.htm",target:"_blank",rel:"noopener noreferrer"},b=i("br",null,null,-1),_=i("img",{src:"https://1013399.github.io/image-2/119/1020708044_l.jpg",alt:"月眉山步道、月眉山",loading:"lazy"},null,-1);function u(y,j){const a=g("ExternalLinkIcon");return p(),o("div",null,[h,m,e(" more "),c,i("p",null,[t("原圖連結→"),i("a",d,[t("偶而獨步山林間(蕭郎)"),r(a)]),b,_])])}const f=l(n,[["render",u],["__file","post-119-2014-03-04.html.vue"]]);export{f as default};
