import{_ as g}from"./plugin-vue_export-helper-c27b6911.js";import{r as l,o as p,c as o,a as r,b as i,f as t,d as s,e}from"./app-2f66486a.js";const h={},n=i("p",null,[i("strong",null,"草嶺山"),t("，海拔347公尺，位於桃園大溪慈湖附近，山頂有顆三等三角點No.1010，樹林間無展望。")],-1),m=i("p",null,[i("strong",null,"白石山"),t("，海拔625公尺，位於桃園大溪鎮，山頂有白漆書寫「白石山」三字的一巨大岩塊，因岩塊在陽光照耀下呈白色而得名，山頂展望極佳，可眺望金面山、十三分山，俯瞰大溪地區。")],-1),b=e('<p>走走日期： 2013/01/26</p><h2 id="行車路線" tabindex="-1"><a class="header-anchor" href="#行車路線" aria-hidden="true">#</a> 行車路線</h2><p>今日參加新豐鄉鄉野健行隊活動，搭乘遊覽車於國道1號上湖口交流道轉台66快速道路往大溪，至大溪走台7線往復興鄉，至慈湖停車場下車。</p><h2 id="走走路線" tabindex="-1"><a class="header-anchor" href="#走走路線" aria-hidden="true">#</a> 走走路線</h2><p>→由停車場過白色拱橋進入慈湖紀念雕塑公園<br><img src="https://1013399.github.io/image-2/122/1018212317_l.jpg" alt="慈湖紀念雕塑公園" loading="lazy"></p><p>→穿越公園接柏油路至草嶺山登山口(旁有土地公)<br><img src="https://1013399.github.io/image-2/122/1018212345_l.jpg" alt="草嶺山登山口" loading="lazy"></p><p>→依序進入泥土山徑，一路往上<br><img src="https://1013399.github.io/image-2/122/1018212349_l.jpg" alt="草嶺山" loading="lazy"></p><p>→叉路，右往白石山，左側鐵架下方即為草嶺山基石<br><img src="https://1013399.github.io/image-2/122/1018212355_l.jpg" alt="草嶺山" loading="lazy"></p><p>→草嶺山，三等三角點No.1010<br><img src="https://1013399.github.io/image-2/122/1018212358_l.jpg" alt="草嶺山" loading="lazy"></p><p>→續行緩下山徑<br><img src="https://1013399.github.io/image-2/122/1018212365_l.jpg" alt="草嶺山" loading="lazy"></p><p>→叉路，左下可往三層社區，續直行<br><img src="https://1013399.github.io/image-2/122/1018212369_l.jpg" alt="草嶺山" loading="lazy"></p><p>→陡上再陡下<br><img src="https://1013399.github.io/image-2/122/1018212374_l.jpg" alt="草嶺山" loading="lazy"></p><p>→廢崗哨叉路，左往打鐵寮古道三層段，取直續行<br><img src="https://1013399.github.io/image-2/122/1018212378_l.jpg" alt="草嶺山" loading="lazy"></p><p>→轉為陡上<br><img src="https://1013399.github.io/image-2/122/1018212382_l.jpg" alt="草嶺山" loading="lazy"></p><p>→叉路，左往電塔、石厝坑山，取右續行<br><img src="https://1013399.github.io/image-2/122/1018212387_l.jpg" alt="草嶺山" loading="lazy"></p><p>→又轉為陡下<br><img src="https://1013399.github.io/image-2/122/1018212390_l.jpg" alt="草嶺山" loading="lazy"></p><p>→下至溪谷，沿溪邊而行<br><img src="https://1013399.github.io/image-2/122/1018212394_l.jpg" alt="草嶺山" loading="lazy"></p><p>→建橋芳名紀念石碑，旁有石砌土地公<br><img src="https://1013399.github.io/image-2/122/1018212398_l.jpg" alt="草嶺山 建橋芳名紀念石碑" loading="lazy"></p><p>→踏石跨溪<br><img src="https://1013399.github.io/image-2/122/1018212402_l.jpg" alt="草嶺山" loading="lazy"></p><p>→叉路，右側過溪往東興橋，取直續行<br><img src="https://1013399.github.io/image-2/122/1018212406_l.jpg" alt="草嶺山" loading="lazy"></p><p>→木橋<br><img src="https://1013399.github.io/image-2/122/1018212411_l.jpg" alt="草嶺山" loading="lazy"></p><p>→橋頭左側有叉路可往電塔、石厝、三層，取右過橋<br><img src="https://1013399.github.io/image-2/122/1018212415_l.jpg" alt="草嶺山" loading="lazy"></p><p>→叉路，右往百吉隧道，取左續行<br><img src="https://1013399.github.io/image-2/122/1018212418_l.jpg" alt="草嶺山" loading="lazy"></p><p>→走一段寬大平緩土徑<br><img src="https://1013399.github.io/image-2/122/1018212423_l.jpg" alt="草嶺山" loading="lazy"></p><p>→寬大土徑結束，前有休息椅，取右彎<br><img src="https://1013399.github.io/image-2/122/1018212427_l.jpg" alt="草嶺山" loading="lazy"></p><p>→陡上山徑<br><img src="https://1013399.github.io/image-2/122/1018212432_l.jpg" alt="草嶺山" loading="lazy"></p><p>→叉路，左為白石山腳腰繞路線(也可上白石山)，取右續行<br><img src="https://1013399.github.io/image-2/122/1018212439_l.jpg" alt="草嶺山" loading="lazy"></p><p>→拉繩陡上<br><img src="https://1013399.github.io/image-2/122/1018212443_l.jpg" alt="草嶺山" loading="lazy"></p><p>→白石埤叉路，直行往白石埤出產業道路可接台7線，取左陡上<br><img src="https://1013399.github.io/image-2/122/1018212448_l.jpg" alt="草嶺山 白石埤叉路" loading="lazy"></p><p>→進入白石山岩稜<br><img src="https://1013399.github.io/image-2/122/1018212454_l.jpg" alt="白石山岩稜" loading="lazy"></p><p>→岩稜趨緩，沿途視野佳<br><img src="https://1013399.github.io/image-2/122/1018212458_l.jpg" alt="白石山" loading="lazy"></p><p>→經過白石山透天厝，樹上的「爬山歌」<br><img src="https://1013399.github.io/image-2/122/1018212464_l.jpg" alt="白石山 爬山歌" loading="lazy"></p><p>→白石山山頂，視野極佳<br><img src="https://1013399.github.io/image-2/122/1018212470_l.jpg" alt="白石山山頂" loading="lazy"></p><p>→眺望金面山、十三分山，賞景後原路折返<br><img src="https://1013399.github.io/image-2/122/1018212476_l.jpg" alt="白石山" loading="lazy"></p><p>→回到白石埤叉路，取左往白石埤</p><p>→循稜而下後轉之字型緩下<br><img src="https://1013399.github.io/image-2/122/1018212487_l.jpg" alt="白石山" loading="lazy"></p><p>→叉路，右上不知去向，續往下行<br><img src="https://1013399.github.io/image-2/122/1018212492_l.jpg" alt="白石山" loading="lazy"></p><p>→叉路，旁有方形水池，直行不知去向，取右下<br><img src="https://1013399.github.io/image-2/122/1018212494_l.jpg" alt="白石山" loading="lazy"></p><p>→路徑寬大，像是保線路<br><img src="https://1013399.github.io/image-2/122/1018212499_l.jpg" alt="白石山" loading="lazy"></p><p>→叉路，前方有鐵塔，取左下<br><img src="https://1013399.github.io/image-2/122/1018212502_l.jpg" alt="白石山" loading="lazy"></p><p>→接柏油產道，前方即為白石埤(白石湖)<br><img src="https://1013399.github.io/image-2/122/1018212504_l.jpg" alt="白石山 白石埤(白石湖)" loading="lazy"></p><p>→叉路，右側寬大土路可往東興橋，續直行<br><img src="https://1013399.github.io/image-2/122/1018212508_l.jpg" alt="白石山" loading="lazy"></p><p>→叉路，右往百吉林蔭步道，續直行<br><img src="https://1013399.github.io/image-2/122/1018212511_l.jpg" alt="白石山 百吉林蔭步道" loading="lazy"></p><p>→接台7線，取右回<br><img src="https://1013399.github.io/image-2/122/1018212515_l.jpg" alt="白石山" loading="lazy"></p><p>→百吉隧道，穿越馬路往舊百吉隧道(行人專用)<br><img src="https://1013399.github.io/image-2/122/1018212518_l.jpg" alt="白石山" loading="lazy"></p><p>→進入舊百吉隧道<br><img src="https://1013399.github.io/image-2/122/1018212523_l.jpg" alt="白石山 舊百吉隧道" loading="lazy"></p><p>→出隧道後，前方即為總督府古道口，續直行台7線<br><img src="https://1013399.github.io/image-2/122/1018212527_l.jpg" alt="白石山 總督府古道口" loading="lazy"></p><p>→經過慈湖陵寢<br><img src="https://1013399.github.io/image-2/122/1018212529_l.jpg" alt="白石山 慈湖陵寢" loading="lazy"></p><p>→回到慈湖停車場<br><img src="https://1013399.github.io/image-2/122/1018212536_l.jpg" alt="慈湖停車場" loading="lazy"></p><h2 id="走走資訊" tabindex="-1"><a class="header-anchor" href="#走走資訊" aria-hidden="true">#</a> 走走資訊</h2><div class="hint-container info"><p class="hint-container-title">相關信息</p><p>走走時間：約4小時(含休息、午餐)</p><p>高低落差：約380公尺(不含上下起伏)</p></div><h2 id="參考地圖" tabindex="-1"><a class="header-anchor" href="#參考地圖" aria-hidden="true">#</a> 參考地圖</h2>',52),c={href:"http://blog.xuite.net/fuli19610302/blog/122278558",target:"_blank",rel:"noopener noreferrer"},d=i("br",null,null,-1),_=i("img",{src:"https://1013399.github.io/image-2/122/1018212573_l.jpg",alt:"草嶺山、白石山 參考地圖",loading:"lazy"},null,-1);function u(j,y){const a=l("ExternalLinkIcon");return p(),o("div",null,[n,m,r(" more "),b,i("p",null,[t("原圖連結→"),i("a",c,[t("山影狐蹤部落格"),s(a)]),d,_])])}const x=g(h,[["render",u],["__file","post-122-2014-01-28.html.vue"]]);export{x as default};
