import{_ as g}from"./plugin-vue_export-helper-c27b6911.js";import{r as l,o as p,c as o,a as r,b as i,e as a,d as s,f as e}from"./app-272e1b70.js";const h={},n=i("p",null,[i("strong",null,"尾寮山"),a("，海拔1427公尺，位於屏東縣三地門鄉與高雄市茂林區交界處，山頂有顆二等三角點No.1653；尾寮山登山步道全長約9.5Km，前段產業道路長約6.2Km、爬升約630公尺，後段陡上山徑長約3.3Km、爬升約650公尺，總爬升合計約1280公尺，一般山友以此山當作攀登大山的行前訓練，該山名列台灣小百岳No.073。")],-1),m=i("p",null,[i("strong",null,"尾寮山西北峰"),a("，位於觀景亭與尾寮山步道6.5K中間小山頭，有顆省府圖根補點No.385，基石就在步道旁，無視野。")],-1),b=e('<p>尾寮山兩個登山口，一個從口社村的沙溪林道23.5K處進入，較少山友利用，大部份山友選擇185縣道(沿山公路)起點旁的產業道路起登，也有山友將車開至產道3.5Km處再步行，但產道狹窄崎嶇，並不建議。</p><p>走走日期：2013/12/29</p><h2 id="行車路線" tabindex="-1"><a class="header-anchor" href="#行車路線" aria-hidden="true">#</a> 行車路線</h2><p>茂林活動中心出發，回台27線直行過大津橋，過橋後停車於台27線與185縣道叉路附近。</p><h2 id="走走路線" tabindex="-1"><a class="header-anchor" href="#走走路線" aria-hidden="true">#</a> 走走路線</h2><p>→尾寮山步道登山口，天色未亮<br><img src="https://1013399.github.io/image-2/123/1014664520_l.jpg" alt="尾寮山步道登山口" loading="lazy"></p><p>→循著產業道路往上走<br><img src="https://1013399.github.io/image-2/123/1014664523_l.jpg" alt="尾寮山西北峰、尾寮山" loading="lazy"></p><p>→叉路，左上可往大津山、沿山公路，續直行產道<br><img src="https://1013399.github.io/image-2/123/1014664527_l.jpg" alt="尾寮山西北峰、尾寮山" loading="lazy"></p><p>→叉路，直行可往集義亭、尾寮山前峰，取右續行產道<br><img src="https://1013399.github.io/image-2/123/1014664529_l.jpg" alt="尾寮山西北峰、尾寮山" loading="lazy"></p><p>→指標下方登山導覽圖<br><img src="https://1013399.github.io/image-2/123/1014664535_l.jpg" alt="尾寮山西北峰、尾寮山" loading="lazy"></p><p>→經過觀音小廟<br><img src="https://1013399.github.io/image-2/123/1014664539_l.jpg" alt="尾寮山西北峰、尾寮山" loading="lazy"></p><p>→叉路，左往集義亭，取右續行產道<br><img src="https://1013399.github.io/image-2/123/1014664543_l.jpg" alt="尾寮山西北峰、尾寮山" loading="lazy"></p><p>→沿途經過尾寮山步道2.5K、3K、3.5K里程樁(看到的第一根里程樁為2.5K)<br><img src="https://1013399.github.io/image-2/123/1014664545_l.jpg" alt="尾寮山西北峰、尾寮山" loading="lazy"></p><p>→叉路，左前方有民宅，直行為捷徑(約在4.1K接回步道)，取右續行<br><img src="https://1013399.github.io/image-2/123/1014664546_l.jpg" alt="尾寮山西北峰、尾寮山" loading="lazy"></p><p>→叉路，直行往大津瀑布，取左續行<br><img src="https://1013399.github.io/image-2/123/1014664550_l.jpg" alt="尾寮山西北峰、尾寮山" loading="lazy"></p><p>→經過4K、4.5K里程樁後，右側有捷徑(約在4.8K接回步道)，取直續行，有山友在此紮營<br><img src="https://1013399.github.io/image-2/123/1014664552_l.jpg" alt="尾寮山西北峰、尾寮山" loading="lazy"></p><p>→經過5K里程樁後，左側有捷徑(約在5.4K接回步道)，續直行，再經過5.5K、6K里程樁<br><img src="https://1013399.github.io/image-2/123/1014664557_l.jpg" alt="尾寮山西北峰、尾寮山" loading="lazy"></p><p>→產業道路終止(約在6.2K)<br><img src="https://1013399.github.io/image-2/123/1014664558_l.jpg" alt="尾寮山西北峰、尾寮山" loading="lazy"></p><p>→取右進入竹林，拉繩陡上<br><img src="https://1013399.github.io/image-2/123/1014664563_l.jpg" alt="尾寮山西北峰、尾寮山" loading="lazy"></p><p>→觀景亭，休息賞景<br><img src="https://1013399.github.io/image-2/123/1014664567_l.jpg" alt="尾寮山西北峰、尾寮山" loading="lazy"></p><p>→俯瞰茂林和萬山部落，霾害影響，一片灰濛濛<br><img src="https://1013399.github.io/image-2/123/1014664569_l.jpg" alt="尾寮山西北峰、尾寮山" loading="lazy"></p><p>→續行陡上<br><img src="https://1013399.github.io/image-2/123/1014664572_l.jpg" alt="尾寮山西北峰、尾寮山" loading="lazy"></p><p>→尾寮山西北峰，省府圖根補點No.385<br><img src="https://1013399.github.io/image-2/123/1014664574_l.jpg" alt="尾寮山西北峰、尾寮山" loading="lazy"></p><p>→經過6.5K里程樁<br><img src="https://1013399.github.io/image-2/123/1014664576_l.jpg" alt="尾寮山西北峰、尾寮山" loading="lazy"></p><p>→休息區<br><img src="https://1013399.github.io/image-2/123/1014664579_l.jpg" alt="尾寮山西北峰、尾寮山" loading="lazy"></p><p>→經過休息區不久繼續陡上<br><img src="https://1013399.github.io/image-2/123/1014664582_l.jpg" alt="尾寮山西北峰、尾寮山" loading="lazy"></p><p>→經過7K里程樁<br><img src="https://1013399.github.io/image-2/123/1014664583_l.jpg" alt="尾寮山西北峰、尾寮山" loading="lazy"></p><p>→一路陡上<br><img src="https://1013399.github.io/image-2/123/1014664587_l.jpg" alt="尾寮山西北峰、尾寮山" loading="lazy"></p><p>→保安林水泥柱，沿途共看見3根<br><img src="https://1013399.github.io/image-2/123/1014664590_l.jpg" alt="尾寮山西北峰、尾寮山" loading="lazy"></p><p>→經過7.5K里程樁<br><img src="https://1013399.github.io/image-2/123/1014664591_l.jpg" alt="尾寮山西北峰、尾寮山" loading="lazy"></p><p>→休息地<br><img src="https://1013399.github.io/image-2/123/1014664594_l.jpg" alt="尾寮山西北峰、尾寮山" loading="lazy"></p><p>→抵達8K觀雲台<br><img src="https://1013399.github.io/image-2/123/1014664595_l.jpg" alt="尾寮山西北峰、尾寮山" loading="lazy"></p><p>→觀雲台解說牌及展望<br><img src="https://1013399.github.io/image-2/123/1014664597_l.jpg" alt="尾寮山西北峰、尾寮山" loading="lazy"></p><p>→過了觀雲台依舊繼續陡上<br><img src="https://1013399.github.io/image-2/123/1014664600_l.jpg" alt="尾寮山西北峰、尾寮山" loading="lazy"></p><p>→上抵稜線，取右行<br><img src="https://1013399.github.io/image-2/123/1014664605_l.jpg" alt="尾寮山西北峰、尾寮山" loading="lazy"></p><p>→一小段平緩路後，到達8.5K里程樁，再繼續陡上<br><img src="https://1013399.github.io/image-2/123/1014664608_l.jpg" alt="尾寮山西北峰、尾寮山" loading="lazy"></p><p>→步道9K里程樁<br><img src="https://1013399.github.io/image-2/123/1014664610_l.jpg" alt="尾寮山西北峰、尾寮山" loading="lazy"></p><p>→經過9K里程樁後，轉為緩上<br><img src="https://1013399.github.io/image-2/123/1014664617_l.jpg" alt="尾寮山西北峰、尾寮山" loading="lazy"></p><p>→步道轉為圓木步道，即將到達尾寮山山頂<br><img src="https://1013399.github.io/image-2/123/1014664621_l.jpg" alt="尾寮山西北峰、尾寮山" loading="lazy"></p><p>→到達山頂前，左側有個休息展望平台<br><img src="https://1013399.github.io/image-2/123/1014664625_l.jpg" alt="尾寮山西北峰、尾寮山" loading="lazy"></p><p>→到達尾寮山山頂<br><img src="https://1013399.github.io/image-2/123/1014664630_l.jpg" alt="尾寮山西北峰、尾寮山" loading="lazy"></p><p>→尾寮山，二等三角點No.1653<br><img src="https://1013399.github.io/image-2/123/1014664640_l.jpg" alt="尾寮山西北峰、尾寮山" loading="lazy"></p><p>→山頂有叉路，可往沙溪林道23.5K處登山口，休息拍照後，原路下山<br><img src="https://1013399.github.io/image-2/123/1014664645_l.jpg" alt="尾寮山西北峰、尾寮山" loading="lazy"></p><p>→回到5.4K捷徑叉路，取右下捷徑，約於5.1K接回步道<br><img src="https://1013399.github.io/image-2/123/1014664648_l.jpg" alt="尾寮山西北峰、尾寮山" loading="lazy"></p><p>→過了5K約30公尺(即4.97K)，發現樹上有尾寮西峰登山口釘牌<br>   往下望，綁有路條、但雜草多，不想造訪、續行步道<br><img src="https://1013399.github.io/image-2/123/1014664652_l.jpg" alt="尾寮山西北峰、尾寮山" loading="lazy"></p><p>→回到4.8K捷徑叉路，取左下捷徑<br><img src="https://1013399.github.io/image-2/123/1014664660_l.jpg" alt="尾寮山西北峰、尾寮山" loading="lazy"></p><p>→回到4.1K捷徑叉路，取右下捷徑<br><img src="https://1013399.github.io/image-2/123/1014664667_l.jpg" alt="尾寮山西北峰、尾寮山" loading="lazy"></p><p>→下山途中俯瞰大津橋、新威大橋及六龜地區<br><img src="https://1013399.github.io/image-2/123/1014664670_l.jpg" alt="尾寮山西北峰、尾寮山" loading="lazy"></p><p>→回到登山口<br><img src="https://1013399.github.io/image-2/123/1014664674_l.jpg" alt="尾寮山西北峰、尾寮山" loading="lazy"></p><h2 id="走走資訊" tabindex="-1"><a class="header-anchor" href="#走走資訊" aria-hidden="true">#</a> 走走資訊</h2><div class="hint-container info"><p class="hint-container-title">相關信息</p><p>走走時間：約7小時</p><p>高低落差：約1280公尺</p></div><h2 id="參考地圖" tabindex="-1"><a class="header-anchor" href="#參考地圖" aria-hidden="true">#</a> 參考地圖</h2>',52),c={href:"http://www.keepon.com.tw/DiscussLoad.aspx?code=314B5CF9AEC3A19113F6CAA6F539A6621F69A753B64927D9",target:"_blank",rel:"noopener noreferrer"},d=i("br",null,null,-1),_=i("img",{src:"https://1013399.github.io/image-2/123/1014664763_l.jpg",alt:"尾寮山西北峰、尾寮山 參考地圖",loading:"lazy"},null,-1);function u(j,y){const t=l("ExternalLinkIcon");return p(),o("div",null,[n,m,r(" more "),b,i("p",null,[a("原圖連結→"),i("a",c,[a("登山補給站-brownlee"),s(t)]),d,_])])}const f=g(h,[["render",u],["__file","post-123-2014-01-07.html.vue"]]);export{f as default};
