import{_ as g}from"./plugin-vue_export-helper-c27b6911.js";import{r as l,o as p,c as o,a as r,b as i,f as a,d as e,e as s}from"./app-6d4d6805.js";const h={},n=i("p",null,[i("strong",null,"東藤枝山"),a("，海拔1806公尺，位於高雄市桃源區寶山里藤枝森林遊樂區內，山頂立有瞭望台，有顆三等三角點No.7205及一等衛星控制點，登頂可遠眺遠眺玉山、北大武山、大小關山等群山，該山名列台灣小百岳No.068。")],-1),m=i("p",null,"藤枝森林遊樂區內群山環繞，森林蒼翠，適合登山健行、賞花賞鳥等活動，有南台灣小溪頭之稱，但自八八風災後，藤枝林道嚴重受創，一直休園至今。",-1),b=i("p",null,"欲造訪東藤枝山，目前暫時無法從遊樂區大門進入，只能低調從旁進入，上午與台中山友走走烏山嶺後，特來此走走網路上的非正規路線，如無必要，還是等藤枝森林遊樂區重新開放後，再來一遊較為適宜。",-1),c=s('<p>走走日期：2013/12/28</p><h2 id="行車路線" tabindex="-1"><a class="header-anchor" href="#行車路線" aria-hidden="true">#</a> 行車路線</h2><p>走完烏山嶺後，回到台3線385k右轉北上，接台20線(南橫公路)右轉，經過甲仙至荖濃溫泉區右轉台27線，於台27線8.9Km處左後轉藤枝林道(荖濃溪林道)至藤枝林道18Km處路旁停車。</p><h2 id="走走路線" tabindex="-1"><a class="header-anchor" href="#走走路線" aria-hidden="true">#</a> 走走路線</h2><p>→藤枝林道18K，一般小車只能至此，前方為大坍方(海拔1476公尺)<br><img src="https://1013399.github.io/image-2/124/1014661325_l.jpg" alt="東藤枝山" loading="lazy"></p><p>→取左上土石便道高遶<br><img src="https://1013399.github.io/image-2/124/1014661333_l.jpg" alt="東藤枝山" loading="lazy"></p><p>→叉路，左上為新建之藤枝林下步道，續直行土石路<br><img src="https://1013399.github.io/image-2/124/1014661336_l.jpg" alt="東藤枝山" loading="lazy"></p><p>→俯瞰坍方路段，道路完全不見了，不久經過小社區，幾乎荒廢<br><img src="https://1013399.github.io/image-2/124/1014661341_l.jpg" alt="東藤枝山" loading="lazy"></p><p>→叉路，右側有土堆擋道，取左上水泥路<br><img src="https://1013399.github.io/image-2/124/1014661348_l.jpg" alt="東藤枝山" loading="lazy"></p><p>→叉路，取右下<br><img src="https://1013399.github.io/image-2/124/1014661357_l.jpg" alt="東藤枝山" loading="lazy"></p><p>→此處崩塌處已闢出一條便道<br><img src="https://1013399.github.io/image-2/124/1014661361_l.jpg" alt="東藤枝山" loading="lazy"></p><p>→接回藤枝林道(柏油路)<br><img src="https://1013399.github.io/image-2/124/1014661367_l.jpg" alt="東藤枝山" loading="lazy"></p><p>→再經過一處崩塌<br><img src="https://1013399.github.io/image-2/124/1014661376_l.jpg" alt="東藤枝山" loading="lazy"></p><p>→經過藤枝森林遊樂區大門<br><img src="https://1013399.github.io/image-2/124/1014661379_l.jpg" alt="東藤枝山" loading="lazy"></p><p>→經過藤枝山莊(在左上方)<br><img src="https://1013399.github.io/image-2/124/1014661388_l.jpg" alt="東藤枝山" loading="lazy"></p><p>→看見前方的基地台天線了，待會兒會經過<br><img src="https://1013399.github.io/image-2/124/1014661395_l.jpg" alt="東藤枝山" loading="lazy"></p><p>→叉路，旁有寶山#233電桿，直行往石山林道，取左上<br><img src="https://1013399.github.io/image-2/124/1014661399_l.jpg" alt="東藤枝山" loading="lazy"></p><p>→轉二個彎後至寶山#101電桿叉路，取左上經過民宅(數隻小狗狂吠，不予理會就不會再叫了)<br><img src="https://1013399.github.io/image-2/124/1014661410_l.jpg" alt="東藤枝山" loading="lazy"></p><p>→到達基地台天線<br><img src="https://1013399.github.io/image-2/124/1014661416_l.jpg" alt="東藤枝山" loading="lazy"></p><p>→續行左上，右側似乎為雨林前輩所提到的鐵架<br><img src="https://1013399.github.io/image-2/124/1014661424_l.jpg" alt="東藤枝山" loading="lazy"></p><p>→過了基地台天線，約行20~30公尺為登山口(綁有大台中登山隊路條)<br><img src="https://1013399.github.io/image-2/124/1014661430_l.jpg" alt="東藤枝山" loading="lazy"></p><p>→陡上山徑<br><img src="https://1013399.github.io/image-2/124/1014661436_l.jpg" alt="東藤枝山" loading="lazy"></p><p>→休息小空地<br><img src="https://1013399.github.io/image-2/124/1014661444_l.jpg" alt="東藤枝山" loading="lazy"></p><p>→空地旁有顆大樹如麻花捲<br><img src="https://1013399.github.io/image-2/124/1014661451_l.jpg" alt="東藤枝山" loading="lazy"></p><p>→過了休息空地後，沿山腰小徑緩下<br><img src="https://1013399.github.io/image-2/124/1014661455_l.jpg" alt="東藤枝山" loading="lazy"></p><p>→高繞小崩塌，續行山腰小徑<br><img src="https://1013399.github.io/image-2/124/1014661460_l.jpg" alt="東藤枝山" loading="lazy"></p><p>→進入藤枝森林遊樂區，接上「秋海棠步道」，左往遊客中心，取右行<br><img src="https://1013399.github.io/image-2/124/1014661467_l.jpg" alt="東藤枝山" loading="lazy"></p><p>→於步道缺口處俯瞰大崩塌，幸未危及步道<br><img src="https://1013399.github.io/image-2/124/1014661469_l.jpg" alt="東藤枝山" loading="lazy"></p><p>→枕木步道，寬大好走<br><img src="https://1013399.github.io/image-2/124/1014661471_l.jpg" alt="東藤枝山" loading="lazy"></p><p>→此處為莫拉克風災造成的崩塌<br><img src="https://1013399.github.io/image-2/124/1014661476_l.jpg" alt="東藤枝山" loading="lazy"></p><p>→陡上階梯<br><img src="https://1013399.github.io/image-2/124/1014661483_l.jpg" alt="東藤枝山" loading="lazy"></p><p>→秋海棠步道1K里程樁，距離瞭望台還有0.4K<br><img src="https://1013399.github.io/image-2/124/1014661486_l.jpg" alt="東藤枝山" loading="lazy"></p><p>→再陡上木棧道<br><img src="https://1013399.github.io/image-2/124/1014661492_l.jpg" alt="東藤枝山" loading="lazy"></p><p>→休息涼亭，秋海棠步道終點<br><img src="https://1013399.github.io/image-2/124/1014661496_l.jpg" alt="東藤枝山" loading="lazy"></p><p>→涼亭前叉路，左為「雲杉步道」可回遊客中心，取直上「西施花步道」往瞭望台<br><img src="https://1013399.github.io/image-2/124/1014661501_l.jpg" alt="東藤枝山" loading="lazy"></p><p>→抵達藤枝瞭望台，東藤枝山三角點在瞭望台後方左外側<br><img src="https://1013399.github.io/image-2/124/1014661512_l.jpg" alt="東藤枝山" loading="lazy"></p><p>→東藤枝山，三等三角點No.7205<br><img src="https://1013399.github.io/image-2/124/1014661514_l.jpg" alt="東藤枝山" loading="lazy"></p><p>→東藤枝山，一等衛星控制點<br><img src="https://1013399.github.io/image-2/124/1014661521_l.jpg" alt="東藤枝山" loading="lazy"></p><p>→與台中友拍個攻頂紀念照，休息後原路下山<br><img src="https://1013399.github.io/image-2/124/1014661528_l.jpg" alt="東藤枝山" loading="lazy"><br><img src="https://1013399.github.io/image-2/124/1014661530_l.jpg" alt="東藤枝山" loading="lazy"></p><p>→回到藤枝林道18K停車處<br> →取車原路回，與台中山友告別後，循台27線南下往六龜方向<br> →經過六龜，吃過晚餐後，前往茂林風景區，明日準備爬尾寮山<br> →夜宿鳳山農會茂林活動中心(二人房1000元)</p><h2 id="走走資訊" tabindex="-1"><a class="header-anchor" href="#走走資訊" aria-hidden="true">#</a> 走走資訊</h2><div class="hint-container info"><p class="hint-container-title">相關信息</p><p>走走時間：約2.5小時</p><p>高低落差：約330公尺</p></div><h2 id="藤枝林道行車路線" tabindex="-1"><a class="header-anchor" href="#藤枝林道行車路線" aria-hidden="true">#</a> 藤枝林道行車路線</h2><p>(藤枝林道行車路線-取自Google地圖)<br><img src="https://1013399.github.io/image-2/124/1014661762_l.jpg" alt="藤枝林道行車路線" loading="lazy"></p><h2 id="參考地圖" tabindex="-1"><a class="header-anchor" href="#參考地圖" aria-hidden="true">#</a> 參考地圖</h2><p>(將其GPX航跡轉圖檔後裁剪)</p>',46),d={href:"http://www.keepon.com.tw/DiscussLoad.aspx?code=314B5CF9AEC3A19113F6CAA6F539A66238088DFC179CCF6A",target:"_blank",rel:"noopener noreferrer"},_=i("br",null,null,-1),u=i("img",{src:"https://1013399.github.io/image-2/124/1014661759_l.jpg",alt:"東藤枝山 參考地圖",loading:"lazy"},null,-1);function y(j,z){const t=l("ExternalLinkIcon");return p(),o("div",null,[n,m,b,r(" more "),c,i("p",null,[a("原圖連結→"),i("a",d,[a("登山補給站-panyulin(雨林)"),e(t)]),_,u])])}const C=g(h,[["render",y],["__file","post-124-2014-01-03.html.vue"]]);export{C as default};
