import{_ as l}from"./plugin-vue_export-helper-c27b6911.js";import{r as g,o as e,c as p,a as o,b as a,f as i,d as r,e as n}from"./app-a9847bbb.js";const s={},h=a("p",null,[a("strong",null,"烏山嶺"),i("，也稱刣牛湖山，海拔799公尺，位於台南南化烏山步道上，烏山步道全長12公里，為南台灣著名的縱走路線，刣牛湖山為步道最高點，山頂原點遺失，另立聯勤二等三角點內補No.051，該山名列台灣小百岳No.070。")],-1),c=a("p",null,[a("strong",null,"烏山步道"),i("，北從下歸林為起點，途中經過風空子山、噍吧哖抗日英雄紀念碑、烏山、刣牛湖山、金光山財神爺、金光山、石松子山、王爺崙、長青嶺等，直至台3線的金馬寮，縱走一趟約需九至十個小時，今日受台中「玩登小百岳」山友之邀，只拜訪刣牛湖山，直接將車開到NCC監測站，來回僅需45分鐘。")],-1),d=n('<p>走走日期：2013/12/28</p><h2 id="行車路線" tabindex="-1"><a class="header-anchor" href="#行車路線" aria-hidden="true">#</a> 行車路線</h2><p>國道1號於下營系統交流道轉台84快速道路往玉井方向，台84路底接台20(中山路)左轉往甲仙，經過玉井市區遇中華路右轉接台3線，於台3線385k左轉厚德紫竹寺(路口有指標)，至紫竹寺叉路取左往金光山福德祠...</p><p>→金光山福德祠<br><img src="https://1013399.github.io/image-2/125/1014649411_l.jpg" alt="烏山嶺(刣牛湖山)" loading="lazy"></p><p>→賣菜義公園叉路，左山徑為烏山步道起點(或甲仙步道入口)，左水泥路往墓園，取右下<br><img src="https://1013399.github.io/image-2/125/1014649408_l.jpg" alt="烏山嶺(刣牛湖山)" loading="lazy"></p><p>→叉路口指示牌<br><img src="https://1013399.github.io/image-2/125/1014649409_l.jpg" alt="烏山嶺(刣牛湖山)" loading="lazy"></p><p>→聖煌宮叉路，直行<br> →NCC監測站停車<br><img src="https://1013399.github.io/image-2/125/1014649337_l.jpg" alt="烏山嶺(刣牛湖山)" loading="lazy"></p><h2 id="走走路線" tabindex="-1"><a class="header-anchor" href="#走走路線" aria-hidden="true">#</a> 走走路線</h2><p>→繞過NCC監測站，接烏山步道，左往金光山福德祠，取右行<br><img src="https://1013399.github.io/image-2/125/1014649357_l.jpg" alt="烏山嶺(刣牛湖山)" loading="lazy"></p><p>→烏山步道雜草稍多，但路跡明顯<br><img src="https://1013399.github.io/image-2/125/1014649359_l.jpg" alt="烏山嶺(刣牛湖山)" loading="lazy"></p><p>→進入樹林，走在稜線上<br><img src="https://1013399.github.io/image-2/125/1014649366_l.jpg" alt="烏山嶺(刣牛湖山)" loading="lazy"></p><p>→經過廢棄貨櫃屋<br><img src="https://1013399.github.io/image-2/125/1014649373_l.jpg" alt="烏山嶺(刣牛湖山)" loading="lazy"></p><p>→叉路，右不知去向，續直行<br><img src="https://1013399.github.io/image-2/125/1014649379_l.jpg" alt="烏山嶺(刣牛湖山)" loading="lazy"></p><p>→山徑趨陡<br><img src="https://1013399.github.io/image-2/125/1014649382_l.jpg" alt="烏山嶺(刣牛湖山)" loading="lazy"></p><p>→曾文溪水源保護區界樁<br><img src="https://1013399.github.io/image-2/125/1014649384_l.jpg" alt="烏山嶺(刣牛湖山)" loading="lazy"></p><p>→再鑽雜草山徑<br><img src="https://1013399.github.io/image-2/125/1014649386_l.jpg" alt="烏山嶺(刣牛湖山)" loading="lazy"></p><p>→最後攻頂<br><img src="https://1013399.github.io/image-2/125/1014649388_l.jpg" alt="烏山嶺(刣牛湖山)" loading="lazy"></p><p>→刣牛湖山山頂<br><img src="https://1013399.github.io/image-2/125/1014649389_l.jpg" alt="烏山嶺(刣牛湖山)" loading="lazy"></p><p>→往回望NCC監測站，霾害影響，一片灰濛濛<br><img src="https://1013399.github.io/image-2/125/1014649393_l.jpg" alt="烏山嶺(刣牛湖山)" loading="lazy"></p><p>→刣牛湖山聯勤二等三角點內補No.051<br><img src="https://1013399.github.io/image-2/125/1014649396_l.jpg" alt="烏山嶺(刣牛湖山)" loading="lazy"></p><p>→續前行，可往噍吧哖紀念碑<br><img src="https://1013399.github.io/image-2/125/1014649399_l.jpg" alt="烏山嶺(刣牛湖山)" loading="lazy"></p><p>→天氣晴朗時，視野應該不錯，拍照後原路折返<br><img src="https://1013399.github.io/image-2/125/1014649402_l.jpg" alt="烏山嶺(刣牛湖山)" loading="lazy"></p><p>→回到NCC監測站<br><img src="https://1013399.github.io/image-2/125/1014649405_l.jpg" alt="烏山嶺(刣牛湖山)" loading="lazy"></p><h2 id="走走資訊" tabindex="-1"><a class="header-anchor" href="#走走資訊" aria-hidden="true">#</a> 走走資訊</h2><div class="hint-container info"><p class="hint-container-title">相關信息</p><p>走走時間：約45分鐘</p><p>高低落差：約40公尺</p></div><h2 id="參考地圖" tabindex="-1"><a class="header-anchor" href="#參考地圖" aria-hidden="true">#</a> 參考地圖</h2>',26),m={href:"http://living.donghong.info/blog/?p=626",target:"_blank",rel:"noopener noreferrer"},b=a("br",null,null,-1),_=a("img",{src:"https://1013399.github.io/image-2/125/1014660503_l.jpg",alt:"烏山嶺(刣牛湖山) 參考地圖",loading:"lazy"},null,-1);function u(j,y){const t=g("ExternalLinkIcon");return e(),p("div",null,[h,c,o(" more "),d,a("p",null,[i("原圖連結→"),a("a",m,[i("冬烘居部落格"),r(t)]),b,_])])}const N=l(s,[["render",u],["__file","post-125-2014-01-02.html.vue"]]);export{N as default};
