import{_ as l}from"./plugin-vue_export-helper-c27b6911.js";import{r as g,o as p,c as e,a as o,b as i,f as a,d as r,e as s}from"./app-63889d57.js";const h={},n=i("p",null,[i("strong",null,"西阿里關山"),a("，又名小林山，海拔973公尺，位於高雄甲仙區與台南南化區交界處，屬阿里山山脈餘脈。山頂有三等三角點No.5020及廢棄瞭望台，該山名列台灣小百岳No.066。")],-1),c=i("p",null,"西阿里關山林道原為清朝時期修築的小林古道，是由甲仙越嶺至南化關山村的古道，原登山步道係由甲仙起登，88風災後已坍方不通，現一般都改由台南南化區進入。",-1),m=s('<p>走走日期：2013/12/22</p><h2 id="行車路線" tabindex="-1"><a class="header-anchor" href="#行車路線" aria-hidden="true">#</a> 行車路線</h2><p>甲仙出發，行駛台20線往玉井方向，在台20線52.4Km右轉南179鄉道往關山，於179鄉道21.8K右轉接179-1鄉道往平坑，於三界玄廟前取右側產道至出火仔叉路下車(一般轎車停車於此，不宜再往前開)，再轉搭四輪傳動車至竹林五叉路登山口下車。</p><h2 id="走走路線" tabindex="-1"><a class="header-anchor" href="#走走路線" aria-hidden="true">#</a> 走走路線</h2><p>→出火仔叉路，直行往西阿里關山，左上往出火<br><img src="https://1013399.github.io/image-2/128/961830898_l.jpg" alt="西阿里關山" loading="lazy"></p><p>→叉路旁有平溪高分259電桿<br><img src="https://1013399.github.io/image-2/128/961832855_l.jpg" alt="西阿里關山" loading="lazy"></p><p>→路旁有許多咬人貓<br><img src="https://1013399.github.io/image-2/128/961834256_l.jpg" alt="西阿里關山" loading="lazy"></p><p>→附近種植許多梅樹，已有梅花初綻<br><img src="https://1013399.github.io/image-2/128/961834889_l.jpg" alt="西阿里關山" loading="lazy"></p><p>→利用等候接駁時間，先取左上參觀出火景觀<br><img src="https://1013399.github.io/image-2/128/961835485_l.jpg" alt="西阿里關山" loading="lazy"></p><p>→搭接駁車抵達竹林五叉路登山口<br><img src="https://1013399.github.io/image-2/128/961836084_l.jpg" alt="西阿里關山" loading="lazy"></p><p>→整裝後取右二叉路進入林道<br><img src="https://1013399.github.io/image-2/128/961836623_l.jpg" alt="西阿里關山" loading="lazy"></p><p>→叉路，取右續行<br><img src="https://1013399.github.io/image-2/128/961837259_l.jpg" alt="西阿里關山" loading="lazy"></p><p>→叉路，取右上<br><img src="https://1013399.github.io/image-2/128/961837889_l.jpg" alt="西阿里關山" loading="lazy"></p><p>→樹林中右側缺口，視野不錯<br><img src="https://1013399.github.io/image-2/128/961838384_l.jpg" alt="西阿里關山" loading="lazy"></p><p>→叉路，往左不知是否往茅山南峰，續直行<br><img src="https://1013399.github.io/image-2/128/961839790_l.jpg" alt="西阿里關山" loading="lazy"></p><p>→林道一路緩上，走起來輕鬆<br><img src="https://1013399.github.io/image-2/128/961841007_l.jpg" alt="西阿里關山" loading="lazy"></p><p>→進入雜草路徑<br><img src="https://1013399.github.io/image-2/128/961841621_l.jpg" alt="西阿里關山" loading="lazy"></p><p>→走出雜草後是一片大崩塌<br><img src="https://1013399.github.io/image-2/128/961842686_l.jpg" alt="西阿里關山" loading="lazy"></p><p>→崩塌處展望佳<br><img src="https://1013399.github.io/image-2/128/961842090_l.jpg" alt="西阿里關山" loading="lazy"></p><p>→取左下切繞過崩塌後，回望崩塌處<br><img src="https://1013399.github.io/image-2/128/961843326_l.jpg" alt="西阿里關山" loading="lazy"></p><p>→續行，跨倒木<br><img src="https://1013399.github.io/image-2/128/961843926_l.jpg" alt="西阿里關山" loading="lazy"></p><p>→叉路，需取右上，為防止隊友走錯，大隊長在此站崗引導<br><img src="https://1013399.github.io/image-2/128/961844570_l.jpg" alt="西阿里關山" loading="lazy"></p><p>→林道坡度趨陡<br><img src="https://1013399.github.io/image-2/128/961845387_l.jpg" alt="西阿里關山" loading="lazy"></p><p>→到達西阿里關山基點前，有座廢棄瞭望台及類似天線的設施<br><img src="https://1013399.github.io/image-2/128/961846018_l.jpg" alt="西阿里關山" loading="lazy"></p><p>→西阿里關山山頂，三角點在大岩石上<br><img src="https://1013399.github.io/image-2/128/961846777_l.jpg" alt="西阿里關山" loading="lazy"></p><p>→三角點旁還有顆曾文溪水源保護區界23號水泥椿<br><img src="https://1013399.github.io/image-2/128/961847452_l.jpg" alt="西阿里關山" loading="lazy"></p><p>→西阿里關山，三等三角點No.5020<br><img src="https://1013399.github.io/image-2/128/961848385_l.jpg" alt="西阿里關山" loading="lazy"></p><p>→與大隊長及新竹山友拍張合照<br><img src="https://1013399.github.io/image-2/128/961849278_l.jpg" alt="西阿里關山" loading="lazy"></p><p>→山頂展望有限，休息後，原路下山<br><img src="https://1013399.github.io/image-2/128/961850335_l.jpg" alt="西阿里關山" loading="lazy"></p><p>→回到登山口<br><img src="https://1013399.github.io/image-2/128/961850905_l.jpg" alt="西阿里關山" loading="lazy"></p><h2 id="走走資訊" tabindex="-1"><a class="header-anchor" href="#走走資訊" aria-hidden="true">#</a> 走走資訊</h2><div class="hint-container info"><p class="hint-container-title">相關信息</p><p>走走時間：約2小時</p><p>高低落差：約220公尺</p></div><h2 id="參考地圖" tabindex="-1"><a class="header-anchor" href="#參考地圖" aria-hidden="true">#</a> 參考地圖</h2>',33),d={href:"http://blog.xuite.net/lin6151/blog/163123546",target:"_blank",rel:"noopener noreferrer"},b=i("br",null,null,-1),_=i("img",{src:"https://1013399.github.io/image-2/128/1002805376_l.jpg",alt:"西阿里關山 參考地圖",loading:"lazy"},null,-1);function u(j,y){const t=g("ExternalLinkIcon");return p(),e("div",null,[n,c,o(" more "),m,i("p",null,[a("原圖連結→"),i("a",d,[a("黃家大嫂部落格"),r(t)]),b,_])])}const x=l(h,[["render",u],["__file","post-128-2013-12-27.html.vue"]]);export{x as default};
