import{_ as l}from"./plugin-vue_export-helper-c27b6911.js";import{r as p,o,c as r,a as s,b as i,e as t,d as g,f as e}from"./app-e9e80f6e.js";const h={},n=i("p",null,[i("strong",null,"炮子崙步道"),t("，也稱深坑步道，全長1.3公里，原是越嶺往新光路清龍宮的古道，步道途中有開闊的視野，路況佳、適合親子同遊。")],-1),m=i("p",null,[i("strong",null,"猴山岳"),t("，又稱猿山，海拔551公尺，與二格山、筆架山連貫，位於木柵與深坑交界，行政區屬台北市文山區，山頂有顆土地調查局圖根點基石，樹林中無視野。")],-1),b=i("p",null,[i("strong",null,"猴山岳前峰"),t("，海拔514公尺，山頂有北市三角點No.95基石，位於峭壁旁，視野極佳。")],-1),c=i("p",null,[i("strong",null,"茶山古道"),t("，為深坑炮子崙地區與木柵草湳之間往來的古道，沿途會經過農家、菜園、茶園、土埆厝等，可欣賞傳統農村田園景色。")],-1),d=e('<p>走走日期：2013/12/01</p><h2 id="行車路線" tabindex="-1"><a class="header-anchor" href="#行車路線" aria-hidden="true">#</a> 行車路線</h2><p>國道3號北上，出深坑交流道往深坑方向接106乙縣道，於106乙縣道1.4Km(台塑加油站前)右轉炮子崙產業道路，約1.1Km叉路右轉往萬家香土雞城，至萬家香土雞城停車場停車(計次收費50元)。</p><h2 id="走走路線" tabindex="-1"><a class="header-anchor" href="#走走路線" aria-hidden="true">#</a> 走走路線</h2><p>→炮子崙步道登山口，萬家香土雞城對面<br><img src="https://1013399.github.io/image-3/131/844802472_l.jpg" alt="炮子崙步道登山口" loading="lazy"></p><p>→緩上石板及枕木步道<br><img src="https://1013399.github.io/image-3/131/844797054_l.jpg" alt="炮子崙步道" loading="lazy"></p><p>→進入泥土山徑繼續緩上，至菜園叉路取右行<br><img src="https://1013399.github.io/image-3/131/844797153_l.jpg" alt="炮子崙步道" loading="lazy"></p><p>→廢棄土厝，旁有菜園<br><img src="https://1013399.github.io/image-3/131/844797268_l.jpg" alt="炮子崙步道" loading="lazy"></p><p>→越嶺鞍部十字叉路，右往天龍宮，直行下清龍宮，取左往新光路<br><img src="https://1013399.github.io/image-3/131/844797411_l.jpg" alt="炮子崙步道" loading="lazy"></p><p>→古厝涼亭叉路，前有涼亭，直行應可出產道，取右續往新光路<br><img src="https://1013399.github.io/image-3/131/844797722_l.jpg" alt="炮子崙步道" loading="lazy"></p><p>→叉路，左不知去向，取右續行<br><img src="https://1013399.github.io/image-3/131/844797821_l.jpg" alt="炮子崙步道" loading="lazy"></p><p>→進入竹林，右側有鐵網圍籬<br><img src="https://1013399.github.io/image-3/131/844797938_l.jpg" alt="炮子崙步道" loading="lazy"></p><p>→轉個彎後開始陡上<br><img src="https://1013399.github.io/image-3/131/844798025_l.jpg" alt="炮子崙步道" loading="lazy"></p><p>→經過菜園<br><img src="https://1013399.github.io/image-3/131/844798105_l.jpg" alt="炮子崙步道" loading="lazy"></p><p>→菜園視野開闊，俯瞰深坑地區<br><img src="https://1013399.github.io/image-3/131/844798226_l.jpg" alt="炮子崙步道" loading="lazy"></p><p>→抵達第二座涼亭，接柏油路(新光路二段71巷底)，在此休息<br><img src="https://1013399.github.io/image-3/131/844798328_l.jpg" alt="炮子崙步道" loading="lazy"></p><p>→涼亭視野極佳，俯瞰大台北市區<br><img src="https://1013399.github.io/image-3/131/844798409_l.jpg" alt="炮子崙步道" loading="lazy"></p><p>→續行柏油路<br><img src="https://1013399.github.io/image-3/131/844798517_l.jpg" alt="炮子崙步道" loading="lazy"></p><p>→泰元農園叉路，取左再取右<br><img src="https://1013399.github.io/image-3/131/844798781_l.jpg" alt="炮子崙步道" loading="lazy"></p><p>→經過菜園小徑進入樹林<br><img src="https://1013399.github.io/image-3/131/844798865_l.jpg" alt="炮子崙步道" loading="lazy"></p><p>→叉路，直行往茶山古道，取右往猴山岳<br><img src="https://1013399.github.io/image-3/131/844798948_l.jpg" alt="炮子崙步道" loading="lazy"></p><p>→經過電塔011號<br><img src="https://1013399.github.io/image-3/131/844799107_l.jpg" alt="炮子崙步道" loading="lazy"></p><p>→叉路，右往金頭山，取直續行<br><img src="https://1013399.github.io/image-3/131/844799288_l.jpg" alt="炮子崙步道" loading="lazy"></p><p>→行約十餘公尺遇叉路，直行或取左皆可登上猴山岳<br><img src="https://1013399.github.io/image-3/131/844799478_l.jpg" alt="炮子崙步道" loading="lazy"></p><p>→決定取左陡上往猴山岳<br><img src="https://1013399.github.io/image-3/131/844799559_l.jpg" alt="炮子崙步道 猴山岳" loading="lazy"></p><p>→叉路，左不知去向，取右行<br><img src="https://1013399.github.io/image-3/131/844799641_l.jpg" alt="猴山岳" loading="lazy"></p><p>→拉繩攀爬峭壁<br><img src="https://1013399.github.io/image-3/131/844799724_l.jpg" alt="猴山岳" loading="lazy"></p><p>→上至稜線，左往猴山岳，右方即為猴山岳前峰<br><img src="https://1013399.github.io/image-3/131/844799817_l.jpg" alt="猴山岳" loading="lazy"></p><p>→猴山岳前峰，左前方往下可往木柵萬壽路<br><img src="https://1013399.github.io/image-3/131/844799923_l.jpg" alt="猴山岳前峰" loading="lazy"></p><p>→猴山岳前峰，北市三角點No.95基石<br><img src="https://1013399.github.io/image-3/131/844800094_l.jpg" alt="猴山岳前峰" loading="lazy"></p><p>→猴山岳前峰展望極佳，俯瞰大台北市區<br><img src="https://1013399.github.io/image-3/131/844800192_l.jpg" alt="猴山岳前峰" loading="lazy"></p><p>→休息後續往猴山岳<br><img src="https://1013399.github.io/image-3/131/844800290_l.jpg" alt="猴山岳" loading="lazy"></p><p>→到達猴山岳，不像一個山頭<br><img src="https://1013399.github.io/image-3/131/844800492_l.jpg" alt="猴山岳" loading="lazy"></p><p>→猴山岳，土地調查局圖根點<br><img src="https://1013399.github.io/image-3/131/844800399_l.jpg" alt="猴山岳" loading="lazy"></p><p>→續行，路旁有株黃色蘭花<br><img src="https://1013399.github.io/image-3/131/844800578_l.jpg" alt="黃色蘭花" loading="lazy"></p><p>→開始下坡<br><img src="https://1013399.github.io/image-3/131/844800692_l.jpg" alt="猴山岳" loading="lazy"></p><p>→途中看到兩顆圖根點<br><img src="https://1013399.github.io/image-3/131/844800785_l.jpg" alt="猴山岳 兩顆圖根點" loading="lazy"></p><p>→叉路，左右皆可，續直行步道<br><img src="https://1013399.github.io/image-3/131/844800882_l.jpg" alt="猴山岳" loading="lazy"></p><p>→前一叉路在此交會，取右續行<br><img src="https://1013399.github.io/image-3/131/844801097_l.jpg" alt="猴山岳" loading="lazy"></p><p>→鞍部十字叉路，右下往草湳大榕樹，直行往二格山<br><img src="https://1013399.github.io/image-3/131/844801246_l.jpg" alt="猴山岳" loading="lazy"></p><p>→取左下往林家草厝<br><img src="https://1013399.github.io/image-3/131/844801349_l.jpg" alt="猴山岳 林家草厝" loading="lazy"></p><p>→古厝遺跡<br><img src="https://1013399.github.io/image-3/131/844801434_l.jpg" alt="猴山岳 林家草厝" loading="lazy"></p><p>→進入茶園<br><img src="https://1013399.github.io/image-3/131/844801577_l.jpg" alt="猴山岳 林家草厝" loading="lazy"></p><p>→林家草厝，據說有百年歷史<br><img src="https://1013399.github.io/image-3/131/844801642_l.jpg" alt="猴山岳 林家草厝" loading="lazy"></p><p>→續行，經過水田、菜園，進入樹林<br><img src="https://1013399.github.io/image-3/131/844801695_l.jpg" alt="猴山岳 林家草厝" loading="lazy"></p><p>→叉路，旁有菜園，右往阿柔洋，取左續行<br><img src="https://1013399.github.io/image-3/131/844801754_l.jpg" alt="茶山古道" loading="lazy"></p><p>→叉路，右似乎回前一叉路，取左續行<br><img src="https://1013399.github.io/image-3/131/844801822_l.jpg" alt="茶山古道" loading="lazy"></p><p>→叉路，左可往炮子崙步道、指南宮，取直行<br><img src="https://1013399.github.io/image-3/131/844801881_l.jpg" alt="茶山古道" loading="lazy"></p><p>→叉路，左不知去向，取右行<br><img src="https://1013399.github.io/image-3/131/844801939_l.jpg" alt="茶山古道" loading="lazy"></p><p>→叉路，右往王軍寮，續直行<br><img src="https://1013399.github.io/image-3/131/844801996_l.jpg" alt="茶山古道" loading="lazy"></p><p>→這段路頗有古道的味道<br><img src="https://1013399.github.io/image-3/131/844802047_l.jpg" alt="茶山古道" loading="lazy"></p><p>→接上不鏽鋼護欄水泥路<br><img src="https://1013399.github.io/image-3/131/844802103_l.jpg" alt="茶山古道" loading="lazy"></p><p>→水泥路結束，進入菜園<br><img src="https://1013399.github.io/image-3/131/844802175_l.jpg" alt="茶山古道" loading="lazy"></p><p>→十字叉路，續直行往下<br><img src="https://1013399.github.io/image-3/131/844802232_l.jpg" alt="茶山古道" loading="lazy"></p><p>→接上炮子崙產業道路，取右回<br><img src="https://1013399.github.io/image-3/131/844802347_l.jpg" alt="茶山古道" loading="lazy"></p><p>→回到萬家香土雞城叉路，取左上往萬家香停車場取車<br><img src="https://1013399.github.io/image-3/131/844802410_l.jpg" alt="茶山古道 萬家香土雞城" loading="lazy"></p><p>→回到萬家香土雞城停車場，取車往深坑老街逛逛</p><h2 id="走走資訊" tabindex="-1"><a class="header-anchor" href="#走走資訊" aria-hidden="true">#</a> 走走資訊</h2><div class="hint-container info"><p class="hint-container-title">相關信息</p><p>走走時間：約4小時(含休息、午餐)</p><p>高低落差：約450公尺</p></div><h2 id="參考地圖" tabindex="-1"><a class="header-anchor" href="#參考地圖" aria-hidden="true">#</a> 參考地圖</h2>',60),_={href:"http://www.keepon.com.tw/DiscussLoad.aspx?code=314B5CF9AEC3A19113F6CAA6F539A662287FF9E134D0379B",target:"_blank",rel:"noopener noreferrer"},u=i("br",null,null,-1),y=i("img",{src:"https://1013399.github.io/image-3/131/844803189_l.jpg",alt:"炮子崙步道、猴山岳前峰、猴山岳、茶山古道 參考地圖",loading:"lazy"},null,-1),j={href:"http://www.yougoipay.com/kenny/w979/index.htm",target:"_blank",rel:"noopener noreferrer"},z=i("br",null,null,-1),f=i("img",{src:"https://1013399.github.io/image-3/131/848138969_l.jpg",alt:"炮子崙步道、猴山岳前峰、猴山岳、茶山古道 參考地圖",loading:"lazy"},null,-1);function x(w,k){const a=p("ExternalLinkIcon");return o(),r("div",null,[n,m,b,c,s(" more "),d,i("p",null,[t("原圖連結→"),i("a",_,[t("登山補給站-brownlee(小李)"),g(a)]),u,y]),i("p",null,[t("原圖連結→"),i("a",j,[t("偶而獨步山林間(蕭郎)"),g(a)]),z,f])])}const A=l(h,[["render",x],["__file","post-131-2013-12-05.html.vue"]]);export{A as default};
