import{_ as l}from"./plugin-vue_export-helper-c27b6911.js";import{r as g,o as p,c as o,a as r,b as i,e as t,d as e,f as s}from"./app-cb5d936b.js";const h={},n=i("p",null,[i("strong",null,"扇子排山"),t("，位於新竹縣五峰鄉，海拔955公尺，山頂有顆三等三角點No.6271，樹林間僅西側可遠眺北埔五指山。")],-1),m=i("p",null,[i("strong",null,"扇子排山西北峰"),t("，位於新竹縣五峰鄉，海拔910公尺，有顆山字森林三角點及一顆山字水泥柱，山頂為平坦地，樹林間無展望。")],-1),c=s('<p>走走日期：2013/11/23</p><h2 id="行車路線" tabindex="-1"><a class="header-anchor" href="#行車路線" aria-hidden="true">#</a> 行車路線</h2><p>竹東走122縣道往五峰，於122縣道34.4k左轉過五峰大橋接竹63鄉道，於3.7k花園派出所辦理入山證，過派出所10公尺T型叉路右轉，至竹63鄉道8.5K指標路旁停車，附近谷亮緣露營區，即為扇子排山登山口。</p><h2 id="走走路線" tabindex="-1"><a class="header-anchor" href="#走走路線" aria-hidden="true">#</a> 走走路線</h2><p>→竹63鄉道8.5K指標停車處仰望扇子排山<br><img src="https://1013399.github.io/image-3/132/818622829_l.jpg" alt="扇子排山、扇子排山西北峰" loading="lazy"></p><p>→往谷亮露營區方向，繞過路障進入<br><img src="https://1013399.github.io/image-3/132/818619473_l.jpg" alt="扇子排山、扇子排山西北峰" loading="lazy"></p><p>→經過谷亮露營區<br><img src="https://1013399.github.io/image-3/132/818619868_l.jpg" alt="扇子排山、扇子排山西北峰" loading="lazy"></p><p>→扇子排山登山口，取右上土徑<br><img src="https://1013399.github.io/image-3/132/818619974_l.jpg" alt="扇子排山、扇子排山西北峰" loading="lazy"></p><p>→沿著露營區邊緣往上行<br><img src="https://1013399.github.io/image-3/132/818620119_l.jpg" alt="扇子排山、扇子排山西北峰" loading="lazy"></p><p>→露營區高點展望，前方山頭應是梅山、民都有山<br><img src="https://1013399.github.io/image-3/132/818622580_l.jpg" alt="扇子排山、扇子排山西北峰" loading="lazy"></p><p>→進入竹林<br><img src="https://1013399.github.io/image-3/132/818620209_l.jpg" alt="扇子排山、扇子排山西北峰" loading="lazy"></p><p>→倒竹，從旁繞過<br><img src="https://1013399.github.io/image-3/132/818620313_l.jpg" alt="扇子排山、扇子排山西北峰" loading="lazy"></p><p>→開始陡上<br><img src="https://1013399.github.io/image-3/132/818620438_l.jpg" alt="扇子排山、扇子排山西北峰" loading="lazy"></p><p>→路旁&quot;亍&quot;基石<br><img src="https://1013399.github.io/image-3/132/818620545_l.jpg" alt="扇子排山、扇子排山西北峰" loading="lazy"></p><p>→轉為大角度陡上<br><img src="https://1013399.github.io/image-3/132/818620636_l.jpg" alt="扇子排山、扇子排山西北峰" loading="lazy"></p><p>→走出竹林<br><img src="https://1013399.github.io/image-3/132/818620808_l.jpg" alt="扇子排山、扇子排山西北峰" loading="lazy"></p><p>→上稜線，取右行<br><img src="https://1013399.github.io/image-3/132/818620958_l.jpg" alt="扇子排山、扇子排山西北峰" loading="lazy"></p><p>→前方山頭即為扇子排山<br><img src="https://1013399.github.io/image-3/132/818621033_l.jpg" alt="扇子排山、扇子排山西北峰" loading="lazy"></p><p>→到達扇子排山<br><img src="https://1013399.github.io/image-3/132/818621108_l.jpg" alt="扇子排山、扇子排山西北峰" loading="lazy"></p><p>→扇子排山，三等三角點No.6271，桃園鐮刀登山隊上周才剛來過<br><img src="https://1013399.github.io/image-3/132/818621170_l.jpg" alt="扇子排山、扇子排山西北峰" loading="lazy"></p><p>→往西側遠眺北埔五指山<br><img src="https://1013399.github.io/image-3/132/818621251_l.jpg" alt="扇子排山、扇子排山西北峰" loading="lazy"></p><p>→續行往扇子排山西北峰<br><img src="https://1013399.github.io/image-3/132/818621359_l.jpg" alt="扇子排山、扇子排山西北峰" loading="lazy"></p><p>→往西北峰的路徑較平緩，但較難走，幸好桃園鐮刀隊剛砍過草<br><img src="https://1013399.github.io/image-3/132/818621438_l.jpg" alt="扇子排山、扇子排山西北峰" loading="lazy"></p><p>→第二顆&quot;亍&quot;基石<br><img src="https://1013399.github.io/image-3/132/818621509_l.jpg" alt="扇子排山、扇子排山西北峰" loading="lazy"></p><p>→基石旁有棵巨木<br><img src="https://1013399.github.io/image-3/132/818621605_l.jpg" alt="扇子排山、扇子排山西北峰" loading="lazy"></p><p>→進入竹林，跟著路條走，否則容易迷路<br><img src="https://1013399.github.io/image-3/132/818621698_l.jpg" alt="扇子排山、扇子排山西北峰" loading="lazy"></p><p>→第三顆&quot;亍&quot;基石<br><img src="https://1013399.github.io/image-3/132/818621778_l.jpg" alt="扇子排山、扇子排山西北峰" loading="lazy"></p><p>→走出竹林，進入雜木林<br><img src="https://1013399.github.io/image-3/132/818621853_l.jpg" alt="扇子排山、扇子排山西北峰" loading="lazy"></p><p>→第四顆&quot;亍&quot;基石(根據yang5757兄的資料，應有五顆)<br><img src="https://1013399.github.io/image-3/132/818622123_l.jpg" alt="扇子排山、扇子排山西北峰" loading="lazy"></p><p>→平坦休息地<br><img src="https://1013399.github.io/image-3/132/818621912_l.jpg" alt="扇子排山、扇子排山西北峰" loading="lazy"></p><p>→到達扇子排山西北峰<br><img src="https://1013399.github.io/image-3/132/818621942_l.jpg" alt="扇子排山、扇子排山西北峰" loading="lazy"></p><p>→扇子排山西北峰，山字森林三角點<br><img src="https://1013399.github.io/image-3/132/818621977_l.jpg" alt="扇子排山、扇子排山西北峰" loading="lazy"></p><p>→旁邊有顆山字水泥柱，休息後原路折返<br><img src="https://1013399.github.io/image-3/132/818622008_l.jpg" alt="扇子排山、扇子排山西北峰" loading="lazy"></p><p>→回到扇子排山<br><img src="https://1013399.github.io/image-3/132/818622320_l.jpg" alt="扇子排山、扇子排山西北峰" loading="lazy"></p><p>→回到谷亮露營區<br><img src="https://1013399.github.io/image-3/132/818622526_l.jpg" alt="扇子排山、扇子排山西北峰" loading="lazy"></p><p>→露營區已有遊客在此紮營<br><img src="https://1013399.github.io/image-3/132/818622652_l.jpg" alt="扇子排山、扇子排山西北峰" loading="lazy"></p><p>→回到停車處</p><h2 id="走走資訊" tabindex="-1"><a class="header-anchor" href="#走走資訊" aria-hidden="true">#</a> 走走資訊</h2><div class="hint-container info"><p class="hint-container-title">相關信息</p><p>走走時間：約2.5小時(含休息)</p><p>高低落差：約160公尺</p></div><h2 id="參考地圖" tabindex="-1"><a class="header-anchor" href="#參考地圖" aria-hidden="true">#</a> 參考地圖</h2>',40),b={href:"http://www.keepon.com.tw/DiscussLoad.aspx?code=314B5CF9AEC3A19113F6CAA6F539A6620B82E5E60B86EE53",target:"_blank",rel:"noopener noreferrer"},d=i("br",null,null,-1),_=i("img",{src:"https://1013399.github.io/image-3/132/818623659_l.jpg",alt:"扇子排山、扇子排山西北峰 參考地圖",loading:"lazy"},null,-1);function u(y,j){const a=g("ExternalLinkIcon");return p(),o("div",null,[n,m,r(" more "),c,i("p",null,[t("原圖連結→"),i("a",b,[t("登山補給站-brownlee(小李)"),e(a)]),d,_])])}const x=l(h,[["render",u],["__file","post-132-2013-11-27.html.vue"]]);export{x as default};
