import{_ as g}from"./plugin-vue_export-helper-c27b6911.js";import{r as p,o as r,c as e,a as o,b as t,f as a,d as l,e as s}from"./app-c5577115.js";const h={},n=t("p",null,[t("strong",null,"通安步道"),a("，位於苗栗南庄南江村的福南地區，是日據時期運送軍事補給至山脊線防守砲台的重要通道，沿路風光明媚，長約700公尺，步行單程約需50分鐘，來回1個小時又40分鐘；因地勢較高，可遠眺南庄鄉東村、西村、南江村的山嵐美景，或欣賞蓬萊溪地形景觀和玉山紫金牛、烏心石等植物生態。")],-1),m={href:"http://www.trimt-nsa.gov.tw/cht/unit_04_1_2.aspx?subsiteID=3&hotID=61",target:"_blank",rel:"noopener noreferrer"},c=s('<p><strong>通安山</strong>，海拔約661公尺，位於通安步道最高點，山頂為休憩平台，設有砲台遺址解說牌，無基石，為廣興兩公婆於2010/11/21所命名。<br>     曾於2013/01/26單獨一人來此造訪，受到民宅兩隻大狼犬驚嚇，而沒走成，今日重遊，兇犬仍在，民宅主人在旁除草，告知可不予理會，安心通過。</p><p>走走日期： 2013/11/17</p><h2 id="行車路線" tabindex="-1"><a class="header-anchor" href="#行車路線" aria-hidden="true">#</a> 行車路線</h2><p>國道1號下頭份交流道，接上124縣道往三灣、南庄方向，經過南庄大橋後續直行124縣道，於124縣道31.3Km處右轉，過通安橋再右轉，沿主線行駛約500公尺，即可到達入口停車場。</p><h2 id="走走路線" tabindex="-1"><a class="header-anchor" href="#走走路線" aria-hidden="true">#</a> 走走路線</h2><p>→停車整裝後往鐵皮屋方向<br><img src="https://1013399.github.io/image-3/133/799402481_l.jpg" alt="通安步道、通安山" loading="lazy"></p><p>→過鐵皮屋再取左石板步道<br><img src="https://1013399.github.io/image-3/133/799402621_l.jpg" alt="通安步道、通安山" loading="lazy"></p><p>→叉路，左右皆可，取右上階梯<br><img src="https://1013399.github.io/image-3/133/799402718_l.jpg" alt="通安步道、通安山" loading="lazy"></p><p>→通安步道入口<br><img src="https://1013399.github.io/image-3/133/799402805_l.jpg" alt="通安步道入口" loading="lazy"></p><p>→入口旁導覽圖<br><img src="https://1013399.github.io/image-3/133/799402964_l.jpg" alt="通安步道導覽圖" loading="lazy"></p><p>→於入口處遠眺南庄鄉東村、西村、南江村及蓬萊溪<br><img src="https://1013399.github.io/image-3/133/799402885_l.jpg" alt="通安步道、通安山" loading="lazy"></p><p>→隨即開始陡上木棧階梯，廣興兩公婆曾數過，共有1635個階梯<br><img src="https://1013399.github.io/image-3/133/799403047_l.jpg" alt="通安步道、通安山" loading="lazy"></p><p>→第一平台，上達稜線，設有休息椅<br><img src="https://1013399.github.io/image-3/133/799403149_l.jpg" alt="通安步道、通安山" loading="lazy"></p><p>→旁有往古砲台30分鐘指標<br><img src="https://1013399.github.io/image-3/133/799403250_l.jpg" alt="通安步道、通安山" loading="lazy"></p><p>→休息後繼續陡上<br><img src="https://1013399.github.io/image-3/133/799403412_l.jpg" alt="通安步道、通安山" loading="lazy"></p><p>→經過倒木<br><img src="https://1013399.github.io/image-3/133/799403759_l.jpg" alt="通安步道、通安山" loading="lazy"></p><p>→上至平緩處，木棧道結束<br><img src="https://1013399.github.io/image-3/133/799403891_l.jpg" alt="通安步道、通安山" loading="lazy"></p><p>→再往前走幾步，即為第二平台<br><img src="https://1013399.github.io/image-3/133/799404018_l.jpg" alt="通安步道、通安山" loading="lazy"></p><p>→續行枕木階梯步道<br><img src="https://1013399.github.io/image-3/133/799404180_l.jpg" alt="通安步道、通安山" loading="lazy"></p><p>→再經過倒木<br><img src="https://1013399.github.io/image-3/133/799405826_l.jpg" alt="通安步道、通安山" loading="lazy"></p><p>→經過崩塌處，尚未危及步道<br><img src="https://1013399.github.io/image-3/133/799406054_l.jpg" alt="通安步道、通安山" loading="lazy"></p><p>→枕木步道結束，再接木棧道<br><img src="https://1013399.github.io/image-3/133/799406487_l.jpg" alt="通安步道、通安山" loading="lazy"></p><p>→黃藤擋道，無法旁繞，只好小心爬行躦過<br><img src="https://1013399.github.io/image-3/133/799406750_l.jpg" alt="通安步道、通安山" loading="lazy"></p><p>→第三平台休息椅<br><img src="https://1013399.github.io/image-3/133/799407007_l.jpg" alt="通安步道、通安山" loading="lazy"></p><p>→繼續陡上木棧道<br><img src="https://1013399.github.io/image-3/133/799407292_l.jpg" alt="通安步道、通安山" loading="lazy"></p><p>→第四平台，倒木擋道，可輕鬆通過<br><img src="https://1013399.github.io/image-3/133/799407525_l.jpg" alt="通安步道、通安山" loading="lazy"></p><p>→最後陡上，階距加大，步道更陡<br><img src="https://1013399.github.io/image-3/133/799407702_l.jpg" alt="通安步道、通安山" loading="lazy"></p><p>→步道趨緩，即將到達山頂<br><img src="https://1013399.github.io/image-3/133/799407911_l.jpg" alt="通安步道山頂" loading="lazy"></p><p>→山頂平台前解說牌，說明此處為昔日「日阿拐事件」的砲台位置<br><img src="https://1013399.github.io/image-3/133/799408107_l.jpg" alt="通安步道 昔日「日阿拐事件」的砲台位置" loading="lazy"></p><p>→山頂平台，設有參山國家風景區管理處巡邏箱<br><img src="https://1013399.github.io/image-3/133/799408402_l.jpg" alt="通安步道山頂平台 設有參山國家風景區管理處巡邏箱" loading="lazy"></p><p>→廣興兩公婆設立的山頂標示牌<br><img src="https://1013399.github.io/image-3/133/799408849_l.jpg" alt="通安步道山頂標示牌" loading="lazy"></p><p>→山頂展望佳<br><img src="https://1013399.github.io/image-3/133/799408598_l.jpg" alt="通安步道山頂展望佳" loading="lazy"></p><p>→休息後原路折返，陡下木棧道<br><img src="https://1013399.github.io/image-3/133/799409212_l.jpg" alt="通安步道、通安山" loading="lazy"></p><p>→回到停車處</p><h2 id="走走資訊" tabindex="-1"><a class="header-anchor" href="#走走資訊" aria-hidden="true">#</a> 走走資訊</h2><div class="hint-container info"><p class="hint-container-title">相關信息</p><p>走走時間：約1.5小時</p><p>高低落差：約310公尺</p></div><h2 id="參考地圖" tabindex="-1"><a class="header-anchor" href="#參考地圖" aria-hidden="true">#</a> 參考地圖</h2>',37),b={href:"http://blog.yam.com/ppapago2010/article/71012628",target:"_blank",rel:"noopener noreferrer"},d=t("br",null,null,-1),_=t("img",{src:"https://1013399.github.io/image-3/133/799411884_l.jpg",alt:"通安步道、通安山",loading:"lazy"},null,-1);function u(y,j){const i=p("ExternalLinkIcon");return r(),e("div",null,[n,o(" more "),t("p",null,[a("(資料來源："),t("a",m,[a("參山國家風景區管理處"),l(i)]),a(")")]),c,t("p",null,[a("原圖連結→"),t("a",b,[a("山林龜影部落格"),l(i)]),d,_])])}const x=g(h,[["render",u],["__file","post-133-2013-11-19.html.vue"]]);export{x as default};
