import{_ as l}from"./plugin-vue_export-helper-c27b6911.js";import{r as p,o,c as r,b as i,e as t,d as g,a as s,f as e}from"./app-157980c9.js";const h={},n=i("p",null,[i("strong",null,"立霧山"),t("，又稱崇德山，海拔1274公尺，位於花蓮縣秀林鄉，擁有一顆二等三角點No.1221，是清水大山南稜經千里眼山，一路緩降數公里後又突起的狹長型山頭，而其南脈直至太魯閣口結束。此山北南西三稜坡緩，只有東面無稜直接垂懸太平洋岸，整體猶如一橫屏狀平板形山體。因同禮步道健行盛行而漸為登山客青睞，攀登路線風景與林相優美，又不像攀登清水大山般艱苦，2006年特選為台灣小百岳。")],-1),m={href:"http://www.twmount.com.tw/Content/Content-Detail.aspx?seri=3730&issue=109",target:"_blank",rel:"noopener noreferrer"},b=i("p",null,"該山名列台灣小百岳No.087，與北方千里眼山、清水大山號稱砂卡礑三雄。",-1),c=e('<p><strong>得卡倫步道</strong>，位於太魯閣台地，原為太魯閣族得卡倫部落領域，步道大部分已鋪設階梯棧道，沿途並設有數個休憩平台，上到高點後可遠眺長春祠步道上的鐘樓，並可俯瞰立霧溪、太魯閣台地等景觀，也是前往大同、大禮部落或攀登立霧山、千里眼山、清水大山三條步道之一(另二步道為砂卡礑步道及台電立霧發電廠輸水管旁的石階步道)。</p><p>今日與「新竹科學園區健行登山社」嚮導高大哥約同，利用兩天走走花蓮兩座小百岳(立霧山與祖輪山)，一行共11人，清晨5點半會合後出發。第一天從太魯閣台地登立霧山，爬升約1200公尺，算是硬斗的行程，但與科園山社山友同行，沿途說說笑笑、交換登山心得，走來輕鬆愉快，減輕了登山的疲累。</p><p>走走日期： 2013/11/09</p><h2 id="行車路線" tabindex="-1"><a class="header-anchor" href="#行車路線" aria-hidden="true">#</a> 行車路線</h2><p>前一日先夜宿花蓮市，凌晨4點半花蓮市出發，行駛台9線北上，於台9線183.7Km叉路取左接台8線往太魯閣，至東西橫貫公路牌樓取右過錦文橋，依指標抵達太魯閣遊客中心停車場停車。</p><h2 id="走走路線" tabindex="-1"><a class="header-anchor" href="#走走路線" aria-hidden="true">#</a> 走走路線</h2><p>→得卡倫步道入口，位於太魯閣遊客中心停車場旁<br><img src="https://1013399.github.io/image-3/135/785201347_l.jpg" alt="得卡倫步道入口" loading="lazy"></p><p>→步上棧道後往左行<br><img src="https://1013399.github.io/image-3/135/785201832_l.jpg" alt="得卡倫步道" loading="lazy"></p><p>→接土石路，依指標續行得卡倫步道，往大禮部落<br><img src="https://1013399.github.io/image-3/135/785201955_l.jpg" alt="得卡倫步道" loading="lazy"></p><p>→叉路，左下往流籠頭站(太魯閣台地端)，續直行<br><img src="https://1013399.github.io/image-3/135/785202090_l.jpg" alt="得卡倫步道" loading="lazy"></p><p>→叉路，左上為較陡捷徑(可少走約500公尺但較難走)，續直行<br><img src="https://1013399.github.io/image-3/135/785202214_l.jpg" alt="得卡倫步道" loading="lazy"></p><p>→再接回棧道<br><img src="https://1013399.github.io/image-3/135/785202319_l.jpg" alt="得卡倫步道" loading="lazy"></p><p>→陡上棧道<br><img src="https://1013399.github.io/image-3/135/785202454_l.jpg" alt="得卡倫步道" loading="lazy"></p><p>→第一觀景平台<br><img src="https://1013399.github.io/image-3/135/785202580_l.jpg" alt="得卡倫步道" loading="lazy"></p><p>→觀景平台上展望佳，俯瞰立霧溪，並可遠眺長春祠步道上的鐘樓<br><img src="https://1013399.github.io/image-3/135/785202704_l.jpg" alt="得卡倫步道" loading="lazy"></p><p>→休息後繼續陡上<br><img src="https://1013399.github.io/image-3/135/785203022_l.jpg" alt="得卡倫步道" loading="lazy"></p><p>→第二觀景平台<br><img src="https://1013399.github.io/image-3/135/785229033_l.jpg" alt="得卡倫步道" loading="lazy"></p><p>→此觀景台，展望一樣很棒<br><img src="https://1013399.github.io/image-3/135/785206242_l.jpg" alt="得卡倫步道" loading="lazy"></p><p>→繼續陡上棧道<br><img src="https://1013399.github.io/image-3/135/785206570_l.jpg" alt="得卡倫步道" loading="lazy"></p><p>→得卡倫步道1K指標<br><img src="https://1013399.github.io/image-3/135/785206659_l.jpg" alt="得卡倫步道1K指標" loading="lazy"></p><p>→曲折的棧道依舊陡上<br><img src="https://1013399.github.io/image-3/135/785206752_l.jpg" alt="得卡倫步道" loading="lazy"></p><p>→叉路，右下往立霧發電廠水管石階步道可回台8線，取左上泥土山徑<br><img src="https://1013399.github.io/image-3/135/785206926_l.jpg" alt="得卡倫步道" loading="lazy"></p><p>→叉路，之前捷徑自左方於此會合，取右上(至此約已爬升400公尺)<br><img src="https://1013399.github.io/image-3/135/785207262_l.jpg" alt="得卡倫步道" loading="lazy"></p><p>→陡上石塊山徑<br><img src="https://1013399.github.io/image-3/135/785207367_l.jpg" alt="得卡倫步道" loading="lazy"></p><p>→得卡倫步道2K指標<br><img src="https://1013399.github.io/image-3/135/785207451_l.jpg" alt="得卡倫步道" loading="lazy"></p><p>→展望點，俯瞰台8線及太平洋<br><img src="https://1013399.github.io/image-3/135/785207644_l.jpg" alt="得卡倫步道" loading="lazy"></p><p>→步道趨緩<br><img src="https://1013399.github.io/image-3/135/785207729_l.jpg" alt="得卡倫步道" loading="lazy"></p><p>→索道遺跡，旁有雨量計，停放數部機車及運輸車<br><img src="https://1013399.github.io/image-3/135/785207866_l.jpg" alt="得卡倫步道" loading="lazy"></p><p>→開始進入平緩漫長的砂卡噹林道<br><img src="https://1013399.github.io/image-3/135/785208090_l.jpg" alt="得卡倫步道 砂卡噹林道" loading="lazy"></p><p>→叉路，旁有大禮大同步道解說牌，左下往大禮部落，取直續行水泥路<br><img src="https://1013399.github.io/image-3/135/785208235_l.jpg" alt="得卡倫步道" loading="lazy"></p><p>→林相優美的砂卡噹林道<br><img src="https://1013399.github.io/image-3/135/785208325_l.jpg" alt="得卡倫步道 砂卡噹林道" loading="lazy"></p><p>→沿途有很多鳳仙花及油點草<br><img src="https://1013399.github.io/image-3/135/785208521_l.jpg" alt="得卡倫步道 鳳仙花 油點草" loading="lazy"></p><p>→經過工寮<br><img src="https://1013399.github.io/image-3/135/785208645_l.jpg" alt="得卡倫步道 工寮" loading="lazy"></p><p>→流籠頭站(砂卡噹林道端)<br><img src="https://1013399.github.io/image-3/135/785208970_l.jpg" alt="得卡倫步道 流籠頭站(砂卡噹林道端)" loading="lazy"></p><p>→遠眺群山，頗有大山氣勢<br><img src="https://1013399.github.io/image-3/135/785209174_l.jpg" alt="得卡倫步道 遠眺群山" loading="lazy"></p><p>→經過白色大岩壁<br><img src="https://1013399.github.io/image-3/135/785209621_l.jpg" alt="得卡倫步道 白色大岩壁" loading="lazy"></p><p>→叉路，直行往大同部落、千里眼山、清水大山，取右往立霧山<br><img src="https://1013399.github.io/image-3/135/785213013_l.jpg" alt="得卡倫步道" loading="lazy"></p><p>→約行數十公尺，可見掛滿登山路條的立霧山西稜線登山口，取右上<br>   (直行至路底為達道頭目的家，可提供住宿，也是立霧山北稜線登山口)<br><img src="https://1013399.github.io/image-3/135/785213200_l.jpg" alt="立霧山西稜線登山口" loading="lazy"></p><p>→陡上約40分鐘即到達立霧山山頂<br><img src="https://1013399.github.io/image-3/135/785213377_l.jpg" alt="立霧山山頂" loading="lazy"></p><p>→立霧山二等三角點No.1221<br><img src="https://1013399.github.io/image-3/135/785213575_l.jpg" alt="立霧山二等三角點No.1221" loading="lazy"></p><p>→續往前行即為北稜線上來的路<br><img src="https://1013399.github.io/image-3/135/785213957_l.jpg" alt="立霧山" loading="lazy"></p><p>→等全員到齊後，拍張攻頂紀念照，休息後原路下山<br><img src="https://1013399.github.io/image-3/135/785214116_l.jpg" alt="立霧山 攻頂紀念照" loading="lazy"></p><p>→回到大同部落叉路，先取右走一小段，便可見下方的大同部落<br><img src="https://1013399.github.io/image-3/135/785214276_l.jpg" alt="立霧山 大同部落叉路" loading="lazy"></p><p>→也可遠眺千里眼山及清水大山，賞景後原路退回<br><img src="https://1013399.github.io/image-3/135/785232357_l.jpg" alt="立霧山 遠眺千里眼山及清水大山" loading="lazy"></p><p>→回到上午的捷徑叉路，取右改走捷徑下山<br><img src="https://1013399.github.io/image-3/135/785218622_l.jpg" alt="立霧山 捷徑叉路" loading="lazy"></p><p>→捷徑果然很陡<br><img src="https://1013399.github.io/image-3/135/785228879_l.jpg" alt="立霧山 捷徑" loading="lazy"></p><p>→流籠中繼站，為太魯閣台地及砂卡噹林道兩端流籠頭的中點站<br><img src="https://1013399.github.io/image-3/135/785224256_l.jpg" alt="立霧山 流籠中繼站" loading="lazy"></p><p>→中繼站視野極佳，俯瞰太魯閣遊客中心<br><img src="https://1013399.github.io/image-3/135/785224574_l.jpg" alt="立霧山 流籠中繼站 俯瞰太魯閣遊客中心" loading="lazy"></p><p>→取中繼站右後方陡下石瀑路徑，雖少走500公尺，卻超級難走，反而更耗時(不建議由此下山)<br><img src="https://1013399.github.io/image-3/135/785225083_l.jpg" alt="立霧山 石瀑路徑" loading="lazy"></p><p>→接回得卡倫步道，取右回<br><img src="https://1013399.github.io/image-3/135/785225571_l.jpg" alt="得卡倫步道" loading="lazy"></p><p>→回到太魯閣遊客中心停車場<br><img src="https://1013399.github.io/image-3/135/785225804_l.jpg" alt="太魯閣遊客中心停車場" loading="lazy"></p><p>→夜宿雲莊谷園(此民宿距離祖輪山登山口迴頭彎僅1公里)</p><h2 id="走走資訊" tabindex="-1"><a class="header-anchor" href="#走走資訊" aria-hidden="true">#</a> 走走資訊</h2><div class="hint-container info"><p class="hint-container-title">相關信息</p><p>走走時間：約9.5小時(含午餐、休息約2.5小時)</p><p>高低落差：約1180公尺</p></div><h2 id="參考地圖" tabindex="-1"><a class="header-anchor" href="#參考地圖" aria-hidden="true">#</a> 參考地圖</h2>',55),d={href:"http://www.keepon.com.tw/DiscussLoad.aspx?code=314B5CF9AEC3A19113F6CAA6F539A662BD9C920770115147",target:"_blank",rel:"noopener noreferrer"},_=i("br",null,null,-1),u=i("img",{src:"https://1013399.github.io/image-3/135/785237507_l.jpg",alt:"得卡倫步道、立霧山 參考地圖",loading:"lazy"},null,-1);function j(y,z){const a=p("ExternalLinkIcon");return o(),r("div",null,[n,i("p",null,[t("(資料摘自："),i("a",m,[t("台灣山岳雜誌第109期"),g(a)]),t(")")]),b,s(" more "),c,i("p",null,[t("原圖連結→"),i("a",d,[t("登山補給站-brownlee"),g(a)]),_,u])])}const w=l(h,[["render",j],["__file","post-135-2013-11-13.html.vue"]]);export{w as default};
