import{_ as l}from"./plugin-vue_export-helper-c27b6911.js";import{r as p,o,c as r,a as s,b as i,f as t,d as g,e}from"./app-c5577115.js";const h={},n=i("p",null,[i("strong",null,"跑馬古道"),t("位於宜蘭縣礁溪鄉，屬於「淡蘭古道」南線的其中一段，位於石牌(北宜公路最高點，新北、宜蘭交界)與礁溪之間，因二次大戰期間，日軍官兵經常騎馬巡邏於這條道路而得名，是北宜古道保存最完整的一段，沿線依山傍崖、山明水秀，途中可眺望龜山島景緻及蘭陽平原風貌，是個坡度和緩、景色優美的熱門登山健行步道。")],-1),m=i("p",null,"跑馬古道全長約6.5Km，約分為三段：第一段是由石牌至上新花園，長約2Km；第二段為上新花園至古道南口，約3.1Km；第三段則為古道南口至五峰路的產業道路，約1.4Km，大部份的遊客都選擇走第二段的菁華路段。今日參加公司登山社活動，由石牌縱走至礁溪五峰路，由上往下走，屬輕鬆健行活動。",-1),b=e('<p>走走日期： 2013/10/19</p><h2 id="行車路線" tabindex="-1"><a class="header-anchor" href="#行車路線" aria-hidden="true">#</a> 行車路線</h2><p>國道3號轉國道5號下坪林交流道，經過坪林接台9線往宜蘭方向，至台9線56.5Km石牌公園(新北市與宜蘭縣交界處)下車。</p><h2 id="走走路線" tabindex="-1"><a class="header-anchor" href="#走走路線" aria-hidden="true">#</a> 走走路線</h2><p>→石牌公園下車<br><img src="https://1013399.github.io/image-3/137/654423023_l.jpg" alt="跑馬古道 石牌公園" loading="lazy"></p><p>→石牌公園位於台9線56.5Km處，前方即為跑馬古道北端入口<br><img src="https://1013399.github.io/image-3/137/654424196_l.jpg" alt="跑馬古道" loading="lazy"></p><p>→出發前先拍張合照<br><img src="https://1013399.github.io/image-3/137/654425024_l.jpg" alt="跑馬古道" loading="lazy"></p><p>→入口旁有座虎字碑<br><img src="https://1013399.github.io/image-3/137/654425829_l.jpg" alt="跑馬古道" loading="lazy"></p><p>→入口旁設有步道導覽圖<br><img src="https://1013399.github.io/image-3/137/654427642_l.jpg" alt="跑馬古道" loading="lazy"></p><p>→進入步道便可見「金面大觀」石碑，此處居高臨下，可眺望蘭陽平原美景<br><img src="https://1013399.github.io/image-3/137/654432014_l.jpg" alt="跑馬古道 金面大觀 石碑" loading="lazy"></p><p>→步道緩下好走<br><img src="https://1013399.github.io/image-3/137/654433479_l.jpg" alt="跑馬古道" loading="lazy"></p><p>→沿途多處設有植物解說牌<br><img src="https://1013399.github.io/image-3/137/654434074_l.jpg" alt="跑馬古道" loading="lazy"></p><p>→空曠處往左俯瞰蘭陽平原<br><img src="https://1013399.github.io/image-3/137/654434629_l.jpg" alt="跑馬古道" loading="lazy"></p><p>→往右仰望鵲子山(小百岳No.84，也稱鴻子山或礁溪富士山)<br><img src="https://1013399.github.io/image-3/137/654435065_l.jpg" alt="跑馬古道 鵲子山" loading="lazy"></p><p>→進入樹林中，林蔭遮天、清涼舒適<br><img src="https://1013399.github.io/image-3/137/654435694_l.jpg" alt="跑馬古道" loading="lazy"></p><p>→石頭公<br><img src="https://1013399.github.io/image-3/137/654436246_l.jpg" alt="跑馬古道 石頭公" loading="lazy"></p><p>→步道0.5K木樁，每500公尺設一根<br><img src="https://1013399.github.io/image-3/137/654436776_l.jpg" alt="跑馬古道 步道0.5K木樁" loading="lazy"></p><p>→步道轉趨明顯下坡<br><img src="https://1013399.github.io/image-3/137/654437192_l.jpg" alt="跑馬古道" loading="lazy"></p><p>→接上柏油產業道路，左往台9線，取右續行<br><img src="https://1013399.github.io/image-3/137/654437562_l.jpg" alt="跑馬古道" loading="lazy"></p><p>→叉路，右往圓通寺可登鵲子山，續往前行<br><img src="https://1013399.github.io/image-3/137/654437850_l.jpg" alt="跑馬古道" loading="lazy"></p><p>→日據時期駐在所遺址，左側為荒廢的上新花園(已被鐵皮圍起來)<br><img src="https://1013399.github.io/image-3/137/654438584_l.jpg" alt="跑馬古道 日據時期駐在所遺址" loading="lazy"></p><p>→進入跑馬古道第二段<br><img src="https://1013399.github.io/image-3/137/654438926_l.jpg" alt="跑馬古道" loading="lazy"></p><p>→碎石步道緩下，竹林夾道<br><img src="https://1013399.github.io/image-3/137/654439298_l.jpg" alt="跑馬古道" loading="lazy"></p><p>→過猴洞坑溪<br><img src="https://1013399.github.io/image-3/137/654439700_l.jpg" alt="跑馬古道 猴洞坑溪" loading="lazy"></p><p>→溪旁有顆跑馬古道勒石，是由礁溪書法家周澄先生所題<br><img src="https://1013399.github.io/image-3/137/654439922_l.jpg" alt="馬古道勒石 由礁溪書法家周澄先生所題" loading="lazy"></p><p>→觀景平台<br><img src="https://1013399.github.io/image-3/137/654441242_l.jpg" alt="跑馬古道 觀景平台" loading="lazy"></p><p>→觀景平台展望<br><img src="https://1013399.github.io/image-3/137/654441925_l.jpg" alt="跑馬古道" loading="lazy"></p><p>→觀景平台附近為昔日的黑鉛採礦場<br><img src="https://1013399.github.io/image-3/137/654442815_l.jpg" alt="跑馬古道 昔日的黑鉛採礦場" loading="lazy"></p><p>→山神廟，麻竹坑溪旁<br><img src="https://1013399.github.io/image-3/137/654443587_l.jpg" alt="跑馬古道 山神廟，麻竹坑溪旁" loading="lazy"></p><p>→俯瞰蘭陽平原，蘭陽平原是蘭陽溪沖積而成的「沖積扇」<br><img src="https://1013399.github.io/image-3/137/654444413_l.jpg" alt="跑馬古道 俯瞰蘭陽平原 沖積扇" loading="lazy"></p><p>→眺望龜山島與蘭陽平原，解說牌敘述龜將軍與蘭陽公主的愛情故事<br><img src="https://1013399.github.io/image-3/137/654444894_l.jpg" alt="跑馬古道 眺望龜山島與蘭陽平" loading="lazy"></p><p>→「龜蛇把海口」，龜神與蛇神共同守護著蘭陽平原這片好山好水<br><img src="https://1013399.github.io/image-3/137/654445603_l.jpg" alt="跑馬古道 龜蛇把海口" loading="lazy"></p><p>→山字水泥柱<br><img src="https://1013399.github.io/image-3/137/654445800_l.jpg" alt="跑馬古道 山字水泥柱" loading="lazy"></p><p>→位於跑馬幹四九電信木桿下<br><img src="https://1013399.github.io/image-3/137/654445926_l.jpg" alt="跑馬古道" loading="lazy"></p><p>→接上水泥產道，右不知去向，續直行<br><img src="https://1013399.github.io/image-3/137/654446512_l.jpg" alt="跑馬古道 水泥產道" loading="lazy"></p><p>→叉路，左下往猴洞坑瀑布，續直行<br><img src="https://1013399.github.io/image-3/137/654446800_l.jpg" alt="跑馬古道" loading="lazy"></p><p>→叉路，直行往民宅，取左下<br><img src="https://1013399.github.io/image-3/137/654446920_l.jpg" alt="跑馬古道" loading="lazy"></p><p>→木馬道與木馬展示區，眺望蘭陽平原、國道5號<br><img src="https://1013399.github.io/image-3/137/654447066_l.jpg" alt="跑馬古道 木馬道與木馬展示區，眺望蘭陽平原、國道5號" loading="lazy"></p><p>→俯瞰礁溪地區<br><img src="https://1013399.github.io/image-3/137/654447279_l.jpg" alt="跑馬古道 俯瞰礁溪地區" loading="lazy"></p><p>→續行碎石路，寬闊好走，緩緩向下<br><img src="https://1013399.github.io/image-3/137/654447539_l.jpg" alt="跑馬古道" loading="lazy"></p><p>→叉路，右上有休憩涼棚，續直行<br><img src="https://1013399.github.io/image-3/137/654448049_l.jpg" alt="跑馬古道" loading="lazy"></p><p>→十一股溪木橋<br><img src="https://1013399.github.io/image-3/137/654448780_l.jpg" alt="跑馬古道 十一股溪木橋" loading="lazy"></p><p>→過橋後，右側為玉龍居涼亭<br><img src="https://1013399.github.io/image-3/137/654449051_l.jpg" alt="跑馬古道 玉龍居涼亭" loading="lazy"></p><p>→跑馬古道南端入口，約在步道5.1K處<br><img src="https://1013399.github.io/image-3/137/654449334_l.jpg" alt="跑馬古道南端入口" loading="lazy"></p><p>→跑馬古道南端入口意象<br><img src="https://1013399.github.io/image-3/137/654449858_l.jpg" alt="跑馬古道南端入口" loading="lazy"></p><p>→叉路，右往清修宮也可登鵲子山，續直走<br><img src="https://1013399.github.io/image-3/137/654450495_l.jpg" alt="跑馬古道" loading="lazy"></p><p>→經過一座碉堡<br><img src="https://1013399.github.io/image-3/137/654452277_l.jpg" alt="跑馬古道 碉堡" loading="lazy"></p><p>→公廁，前有叉路，右上可往礁溪山，左下階梯可往毘盧寺，續直行<br><img src="https://1013399.github.io/image-3/137/654452795_l.jpg" alt="跑馬古道" loading="lazy"></p><p>→到達五峰路，遊覽車已在此等候，路口有6.5K木樁及導覽圖<br><img src="https://1013399.github.io/image-3/137/654453511_l.jpg" alt="跑馬古道終點" loading="lazy"></p><p>→跑馬古道終點6.5K木樁<br><img src="https://1013399.github.io/image-3/137/654453829_l.jpg" alt="跑馬古道終點6.5K木樁" loading="lazy"></p><p>→上車，前往五峰旗瀑布風景區走走</p><h2 id="走走資訊" tabindex="-1"><a class="header-anchor" href="#走走資訊" aria-hidden="true">#</a> 走走資訊</h2><div class="hint-container info"><p class="hint-container-title">相關信息</p><p>走走時間：約2小時</p><p>高低落差：約下降490公尺</p></div><h2 id="參考地圖" tabindex="-1"><a class="header-anchor" href="#參考地圖" aria-hidden="true">#</a> 參考地圖</h2>',54),c={href:"http://recreation.forest.gov.tw/RT/RT_2_1.aspx?TR_ID=007",target:"_blank",rel:"noopener noreferrer"},d=i("br",null,null,-1),_=i("img",{src:"https://1013399.github.io/image-3/137/654469088_l.jpg",alt:"跑馬古道 參考地圖",loading:"lazy"},null,-1),u={href:"http://www.tonyhuang39.com/tony0276.html",target:"_blank",rel:"noopener noreferrer"},y=i("br",null,null,-1),j=i("img",{src:"https://1013399.github.io/image-3/137/654469930_l.jpg",alt:"跑馬古道 參考地圖",loading:"lazy"},null,-1);function z(f,K){const a=p("ExternalLinkIcon");return o(),r("div",null,[n,m,s(" more "),b,i("p",null,[t("原圖連結→"),i("a",c,[t("台灣山林悠遊網"),g(a)]),d,_]),i("p",null,[t("原圖連結→"),i("a",u,[t("Tony的自然人文旅記"),g(a)]),y,j])])}const v=l(h,[["render",z],["__file","post-137-2013-10-23.html.vue"]]);export{v as default};
