import{_ as p}from"./plugin-vue_export-helper-c27b6911.js";import{r as o,o as r,c as e,b as i,f as t,d as l,a as g,e as s}from"./app-a1659b92.js";const h={},n=i("p",null,[i("strong",null,"石光古道"),t("，是昔日是石岡子與龍潭之間的一條重要農產古道，已有近兩百年的歷史，直到幾十年前公路興建後，古道才逐漸沒落。近年在當地社區發展協會努力下，獲政府專款輔助，由石光社區發展協會及政府重新整修石光古道，古道的起點為石光國中，終點為茶園涼亭，並於沿途設置涼亭、觀景台及木椅，以供遊客休憩。本次調查將石光步道及石光古道合起來，石光步道為聖母院至茶園涼亭。因為此步道行經老虎山故當地人稱此條步道為老虎山步道。")],-1),m={href:"http://travel.hsinchu.gov.tw/page.aspx?wtp=1&wnd=63&id=311",target:"_blank",rel:"noopener noreferrer"},b=i("p",null,[i("strong",null,"面埔頂山"),t("，海拔294公尺，位於石光古道旁，有顆三等三角點No.40基石，山頂無視野。")],-1),c={href:"http://blog.xuite.net/shiun101/1013399/23997470",target:"_blank",rel:"noopener noreferrer"},d=s('<p>走走日期：2013/10/05</p><h2 id="行車路線" tabindex="-1"><a class="header-anchor" href="#行車路線" aria-hidden="true">#</a> 行車路線</h2><p>今日活動採自行開車方式，家中出發上68快速道路，下芎林交流道往芎林方向，至芎林續往新埔行駛(115縣道)，過了監理所轉118縣道往關西，行駛至118縣道20.4Km國道3號橋下停車，搭乘公司準備的接駁車至石光國中下車。</p><h2 id="走走路線" tabindex="-1"><a class="header-anchor" href="#走走路線" aria-hidden="true">#</a> 走走路線</h2><p>→石光國中，位於關西鎮石岡子308巷內<br><img src="https://1013399.github.io/image-3/139/531000189_l.jpg" alt="石光國中" loading="lazy"></p><p>→報到後出發，經過石光人文藝術館<br><img src="https://1013399.github.io/image-3/139/531006164_l.jpg" alt="石光人文藝術館" loading="lazy"></p><p>→石光古道入口，位於石光一號橋橋頭<br><img src="https://1013399.github.io/image-3/139/531007809_l.jpg" alt="石光古道入口，位於石光一號橋橋頭" loading="lazy"></p><p>→石光古道緣由解說牌<br><img src="https://1013399.github.io/image-3/139/531008831_l.jpg" alt="石光古道緣由解說牌" loading="lazy"></p><p>→石光古道入口意象<br><img src="https://1013399.github.io/image-3/139/531009805_l.jpg" alt="石光古道入口" loading="lazy"></p><p>→經過羅家三合院古厝─豫章堂<br><img src="https://1013399.github.io/image-3/139/531011067_l.jpg" alt="石光古道 羅家三合院古厝─豫章堂" loading="lazy"></p><p>→進入土石山徑<br><img src="https://1013399.github.io/image-3/139/531012182_l.jpg" alt="石光古道" loading="lazy"></p><p>→鵝卵石步道，略陡<br><img src="https://1013399.github.io/image-3/139/531013810_l.jpg" alt="石光古道" loading="lazy"></p><p>→休憩小平台，續往上行<br><img src="https://1013399.github.io/image-3/139/531014518_l.jpg" alt="石光古道" loading="lazy"></p><p>→大鵝卵石路，一路蜿蜒而上<br><img src="https://1013399.github.io/image-3/139/531015642_l.jpg" alt="石光古道" loading="lazy"></p><p>→休息椅，沿途共設了10張，已被重新油漆過，編號已不見<br><img src="https://1013399.github.io/image-3/139/531017358_l.jpg" alt="石光古道" loading="lazy"></p><p>→步道旁客家話諺語及「大伯公」木牌<br><img src="https://1013399.github.io/image-3/139/531018281_l.jpg" alt="客家話諺語及「大伯公」木牌" loading="lazy"></p><p>→抵達第一座涼亭，再往前叉路有指標，可往乾元宮(路跡不明顯)<br><img src="https://1013399.github.io/image-3/139/531019249_l.jpg" alt="石光古道 第一座涼亭" loading="lazy"></p><p>→休息後續行<br><img src="https://1013399.github.io/image-3/139/531021364_l.jpg" alt="石光古道" loading="lazy"></p><p>→「三伯公」木牌，不知何意<br><img src="https://1013399.github.io/image-3/139/531022179_l.jpg" alt="石光古道 「三伯公」木牌" loading="lazy"></p><p>→步道500公尺處<br><img src="https://1013399.github.io/image-3/139/531023865_l.jpg" alt="石光古道 步道500公尺處" loading="lazy"></p><p>→步道轉為平緩土路<br><img src="https://1013399.github.io/image-3/139/531024582_l.jpg" alt="石光古道" loading="lazy"></p><p>→抵達觀景台<br><img src="https://1013399.github.io/image-3/139/531025253_l.jpg" alt="石光古道 觀景台" loading="lazy"></p><p>→觀景台下設有沿途植物的解說牌<br><img src="https://1013399.github.io/image-3/139/531026677_l.jpg" alt="石光古道 植物的解說牌" loading="lazy"></p><p>→登上觀景台二樓，設有景觀對照圖，但雨霧中展望差<br><img src="https://1013399.github.io/image-3/139/531028446_l.jpg" alt="石光古道 觀景台二樓" loading="lazy"></p><p>→休息後續行，步道自此接水泥產業道路<br><img src="https://1013399.github.io/image-3/139/531030422_l.jpg" alt="石光古道 水泥產業道路" loading="lazy"></p><p>→「七伯公」木牌<br><img src="https://1013399.github.io/image-3/139/531031837_l.jpg" alt="石光古道 「七伯公」木牌" loading="lazy"></p><p>→9號休息椅，尚未重新油漆過，編號仍在<br><img src="https://1013399.github.io/image-3/139/531033668_l.jpg" alt="石光古道 9號休息椅" loading="lazy"></p><p>→休息椅展望，俯瞰國道3號<br><img src="https://1013399.github.io/image-3/139/531034188_l.jpg" alt="石光古道 俯瞰國道3號" loading="lazy"></p><p>→續行平緩的水泥產道<br><img src="https://1013399.github.io/image-3/139/531034864_l.jpg" alt="石光古道 水泥產道" loading="lazy"></p><p>→叉路，先取右探面埔頂山，約3分鐘可達<br><img src="https://1013399.github.io/image-3/139/531035521_l.jpg" alt="石光古道、面埔頂山" loading="lazy"></p><p>→面埔頂山，三等三角點No.40，拍照後原路回<br><img src="https://1013399.github.io/image-3/139/531036320_l.jpg" alt="面埔頂山，三等三角點No.40" loading="lazy"></p><p>→回步道續行遇叉路，左往東平里，續直行<br><img src="https://1013399.github.io/image-3/139/531037247_l.jpg" alt="石光古道、面埔頂山" loading="lazy"></p><p>→第二座涼亭，此處為石光古道終點，左叉路往龍潭，取右下往露德聖母朝聖地<br><img src="https://1013399.github.io/image-3/139/531038943_l.jpg" alt="石光古道終點" loading="lazy"></p><p>→經過茅子埔34之8號民宅<br><img src="https://1013399.github.io/image-3/139/531039812_l.jpg" alt="石光古道、面埔頂山 茅子埔" loading="lazy"></p><p>→叉路，左可經龍元宮接118縣道，取右續行<br><img src="https://1013399.github.io/image-3/139/531042315_l.jpg" alt="石光古道、面埔頂山 經龍元宮接118縣道" loading="lazy"></p><p>→抵達露德聖母朝聖地後門，右側為其停車場<br><img src="https://1013399.github.io/image-3/139/531044032_l.jpg" alt="石光古道、面埔頂山 露德聖母朝聖地" loading="lazy"></p><p>→露德聖母朝聖地，牧師傳道中，不便參觀打擾<br><img src="https://1013399.github.io/image-3/139/531044908_l.jpg" alt="石光古道、面埔頂山 露德聖母朝聖地" loading="lazy"></p><p>→取正前方小徑下階梯<br><img src="https://1013399.github.io/image-3/139/531046560_l.jpg" alt="石光古道、面埔頂山" loading="lazy"></p><p>→一路陡下階梯，這是天主教徒的朝聖步道<br><img src="https://1013399.github.io/image-3/139/531049400_l.jpg" alt="石光古道、面埔頂山 天主教徒的朝聖步道" loading="lazy"></p><p>→過水泥橋，出拱門後接118縣道<br><img src="https://1013399.github.io/image-3/139/531052598_l.jpg" alt="石光古道、面埔頂山 水泥橋，出拱門後接118縣道" loading="lazy"></p><p>→取左回到國道3號橋下取車，前往宏軒客家餐廳聚餐<br><img src="https://1013399.github.io/image-3/139/531054119_l.jpg" alt="國道3號橋下" loading="lazy"></p><h2 id="走走資訊" tabindex="-1"><a class="header-anchor" href="#走走資訊" aria-hidden="true">#</a> 走走資訊</h2><div class="hint-container info"><p class="hint-container-title">相關信息</p><p>走走時間：約100分鐘(含休息)</p><p>高低落差：約200公尺</p></div><h2 id="參考地圖" tabindex="-1"><a class="header-anchor" href="#參考地圖" aria-hidden="true">#</a> 參考地圖</h2>',44),_={href:"http://www.tonyhuang39.com/tony0438/tony0438.html",target:"_blank",rel:"noopener noreferrer"},u=i("br",null,null,-1),y=i("img",{src:"https://1013399.github.io/image-3/139/531058602_l.jpg",alt:"石光古道、面埔頂山 參考地圖",loading:"lazy"},null,-1);function j(z,f){const a=o("ExternalLinkIcon");return r(),e("div",null,[n,i("p",null,[t("(資料來源："),i("a",m,[t("新竹縣政府交通旅遊處"),l(a)]),t(")")]),b,g(" more "),g(" TODO: 更新文章連結 "),i("p",null,[t("2008/08/10曾造訪過石光古道("),i("a",c,[t("前篇文章"),l(a)]),t(")，但未拍照，今日參加公司福利會健行活動，再做個記錄，同時因受菲特颱風外圍環流影響，於風雨中進行，但古道還算好走，並未影響走完全程。")]),d,i("p",null,[t("原圖連結→"),i("a",_,[t("Tony的自然人文旅記"),l(a)]),u,y])])}const k=p(h,[["render",j],["__file","post-139-2013-10-08.html.vue"]]);export{k as default};
