import{_ as g}from"./plugin-vue_export-helper-c27b6911.js";import{r as e,o as s,c as r,b as t,f as i,d as a,a as n,w as p,e as h}from"./app-7f62a6ff.js";const d={},c=t("p",null,[t("strong",null,"太武山"),i("位於金門東半島中部，雄偉莊嚴，由於其形狀有如武士的帽，故以太武山為名。海拔高度253公尺，為金門第一高峰。越江遠望，山形蜿蜒，若仙人倒地之狀，故云：「浯江斷嶼入海水，仙人倒地臥不起。」因此太武山別稱仙山，俗稱大山。")],-1),m={href:"http://tour.kinmen.gov.tw/chinese/trip_detail.aspx?sn=22&n=10474",target:"_blank",rel:"noopener noreferrer"},u=h('<p>該山名列台灣小百岳No.099。</p><p>登太武山路徑有四，一般都由太武山公墓啟登，另有屏東線、斗門古道及蔡厝古道。太武山主峰位於海印寺後方，有顆先總統蔣中正所題毋忘在莒石刻，而金門最高峰位於北太武山，標高262公尺為，立有三角點，另有南太武山，標高173公尺，但北太武山與南太武山皆是軍事管制區，無法進入。</p><p>走走日期：2013/09/06</p><h2 id="交通路線" tabindex="-1"><a class="header-anchor" href="#交通路線" aria-hidden="true">#</a> 交通路線</h2><p>從伯玉路轉玉章路進入太武山山區，停車於太武山公墓停車場。</p><h2 id="走走路線" tabindex="-1"><a class="header-anchor" href="#走走路線" aria-hidden="true">#</a> 走走路線</h2><p>→太武山公墓牌樓，續往前停車場停車</p><figure><img src="https://1013399.github.io/image-3/145/344762284_l.jpg" alt="太武山" tabindex="0" loading="lazy"><figcaption>太武山</figcaption></figure><p>→陣亡將士紀念碑，後方為忠烈祠<br><img src="https://1013399.github.io/image-3/145/344763240_l.jpg" alt="太武山" loading="lazy"></p><p>→出發前先拍張合照<br><img src="https://1013399.github.io/image-3/145/403603348_l.jpg" alt="太武山" loading="lazy"></p><p>→取左往營區，於營區前再右轉<br><img src="https://1013399.github.io/image-3/145/344763860_l.jpg" alt="太武山" loading="lazy"></p><p>→至路底遇營區取左，路口有太武山指標<br><img src="https://1013399.github.io/image-3/145/344764553_l.jpg" alt="太武山" loading="lazy"></p><p>→經過玉章路牌樓<br><img src="https://1013399.github.io/image-3/145/344765026_l.jpg" alt="太武山" loading="lazy"></p><p>→沿著水泥路往上<br><img src="https://1013399.github.io/image-3/145/344765676_l.jpg" alt="太武山" loading="lazy"></p><p>→思源亭<br><img src="https://1013399.github.io/image-3/145/344766346_l.jpg" alt="太武山" loading="lazy"></p><p>→劉玉章雕像<br><img src="https://1013399.github.io/image-3/145/344767054_l.jpg" alt="太武山" loading="lazy"></p><p>→途中眺望太武山東峰<br><img src="https://1013399.github.io/image-3/145/344767539_l.jpg" alt="太武山" loading="lazy"></p><p>→俯瞰太湖、料羅港<br><img src="https://1013399.github.io/image-3/145/344768125_l.jpg" alt="太武山" loading="lazy"></p><p>→太武山的地質為花崗片麻岩<br><img src="https://1013399.github.io/image-3/145/344768722_l.jpg" alt="太武山" loading="lazy"></p><p>→倒影塔叉路<br><img src="https://1013399.github.io/image-3/145/344769376_l.jpg" alt="太武山" loading="lazy"></p><p>→取左上往倒影塔<br><img src="https://1013399.github.io/image-3/145/344770069_l.jpg" alt="太武山" loading="lazy"></p><p>→上至觀景台<br><img src="https://1013399.github.io/image-3/145/344770840_l.jpg" alt="太武山" loading="lazy"></p><p>→觀景台視野佳<br><img src="https://1013399.github.io/image-3/145/344771439_l.jpg" alt="太武山" loading="lazy"></p><p>→觀景台後方洞穴為延平郡王觀兵奕棋處<br><img src="https://1013399.github.io/image-3/145/344774168_l.jpg" alt="太武山" loading="lazy"></p><p>→倒影塔，金門三大古塔之一，據說海裡魚群看到此塔倒影，就會聚集過來<br><img src="https://1013399.github.io/image-3/145/344773108_l.jpg" alt="太武山" loading="lazy"></p><p>→退回叉路續行，不久便抵達毋忘在莒<br><img src="https://1013399.github.io/image-3/145/344775647_l.jpg" alt="太武山" loading="lazy"></p><p>→毋忘在莒巨石<br><img src="https://1013399.github.io/image-3/145/344776394_l.jpg" alt="太武山" loading="lazy"></p><p>→巨石旁設有毋忘在莒本義說明<br><img src="https://1013399.github.io/image-3/145/344778205_l.jpg" alt="太武山" loading="lazy"></p><p>→時間有限，不去海印寺了，原路折返<br> →回到停車處</p><p>→葡京港式海鮮餐廳晚餐<br><img src="https://1013399.github.io/image-3/145/344780047_l.jpg" alt="太武山" loading="lazy"></p><h2 id="走走資訊" tabindex="-1"><a class="header-anchor" href="#走走資訊" aria-hidden="true">#</a> 走走資訊</h2><div class="hint-container info"><p class="hint-container-title">相關信息</p><p>走走時間：約100分鐘</p><p>高低落差：約150公尺</p></div><h2 id="相關行程" tabindex="-1"><a class="header-anchor" href="#相關行程" aria-hidden="true">#</a> 相關行程</h2>',33),_=t("h2",{id:"參考地圖",tabindex:"-1"},[t("a",{class:"header-anchor",href:"#參考地圖","aria-hidden":"true"},"#"),i(" 參考地圖")],-1),b={href:"http://tour.kinmen.gov.tw/upload/relpic/trip/634232534104236962.jpg",target:"_blank",rel:"noopener noreferrer"},f=t("br",null,null,-1),j=t("img",{src:"https://1013399.github.io/image-3/145/403618344_l.jpg",alt:"太武山 參考地圖",loading:"lazy"},null,-1);function y(z,x){const o=e("ExternalLinkIcon"),l=e("RouterLink");return s(),r("div",null,[c,t("p",null,[i("(資料來源："),t("a",m,[i("金門觀光旅遊網"),a(o)]),i(")")]),n(" more "),u,t("ul",null,[t("li",null,[a(l,{to:"/posts/post-150-2013-09-23.html"},{default:p(()=>[i("【金門金城】莒光樓、水頭古聚落、金門古城、燕南書院、文臺寶塔、虛江嘯臥碣群、翟山坑道")]),_:1})]),t("li",null,[a(l,{to:"/posts/post-149-2013-09-23.html"},{default:p(()=>[i("【金門金寧】金門國家公園(乳山)【金門金城】建功嶼、後浦美麗小鎮(總兵署、陳詩吟洋樓、奎閣、邱良功母節孝坊、模範街)")]),_:1})]),t("li",null,[a(l,{to:"/posts/post-148-2013-09-23.html"},{default:p(()=>[i("【金門金城】漢影雲根碣、梁山、古崗湖、古崗樓")]),_:1})]),t("li",null,[a(l,{to:"/posts/post-147-2013-09-23.html"},{default:p(()=>[i("【金門烈嶼】將軍堡、鐵漢堡、勇士堡、貓公石、湖井頭戰史館、烈女廟、八達樓子、九宮坑道")]),_:1})]),t("li",null,[a(l,{to:"/posts/post-146-2013-09-23.html"},{default:p(()=>[i("【金門金湖】陳景蘭洋樓、成功海防坑道、金門植物園、老兵故事館")]),_:1})]),t("li",null,[a(l,{to:"/posts/post-144-2013-09-23.html"},{default:p(()=>[i("【金門金城】茅山塔、茅山、天后宮")]),_:1})]),t("li",null,[a(l,{to:"/posts/post-143-2013-09-23.html"},{default:p(()=>[i("【金門金沙】馬山三角堡、馬山觀測所、獅山砲陣地、民俗文化村【金門金湖】特約茶室展示館、八二三戰史館、俞大維先生紀念館、迎賓館")]),_:1})]),t("li",null,[a(l,{to:"/posts/post-142-2013-09-23.html"},{default:p(()=>[i("【金門金寧】古寧頭戰史館、古寧頭播音牆、雙鯉濕地自然中心、慈湖三角堡")]),_:1})])]),_,t("p",null,[i("原圖連結→"),t("a",b,[i("金門觀光旅遊網"),a(o)]),f,j])])}const N=g(d,[["render",y],["__file","post-145-2013-09-23.html.vue"]]);export{N as default};
