import{_ as s}from"./plugin-vue_export-helper-c27b6911.js";import{r as p,o as e,c as r,a as h,b as t,d as l,w as g,e as i,f as n}from"./app-cb5d936b.js";const m={},d=t("h2",{id:"走走行程",tabindex:"-1"},[t("a",{class:"header-anchor",href:"#走走行程","aria-hidden":"true"},"#"),i(" 走走行程")],-1),b=t("p",null,[i("上午行程為小金門(烈嶼鄉)半日遊"),t("br"),i(" →水頭碼頭→九宮碼頭→將軍堡→鐵漢堡→勇士堡→貓公石→湖井頭戰史館→烈女廟→八達樓子→九宮坑道→九宮碼頭→水頭碼頭")],-1),c=t("p",null,"走走日期：2013/09/06",-1),_=n('<h2 id="走走路線" tabindex="-1"><a class="header-anchor" href="#走走路線" aria-hidden="true">#</a> 走走路線</h2><p>→水頭碼頭，搭乘09:00浯江號前往小金門，機車一同上船<br><img src="https://1013399.github.io/image-3/147/344717597_l.jpg" alt="水頭碼頭" loading="lazy"></p><p>→十餘分鐘後抵達九宮碼頭<br><img src="https://1013399.github.io/image-3/147/344718527_l.jpg" alt="九宮碼頭" loading="lazy"></p><p>→導遊先帶我們前往將軍堡<br><img src="https://1013399.github.io/image-3/147/344719413_l.jpg" alt="將軍堡" loading="lazy"></p><p>→將軍堡目前已無駐軍，金管處將其改建成小型軍事博物館<br><img src="https://1013399.github.io/image-3/147/344720224_l.jpg" alt="將軍堡" loading="lazy"></p><p>→將軍堡監控大小金門間的「金烈水道」<br><img src="https://1013399.github.io/image-3/147/344720924_l.jpg" alt="將軍堡 金烈水道" loading="lazy"></p><p>→將軍堡設有24個機槍、步槍射口<br><img src="https://1013399.github.io/image-3/147/344721311_l.jpg" alt="將軍堡 24個機槍、步槍射口" loading="lazy"></p><p>→接著來到鐵漢堡，入口貼著告示：要由勇士堡進入參觀<br><img src="https://1013399.github.io/image-3/147/344721694_l.jpg" alt="鐵漢堡" loading="lazy"></p><p>→依地圖前往勇士堡<br><img src="https://1013399.github.io/image-3/147/344722225_l.jpg" alt="鐵漢堡" loading="lazy"></p><p>→勇士堡，又名四八高地，分為上下2層<br><img src="https://1013399.github.io/image-3/147/344723010_l.jpg" alt="勇士堡" loading="lazy"></p><p>→循斜坡進入勇士堡，縣府已規劃鐵漢堡及勇士堡成為地雷主題館<br><img src="https://1013399.github.io/image-3/147/344725770_l.jpg" alt="勇士堡" loading="lazy"></p><p>→取右往鐵漢堡串聯坑道<br><img src="https://1013399.github.io/image-3/147/344726343_l.jpg" alt="鐵漢堡串聯坑道" loading="lazy"></p><p>→串聯坑道內展示金門雷區、地雷歷史與各式地雷介紹<br><img src="https://1013399.github.io/image-3/147/344726886_l.jpg" alt="鐵漢堡串聯坑道" loading="lazy"></p><p>→經過地雷體驗區，兼具聲光效果<br><img src="https://1013399.github.io/image-3/147/344728134_l.jpg" alt="地雷體驗區" loading="lazy"></p><p>→到達鐵漢堡五七戰防砲，除此之外，另有5座機槍堡<br><img src="https://1013399.github.io/image-3/147/344728964_l.jpg" alt="鐵漢堡串聯坑道 鐵漢堡五七戰防砲" loading="lazy"></p><p>→走出坑道<br><img src="https://1013399.github.io/image-3/147/344729515_l.jpg" alt="鐵漢堡串聯坑道" loading="lazy"></p><p>→鐵漢堡地上有砲堡、中山室、彈藥庫<br><img src="https://1013399.github.io/image-3/147/344730152_l.jpg" alt="鐵漢堡地上有砲堡、中山室、彈藥庫" loading="lazy"></p><p>→至鐵漢堡出入口，接道路取右回<br><img src="https://1013399.github.io/image-3/147/344731028_l.jpg" alt="鐵漢堡出入口" loading="lazy"></p><p>→經過黃厝社區<br><img src="https://1013399.github.io/image-3/147/344731637_l.jpg" alt="黃厝社區" loading="lazy"></p><p>→到達三層樓芋頭餐館，吃碗芋頭冰<br><img src="https://1013399.github.io/image-3/147/344732194_l.jpg" alt="芋頭餐館 芋頭冰" loading="lazy"></p><p>→取車前往貓公石<br><img src="https://1013399.github.io/image-3/147/344725077_l.jpg" alt="貓公石" loading="lazy"></p><p>→貓公石觀景台<br><img src="https://1013399.github.io/image-3/147/344724123_l.jpg" alt="貓公石觀景台" loading="lazy"></p><p>→貓公石是指紅色蜂窩狀的鐵質膠結岩塊<br><img src="https://1013399.github.io/image-3/147/344724556_l.jpg" alt="貓公石是指紅色蜂窩狀的鐵質膠結岩塊" loading="lazy"></p><p>→續往湖井頭戰史館參觀<br><img src="https://1013399.github.io/image-3/147/344732783_l.jpg" alt="湖井頭戰史館" loading="lazy"></p><p>→戰史館頂端砌以仿古城垛，凸顯出濃厚的戰地氣息<br><img src="https://1013399.github.io/image-3/147/344733466_l.jpg" alt="湖井頭戰史館" loading="lazy"></p><p>→館前廣場陳列著高砲、五七戰防砲和機槍等武器<br><img src="https://1013399.github.io/image-3/147/344734090_l.jpg" alt="湖井頭戰史館" loading="lazy"></p><p>→館內展示大二膽、九三、八二三歷次戰役實況及烈嶼軍經建設<br><img src="https://1013399.github.io/image-3/147/344734713_l.jpg" alt="湖井頭戰史館" loading="lazy"></p><p>→瞭望體驗區，遠望對岸之廈門地區<br><img src="https://1013399.github.io/image-3/147/344735208_l.jpg" alt="湖井頭戰史館" loading="lazy"></p><p>→接著來到烈女廟<br><img src="https://1013399.github.io/image-3/147/344735832_l.jpg" alt="烈女廟 仙姑廟 貞烈祠" loading="lazy"></p><p>→入口旁小池塘<br><img src="https://1013399.github.io/image-3/147/344736320_l.jpg" alt="烈女廟 仙姑廟 貞烈祠 小池塘" loading="lazy"></p><p>→烈女廟又稱仙姑廟，原稱貞烈祠，供奉王仙姑，一旁廟誌有詳細解說<br><img src="https://1013399.github.io/image-3/147/344737051_l.jpg" alt="烈女廟 仙姑廟 貞烈祠 供奉王仙姑" loading="lazy"></p><p>→參觀完前往八達樓子<br>   八達樓子為紀念中日長城戰役，壯烈成仁的七勇士所建，內有樓梯可通道頂樓<br><img src="https://1013399.github.io/image-3/147/344737745_l.jpg" alt="八達樓子" loading="lazy"></p><p>→來到了九宮坑道入口，就在九宮碼頭旁邊<br><img src="https://1013399.github.io/image-3/147/344738494_l.jpg" alt="九宮坑道入口" loading="lazy"></p><p>→九宮坑道，又稱四維坑道<br><img src="https://1013399.github.io/image-3/147/344740307_l.jpg" alt="九宮坑道" loading="lazy"></p><p>→坑道內採雙丁字型建構，包含5個副坑道及4個出入口<br><img src="https://1013399.github.io/image-3/147/344739589_l.jpg" alt="九宮坑道" loading="lazy"></p><p>→參觀完即結束小金門半日遊，前往九宮碼頭搭乘12:00船班回大金門</p><h2 id="相關行程" tabindex="-1"><a class="header-anchor" href="#相關行程" aria-hidden="true">#</a> 相關行程</h2>',37),u=t("h2",{id:"參考地圖",tabindex:"-1"},[t("a",{class:"header-anchor",href:"#參考地圖","aria-hidden":"true"},"#"),i(" 參考地圖")],-1),j={href:"http://tour.kinmen.gov.tw/upload/relfile/trip/633941419108138807.jpg",target:"_blank",rel:"noopener noreferrer"},y=t("br",null,null,-1),z=t("img",{src:"https://1013399.github.io/image-3/147/343898065_l.jpg",alt:"null",loading:"lazy"},null,-1);function f(x,k){const a=p("RouterLink"),o=p("ExternalLinkIcon");return e(),r("div",null,[d,b,c,h(" more "),_,t("ul",null,[t("li",null,[l(a,{to:"/posts/post-150-2013-09-23.html"},{default:g(()=>[i("【金門金城】莒光樓、水頭古聚落、金門古城、燕南書院、文臺寶塔、虛江嘯臥碣群、翟山坑道")]),_:1})]),t("li",null,[l(a,{to:"/posts/post-149-2013-09-23.html"},{default:g(()=>[i("【金門金寧】金門國家公園(乳山)【金門金城】建功嶼、後浦美麗小鎮(總兵署、陳詩吟洋樓、奎閣、邱良功母節孝坊、模範街)")]),_:1})]),t("li",null,[l(a,{to:"/posts/post-148-2013-09-23.html"},{default:g(()=>[i("【金門金城】漢影雲根碣、梁山、古崗湖、古崗樓")]),_:1})]),t("li",null,[l(a,{to:"/posts/post-146-2013-09-23.html"},{default:g(()=>[i("【金門金湖】陳景蘭洋樓、成功海防坑道、金門植物園、老兵故事館")]),_:1})]),t("li",null,[l(a,{to:"/posts/post-145-2013-09-23.html"},{default:g(()=>[i("【金門金湖】太武山")]),_:1})]),t("li",null,[l(a,{to:"/posts/post-144-2013-09-23.html"},{default:g(()=>[i("【金門金城】茅山塔、茅山、天后宮")]),_:1})]),t("li",null,[l(a,{to:"/posts/post-143-2013-09-23.html"},{default:g(()=>[i("【金門金沙】馬山三角堡、馬山觀測所、獅山砲陣地、民俗文化村【金門金湖】特約茶室展示館、八二三戰史館、俞大維先生紀念館、迎賓館")]),_:1})]),t("li",null,[l(a,{to:"/posts/post-142-2013-09-23.html"},{default:g(()=>[i("【金門金寧】古寧頭戰史館、古寧頭播音牆、雙鯉濕地自然中心、慈湖三角堡")]),_:1})])]),u,t("p",null,[i("原圖連結→"),t("a",j,[i("金門觀光旅遊網"),l(o)]),y,z])])}const v=s(m,[["render",f],["__file","post-147-2013-09-23.html.vue"]]);export{v as default};
