import{_ as l}from"./plugin-vue_export-helper-c27b6911.js";import{r as p,o,c as r,a as e,b as i,f as t,d as g,e as s}from"./app-9b9d0e2c.js";const h={},n=i("p",null,[i("strong",null,"高台山"),t("，海拔1510公尺，位於新竹縣尖石鄉，山頂有三等三角點No.6286及森林三角點，山頂無展望，續往前行至觀景台則展望極佳，其間經過筆直杉木林、針葉鋪地，走來清涼舒適、心曠神怡，此段路被藍天地圖譽為最佳森林浴路段。")],-1),m=i("p",null,[i("strong",null,"島田山"),t("，是指小島田山(海拔1670公尺)、中島田山(海拔1805公尺)和大島田山(海拔1824公尺)三座山頭，山頂皆無基石亦無展望，沿途會經過瘦稜險崖、箭竹、杜鵑林，林相景觀優美。高台山縱走島田山，山友稱之為高島縱走。")],-1),b=s('<p>走走日期：2018/09/22</p><h2 id="行車路線" tabindex="-1"><a class="header-anchor" href="#行車路線" aria-hidden="true">#</a> 行車路線</h2><p>新竹出發，上68快速道路往東行至終點接台3線左轉北上，約於台3線68.5Km右轉120縣道往內灣，經過內灣後，右轉過尖石大橋接竹60鄉道，至那羅溪與錦屏溪交會的三叉路口時，左轉過錦屏大橋，於竹60鄉道6K之前右轉過柿山大橋，依「高台山露營區」指示牌一路前行，約行4.8Km遇T叉路，左轉至高台山第一登山口停車(右往高台山露營區及第二登山口)。</p><h2 id="走走路線" tabindex="-1"><a class="header-anchor" href="#走走路線" aria-hidden="true">#</a> 走走路線</h2><p>→高台山第一登山口<br><img src="https://1013399.github.io/image-1/15/1197676329_l.jpg" alt="DSCN4809.JPG - 新竹尖石高台山、島田山" loading="lazy"></p><p>→拍張合照後出發<br><img src="https://1013399.github.io/image-1/15/1197677129_l.jpg" alt="DSCN4809A.JPG - 新竹尖石高台山、島田山" loading="lazy"></p><p>→一開始就是一路陡上<br><img src="https://1013399.github.io/image-1/15/1197677053_l.jpg" alt="DSCN4811-12.JPG - 新竹尖石高台山、島田山" loading="lazy"></p><p>→叉路，右往第二登山口，取左續行<br><img src="https://1013399.github.io/image-1/15/1197676026_l.jpg" alt="DSCN4813.JPG - 新竹尖石高台山、島田山" loading="lazy"></p><p>→繼續陡上<br><img src="https://1013399.github.io/image-1/15/1197676448_l.jpg" alt="DSCN4814-15.JPG - 新竹尖石高台山、島田山" loading="lazy"></p><p>→上稜線叉路，右往第三登山口(設有路障，似乎不讓人通行)，休息等候隊友<br><img src="https://1013399.github.io/image-1/15/1197676027_l.jpg" alt="DSCN4816.JPG - 新竹尖石高台山、島田山" loading="lazy"></p><p>→取左續行，緩上坡<br><img src="https://1013399.github.io/image-1/15/1197676902_l.jpg" alt="DSCN4817.JPG - 新竹尖石高台山、島田山" loading="lazy"></p><p>→經過兩處休息空地<br><img src="https://1013399.github.io/image-1/15/1197676903_l.jpg" alt="DSCN4818-19.JPG - 新竹尖石高台山、島田山" loading="lazy"></p><p>→山徑再度趨陡<br><img src="https://1013399.github.io/image-1/15/1197677054_l.jpg" alt="DSCN4820-21.JPG - 新竹尖石高台山、島田山" loading="lazy"></p><p>→到達高台山<br><img src="https://1013399.github.io/image-1/15/1197675942_l.jpg" alt="DSCN4824.JPG - 新竹尖石高台山、島田山" loading="lazy"></p><p>→高台山，三等三角點No.6286及森林三角點，在此休息<br><img src="https://1013399.github.io/image-1/15/1197677055_l.jpg" alt="DSCN4825-27.JPG - 新竹尖石高台山、島田山" loading="lazy"></p><p>→拍張合照後續往島田山<br><img src="https://1013399.github.io/image-1/15/1197677272_l.jpg" alt="DSCN4827A.jpg - 新竹尖石高台山、島田山" loading="lazy"></p><p>→進入平緩的杉木林森林浴路段<br><img src="https://1013399.github.io/image-1/15/1197677176_l.jpg" alt="DSCN4828-32.JPG - 新竹尖石高台山、島田山" loading="lazy"></p><p>→走完杉木林，進入上坡雜木林<br><img src="https://1013399.github.io/image-1/15/1197676136_l.jpg" alt="DSCN4834.JPG - 新竹尖石高台山、島田山" loading="lazy"></p><p>→再上稜線，途中展望點，可眺望尖石、關西一帶<br><img src="https://1013399.github.io/image-1/15/1197677130_l.jpg" alt="DSCN4837.JPG - 新竹尖石高台山、島田山" loading="lazy"></p><p>→稜線上坡，有點陡<br><img src="https://1013399.github.io/image-1/15/1197677056_l.jpg" alt="DSCN4838-42.JPG - 新竹尖石高台山、島田山" loading="lazy"></p><p>→先後經過箭竹林、杜鵑林<br><img src="https://1013399.github.io/image-1/15/1197677057_l.jpg" alt="DSCN4843-44.JPG - 新竹尖石高台山、島田山" loading="lazy"></p><p>→小上坡後到達高島觀景台<br><img src="https://1013399.github.io/image-1/15/1197676138_l.jpg" alt="DSCN4846.JPG - 新竹尖石高台山、島田山" loading="lazy"></p><p>→高島觀景台，旁有東-65不鏽鋼牌<br><img src="https://1013399.github.io/image-1/15/1197677132_l.jpg" alt="DSCN4847-48.JPG - 新竹尖石高台山、島田山" loading="lazy"></p><p>→已起霧，展望有限，眺望前方的小島田山<br><img src="https://1013399.github.io/image-1/15/1197676139_l.jpg" alt="DSCN4849.JPG - 新竹尖石高台山、島田山" loading="lazy"></p><p>→休息後續行往島田山<br><img src="https://1013399.github.io/image-1/15/1197677274_l.jpg" alt="DSCN4850.JPG - 新竹尖石高台山、島田山" loading="lazy"></p><p>→叉路，左為稜線路徑、右為腰繞路，皆可到達小島田山，取右腰繞路<br>   (要上小島田山，有三個登山口，此處為第一個登山口，需越過兩個小山頭)<br><img src="https://1013399.github.io/image-1/15/1197676140_l.jpg" alt="DSCN4852.JPG - 新竹尖石高台山、島田山" loading="lazy"></p><p>→叉路，旁有東-71不鏽鋼牌，小島田山第二個登山口<br><img src="https://1013399.github.io/image-1/15/1197676029_l.jpg" alt="DSCN4853.JPG - 新竹尖石高台山、島田山" loading="lazy"></p><p>→由此左上往小島田山<br><img src="https://1013399.github.io/image-1/15/1197676828_l.jpg" alt="DSCN4855.JPG - 新竹尖石高台山、島田山" loading="lazy"></p><p>→叉路，旁有東-72不鏽鋼牌，在此與第一個登山口上來之路徑會合，取右續行<br><img src="https://1013399.github.io/image-1/15/1197677378_l.jpg" alt="DSCN4856.JPG - 新竹尖石高台山、島田山" loading="lazy"></p><p>→叉路，右下往腰繞路(小島田山第三個登山口)，續直行<br>   (若由第三個登山口上小島田山，可少爬兩個小山頭)<br><img src="https://1013399.github.io/image-1/15/1197676449_l.jpg" alt="DSCN4858.JPG - 新竹尖石高台山、島田山" loading="lazy"></p><p>→陡上<br><img src="https://1013399.github.io/image-1/15/1197676450_l.jpg" alt="DSCN4860.JPG - 新竹尖石高台山、島田山" loading="lazy"></p><p>→到達小島田山，無基石亦無展望<br><img src="https://1013399.github.io/image-1/15/1197677276_l.jpg" alt="DSCN4861.JPG - 新竹尖石高台山、島田山" loading="lazy"></p><p>→休息後，續行下坡往中島田山，舒服的杜鵑林地毯路<br><img src="https://1013399.github.io/image-1/15/1197676830_l.jpg" alt="DSCN4863.JPG - 新竹尖石高台山、島田山" loading="lazy"></p><p>→下抵鞍部後轉上坡<br><img src="https://1013399.github.io/image-1/15/1197676831_l.jpg" alt="DSCN4866.JPG - 新竹尖石高台山、島田山" loading="lazy"></p><p>→窄稜拉繩長陡上，有點危險、必須小心<br><img src="https://1013399.github.io/image-1/15/1197676224_l.jpg" alt="DSCN4867-71.JPG - 新竹尖石高台山、島田山" loading="lazy"></p><p>→到達中島田山，無基石<br><img src="https://1013399.github.io/image-1/15/1197676225_l.jpg" alt="DSCN4872.JPG - 新竹尖石高台山、島田山" loading="lazy"></p><p>→休息後，往大島田山續行<br><img src="https://1013399.github.io/image-1/15/1197677380_l.jpg" alt="DSCN4874.JPG - 新竹尖石高台山、島田山" loading="lazy"></p><p>→下抵鞍部後再陡上<br><img src="https://1013399.github.io/image-1/15/1197677180_l.jpg" alt="DSCN4877-81.JPG - 新竹尖石高台山、島田山" loading="lazy"></p><p>→到達大島田山<br><img src="https://1013399.github.io/image-1/15/1197676452_l.jpg" alt="DSCN4882.JPG - 新竹尖石高台山、島田山" loading="lazy"></p><p>→大島田山，無基石亦無展望，有棵巨大檜木<br><img src="https://1013399.github.io/image-1/15/1197677181_l.jpg" alt="DSCN4883.JPG - 新竹尖石高台山、島田山" loading="lazy"></p><p>→在此午餐、泡茶，休息了約1小時<br><img src="https://1013399.github.io/image-1/15/1197676834_l.jpg" alt="DSCN4884.JPG - 新竹尖石高台山、島田山" loading="lazy"></p><p>→餐畢，拍張合照，準備下山<br><img src="https://1013399.github.io/image-1/15/1197676144_l.jpg" alt="DSCN4884A.jpg - 新竹尖石高台山、島田山" loading="lazy"></p><p>→下山，續往前行循稜而下，走腰繞路回<br><img src="https://1013399.github.io/image-1/15/1197677182_l.jpg" alt="DSCN4885.JPG - 新竹尖石高台山、島田山" loading="lazy"></p><p>→接上腰繞路，左可往東穗山、石麻達山，取右回<br><img src="https://1013399.github.io/image-1/15/1197676835_l.jpg" alt="DSCN4887.JPG - 新竹尖石高台山、島田山" loading="lazy"></p><p>→經過廢棄香菇寮，續行<br><img src="https://1013399.github.io/image-1/15/1197677059_l.jpg" alt="DSCN4889.JPG - 新竹尖石高台山、島田山" loading="lazy"></p><p>→經過小島田山第三個登山口，續行腰繞路<br><img src="https://1013399.github.io/image-1/15/1197676145_l.jpg" alt="DSCN4890.JPG - 新竹尖石高台山、島田山" loading="lazy"></p><p>→回到小島田山第二個登山口(稍早由此上小島田山)，取左續行<br><img src="https://1013399.github.io/image-1/15/1197676146_l.jpg" alt="DSCN4891.JPG - 新竹尖石高台山、島田山" loading="lazy"></p><p>→回到稜線路、腰繞路叉路(小島田山第一個登山口)，續直行<br><img src="https://1013399.github.io/image-1/15/1197676147_l.jpg" alt="DSCN4892.JPG - 新竹尖石高台山、島田山" loading="lazy"></p><p>→回到高島觀景台，休息等候隊友<br><img src="https://1013399.github.io/image-1/15/1197677138_l.jpg" alt="DSCN4894.JPG - 新竹尖石高台山、島田山" loading="lazy"></p><p>→回到杉木林森林浴場，休息20分鐘<br><img src="https://1013399.github.io/image-1/15/1197676837_l.jpg" alt="DSCN4896.JPG - 新竹尖石高台山、島田山" loading="lazy"></p><p>→回到高台山<br><img src="https://1013399.github.io/image-1/15/1197677183_l.jpg" alt="DSCN4897.JPG - 新竹尖石高台山、島田山" loading="lazy"></p><p>→陸續經過高台山第三、第二登山口叉路，皆取右下<br><img src="https://1013399.github.io/image-1/15/1197676149_l.jpg" alt="DSCN4898-99.JPG - 新竹尖石高台山、島田山" loading="lazy"></p><p>→回到第一登山口<br><img src="https://1013399.github.io/image-1/15/1197676150_l.jpg" alt="DSCN4901.JPG - 新竹尖石高台山、島田山" loading="lazy"></p><h2 id="走走資訊" tabindex="-1"><a class="header-anchor" href="#走走資訊" aria-hidden="true">#</a> 走走資訊</h2><div class="hint-container info"><p class="hint-container-title">相關信息</p><p>走走時間：約7.5小時(含休息、午餐、泡茶，逍遙行程)</p><p>高低落差：約760公尺(不含上下起伏)</p></div><h2 id="參考地圖" tabindex="-1"><a class="header-anchor" href="#參考地圖" aria-hidden="true">#</a> 參考地圖</h2>',56),d={href:"https://www.keepon.com.tw/thread-303f276e-15d8-e411-93ec-000e04b74954.html",target:"_blank",rel:"noopener noreferrer"},c=i("br",null,null,-1),_=i("img",{src:"https://1013399.github.io/image-1/15/1197677281_l.jpg",alt:"新竹縣尖石鄉高台山.島田山 登山簡訊(99.4.11).gif - 新竹尖石高台山、島田山",loading:"lazy"},null,-1),u=i("h2",{id:"落差參考圖",tabindex:"-1"},[i("a",{class:"header-anchor",href:"#落差參考圖","aria-hidden":"true"},"#"),t(" 落差參考圖")],-1),N={href:"https://blog.xuite.net/td01031/blog/350863858",target:"_blank",rel:"noopener noreferrer"},j=i("br",null,null,-1),y=i("img",{src:"https://1013399.github.io/image-1/15/1197676339_l.jpg",alt:"高台山島田山落差圖-閒琴藝誌.jpg - 新竹尖石高台山、島田山",loading:"lazy"},null,-1);function z(C,S){const a=p("ExternalLinkIcon");return o(),r("div",null,[n,m,e(" more "),b,i("p",null,[t("原圖連結→"),i("a",d,[t("登山補給站-祥馬圖集"),g(a)]),c,_]),u,i("p",null,[t("原圖連結→"),i("a",N,[t("閒琴藝誌"),g(a)]),j,y])])}const J=l(h,[["render",z],["__file","post-15-2018-10-05.html.vue"]]);export{J as default};
