import{_ as o}from"./plugin-vue_export-helper-c27b6911.js";import{r as l,o as r,c as p,b as a,f as t,d as e,a as n,e as g}from"./app-2f66486a.js";const s={},h=a("p",null,[a("strong",null,"太麻里山"),t("，海拔1340公尺，位於台東縣金峰鄉境內，擁有360度超寬廣視野的一等三角點，是東部地區最具知名度的聖山，當地居民稱為雙乳山，山友習稱金針山。太麻里山以金針花海聞名，每年8至9月金針花盛開期間，整片山林點綴朵朵橙黃金針，吸引眾多山友及攝影愛好者前來獵影。太麻里的峰頂建有一休息涼亭，天氣晴朗時，綠島、蘭嶼、南大武山、北大武山、台東平原、都蘭山盡收眼底；紮營山頂，更可迎接太平洋第一道曙光，該山名列台灣小百岳No.094。")],-1),c={href:"http://www.taiwan100mt.com/mountain.php?mountain=94",target:"_blank",rel:"noopener noreferrer"},d=g('<p>走走日期：2013/08/04</p><h2 id="行車路線" tabindex="-1"><a class="header-anchor" href="#行車路線" aria-hidden="true">#</a> 行車路線</h2><p>走完都蘭山後，回到台11線後右轉南下，於知本附近接台9線續行往太麻里，在太麻里市區金萱路口右轉過金針山的意像牌樓，循指標前進至丁字路口左轉(12.5K指標)，至丁字路環山道路取左經忘憂亭到雙乳峰鞍部停車場停車。</p><p>(往太麻里山部份路段狹窄，至<strong>環山道路最好採左上右下</strong>，以免會車不易)</p><h2 id="走走路線" tabindex="-1"><a class="header-anchor" href="#走走路線" aria-hidden="true">#</a> 走走路線</h2><p>→雙乳峰鞍部停車場，雙乳峰即指千禧亭及曙光亭兩峰，此停車場算是乳溝<br><img src="https://1013399.github.io/image-3/153/262212827_l.jpg" alt="太麻里山" loading="lazy"></p><p>→往右仰望千禧亭，太麻里山三角點在其旁邊<br><img src="https://1013399.github.io/image-3/153/262212830_l.jpg" alt="太麻里山" loading="lazy"></p><p>→往左仰望曙光亭<br><img src="https://1013399.github.io/image-3/153/262212834_l.jpg" alt="太麻里山" loading="lazy"></p><p>→往前取右循階梯往千禧亭<br><img src="https://1013399.github.io/image-3/153/262212838_l.jpg" alt="太麻里山" loading="lazy"></p><p>→登山口旁設有指標及三角點噴漆<br><img src="https://1013399.github.io/image-3/153/262212864_l.jpg" alt="太麻里山" loading="lazy"></p><p>→抵達千禧亭<br><img src="https://1013399.github.io/image-3/153/262212841_l.jpg" alt="太麻里山" loading="lazy"></p><p>→太麻里山一等三角點<br><img src="https://1013399.github.io/image-3/153/262212849_l.jpg" alt="太麻里山" loading="lazy"></p><p>→太麻里為馳名中外迎接曙光的景點<br><img src="https://1013399.github.io/image-3/153/262212844_l.jpg" alt="太麻里山" loading="lazy"></p><p>→山頂展望極佳，但天色已暗、雲霧漸起<br><img src="https://1013399.github.io/image-3/153/262212853_l.jpg" alt="太麻里山" loading="lazy"></p><p>→山頂金針花尚未全面盛開<br><img src="https://1013399.github.io/image-3/153/262212857_l.jpg" alt="太麻里山" loading="lazy"></p><p>→回到鞍部停車場，取車往台東市<br><img src="https://1013399.github.io/image-3/153/262212860_l.jpg" alt="太麻里山" loading="lazy"></p><p>→至「明日香日式餐廳」晚餐(已歇業，台東市杭州街112號)</p><figure><img src="https://1013399.github.io/image-3/153/262212824_l.jpg" alt="明日香日式餐廳" tabindex="0" loading="lazy"><figcaption>明日香日式餐廳</figcaption></figure><p>→享用招牌的「相撲火鍋」(兩人份)<br><img src="https://1013399.github.io/image-3/153/262212868_l.jpg" alt="明日香日式餐廳 相撲火鍋" loading="lazy"></p><p>→日籍老闆中村吉成熱情招待，餐後合照紀念<br><img src="https://1013399.github.io/image-3/153/262212872_l.jpg" alt="明日香日式餐廳" loading="lazy"></p><p>→夜宿中華電信台東會館</p><h2 id="走走資訊" tabindex="-1"><a class="header-anchor" href="#走走資訊" aria-hidden="true">#</a> 走走資訊</h2><div class="hint-container info"><p class="hint-container-title">相關信息</p><p>走走時間：約5分鐘</p><p>高低落差：約20公尺</p></div><h2 id="參考地圖" tabindex="-1"><a class="header-anchor" href="#參考地圖" aria-hidden="true">#</a> 參考地圖</h2>',24),m={href:"http://www.keepon.com.tw/DiscussLoad.aspx?code=314B5CF9AEC3A19113F6CAA6F539A66265D8DFE1D2F71B1D",target:"_blank",rel:"noopener noreferrer"},_=a("br",null,null,-1),b=a("img",{src:"https://1013399.github.io/image-3/153/262212913_l.jpg",alt:"太麻里山 參考地圖",loading:"lazy"},null,-1);function u(f,j){const i=l("ExternalLinkIcon");return r(),p("div",null,[h,a("p",null,[t("(資料來源："),a("a",c,[t("歐都納小百岳"),e(i)]),t(")")]),n(" more "),d,a("p",null,[t("原圖連結→"),a("a",m,[t("登山補給站-brownlee(小李)"),e(i)]),_,b])])}const x=o(s,[["render",u],["__file","post-153-2013-08-13.html.vue"]]);export{x as default};
