import{_ as l}from"./plugin-vue_export-helper-c27b6911.js";import{r as p,o,c as r,b as i,f as t,d as g,a as e,e as s}from"./app-7f62a6ff.js";const h={},n=i("p",null,[i("strong",null,"挑水古道"),t("，為彰化六大挑夫古道之一，介於芬園鄉大竹村與溪頭村之間，清朝時期因道路交通不發達，當地先民為生活飲水問題，發覺此處終年有泉水源源不絕流出，便在泉水湧出的源頭，挖掘兩處取水井，供村民們飲水之用，並將取水必經的山間小路砌成簡易的步道。從芬園鄉員草路一段159號旁日式拱門進入，全長約2公里，除了濃密的荔枝與龍眼樹林外，並有天梯景觀、涼亭與古井，且附近生態完備，也是賞鳥的好去處。")],-1),b={href:"http://tourism.chcg.gov.tw/tc/hopSpotInfo.aspx?id=249&chk=6ab3b646-e134-4f70-befb-f5c4e97abaa2",target:"_blank",rel:"noopener noreferrer"},m=s('<p><strong>碧山古道</strong>，與挑水古道相互平行，沿稜線行進於荔枝與龍眼樹林間，走來涼爽舒適，步道途中，還有陡峭的「鵝卵天梯」銜接兩條步道。</p><p><strong>碧山</strong>，又稱為溪頭，海拔163公尺，有顆土地調查局的圖根點，不在山頭高點，而位於碧山古道上的駁坎旁，樹林中無視野。</p><p>走走日期：2013/06/16</p><h2 id="行車路線" tabindex="-1"><a class="header-anchor" href="#行車路線" aria-hidden="true">#</a> 行車路線</h2><p>國道3號南下，下草屯交流道往草屯方向(台14線、芬草路)，接台63線右轉進入中投快速公路，至台63線終點接台14乙線(碧興路)，遇碧山路(148縣道)右轉往芬園，過溪頭橋後進入彰化縣轉為員草路，約於148縣道35.3K處左轉(有「無極玄明道院」指標)即到達挑水古道起點，入口有仿日式神社拱門，停車於入口旁停車場。</p><h2 id="走走路線" tabindex="-1"><a class="header-anchor" href="#走走路線" aria-hidden="true">#</a> 走走路線</h2><p>→挑水古道入口意象<br><img src="https://1013399.github.io/image-3/156/259412938_l.jpg" alt="挑水古道入口" loading="lazy"></p><p>→仿日式神社拱門，停車左側停車場<br><img src="https://1013399.github.io/image-3/156/259412943_l.jpg" alt="挑水古道、碧山古道、碧山" loading="lazy"></p><p>→挑水古道解說牌<br><img src="https://1013399.github.io/image-3/156/259412948_l.jpg" alt="挑水古道解說牌" loading="lazy"></p><p>→循水泥產業道路緩上，路旁為油桐花與柚子樹<br><img src="https://1013399.github.io/image-3/156/259412958_l.jpg" alt="挑水古道、碧山古道、碧山" loading="lazy"></p><p>→叉路，右上往碧山古道(等會由此回)，續直行挑水古道<br><img src="https://1013399.github.io/image-3/156/259412968_l.jpg" alt="挑水古道、碧山古道、碧山" loading="lazy"></p><p>→休息區，附近也有許多油桐樹，前方叉路(中間隔著排洪溝渠)，取左取右皆可<br><img src="https://1013399.github.io/image-3/156/259412972_l.jpg" alt="挑水古道、碧山古道、碧山" loading="lazy"></p><p>→前一叉路在此會合，右側叉路為鵝卵天梯(超級陡)可往碧山古道，取直續行<br><img src="https://1013399.github.io/image-3/156/259412980_l.jpg" alt="挑水古道、碧山古道、碧山" loading="lazy"></p><p>→接紅磚步道<br><img src="https://1013399.github.io/image-3/156/259412991_l.jpg" alt="挑水古道、碧山古道、碧山" loading="lazy"></p><p>→籃球場大空地，旁有涼亭，不休息續行<br><img src="https://1013399.github.io/image-3/156/259412998_l.jpg" alt="挑水古道、碧山古道、碧山" loading="lazy"></p><p>→第二座涼亭<br><img src="https://1013399.github.io/image-3/156/259413007_l.jpg" alt="挑水古道、碧山古道、碧山" loading="lazy"></p><p>→接花崗石步道<br><img src="https://1013399.github.io/image-3/156/259413017_l.jpg" alt="挑水古道、碧山古道、碧山" loading="lazy"></p><p>→第三座涼亭，此處較涼爽，在此休息<br><img src="https://1013399.github.io/image-3/156/259413027_l.jpg" alt="挑水古道、碧山古道、碧山" loading="lazy"></p><p>→抵達龍鳳湧井，直行可往139縣道(大彰路)，右往古恩公廟接碧山古道，先取左一探<br><img src="https://1013399.github.io/image-3/156/259413035_l.jpg" alt="挑水古道、碧山古道、碧山" loading="lazy"></p><p>→挑水古道就是從這二處古井挑水<br><img src="https://1013399.github.io/image-3/156/259413045_l.jpg" alt="挑水古道、碧山古道、碧山" loading="lazy"></p><p>→走龍鳳湧井對面階梯步道往古恩公廟，不往大彰路(因為比較炎熱)<br><img src="https://1013399.github.io/image-3/156/259413056_l.jpg" alt="挑水古道、碧山古道、碧山" loading="lazy"></p><p>→步道陡上<br><img src="https://1013399.github.io/image-3/156/259413063_l.jpg" alt="挑水古道、碧山古道、碧山" loading="lazy"></p><p>→抵達古恩公廟，據說有百年歷史<br><img src="https://1013399.github.io/image-3/156/259413068_l.jpg" alt="挑水古道、碧山古道、碧山 古恩公廟" loading="lazy"></p><p>→廟前觀景台，視野佳<br><img src="https://1013399.github.io/image-3/156/259413073_l.jpg" alt="挑水古道、碧山古道、碧山" loading="lazy"></p><p>→叉路，左往139縣道(大彰路)，取右碧山古道<br><img src="https://1013399.github.io/image-3/156/259413086_l.jpg" alt="挑水古道、碧山古道、碧山" loading="lazy"></p><p>→叉路，左往東龍宮，取右碧山古道<br><img src="https://1013399.github.io/image-3/156/259413091_l.jpg" alt="挑水古道、碧山古道、碧山" loading="lazy"></p><p>→循果園柏油小徑緩下，兩側盡是荔枝與龍眼<br><img src="https://1013399.github.io/image-3/156/259413102_l.jpg" alt="挑水古道、碧山古道、碧山" loading="lazy"></p><p>→接碎石路<br><img src="https://1013399.github.io/image-3/156/259413111_l.jpg" alt="挑水古道、碧山古道、碧山" loading="lazy"></p><p>→經過高壓電塔<br><img src="https://1013399.github.io/image-3/156/259413124_l.jpg" alt="挑水古道、碧山古道、碧山" loading="lazy"></p><p>→叉路，右上路不通，續直行<br><img src="https://1013399.github.io/image-3/156/259413128_l.jpg" alt="挑水古道、碧山古道、碧山" loading="lazy"></p><p>→叉路，右下為難走超陡鵝卵天梯可往挑水古道，續直行<br><img src="https://1013399.github.io/image-3/156/259413138_l.jpg" alt="挑水古道、碧山古道、碧山" loading="lazy"></p><p>→走出果園，路旁也有許多油桐樹<br><img src="https://1013399.github.io/image-3/156/259413145_l.jpg" alt="挑水古道、碧山古道、碧山" loading="lazy"></p><p>→叉路，直行往溪谷(需注意)，取右回挑水古道<br><img src="https://1013399.github.io/image-3/156/259413152_l.jpg" alt="挑水古道、碧山古道、碧山" loading="lazy"></p><p>→進入香楠森林<br><img src="https://1013399.github.io/image-3/156/259412844_l.jpg" alt="挑水古道、碧山古道、碧山" loading="lazy"></p><p>→越過駁坎<br><img src="https://1013399.github.io/image-3/156/259412866_l.jpg" alt="挑水古道、碧山古道、碧山" loading="lazy"></p><p>→碧山，土地調查局圖根點，不在高點，位於駁坎旁<br><img src="https://1013399.github.io/image-3/156/259412872_l.jpg" alt="挑水古道、碧山古道、碧山" loading="lazy"></p><p>→叉路，左右皆可，取左較好走<br><img src="https://1013399.github.io/image-3/156/259412880_l.jpg" alt="挑水古道、碧山古道、碧山" loading="lazy"></p><p>→接上水泥產業道路<br><img src="https://1013399.github.io/image-3/156/259412891_l.jpg" alt="挑水古道、碧山古道、碧山" loading="lazy"></p><p>→陡下產道(超陡，需小心)<br><img src="https://1013399.github.io/image-3/156/259412902_l.jpg" alt="挑水古道、碧山古道、碧山" loading="lazy"></p><p>→接回挑水古道，取左回<br><img src="https://1013399.github.io/image-3/156/259412922_l.jpg" alt="挑水古道、碧山古道、碧山" loading="lazy"></p><p>→回到挑水古道入口<br><img src="https://1013399.github.io/image-3/156/259412933_l.jpg" alt="挑水古道入口" loading="lazy"></p><h2 id="走走資訊" tabindex="-1"><a class="header-anchor" href="#走走資訊" aria-hidden="true">#</a> 走走資訊</h2><div class="hint-container info"><p class="hint-container-title">相關信息</p><p>走走時間：約1.5小時(含休息)</p><p>高低落差：約230公尺</p></div><h2 id="參考地圖" tabindex="-1"><a class="header-anchor" href="#參考地圖" aria-hidden="true">#</a> 參考地圖</h2>',44),c={href:"http://blog.xuite.net/laoweiz/blog/35137511",target:"_blank",rel:"noopener noreferrer"},d=i("br",null,null,-1),_=i("img",{src:"https://1013399.github.io/image-3/156/259413225_l.jpg",alt:"挑水古道、碧山古道、碧山 參考地圖",loading:"lazy"},null,-1);function u(z,j){const a=p("ExternalLinkIcon");return o(),r("div",null,[n,i("p",null,[t("(資料來源："),i("a",b,[t("彰化縣政府旅遊資訊網"),g(a)]),t(")")]),e(" more "),m,i("p",null,[t("原圖連結→"),i("a",c,[t("老尉子的部落格"),g(a)]),d,_])])}const x=l(h,[["render",u],["__file","post-156-2013-06-18.html.vue"]]);export{x as default};
