import{_ as l}from"./plugin-vue_export-helper-c27b6911.js";import{r as p,o as g,c as e,a as r,b as a,f as i,d as o,e as h}from"./app-63889d57.js";const s={},n=a("p",null,[a("strong",null,"旗山"),i("，海拔1135公尺，山頂有顆三等三角點No.6229，山頂雜草圍繞無視野，登山口位於台7線73.65Km附近，附近地區另有一座森林三角點的「旗山主峰」，容易與此山混淆。")],-1),d=a("p",null,[a("strong",null,"婆羅山"),i("，海拔1415公尺，又稱婆耀山，山頂有顆三等三角點No.6230，為雪山山脈北段棲蘭山區南延支稜，登山口位於台7線66.8Km，山頂視野有限，可見南邊之梵梵山。")],-1),c=a("p",null,"旗山與婆羅山，皆位於宜蘭縣大同鄉，沿途雜草叢生、倒木擋道、路跡稍亂、少人登臨，需緊跟著登山路條走，有山友安排連走婆羅山、梵梵山及烏山，今日參加新竹市登山協會羚羊隊活動，兩座山頭均採原路來回，算是撿三角點的行程。",-1),m=h('<p>走走日期：2018/08/04</p><h2 id="旗山行車路線" tabindex="-1"><a class="header-anchor" href="#旗山行車路線" aria-hidden="true">#</a> 旗山行車路線</h2><p>新竹科學園區管理局搭乘中巴，走國道1號轉國道3號下大溪交流道，接台3線往大溪方向再接台7線，經大溪、慈湖至復興區下巴陵停車場休息後，續行至宜蘭縣大同鄉，經過明池森林遊樂區，於台7線73.5Km處下車。</p><h2 id="旗山走走路線" tabindex="-1"><a class="header-anchor" href="#旗山走走路線" aria-hidden="true">#</a> 旗山走走路線</h2><p>→台7線73.5Km處下車，整裝後出發，往宜蘭方向走(右上林道過木柵門，可往旗山主峰)<br><img src="https://1013399.github.io/image-1/16/1195074183_l.jpg" alt="DSCN4755.JPG - 宜蘭大同旗山、婆羅山" loading="lazy"></p><p>→旗山登山口，約在台7線73.65Km處，護欄上有三角點噴漆<br><img src="https://1013399.github.io/image-1/16/1195074969_l.jpg" alt="DSCN4756.JPG - 宜蘭大同旗山、婆羅山" loading="lazy"></p><p>→跨越護欄，緩下山路(登山口比較高)<br><img src="https://1013399.github.io/image-1/16/1195074184_l.jpg" alt="DSCN4757.JPG - 宜蘭大同旗山、婆羅山" loading="lazy"></p><p>→緩下緩上再陡下<br><img src="https://1013399.github.io/image-1/16/1195075070_l.jpg" alt="DSCN4758-62.JPG - 宜蘭大同旗山、婆羅山" loading="lazy"></p><p>→下至低鞍<br><img src="https://1013399.github.io/image-1/16/1195074381_l.jpg" alt="DSCN4763.JPG - 宜蘭大同旗山、婆羅山" loading="lazy"></p><p>→緩上攻頂<br><img src="https://1013399.github.io/image-1/16/1195075071_l.jpg" alt="DSCN4764-67.JPG - 宜蘭大同旗山、婆羅山" loading="lazy"></p><p>→最後陡上<br><img src="https://1013399.github.io/image-1/16/1195074186_l.jpg" alt="DSCN4768.JPG - 宜蘭大同旗山、婆羅山" loading="lazy"></p><p>→到達旗山山頂<br><img src="https://1013399.github.io/image-1/16/1195074773_l.jpg" alt="DSCN4769.JPG - 宜蘭大同旗山、婆羅山" loading="lazy"></p><p>→旗山，三等三角點No.6229<br><img src="https://1013399.github.io/image-1/16/1195074187_l.jpg" alt="DSCN4770-71.JPG - 宜蘭大同旗山、婆羅山" loading="lazy"></p><p>→全員到齊，拍照、休息後，原路回<br><img src="https://1013399.github.io/image-1/16/1195075072_l.jpg" alt="DSCN4772.JPG - 宜蘭大同旗山、婆羅山" loading="lazy"></p><p>→回到台7線登山口<br><img src="https://1013399.github.io/image-1/16/1195074382_l.jpg" alt="DSCN4774.JPG - 宜蘭大同旗山、婆羅山" loading="lazy"></p><h2 id="旗山走走資訊" tabindex="-1"><a class="header-anchor" href="#旗山走走資訊" aria-hidden="true">#</a> 旗山走走資訊</h2><div class="hint-container info"><p class="hint-container-title">相關信息</p><p>走走時間：約1.5小時(含休息)</p><p>高低落差：約下降70公尺(不含上下起伏)</p></div><h2 id="婆羅山行車路線" tabindex="-1"><a class="header-anchor" href="#婆羅山行車路線" aria-hidden="true">#</a> 婆羅山行車路線</h2><p>回到台7線73.5Km處上車，往明池森林遊樂區行，於台7線66.8Km處小停車場下車。</p><h2 id="婆羅山走走路線" tabindex="-1"><a class="header-anchor" href="#婆羅山走走路線" aria-hidden="true">#</a> 婆羅山走走路線</h2><p>→台7線66.8Km處停車場，旁有明池森林遊樂區標示牌，先在此午餐<br><img src="https://1013399.github.io/image-1/16/1195074872_l.jpg" alt="DSCN4775.JPG - 宜蘭大同旗山、婆羅山" loading="lazy"></p><p>→餐畢，出發<br><img src="https://1013399.github.io/image-1/16/1195074383_l.jpg" alt="DSCN4777.JPG - 宜蘭大同旗山、婆羅山" loading="lazy"></p><p>→一開始就是一路陡上<br><img src="https://1013399.github.io/image-1/16/1195074971_l.jpg" alt="DSCN4781-84.JPG - 宜蘭大同旗山、婆羅山" loading="lazy"></p><p>→約10分鐘後，陡坡結束，山徑趨緩<br><img src="https://1013399.github.io/image-1/16/1195075073_l.jpg" alt="DSCN4785.JPG - 宜蘭大同旗山、婆羅山" loading="lazy"></p><p>→路跡漸漸不明，倒木橫陳，緊跟著登山路條走<br><img src="https://1013399.github.io/image-1/16/1195074188_l.jpg" alt="DSCN4786-90.JPG - 宜蘭大同旗山、婆羅山" loading="lazy"></p><p>→穿越濃密箭竹<br><img src="https://1013399.github.io/image-1/16/1195074480_l.jpg" alt="DSCN4791.JPG - 宜蘭大同旗山、婆羅山" loading="lazy"></p><p>→過了小山頭後，轉為明顯下坡路<br><img src="https://1013399.github.io/image-1/16/1195074873_l.jpg" alt="DSCN4793.JPG - 宜蘭大同旗山、婆羅山" loading="lazy"></p><p>→下至潮濕地鞍部<br><img src="https://1013399.github.io/image-1/16/1195074189_l.jpg" alt="DSCN4794.JPG - 宜蘭大同旗山、婆羅山" loading="lazy"></p><p>→陡上崎嶇山路<br><img src="https://1013399.github.io/image-1/16/1195074874_l.jpg" alt="DSCN4796-99.JPG - 宜蘭大同旗山、婆羅山" loading="lazy"></p><p>→到達婆羅山山頂<br><img src="https://1013399.github.io/image-1/16/1195074673_l.jpg" alt="DSCN4800.JPG - 宜蘭大同旗山、婆羅山" loading="lazy"></p><p>→婆羅山，三等三角點No.6230<br><img src="https://1013399.github.io/image-1/16/1195074570_l.jpg" alt="DSCN4801-02.JPG - 宜蘭大同旗山、婆羅山" loading="lazy"></p><p>→往南可眺望梵梵山，但烏雲罩頂<br><img src="https://1013399.github.io/image-1/16/1195073977_l.jpg" alt="DSCN4803.JPG - 宜蘭大同旗山、婆羅山" loading="lazy"></p><p>→拍照後原路回，回程途中，雷聲大作，幸未下雨<br><img src="https://1013399.github.io/image-1/16/1195074674_l.jpg" alt="DSCN4804.JPG - 宜蘭大同旗山、婆羅山" loading="lazy"></p><p>→回到台7線登山口<br><img src="https://1013399.github.io/image-1/16/1195074675_l.jpg" alt="DSCN4807.JPG - 宜蘭大同旗山、婆羅山" loading="lazy"></p><h2 id="婆羅山走走資訊" tabindex="-1"><a class="header-anchor" href="#婆羅山走走資訊" aria-hidden="true">#</a> 婆羅山走走資訊</h2><div class="hint-container info"><p class="hint-container-title">相關信息</p><p>走走時間：約2.5小時(含休息)</p><p>高低落差：約上升270公尺(不含上下起伏)</p></div><h2 id="參考地圖" tabindex="-1"><a class="header-anchor" href="#參考地圖" aria-hidden="true">#</a> 參考地圖</h2>',37),b={href:"https://map.happyman.idv.tw/",target:"_blank",rel:"noopener noreferrer"},_=a("br",null,null,-1),u=a("img",{src:"https://1013399.github.io/image-1/16/1195075165_l.jpg",alt:"宜蘭大同婆羅山、旗山-地圖產生器.JPG - 宜蘭大同旗山、婆羅山",loading:"lazy"},null,-1);function N(y,C){const t=p("ExternalLinkIcon");return g(),e("div",null,[n,d,c,r(" more "),m,a("p",null,[i("取自地圖產生器→"),a("a",b,[i("連結網址"),o(t)]),_,u])])}const G=l(s,[["render",N],["__file","post-16-2018-08-17.html.vue"]]);export{G as default};
