import{_ as g}from"./plugin-vue_export-helper-c27b6911.js";import{r as p,o,c as e,b as t,f as i,d as l,a as r,e as s}from"./app-6d4d6805.js";const n={},h=t("p",null,[t("strong",null,"牛埔泥岩教學園區"),i("，位於台南市龍崎區牛埔里，園區範圍內多屬黃砂土及青灰岩地質，抗蝕能力低，在經年累月的地表沖蝕及溪流侵蝕作用下，常寸草不生而形成光禿禿的泥岩惡地，交錯排列於高低起伏的丘陵與溪谷，造就了景觀獨特的月世界風貌。")],-1),m={href:"http://mudstone.swcb.gov.tw/area_01.htm",target:"_blank",rel:"noopener noreferrer"},c=t("p",null,"園區經水土保持局以各種生態工法，將惡地創生機，變成水保示範園區，除了具備水土保持功能，也美化、綠化了牛埔農塘，成為一處吸引外地遊客參觀的新興景點。",-1),b=t("p",null,"整個牛埔農塘的景點，水土保持局設了六個解說教育點，依照不同植被與工法，區分成「飲水思源」、「綻放綠洲」、「古色古香」、「生生不息」、「水中樓台」、「湖光山色」等6個工法特色站，再配合沿路休息涼亭及各項解說牌，可以走走停停，體驗人工與自然調和的美。",-1),d=t("p",null,"牛埔里除了有農塘及泥岩的美外，還有很特殊的人文故事，龍崎區竹編彩繪家林仙化，引經據典的四句聯導覽，豐富的鄉土趣味，吸引遊客的興趣，成為牛埔農塘最佳賣點。",-1),_={href:"http://web2.tainan.gov.tw/commonsystem/news/shownews.aspx?SN=27240",target:"_blank",rel:"noopener noreferrer"},u={href:"http://tw.myblog.yahoo.com/jj-mount/article?mid=12289",target:"_blank",rel:"noopener noreferrer"},y=s('<p>走走日期：2013/04/06</p><h2 id="行車路線" tabindex="-1"><a class="header-anchor" href="#行車路線" aria-hidden="true">#</a> 行車路線</h2><p>看完以下四句聯，便知如何到達牛埔泥岩教學園區：「<strong>落南二高免探聽，關廟向東一直行，一八二線認乎明，開車毋免半點鐘，到龍湖宮斡正爿，牛埔著侄目睭前</strong>」。</p><p>即國道3號下關廟交流道往關廟方向，到關廟走182縣道(中正路)向東往龍崎區，於182縣道25.7Km(龍湖宮前)右轉，約行2.5Km至龍崎工廠前停車。</p><h2 id="走走路線" tabindex="-1"><a class="header-anchor" href="#走走路線" aria-hidden="true">#</a> 走走路線</h2><p>→龍崎工廠前停車，往回走<br><img src="https://1013399.github.io/image-3/160/255154875_l.jpg" alt="牛埔泥岩水土保持教學園區" loading="lazy"></p><p>→在地文化展示館，林先化老師正在向遊客解說有趣的四句聯<br><img src="https://1013399.github.io/image-3/160/255154877_l.jpg" alt="牛埔泥岩水土保持教學園區" loading="lazy"></p><p>→下課後入內參觀<br><img src="https://1013399.github.io/image-3/160/255154951_l.jpg" alt="牛埔泥岩水土保持教學園區" loading="lazy"></p><p>→參觀完，續行至園區入口<br><img src="https://1013399.github.io/image-3/160/255154884_l.jpg" alt="牛埔泥岩水土保持教學園區" loading="lazy"></p><p>→入口旁設有園區導覽圖<br><img src="https://1013399.github.io/image-3/160/255154888_l.jpg" alt="牛埔泥岩水土保持教學園區" loading="lazy"></p><p>→俯瞰美麗的山水景觀，續往下行<br><img src="https://1013399.github.io/image-3/160/255154889_l.jpg" alt="牛埔泥岩水土保持教學園區" loading="lazy"></p><p>→第一站，「飲水思源」，涼亭右下木棧道可往「綻放綠洲」及「古色古香」，取直續行<br><img src="https://1013399.github.io/image-3/160/255154891_l.jpg" alt="牛埔泥岩水土保持教學園區" loading="lazy"></p><p>→牛埔文化走廊，牆上寫著有趣的四句聯<br><img src="https://1013399.github.io/image-3/160/255154892_l.jpg" alt="牛埔泥岩水土保持教學園區" loading="lazy"></p><p>→沿途可見水土保持的成果<br><img src="https://1013399.github.io/image-3/160/255154894_l.jpg" alt="牛埔泥岩水土保持教學園區" loading="lazy"></p><p>→十字叉路，左可經涼亭、竹屋繞行接回，右可往「古色古香」，續直行<br><img src="https://1013399.github.io/image-3/160/255154896_l.jpg" alt="牛埔泥岩水土保持教學園區" loading="lazy"></p><p>→第四站，「生生不息」，續直行<br><img src="https://1013399.github.io/image-3/160/255154898_l.jpg" alt="牛埔泥岩水土保持教學園區" loading="lazy"></p><p>→叉路，左為前一叉路在此接回，直行為溼滑上坡，取右沿牛埔農塘而行較平坦好走<br><img src="https://1013399.github.io/image-3/160/255154899_l.jpg" alt="牛埔泥岩水土保持教學園區" loading="lazy"></p><p>→水土保持模擬示範區，以種植不同植物後，觀察泥土流失情形<br><img src="https://1013399.github.io/image-3/160/255154939_l.jpg" alt="牛埔泥岩水土保持教學園區" loading="lazy"></p><p>→「文昌筆」，位於農塘的對岸<br><img src="https://1013399.github.io/image-3/160/255154909_l.jpg" alt="牛埔泥岩水土保持教學園區" loading="lazy"></p><p>→第五站，「水中樓台」<br><img src="https://1013399.github.io/image-3/160/255154902_l.jpg" alt="牛埔泥岩水土保持教學園區" loading="lazy"></p><p>→左側牆上說明了如何到達此地<br><img src="https://1013399.github.io/image-3/160/255154904_l.jpg" alt="牛埔泥岩水土保持教學園區" loading="lazy"></p><p>→這則說明了牛埔泥岩地質，先取右涼亭參觀、休息<br><img src="https://1013399.github.io/image-3/160/255154936_l.jpg" alt="牛埔泥岩水土保持教學園區" loading="lazy"></p><p>→涼亭天花板上的位置圖及四句聯<br><img src="https://1013399.github.io/image-3/160/255154907_l.jpg" alt="牛埔泥岩水土保持教學園區" loading="lazy"></p><p>→農塘水位高於涼亭，圈型排洪道可將溢流排出，設計相當巧妙，天花板上有解說圖<br><img src="https://1013399.github.io/image-3/160/255154905_l.jpg" alt="牛埔泥岩水土保持教學園區" loading="lazy"></p><p>→續行，來到第六站，「湖光山色」<br><img src="https://1013399.github.io/image-3/160/255154913_l.jpg" alt="牛埔泥岩水土保持教學園區" loading="lazy"></p><p>→叉路，左應可天池，續直行陡上枕木步道<br><img src="https://1013399.github.io/image-3/160/255154916_l.jpg" alt="牛埔泥岩水土保持教學園區" loading="lazy"></p><p>→枕木步道結束接泥岩路，稍後又接枕木步道<br><img src="https://1013399.github.io/image-3/160/255154935_l.jpg" alt="牛埔泥岩水土保持教學園區" loading="lazy"></p><p>→叉路，前方往廁所，取右上<br><img src="https://1013399.github.io/image-3/160/255154919_l.jpg" alt="牛埔泥岩水土保持教學園區" loading="lazy"></p><p>→往右俯瞰水中樓台<br><img src="https://1013399.github.io/image-3/160/255154922_l.jpg" alt="牛埔泥岩水土保持教學園區" loading="lazy"></p><p>→往左眺望天池<br><img src="https://1013399.github.io/image-3/160/255154924_l.jpg" alt="牛埔泥岩水土保持教學園區" loading="lazy"></p><p>→叉路，左往天池，取右上<br><img src="https://1013399.github.io/image-3/160/255154925_l.jpg" alt="牛埔泥岩水土保持教學園區" loading="lazy"></p><p>→到小山頭(127峰)，前方坍塌，已封閉<br><img src="https://1013399.github.io/image-3/160/255154927_l.jpg" alt="牛埔泥岩水土保持教學園區" loading="lazy"></p><p>→小山頭有座茅草涼亭<br><img src="https://1013399.github.io/image-3/160/255154928_l.jpg" alt="牛埔泥岩水土保持教學園區" loading="lazy"></p><p>→涼亭前展望，休息後原路退回<br><img src="https://1013399.github.io/image-3/160/255154930_l.jpg" alt="牛埔泥岩水土保持教學園區" loading="lazy"></p><p>→經過水中樓台回到十字叉路，取左往第三站「古色古香」<br><img src="https://1013399.github.io/image-3/160/255154940_l.jpg" alt="牛埔泥岩水土保持教學園區" loading="lazy"></p><p>→叉路，殊途同歸，取右上往「古農村故事館」(即古色古香)<br><img src="https://1013399.github.io/image-3/160/255154942_l.jpg" alt="牛埔泥岩水土保持教學園區" loading="lazy"></p><p>→第三站，「古色古香」，內部陳設古農村用具<br><img src="https://1013399.github.io/image-3/160/255154944_l.jpg" alt="牛埔泥岩水土保持教學園區" loading="lazy"></p><p>→續往前方的惡地形<br><img src="https://1013399.github.io/image-3/160/255154945_l.jpg" alt="牛埔泥岩水土保持教學園區" loading="lazy"></p><p>→近距離觀察泥岩惡地形<br><img src="https://1013399.github.io/image-3/160/255154946_l.jpg" alt="牛埔泥岩水土保持教學園區" loading="lazy"></p><p>→步道盡頭有座茅草涼亭，原路折返<br><img src="https://1013399.github.io/image-3/160/255154947_l.jpg" alt="牛埔泥岩水土保持教學園區" loading="lazy"></p><p>→回到古農村故事館，取左回，經過一座涼亭<br><img src="https://1013399.github.io/image-3/160/255154948_l.jpg" alt="牛埔泥岩水土保持教學園區" loading="lazy"></p><p>→第二站，「綻放綠洲」<br><img src="https://1013399.github.io/image-3/160/255154949_l.jpg" alt="牛埔泥岩水土保持教學園區" loading="lazy"></p><p>→續行，回到第一站飲水思源，取左回<br><img src="https://1013399.github.io/image-3/160/255154950_l.jpg" alt="牛埔泥岩水土保持教學園區" loading="lazy"></p><p>→回到園區入口，取右回<br> →回到停車處</p><h2 id="走走資訊" tabindex="-1"><a class="header-anchor" href="#走走資訊" aria-hidden="true">#</a> 走走資訊</h2><div class="hint-container info"><p class="hint-container-title">相關信息</p><p>走走時間：約1.5小時(含休息)</p></div><h2 id="參考地圖" tabindex="-1"><a class="header-anchor" href="#參考地圖" aria-hidden="true">#</a> 參考地圖</h2><p>(紅色為去程、綠色為回程)</p>',48),j={href:"http://www.keepon.com.tw/DiscussLoad.aspx?code=314B5CF9AEC3A19170A9A7E294A7989A79ACFD8F3B8446A6",target:"_blank",rel:"noopener noreferrer"},z=t("br",null,null,-1),f=t("img",{src:"https://1013399.github.io/image-3/160/255256637_l.jpg",alt:"牛埔泥岩水土保持教學園區",loading:"lazy"},null,-1);function w(x,k){const a=p("ExternalLinkIcon");return o(),e("div",null,[h,t("p",null,[i("(資料摘自："),t("a",m,[i("農委會水土保持局台南分局"),l(a)]),i(")")]),c,r(" more "),b,d,t("p",null,[i("(以上資料摘自："),t("a",_,[i("台南市龍崎區公所網站"),l(a)]),i(")")]),t("p",null,[i("關於牛埔泥岩教學園區，還可參考這裡→"),t("a",u,[i("JJ 的山水簿"),l(a)]),i("。今日屏東大雷雨，沒能完成計畫的里龍山行程，回程順道到此走走。")]),y,t("p",null,[i("原圖連結→"),t("a",j,[i("登山補給站-祥馬圖集"),l(a)]),z,f])])}const N=g(n,[["render",w],["__file","post-160-2013-04-12.html.vue"]]);export{N as default};
