import{_ as g}from"./plugin-vue_export-helper-c27b6911.js";import{r as l,o as p,c as o,a as e,b as i,f as a,d as r,e as s}from"./app-a9847bbb.js";const h={},n=i("p",null,[i("strong",null,"萬里得山"),a("，海拔522公尺，位於屏東縣滿州鄉南仁湖生態保護區內，山頂為直昇機停機坪，設有軍事微波發射鐵塔，立有三等三角點No.909及聯勤一等三角點內補No.045，山頂展望極佳，為墾丁國家公園內的最高峰，該山名列台灣小百岳No.081。")],-1),m=s('<p>走走日期：2013/04/05</p><h2 id="行車路線" tabindex="-1"><a class="header-anchor" href="#行車路線" aria-hidden="true">#</a> 行車路線</h2><p>恆春行駛200縣道(恆東路)往滿州鄉，於200縣道20.9Km右轉南山路往南仁湖，行駛約0.4Km叉路取右(左往南仁湖)，0.9Km叉路也取右，約1.6Km抵達登山口(已拓寬並重鋪設水泥)，停車於登山口附近路旁較寬處。</p><h2 id="走走路線" tabindex="-1"><a class="header-anchor" href="#走走路線" aria-hidden="true">#</a> 走走路線</h2><p>→登山口，已拓寬並重鋪設水泥，停車方便<br><img src="https://1013399.github.io/image-3/161/255154724_l.jpg" alt="萬里得山" loading="lazy"></p><p>→沿著水泥路往上行<br><img src="https://1013399.github.io/image-3/161/255154726_l.jpg" alt="萬里得山" loading="lazy"></p><p>→雨量計<br><img src="https://1013399.github.io/image-3/161/255154727_l.jpg" alt="萬里得山" loading="lazy"></p><p>→水泥路終止<br><img src="https://1013399.github.io/image-3/161/255154729_l.jpg" alt="萬里得山" loading="lazy"></p><p>→旁路許多這種不知名植物<br><img src="https://1013399.github.io/image-3/161/255154730_l.jpg" alt="萬里得山" loading="lazy"></p><p>→山路一路緩上<br><img src="https://1013399.github.io/image-3/161/255154732_l.jpg" alt="萬里得山" loading="lazy"></p><p>→第一處廢水泥門柱<br><img src="https://1013399.github.io/image-3/161/255154734_l.jpg" alt="萬里得山" loading="lazy"></p><p>→進入芒草路段，已可見前方的萬里得山<br><img src="https://1013399.github.io/image-3/161/255154735_l.jpg" alt="萬里得山" loading="lazy"></p><p>→接回水泥路<br><img src="https://1013399.github.io/image-3/161/255154737_l.jpg" alt="萬里得山" loading="lazy"></p><p>→叉路，右上應為捷徑，續行水泥路<br><img src="https://1013399.github.io/image-3/161/255154738_l.jpg" alt="萬里得山" loading="lazy"></p><p>→第一處崩塌處<br><img src="https://1013399.github.io/image-3/161/255154740_l.jpg" alt="萬里得山" loading="lazy"></p><p>→斷橋<br><img src="https://1013399.github.io/image-3/161/255154742_l.jpg" alt="萬里得山" loading="lazy"></p><p>→第二處崩塌處<br><img src="https://1013399.github.io/image-3/161/255154743_l.jpg" alt="萬里得山" loading="lazy"></p><p>→攔沙壩<br><img src="https://1013399.github.io/image-3/161/255154745_l.jpg" alt="萬里得山" loading="lazy"></p><p>→此處需往左上切，路旁大石有三角點噴漆<br><img src="https://1013399.github.io/image-3/161/255154746_l.jpg" alt="萬里得山" loading="lazy"></p><p>→越過倒木<br><img src="https://1013399.github.io/image-3/161/255154749_l.jpg" alt="萬里得山" loading="lazy"></p><p>→穿越芒萁<br><img src="https://1013399.github.io/image-3/161/255154750_l.jpg" alt="萬里得山" loading="lazy"></p><p>→再鑽過大倒木<br><img src="https://1013399.github.io/image-3/161/255154754_l.jpg" alt="萬里得山" loading="lazy"></p><p>→倒塌水泥電桿<br><img src="https://1013399.github.io/image-3/161/255154757_l.jpg" alt="萬里得山" loading="lazy"></p><p>→林中空隙處仰望萬里得山<br><img src="https://1013399.github.io/image-3/161/255154758_l.jpg" alt="萬里得山" loading="lazy"></p><p>→第二處廢水泥門柱<br><img src="https://1013399.github.io/image-3/161/255154760_l.jpg" alt="萬里得山" loading="lazy"></p><p>→萬里得山之登山路徑，大部份為荒廢水泥道路<br><img src="https://1013399.github.io/image-3/161/255154761_l.jpg" alt="萬里得山" loading="lazy"></p><p>→倒塌木質電桿<br><img src="https://1013399.github.io/image-3/161/255154763_l.jpg" alt="萬里得山" loading="lazy"></p><p>→抵達山頂停機坪<br><img src="https://1013399.github.io/image-3/161/255154764_l.jpg" alt="萬里得山" loading="lazy"></p><p>→取左側缺口爬上擋土牆<br><img src="https://1013399.github.io/image-3/161/255154769_l.jpg" alt="萬里得山" loading="lazy"></p><p>→缺口旁地上有聯勤測量隊「空軍ACMI參考標 NO R6-1」<br><img src="https://1013399.github.io/image-3/161/255154766_l.jpg" alt="萬里得山 空軍ACMI參考標 NO R6-1" loading="lazy"></p><p>→萬里得山三等三角點No.909<br><img src="https://1013399.github.io/image-3/161/255154770_l.jpg" alt="萬里得山" loading="lazy"></p><p>→續往前行數公尺，可發現聯勤一等三角點內補No.045<br><img src="https://1013399.github.io/image-3/161/255154718_l.jpg" alt="萬里得山" loading="lazy"></p><p>→俯瞰軍事微波發射鐵塔<br><img src="https://1013399.github.io/image-3/161/255154721_l.jpg" alt="萬里得山" loading="lazy"></p><p>→山頂視野極佳，遠眺墾丁大尖石山、大山母山<br><img src="https://1013399.github.io/image-3/161/255154719_l.jpg" alt="萬里得山" loading="lazy"></p><p>→俯瞰滿州地區，休息後原路回<br><img src="https://1013399.github.io/image-3/161/255154723_l.jpg" alt="萬里得山" loading="lazy"></p><p>→回到登山口</p><h2 id="走走資訊" tabindex="-1"><a class="header-anchor" href="#走走資訊" aria-hidden="true">#</a> 走走資訊</h2><div class="hint-container info"><p class="hint-container-title">相關信息</p><p>走走時間：約2.5小時(含休息)</p><p>高低落差：約300公尺</p></div><h2 id="參考地圖" tabindex="-1"><a class="header-anchor" href="#參考地圖" aria-hidden="true">#</a> 參考地圖</h2>',39),c={href:"http://www.keepon.com.tw/DiscussLoad.aspx?code=314B5CF9AEC3A19113F6CAA6F539A6621638816056297AAB",target:"_blank",rel:"noopener noreferrer"},b=i("br",null,null,-1),d=i("img",{src:"https://1013399.github.io/image-3/161/255154803_l.jpg",alt:"萬里得山 參考地圖",loading:"lazy"},null,-1);function _(u,j){const t=l("ExternalLinkIcon");return p(),o("div",null,[n,e(" more "),m,i("p",null,[a("原圖連結→"),i("a",c,[a("登山補給站-brownlee(小李)"),r(t)]),b,d])])}const f=g(h,[["render",_],["__file","post-161-2013-04-11.html.vue"]]);export{f as default};
