import{_ as l}from"./plugin-vue_export-helper-c27b6911.js";import{r as g,o as p,c as o,a as e,b as i,e as t,d as r,f as s}from"./app-157980c9.js";const h={},n=i("p",null,[i("strong",null,"笠頂山"),t("，又名笠置山，海拔659公尺，位於屏東縣瑪家鄉佳義村，因外型像斗笠而得名，山頂有顆三等三角點No.7109，每逢假日登山者眾多，是屏東地區熱門登山路線，也有「公務員山」之稱，該山名列台灣小百岳No.076。")],-1),m=i("p",null,"笠頂山有數個登山口，搞不清楚何為一號、二號、三號，這次選擇佳義派出所後方登山口，雖然較陡但停車方便，今日天候不佳，下雨天仍有不少山友前來造訪，果然是座熱門的小百岳。",-1),c=s('<p>走走日期：2013/04/05</p><h2 id="行車路線" tabindex="-1"><a class="header-anchor" href="#行車路線" aria-hidden="true">#</a> 行車路線</h2><p>國道3號下長治交流道，往三地門方向接台24線(水源路)左轉(此路口禁止左轉，需往前迴轉道接回)，再轉屏26鄉道(黎明街)至終點接187縣道(黎東路)左轉，至187與185縣道共線起點右轉往泰武，於185縣道33.5Km(佳義教會前產業道路)左轉往佳義派出所，到達佳義派出所沿右側產道續往上，於路旁停車。<br> 註：佳義派出所旁產道口設有「私人土地，禁止進入」告示，經詢問派出所員警，係因有部分山友破壞農作物及水源，導致當地原住民打算封路，不過目前還是可以將車開到登山口附近。</p><h2 id="走走路線" tabindex="-1"><a class="header-anchor" href="#走走路線" aria-hidden="true">#</a> 走走路線</h2><p>→停車處(其實可以繼續往上開)<br><img src="https://1013399.github.io/image-3/162/255154472_l.jpg" alt="笠頂山停車處" loading="lazy"></p><p>→左側山路捷徑，不取，續行產道<br><img src="https://1013399.github.io/image-3/162/255154473_l.jpg" alt="笠頂山" loading="lazy"></p><p>→左側山路可直達登山口，仍續行產道<br><img src="https://1013399.github.io/image-3/162/255154474_l.jpg" alt="笠頂山" loading="lazy"></p><p>→登山口，取右上<br><img src="https://1013399.github.io/image-3/162/255154475_l.jpg" alt="笠頂山" loading="lazy"></p><p>→登山口旁路線圖，此處為第三條路線<br><img src="https://1013399.github.io/image-3/162/255154477_l.jpg" alt="笠頂山登山口旁路線圖 第三條路線" loading="lazy"></p><p>→路旁大樹，樹上釘牌應為原住民語<br><img src="https://1013399.github.io/image-3/162/255154478_l.jpg" alt="笠頂山" loading="lazy"></p><p>→叉路，左右皆可<br><img src="https://1013399.github.io/image-3/162/255154479_l.jpg" alt="笠頂山" loading="lazy"></p><p>→路旁大樹，被榕樹根緊緊纏繞，相當特殊<br><img src="https://1013399.github.io/image-3/162/255154480_l.jpg" alt="笠頂山" loading="lazy"></p><p>→平台遺址，不知為何，設有服務台、伙房、休息室<br><img src="https://1013399.github.io/image-3/162/255154481_l.jpg" alt="笠頂山" loading="lazy"></p><p>→遺址旁石頭上正面刻「國語碑」，背面刻著「皇紀二千六百年紀念」<br><img src="https://1013399.github.io/image-3/162/255154484_l.jpg" alt="笠頂山 國語碑 皇紀二千六百年紀念" loading="lazy"></p><p>→開始陡上，經過3號噴漆(至稜線共1~9號)<br><img src="https://1013399.github.io/image-3/162/255154485_l.jpg" alt="笠頂山" loading="lazy"></p><p>→叉路，左下應為第四條路線，續往上行<br><img src="https://1013399.github.io/image-3/162/255154486_l.jpg" alt="笠頂山" loading="lazy"></p><p>→經過油桐花及竹林路段<br><img src="https://1013399.github.io/image-3/162/255154487_l.jpg" alt="笠頂山 油桐花 竹林" loading="lazy"></p><p>→這棵樹上架設平台，不知何用(介於5號與6號噴漆之間)<br><img src="https://1013399.github.io/image-3/162/255154488_l.jpg" alt="笠頂山" loading="lazy"></p><p>→繼續陡上，開始下起大雨<br><img src="https://1013399.github.io/image-3/162/255154490_l.jpg" alt="笠頂山" loading="lazy"></p><p>→途中展望<br><img src="https://1013399.github.io/image-3/162/255154491_l.jpg" alt="笠頂山" loading="lazy"></p><p>→最後陡上<br><img src="https://1013399.github.io/image-3/162/255154571_l.jpg" alt="笠頂山" loading="lazy"></p><p>→看到9號噴漆了，即將到達稜線叉路<br><img src="https://1013399.github.io/image-3/162/255154494_l.jpg" alt="笠頂山" loading="lazy"></p><p>→上到稜線<br><img src="https://1013399.github.io/image-3/162/255154495_l.jpg" alt="笠頂山" loading="lazy"></p><p>→左側有座山友休息站，續行可往真笠山，取右往笠頂山<br><img src="https://1013399.github.io/image-3/162/255154502_l.jpg" alt="笠頂山" loading="lazy"></p><p>→稜線往笠頂山途中休憩涼棚林立<br><img src="https://1013399.github.io/image-3/162/255154496_l.jpg" alt="笠頂山" loading="lazy"></p><p>→途中空曠處視野不錯，俯瞰佳義社區<br><img src="https://1013399.github.io/image-3/162/255154497_l.jpg" alt="笠頂山 佳義社區" loading="lazy"></p><p>→抵達笠頂山，續行下山可往佳義國小(第二條路線)<br><img src="https://1013399.github.io/image-3/162/255154498_l.jpg" alt="笠頂山" loading="lazy"></p><p>→笠頂山三等三角點No.7109，原路折返，找個無人涼棚休息、午餐<br><img src="https://1013399.github.io/image-3/162/255154499_l.jpg" alt="笠頂山" loading="lazy"></p><p>→餐畢原路回，回到稜線叉路，取左下<br><img src="https://1013399.github.io/image-3/162/255154501_l.jpg" alt="笠頂山" loading="lazy"></p><p>→回到登山口<br><img src="https://1013399.github.io/image-3/162/255154504_l.jpg" alt="笠頂山登山口" loading="lazy"></p><p>→回到停車處<br><img src="https://1013399.github.io/image-3/162/255154505_l.jpg" alt="笠頂山" loading="lazy"></p><h2 id="參考地圖" tabindex="-1"><a class="header-anchor" href="#參考地圖" aria-hidden="true">#</a> 參考地圖</h2>',32),b=i("br",null,null,-1),d={href:"http://www.keepon.com.tw/DiscussLoad.aspx?code=314B5CF9AEC3A19113F6CAA6F539A662D300FD2BC2630F2F",target:"_blank",rel:"noopener noreferrer"},_=i("br",null,null,-1),u=i("img",{src:"https://1013399.github.io/image-3/162/255188775_l.jpg",alt:"笠頂山 參考地圖",loading:"lazy"},null,-1);function j(y,z){const a=g("ExternalLinkIcon");return p(),o("div",null,[n,m,e(" more "),c,i("p",null,[t("(咖啡色為實際行走路線)"),b,t(" 原圖連結→"),i("a",d,[t("登山補給站-brownlee(小李)"),r(a)]),_,u])])}const N=l(h,[["render",j],["__file","post-162-2013-04-10.html.vue"]]);export{N as default};
