import{_ as g}from"./plugin-vue_export-helper-c27b6911.js";import{r as p,o,c as r,b as i,f as t,d as l,a as s,e}from"./app-6513a715.js";const n={},h=i("p",null,[i("strong",null,"九芎湖"),t("休閒農業區，位於新埔鎮照門里，為一兼具農業生產與觀光休閒的休閒農業區，區內設有觀景步道、涼亭，可提供休閒賞景、健身散步，沿途花卉繽紛，綠化的水塘，美不勝收，此外區內也規劃多條登山步道，提供愛好活動的人士一個登山健行的好地方。")],-1),m={href:"http://emmm.tw/L3_content.php?L3_id=3436",target:"_blank",rel:"noopener noreferrer"},b=i("p",null,[t("九芎湖地區共有"),i("strong",null,"九福步道"),t("、"),i("strong",null,"觀南步道"),t("及"),i("strong",null,"霽月步道"),t("等三條步道，從九福二號橋的明池起登，經九福步道、觀南步道下行至鴛鴦池，再經霄月步道回到明池，O型一圈，舒適輕鬆。")],-1),c=i("p",null,[i("strong",null,"九福步道"),t("，自古便是照門里居民運送農產品的便道，除前段坡度較陡外，大部分的路段沿著山稜線行進，地勢平坦，視野景觀良好，景色優美，可以一覽九芎湖的農村景致。")],-1),d=i("p",null,[i("strong",null,"觀南步道"),t("，為早期箭竹窩居民前往九芎湖開墾，種植稻米，居民為運送農作物回到箭竹窩，慢慢走出的一條挑夫步道，步道設置於民國84年，取名為觀南的主要原因，是行走於步道上可以觀賞到傳統中國建築風格的南園休閒農場。")],-1),_=i("strong",null,"霽月步道",-1),u={href:"http://www.sp.gov.tw/ch/05tourism/tou_02a_list.asp?cate_id=48",target:"_blank",rel:"noopener noreferrer"},y=e('<p><strong>九芎湖山</strong>，海拔230公尺，控制點No.08基石位於步道旁，無標示牌，但有許多登山路條，山頂無視野。</p><p>走走日期：2013/03/16</p><h2 id="行車路線" tabindex="-1"><a class="header-anchor" href="#行車路線" aria-hidden="true">#</a> 行車路線</h2><p>新埔行駛115縣道往楊梅方向，於115縣道26.9Km左轉九福路往九芎湖(路口有九芎湖及金谷農場指標)，過金谷農場後停車於私人停車場(計次收費30元)。</p><h2 id="走走路線" tabindex="-1"><a class="header-anchor" href="#走走路線" aria-hidden="true">#</a> 走走路線</h2><p>→明池，位於九福二號橋旁<br><img src="https://1013399.github.io/image-3/163/253191486_l.jpg" alt="明池 於九福二號橋旁" loading="lazy"></p><p>→旁邊設有導覽圖<br><img src="https://1013399.github.io/image-3/163/253191468_l.jpg" alt="九芎湖步道 導覽圖" loading="lazy"></p><p>→九福步道入口<br><img src="https://1013399.github.io/image-3/163/253191488_l.jpg" alt="九芎湖 九福步道入口" loading="lazy"></p><p>→一開始就上坡，略陡<br><img src="https://1013399.github.io/image-3/163/253191491_l.jpg" alt="九芎湖步道、九芎湖山" loading="lazy"></p><p>→休憩點，以前有賣飲料、茶葉蛋<br><img src="https://1013399.github.io/image-3/163/253191495_l.jpg" alt="九芎湖步道、九芎湖山" loading="lazy"></p><p>→叉路，左為「竹嵐步道」往箭竹窩，續直行<br><img src="https://1013399.github.io/image-3/163/253191499_l.jpg" alt="九芎湖步道、九芎湖山" loading="lazy"></p><p>→步道上下起伏，但落差不大<br><img src="https://1013399.github.io/image-3/163/253191508_l.jpg" alt="九芎湖步道、九芎湖山" loading="lazy"></p><p>→叉路，左為「箭竹步道」往箭竹窩，續直行<br><img src="https://1013399.github.io/image-3/163/253191510_l.jpg" alt="九芎湖步道、九芎湖山" loading="lazy"></p><p>→叉路，右下可回九福路，續直行<br><img src="https://1013399.github.io/image-3/163/253191516_l.jpg" alt="九芎湖步道、九芎湖山" loading="lazy"></p><p>→穿過休憩鐵皮涼亭<br><img src="https://1013399.github.io/image-3/163/253191518_l.jpg" alt="九芎湖步道、九芎湖山" loading="lazy"></p><p>→光風亭，設有觀景台、涼亭及販賣店<br><img src="https://1013399.github.io/image-3/163/253191519_l.jpg" alt="九芎湖步道、九芎湖山" loading="lazy"></p><p>→觀景台上展望佳，休息後續行<br><img src="https://1013399.github.io/image-3/163/253191521_l.jpg" alt="九芎湖步道、九芎湖山" loading="lazy"></p><p>→叉路，右下可回九福路，取左往鴛鴦池(根據蕭郎的資料：此處為照門山)<br><img src="https://1013399.github.io/image-3/163/253191523_l.jpg" alt="九芎湖步道、九芎湖山" loading="lazy"></p><p>→眺望對面的「南園休閒農場」<br><img src="https://1013399.github.io/image-3/163/253191526_l.jpg" alt="九芎湖步道、九芎湖山" loading="lazy"></p><p>→叉路，左為「新箭竹步道」往箭竹窩，續直行<br><img src="https://1013399.github.io/image-3/163/253191532_l.jpg" alt="九芎湖步道、九芎湖山" loading="lazy"></p><p>→叉路，右陡下為好漢坡可接觀南步道，續直行<br><img src="https://1013399.github.io/image-3/163/253191537_l.jpg" alt="九芎湖步道、九芎湖山" loading="lazy"></p><p>→叉路，左往湖口，取右觀南步道往鴛鴦池<br><img src="https://1013399.github.io/image-3/163/253191544_l.jpg" alt="九芎湖步道、九芎湖山" loading="lazy"></p><p>→叉路，右陡上好漢坡可回九福步道，續直行下坡<br><img src="https://1013399.github.io/image-3/163/253191554_l.jpg" alt="九芎湖步道、九芎湖山" loading="lazy"></p><p>→茶亭，後方有住家販賣茶水<br><img src="https://1013399.github.io/image-3/163/253191559_l.jpg" alt="九芎湖步道、九芎湖山" loading="lazy"></p><p>→觀南步道一路緩下<br><img src="https://1013399.github.io/image-3/163/253191563_l.jpg" alt="九芎湖步道、九芎湖山" loading="lazy"></p><p>→鴛鴦池，觀南步道起點，旁有餐廳<br><img src="https://1013399.github.io/image-3/163/253191565_l.jpg" alt="九芎湖步道、九芎湖山" loading="lazy"></p><p>→續行接九福路(九福三號橋)，右往明池，取直水泥路往陳家農場<br><img src="https://1013399.github.io/image-3/163/253191569_l.jpg" alt="九芎湖步道、九芎湖山" loading="lazy"></p><p>→陳家農場，餐廳、果園及獨角仙，是農場主要賣點<br><img src="https://1013399.github.io/image-3/163/253191576_l.jpg" alt="九芎湖步道、九芎湖山" loading="lazy"></p><p>→叉路，右經九福亭可回九福步道，取左續行<br><img src="https://1013399.github.io/image-3/163/253191579_l.jpg" alt="九芎湖步道、九芎湖山" loading="lazy"></p><p>→霽月步道入口，位於陳家農場右側<br><img src="https://1013399.github.io/image-3/163/253191585_l.jpg" alt="九芎湖步道、九芎湖山" loading="lazy"></p><p>→入口有許多「變葉唐竹」<br><img src="https://1013399.github.io/image-3/163/253191595_l.jpg" alt="九芎湖步道、九芎湖山" loading="lazy"></p><p>→步道陡上，但不長<br><img src="https://1013399.github.io/image-3/163/253191592_l.jpg" alt="九芎湖步道、九芎湖山" loading="lazy"></p><p>→陡上結束，休息賞景，俯瞰鴛鴦池<br><img src="https://1013399.github.io/image-3/163/253191598_l.jpg" alt="九芎湖步道、九芎湖山" loading="lazy"></p><p>→眺望南園休閒農場，休息後續行<br><img src="https://1013399.github.io/image-3/163/253191601_l.jpg" alt="九芎湖步道、九芎湖山" loading="lazy"></p><p>→經過果園，視野也不錯<br><img src="https://1013399.github.io/image-3/163/253191603_l.jpg" alt="九芎湖步道、九芎湖山" loading="lazy"></p><p>→十字路口，左可往照鏡(已荒蕪)，右可經汝南園回九福路，續直行<br><img src="https://1013399.github.io/image-3/163/253191605_l.jpg" alt="九芎湖步道、九芎湖山" loading="lazy"></p><p>→九芎湖山，控制點No.08基石，無標示牌，但有許多登山路條<br><img src="https://1013399.github.io/image-3/163/253193185_l.jpg" alt="九芎湖步道、九芎湖山" loading="lazy"></p><p>→步道上上下下，上到小山頭<br><img src="https://1013399.github.io/image-3/163/253191611_l.jpg" alt="九芎湖步道、九芎湖山" loading="lazy"></p><p>→叉路，右下可往九福路至「金谷農場」，續直行<br><img src="https://1013399.github.io/image-3/163/253191614_l.jpg" alt="九芎湖步道、九芎湖山" loading="lazy"></p><p>→經過高壓電塔<br><img src="https://1013399.github.io/image-3/163/253191620_l.jpg" alt="九芎湖步道、九芎湖山" loading="lazy"></p><p>→霽月亭，涼亭後方有產道往九福路至碧池，續直行<br><img src="https://1013399.github.io/image-3/163/253191621_l.jpg" alt="九芎湖步道、九芎湖山" loading="lazy"></p><p>→叉路，右側有新設步道可往九福路，但未開放，續直行<br><img src="https://1013399.github.io/image-3/163/253191625_l.jpg" alt="九芎湖步道、九芎湖山" loading="lazy"></p><p>→霽月步道另一入口，取右行<br><img src="https://1013399.github.io/image-3/163/253191628_l.jpg" alt="霽月步道另一入口" loading="lazy"></p><p>→回到九福路，旁邊為九福一號橋，取右回<br><img src="https://1013399.github.io/image-3/163/253191631_l.jpg" alt="九福路 旁邊為九福一號橋" loading="lazy"></p><p>→照門休閒農業區意象<br><img src="https://1013399.github.io/image-3/163/253191636_l.jpg" alt="照門休閒農業區" loading="lazy"></p><p>→回到明池，續行<br><img src="https://1013399.github.io/image-3/163/253191641_l.jpg" alt="九芎湖步道、九芎湖山 明池" loading="lazy"></p><p>→經過金谷農場，入內參觀<br><img src="https://1013399.github.io/image-3/163/253191643_l.jpg" alt="金谷農場" loading="lazy"></p><p>→回到停車場</p><h2 id="走走資訊" tabindex="-1"><a class="header-anchor" href="#走走資訊" aria-hidden="true">#</a> 走走資訊</h2><div class="hint-container info"><p class="hint-container-title">相關信息</p><p>走走時間：約2.5小時(含休息)</p><p>高低落差：(不含上下起伏)</p><ul><li>明池        →光風亭(九福步道)    約上升130公尺</li><li>光風亭      →九福三號橋          約下降80公尺</li><li>九福三號橋  →九芎湖山(霽月步道)  約上升70公尺</li><li>九芎湖山    →明池                約下降120公尺</li></ul></div><h2 id="參考地圖" tabindex="-1"><a class="header-anchor" href="#參考地圖" aria-hidden="true">#</a> 參考地圖</h2>',51),j={href:"http://www.yougoipay.com/kenny/w700/index.htm",target:"_blank",rel:"noopener noreferrer"},z=i("br",null,null,-1),f=i("img",{src:"https://1013399.github.io/image-3/163/253193218_l.jpg",alt:"九芎湖步道、九芎湖山 參考地圖",loading:"lazy"},null,-1);function x(w,k){const a=p("ExternalLinkIcon");return o(),r("div",null,[h,i("p",null,[t("(資料來源："),i("a",m,[t("美美網"),l(a)]),t(")")]),s(" more "),b,c,d,i("p",null,[_,t("，入口位於陳家農場旁，沿稜開闢的石板步道邊滿是可愛的花草，途中到處可見相思樹林、魯花樹、九重葛、桂花、桐花等植物，居高臨下看看山下充滿綠意的田園景觀，任誰都會感到心曠神怡。(以上資料摘自："),i("a",u,[t("新埔鎮公所網站-照門休閒農業區"),l(a)]),t(")")]),y,i("p",null,[t("原圖連結→"),i("a",j,[t("偶而獨步山林間(蕭郎)"),l(a)]),z,f])])}const V=g(n,[["render",x],["__file","post-163-2013-03-19.html.vue"]]);export{V as default};
