import{_ as g}from"./plugin-vue_export-helper-c27b6911.js";import{r as p,o as e,c as o,a as r,b as i,f as t,d as l,e as s}from"./app-a7b0d29a.js";const h={},n=i("p",null,[i("strong",null,"九九峰"),t("，位於台中霧峰及南投草屯交界，在國道六號上及中潭公路上可清楚看見其連綿山峰壯觀景象，與苗栗三義火炎山、高雄六龜十八羅漢山並稱為台灣三大火炎山地形，九九峰最高峰海拔約779公尺(無法登頂)，第二高峰701峰可從霧峰的樟樹林步道到達，但也路況不佳。")],-1),m=i("p",null,"林務局南投林管處於草屯新建一條森林步道，於2012年9月9日落成啟用，結合原有產業道路，形成環狀觀景步道，讓民眾得以親近九九峰，步道全長約2.5公里，最高海拔約485公尺，沿途視野廣闊，能俯瞰霧峰、草屯、烏溪、國道六號、平林、雙冬里等景觀，成為踏青賞景新據點。",-1),c={href:"http://www.forest.gov.tw/content.asp?cuItem=61324&mp=1",target:"_blank",rel:"noopener noreferrer"},d=s('<p>走走日期： 2013/02/17</p><h2 id="行車路線" tabindex="-1"><a class="header-anchor" href="#行車路線" aria-hidden="true">#</a> 行車路線</h2><p>國道3號轉國道6號下東草屯交流道，遇玉屏路左轉後直行接台14線，過炎峰橋後於台14線27.8K處左轉進入，約30公尺接健行路右轉，沿主線至九九峰生態藝術園區...</p><p>→九九峰生態藝術園區(已廢棄，可進入停車)，取右續行(健行路150巷)<br><img src="https://1013399.github.io/image-3/165/250221872_l.jpg" alt="九九峰生態藝術園區(已廢棄，可進入停車)" loading="lazy"></p><p>約行240公尺叉路左轉，續行170公尺遇叉路，於路旁停車，附近有廁所。<br> →停車處<br><img src="https://1013399.github.io/image-3/165/250221878_l.jpg" alt="九九峰森林步道" loading="lazy"></p><h2 id="走走路線" tabindex="-1"><a class="header-anchor" href="#走走路線" aria-hidden="true">#</a> 走走路線</h2><p>→依指標出發<br><img src="https://1013399.github.io/image-3/165/250221886_l.jpg" alt="九九峰森林步道" loading="lazy"></p><p>→步道沿途植物多為荔枝樹與馬拉巴栗<br><img src="https://1013399.github.io/image-3/165/250221895_l.jpg" alt="九九峰森林步道" loading="lazy"></p><p>→叉路，依指標取右<br><img src="https://1013399.github.io/image-3/165/250221906_l.jpg" alt="九九峰森林步道" loading="lazy"></p><p>→九九峰步道南北端分岔點，可O型一圈<br><img src="https://1013399.github.io/image-3/165/250221920_l.jpg" alt="九九峰森林步道" loading="lazy"></p><p>→叉路口設有步道導覽圖<br><img src="https://1013399.github.io/image-3/165/250221930_l.jpg" alt="九九峰森林步道" loading="lazy"></p><p>→取直進入南端階梯步道<br><img src="https://1013399.github.io/image-3/165/250221943_l.jpg" alt="九九峰森林步道" loading="lazy"></p><p>→步道略陡<br><img src="https://1013399.github.io/image-3/165/250221951_l.jpg" alt="九九峰森林步道" loading="lazy"></p><p>→里程木牌，1代表100公尺<br><img src="https://1013399.github.io/image-3/165/250221961_l.jpg" alt="九九峰森林步道" loading="lazy"></p><p>→步道陡上<br><img src="https://1013399.github.io/image-3/165/250221970_l.jpg" alt="九九峰森林步道" loading="lazy"></p><p>→沿途展望佳，往左俯瞰國道六號及草屯地區<br><img src="https://1013399.github.io/image-3/165/250221982_l.jpg" alt="九九峰森林步道" loading="lazy"></p><p>→往右欣賞九九峰群峰<br><img src="https://1013399.github.io/image-3/165/250221992_l.jpg" alt="九九峰群峰" loading="lazy"></p><p>→進入平緩雜木林<br><img src="https://1013399.github.io/image-3/165/250222003_l.jpg" alt="九九峰森林步道" loading="lazy"></p><p>→鐵棧橋，里程350公尺處<br><img src="https://1013399.github.io/image-3/165/250222011_l.jpg" alt="九九峰森林步道" loading="lazy"></p><p>→過橋後繼續陡上<br><img src="https://1013399.github.io/image-3/165/250222020_l.jpg" alt="九九峰森林步道" loading="lazy"></p><p>→再長陡上<br><img src="https://1013399.github.io/image-3/165/250222033_l.jpg" alt="九九峰森林步道" loading="lazy"></p><p>→爬得越高，展望更佳<br><img src="https://1013399.github.io/image-3/165/250222041_l.jpg" alt="九九峰森林步道" loading="lazy"></p><p>→477峰，里程700公尺處<br><img src="https://1013399.github.io/image-3/165/250222047_l.jpg" alt="九九峰森林步道" loading="lazy"></p><p>→山頂休息涼亭，里程約800公尺處<br><img src="https://1013399.github.io/image-3/165/250222062_l.jpg" alt="九九峰森林步道" loading="lazy"></p><p>→地質解說牌，九九峰為礫岩結構<br><img src="https://1013399.github.io/image-3/165/250222067_l.jpg" alt="九九峰森林步道 地質解說牌，九九峰為礫岩結構" loading="lazy"></p><p>→九九峰山頂標示牌<br><img src="https://1013399.github.io/image-3/165/250222071_l.jpg" alt="九九峰山頂標示牌" loading="lazy"></p><p>→山頂涼亭前展望解說牌<br><img src="https://1013399.github.io/image-3/165/250222081_l.jpg" alt="九九峰森林步道" loading="lazy"></p><p>→仰望前方的573峰<br><img src="https://1013399.github.io/image-3/165/250222089_l.jpg" alt="九九峰森林步道" loading="lazy"></p><p>→續行約10公尺叉路，直行往573峰，被林務局設路障且有危險警告，取右下<br><img src="https://1013399.github.io/image-3/165/250222100_l.jpg" alt="九九峰森林步道" loading="lazy"></p><p>→陡下後接土石產道<br><img src="https://1013399.github.io/image-3/165/250222110_l.jpg" alt="九九峰森林步道" loading="lazy"></p><p>→九九峰步道北端入口，接水泥產道<br><img src="https://1013399.github.io/image-3/165/250222115_l.jpg" alt="九九峰森林步道" loading="lazy"></p><p>→續行水泥產道<br><img src="https://1013399.github.io/image-3/165/250222123_l.jpg" alt="九九峰森林步道" loading="lazy"></p><p>→產道與右側排水渠合併成一條路<br><img src="https://1013399.github.io/image-3/165/250222128_l.jpg" alt="九九峰森林步道" loading="lazy"></p><p>→回到步道南北端分岔點，取左回<br><img src="https://1013399.github.io/image-3/165/250222132_l.jpg" alt="九九峰森林步道" loading="lazy"></p><p>→回到停車處<br><img src="https://1013399.github.io/image-3/165/250222137_l.jpg" alt="九九峰森林步道" loading="lazy"></p><h2 id="走走資訊" tabindex="-1"><a class="header-anchor" href="#走走資訊" aria-hidden="true">#</a> 走走資訊</h2><div class="hint-container info"><p class="hint-container-title">相關信息</p><p>走走時間：約1.5小時(含休息)</p><p>高低落差：約220公尺</p></div><h2 id="參考地圖" tabindex="-1"><a class="header-anchor" href="#參考地圖" aria-hidden="true">#</a> 參考地圖</h2>',38),b={href:"http://www.keepon.com.tw/DiscussLoad.aspx?code=314B5CF9AEC3A19113F6CAA6F539A66274EFDD6CCAE897A3",target:"_blank",rel:"noopener noreferrer"},_=i("br",null,null,-1),u=i("img",{src:"https://1013399.github.io/image-3/165/250222331_l.jpg",alt:"九九峰森林步道",loading:"lazy"},null,-1);function j(y,z){const a=p("ExternalLinkIcon");return e(),o("div",null,[n,r(" more "),m,i("p",null,[t("(資料摘自："),i("a",c,[t("林務局林業新聞"),l(a)]),t(")")]),d,i("p",null,[t("原圖連結→"),i("a",b,[t("登山補給站-minnelee"),l(a)]),_,u])])}const w=g(h,[["render",j],["__file","post-165-2013-02-22.html.vue"]]);export{w as default};
