import{_ as l}from"./plugin-vue_export-helper-c27b6911.js";import{r as p,o,c as r,b as i,e as a,d as g,a as e,f as s}from"./app-39f3f472.js";const h={},n=i("p",null,"漯(ㄊㄚˋ)底山自然公園，位於高雄市彌陀區漯底村，屬於惡地地形，由大小泥岩山群組成，長期為軍事管制區而未對外開放，在軍方撤哨並交由彌陀區公所代管後，現在已經成立了「漯底山自然公園」，目前已完成了登山休閒步道、觀景台等設施，成為了觀賞泥火山特殊奇景及欣賞海景、夕陽的好地方。",-1),m={href:"http://guide.easytravel.com.tw/scenic.aspx?CityID=20&AreaID=273&PlaceID=3170",target:"_blank",rel:"noopener noreferrer"},b=i("p",null,"漯底山，又稱凹底山，海拔53公尺，三等三角點No.494原立於營區內高點(現為觀景台)，但基石已遺失，聯勤測量隊另立二等三角點內補No.042基石於營區大門口附近，山頂雜草中無視野。",-1),c=s('<p>走走日期： 2013/02/12</p><h2 id="行車路線" tabindex="-1"><a class="header-anchor" href="#行車路線" aria-hidden="true">#</a> 行車路線</h2><p>國道1號下岡山交流道往岡山方向(介壽東路)，後接介壽路與介壽西路(台19甲線)直行，再左轉沿頂潭路、石潭路直行，遇忠孝路右轉直行到底接台17線右轉，不久便可見漯底山自然公園指標叉路(此叉路有中央分隔島，無法直接左轉，需往前至下一路口迴轉再右轉進入)，循指標即可抵達。</p><h2 id="走走路線" tabindex="-1"><a class="header-anchor" href="#走走路線" aria-hidden="true">#</a> 走走路線</h2><p>→漯底山自然公園入口，有棵大樹-欖仁公，停車於左邊停車場或路旁<br><img src="https://1013399.github.io/image-3/166/250220830_l.jpg" alt="漯底山自然公園、漯底山" loading="lazy"></p><p>→漯底山自然公園導覽解說牌<br><img src="https://1013399.github.io/image-3/166/250220835_l.jpg" alt="漯底山自然公園、漯底山" loading="lazy"></p><p>→漯底山自然公園入口意象<br><img src="https://1013399.github.io/image-3/166/250220845_l.jpg" alt="漯底山自然公園、漯底山" loading="lazy"></p><p>→進入公園步道<br><img src="https://1013399.github.io/image-3/166/250220849_l.jpg" alt="漯底山自然公園、漯底山" loading="lazy"></p><p>→於營區大門口前約20公尺，右側樹上有三角點噴漆，取右上<br><img src="https://1013399.github.io/image-3/166/250220859_l.jpg" alt="漯底山自然公園、漯底山" loading="lazy"></p><p>→漯底山聯勤二等三角點內補No.042，拍照後原路退回<br><img src="https://1013399.github.io/image-3/166/250220869_l.jpg" alt="漯底山自然公園、漯底山" loading="lazy"></p><p>→回到步道，進入營區大門<br><img src="https://1013399.github.io/image-3/166/250220862_l.jpg" alt="漯底山自然公園、漯底山" loading="lazy"></p><p>→叉路，左往營舍、管理站，取直續行<br><img src="https://1013399.github.io/image-3/166/250220872_l.jpg" alt="漯底山自然公園、漯底山" loading="lazy"></p><p>→營區處處可見崗哨、碉堡，途中有叉路可往神女峰<br><img src="https://1013399.github.io/image-3/166/250220883_l.jpg" alt="漯底山自然公園、漯底山" loading="lazy"></p><p>→觀景台，原三角點位置<br><img src="https://1013399.github.io/image-3/166/250220888_l.jpg" alt="漯底山自然公園、漯底山" loading="lazy"></p><p>→叉路，左往泥火山口(忘了前往)，取右往碉堡觀景台<br><img src="https://1013399.github.io/image-3/166/250220892_l.jpg" alt="漯底山自然公園、漯底山" loading="lazy"></p><p>→叉路，右往北側外環步道，取直續行<br><img src="https://1013399.github.io/image-3/166/250220896_l.jpg" alt="漯底山自然公園、漯底山" loading="lazy"></p><p>→碉堡觀景台<br><img src="https://1013399.github.io/image-3/166/250220900_l.jpg" alt="漯底山自然公園、漯底山" loading="lazy"></p><p>→觀景台前視野極佳，是看海、看夕陽的最佳位置<br><img src="https://1013399.github.io/image-3/166/250220908_l.jpg" alt="漯底山自然公園、漯底山" loading="lazy"></p><p>→續前行往碉堡<br><img src="https://1013399.github.io/image-3/166/250220913_l.jpg" alt="漯底山自然公園、漯底山" loading="lazy"></p><p>→碉堡前展望(七張相片合成)<br><img src="https://1013399.github.io/image-3/166/250221081_l.jpg" alt="漯底山自然公園、漯底山" loading="lazy"></p><p>→碉堡前觀景平台設有解說牌<br><img src="https://1013399.github.io/image-3/166/250220920_l.jpg" alt="漯底山自然公園、漯底山" loading="lazy"></p><p>→續行木棧階梯陡下<br><img src="https://1013399.github.io/image-3/166/250220924_l.jpg" alt="漯底山自然公園、漯底山" loading="lazy"></p><p>→階梯旁便可見漯底山惡地地形<br><img src="https://1013399.github.io/image-3/166/250220931_l.jpg" alt="漯底山自然公園、漯底山" loading="lazy"></p><p>→木棧階梯叉路，左為北橫步道，先取右往北側外環步道<br><img src="https://1013399.github.io/image-3/166/250220938_l.jpg" alt="漯底山自然公園、漯底山" loading="lazy"></p><p>→漯底山泥岩山群，表皮為乾燥的泥漿<br><img src="https://1013399.github.io/image-3/166/250220944_l.jpg" alt="漯底山自然公園、漯底山" loading="lazy"></p><p>→續行步道至拉繩處，遇一家四口遊客稱前方崩塌，不宜前往，於是原路折返<br><img src="https://1013399.github.io/image-3/166/250220951_l.jpg" alt="漯底山自然公園、漯底山" loading="lazy"></p><p>→仰望剛經過的碉堡觀景台<br><img src="https://1013399.github.io/image-3/166/250220960_l.jpg" alt="漯底山自然公園、漯底山" loading="lazy"></p><p>→回到木棧階梯叉路，取直進入北橫步道</p><p>→路旁盡是泥岩山惡地形<br><img src="https://1013399.github.io/image-3/166/250220965_l.jpg" alt="漯底山自然公園、漯底山" loading="lazy"></p><p>→走出泥岩地形區<br><img src="https://1013399.github.io/image-3/166/250220979_l.jpg" alt="漯底山自然公園、漯底山" loading="lazy"></p><p>→北橫步道結束，接上柏油路，右往西南步道，左回營舍、管理站，對面為南橫步道<br><img src="https://1013399.github.io/image-3/166/250220986_l.jpg" alt="漯底山自然公園、漯底山" loading="lazy"></p><p>→取直陡上進入南橫步道<br><img src="https://1013399.github.io/image-3/166/250220994_l.jpg" alt="漯底山自然公園、漯底山" loading="lazy"></p><p>→不久轉為平緩山徑<br><img src="https://1013399.github.io/image-3/166/250220998_l.jpg" alt="漯底山自然公園、漯底山" loading="lazy"></p><p>→路旁隱藏式碉堡<br><img src="https://1013399.github.io/image-3/166/250221003_l.jpg" alt="漯底山自然公園、漯底山" loading="lazy"></p><p>→接木棧道陡下<br><img src="https://1013399.github.io/image-3/166/250221008_l.jpg" alt="漯底山自然公園、漯底山" loading="lazy"></p><p>→陡下再陡上<br><img src="https://1013399.github.io/image-3/166/250221012_l.jpg" alt="漯底山自然公園、漯底山" loading="lazy"></p><p>→南橫步道穿梭於樹林間，無泥岩地形可賞<br><img src="https://1013399.github.io/image-3/166/250221017_l.jpg" alt="漯底山自然公園、漯底山" loading="lazy"></p><p>→觀景平台<br><img src="https://1013399.github.io/image-3/166/250221020_l.jpg" alt="漯底山自然公園、漯底山" loading="lazy"></p><p>→觀景平台展望<br><img src="https://1013399.github.io/image-3/166/250221024_l.jpg" alt="漯底山自然公園、漯底山" loading="lazy"></p><p>→續行陡下<br><img src="https://1013399.github.io/image-3/166/250221025_l.jpg" alt="漯底山自然公園、漯底山" loading="lazy"></p><p>→接回柏油路，取右續行<br><img src="https://1013399.github.io/image-3/166/250221032_l.jpg" alt="漯底山自然公園、漯底山" loading="lazy"></p><p>→漯底山管理站<br><img src="https://1013399.github.io/image-3/166/250221033_l.jpg" alt="漯底山自然公園、漯底山" loading="lazy"></p><p>→管理站對面小公園<br><img src="https://1013399.github.io/image-3/166/250221036_l.jpg" alt="漯底山自然公園、漯底山" loading="lazy"></p><p>→廢棄營舍，旁有廁所<br><img src="https://1013399.github.io/image-3/166/250221041_l.jpg" alt="漯底山自然公園、漯底山" loading="lazy"></p><p>→今日大年初三，溫度竟然高達30℃<br><img src="https://1013399.github.io/image-3/166/250221045_l.jpg" alt="漯底山自然公園、漯底山" loading="lazy"></p><p>→回到之前第一處叉路，左往碉堡觀景台，取右回<br><img src="https://1013399.github.io/image-3/166/250221047_l.jpg" alt="漯底山自然公園、漯底山" loading="lazy"></p><p>→回到營區大門口<br><img src="https://1013399.github.io/image-3/166/250221051_l.jpg" alt="漯底山自然公園、漯底山" loading="lazy"></p><p>→回到停車處</p><h2 id="走走資訊" tabindex="-1"><a class="header-anchor" href="#走走資訊" aria-hidden="true">#</a> 走走資訊</h2><div class="hint-container info"><p class="hint-container-title">相關信息</p><p>走走時間：約1.5小時(含休息)</p><p>高低落差：約30公尺</p></div><h2 id="參考地圖" tabindex="-1"><a class="header-anchor" href="#參考地圖" aria-hidden="true">#</a> 參考地圖</h2>',51),d={href:"http://tw.myblog.yahoo.com/jj-mount/article?mid=11362",target:"_blank",rel:"noopener noreferrer"},_=i("br",null,null,-1),u=i("img",{src:"https://1013399.github.io/image-3/166/250221125_l.jpg",alt:"漯底山自然公園、漯底山",loading:"lazy"},null,-1);function y(j,z){const t=p("ExternalLinkIcon");return o(),r("div",null,[n,i("p",null,[a("(資料摘自："),i("a",m,[a("四方通行旅遊網"),g(t)]),a(")")]),b,e(" more "),c,i("p",null,[a("原圖連結→"),i("a",d,[a("JJ的山水簿"),g(t)]),_,u])])}const N=l(h,[["render",y],["__file","post-166-2013-02-21.html.vue"]]);export{N as default};
