import{_ as g}from"./plugin-vue_export-helper-c27b6911.js";import{r as p,o as e,c as r,b as i,f as a,d as l,a as o,e as s}from"./app-91fc5112.js";const n={},h=i("p",null,[i("strong",null,"新威森林公園"),a("位於台28線"),i("strong",null,"46.5km"),a("處(原資料為45km有誤)，在荖濃溪西岸的台地上，本為荖濃溪舊河床的一部份，後來因土質優良而建為苗圃，居民稱為「坪頂」。佔地30甲，以種植樹苗為主，培植的樣種多達 200餘種，為全台最大的苗圃之一。現已改為森林公園，由茂林國家風景區管理處經營。")],-1),m={href:"http://www.maolin-nsa.gov.tw/user/Article.aspx?Lang=1&SNo=04000075",target:"_blank",rel:"noopener noreferrer"},c=s('<p>裡面樹林成蔭，春夏綠意盎然，其中以桃花心木、竹子、杉木最多。其中有一條桃花心木大道，周邊綠樹成陰，冬末初春時，桃花心木的綠葉會呈現紅黃色，加上鋪滿小徑的落葉，景色顯得詩情畫意。 那成排的林木所延伸而成的天然長廊，有著異國風情，常吸引許多情侶前來散步，也是許多新人拍攝婚紗指定的地點。</p><p>走走日期： 2013/02/11</p><h2 id="行車路線" tabindex="-1"><a class="header-anchor" href="#行車路線" aria-hidden="true">#</a> 行車路線</h2><p>國道3號南下，由於想先至六龜逛逛，故於官田系統交流道轉台84往玉井方向，再接台20線往六龜，經過甲仙至六龜再轉台27線南下，於台27線10.2km右轉台27甲線(若續行台27線路況差)，經過六龜大橋至六龜市區用餐，餐畢續行台27甲線往美濃方向，途經十八羅漢山，停車駐足欣賞...<br> →十八羅漢山<br><img src="https://1013399.github.io/image-3/167/250219603_l.jpg" alt="新威森林公園" loading="lazy"></p><p>續行台27甲線，後接台28線，於台28線46.5km處右轉上山，停車於新威森林公園停車場。</p><h2 id="走走路線" tabindex="-1"><a class="header-anchor" href="#走走路線" aria-hidden="true">#</a> 走走路線</h2><p>→新威森林公園停車場(廁所旁)<br><img src="https://1013399.github.io/image-3/167/250219628_l.jpg" alt="新威森林公園" loading="lazy"></p><p>→日式庭園兩隻龍龜<br><img src="https://1013399.github.io/image-3/167/250219645_l.jpg" alt="新威森林公園" loading="lazy"></p><p>→進入桃花心木步道<br><img src="https://1013399.github.io/image-3/167/250219637_l.jpg" alt="新威森林公園" loading="lazy"></p><p>→碎石步道覆蓋落葉，顯得詩情畫意<br><img src="https://1013399.github.io/image-3/167/250219653_l.jpg" alt="新威森林公園" loading="lazy"></p><p>→桃花心木解說牌<br><img src="https://1013399.github.io/image-3/167/250219657_l.jpg" alt="新威森林公園" loading="lazy"></p><p>→由此切回柏油路過小橋<br><img src="https://1013399.github.io/image-3/167/250219662_l.jpg" alt="新威森林公園" loading="lazy"></p><p>→穿越瓜棚<br><img src="https://1013399.github.io/image-3/167/250219676_l.jpg" alt="新威森林公園" loading="lazy"></p><p>→走在平緩的木棧道上，輕鬆愜意<br><img src="https://1013399.github.io/image-3/167/250219690_l.jpg" alt="新威森林公園" loading="lazy"></p><p>→沿途設有多處涼亭，可供休息<br><img src="https://1013399.github.io/image-3/167/250219698_l.jpg" alt="新威森林公園" loading="lazy"></p><p>→再穿越兩處瓜棚<br><img src="https://1013399.github.io/image-3/167/250219710_l.jpg" alt="新威森林公園" loading="lazy"></p><p>→叉路，左可回廁所、停車場，續直行<br><img src="https://1013399.github.io/image-3/167/250219715_l.jpg" alt="新威森林公園" loading="lazy"></p><p>→接上車道，左往公園入口，直行仍為木棧道，先取右往第一觀景台賞景(往台28方向)<br><img src="https://1013399.github.io/image-3/167/250219730_l.jpg" alt="新威森林公園" loading="lazy"></p><p>→第一觀景台<br><img src="https://1013399.github.io/image-3/167/250219734_l.jpg" alt="新威森林公園" loading="lazy"></p><p>→觀景台旁金絲竹及解說牌<br><img src="https://1013399.github.io/image-3/167/250219747_l.jpg" alt="新威森林公園" loading="lazy"></p><p>→觀景台視野佳<br><img src="https://1013399.github.io/image-3/167/250407703_l.jpg" alt="新威森林公園" loading="lazy"></p><p>→俯瞰台28線<br><img src="https://1013399.github.io/image-3/167/250219741_l.jpg" alt="新威森林公園" loading="lazy"></p><p>→遠眺新威大橋<br><img src="https://1013399.github.io/image-3/167/250407688_l.jpg" alt="新威森林公園" loading="lazy"></p><p>→回到前一叉路，取右續行木棧道，旁有入口意象及導覽圖<br><img src="https://1013399.github.io/image-3/167/250219759_l.jpg" alt="新威森林公園" loading="lazy"></p><p>→涼爽的樹蔭步道，沿途多為麵包樹及波羅蜜<br><img src="https://1013399.github.io/image-3/167/250219766_l.jpg" alt="新威森林公園" loading="lazy"></p><p>→接上車道，取左回<br><img src="https://1013399.github.io/image-3/167/250219784_l.jpg" alt="新威森林公園" loading="lazy"></p><p>→路口旁「觀音棕竹」及解說牌<br><img src="https://1013399.github.io/image-3/167/250219787_l.jpg" alt="新威森林公園" loading="lazy"></p><p>→再進入桃花心木步道<br><img src="https://1013399.github.io/image-3/167/250219791_l.jpg" alt="新威森林公園" loading="lazy"></p><p>→右側圍籬內為已歇業的維多利亞花園<br><img src="https://1013399.github.io/image-3/167/250219835_l.jpg" alt="新威森林公園" loading="lazy"></p><p>→接回車道，左往第一觀景台(台28線)，右為維多利亞花園入口<br><img src="https://1013399.github.io/image-3/167/250219839_l.jpg" alt="新威森林公園" loading="lazy"></p><p>→維多利亞花園大門，春節期間提供當臨時停車場使用<br><img src="https://1013399.github.io/image-3/167/250219845_l.jpg" alt="→維多利亞花園大門" loading="lazy"></p><p>→路口「新苗競秀」石碑，設立於民國49年<br><img src="https://1013399.github.io/image-3/167/250219849_l.jpg" alt="新威森林公園 新苗競秀" loading="lazy"></p><p>→續直行回到停車場</p><h2 id="走走資訊" tabindex="-1"><a class="header-anchor" href="#走走資訊" aria-hidden="true">#</a> 走走資訊</h2><div class="hint-container info"><p class="hint-container-title">相關信息</p><p>走走時間：輕鬆悠遊約1.5小時</p><p>高低落差：約20公尺</p></div><h2 id="參考地圖" tabindex="-1"><a class="header-anchor" href="#參考地圖" aria-hidden="true">#</a> 參考地圖</h2>',36),b={href:"http://www.maolin-nsa.gov.tw/bicycle/bike/line1-1.html",target:"_blank",rel:"noopener noreferrer"},d=i("br",null,null,-1),_=i("img",{src:"https://1013399.github.io/image-3/167/250219904_l.jpg",alt:"新威森林公園",loading:"lazy"},null,-1);function u(y,j){const t=p("ExternalLinkIcon");return e(),r("div",null,[h,i("p",null,[a("(資料來源："),i("a",m,[a("茂林國家風景區管理處"),l(t)]),a(")")]),o(" more "),c,i("p",null,[a("原圖連結→"),i("a",b,[a("茂林國家風景區-自行車路線"),l(t)]),d,_])])}const k=g(n,[["render",u],["__file","post-167-2013-02-20.html.vue"]]);export{k as default};
