import{_ as e}from"./plugin-vue_export-helper-c27b6911.js";import{r as g,o as r,c as o,b as t,e as a,d as l,a as s,f as p}from"./app-cb5d936b.js";const n={},h=t("p",null,[t("strong",null,"小東河步道"),a("，位於苗栗縣南庄鄉小東河地區，步道長約1690公尺，由參山國家風景區管理處所興建，北登山口位於百香農場旁，為較陡之枕木階梯；南登山口位於春谷農場的鱒魚養殖區旁，為平緩之石板步道，其餘則是原始的土石小徑，沿途動植物豐富，全程綠林夾道，是登山休閒、賞桐賞螢的好去處。")],-1),c={href:"http://www.trimt-nsa.gov.tw/cht/unit_04_1_2.aspx?subsiteID=3&hotID=60",target:"_blank",rel:"noopener noreferrer"},d={href:"http://travel.udn.com/mag/travel/storypage.jsp?f_ART_ID=8276",target:"_blank",rel:"noopener noreferrer"},m=p('<p>走完小東河步道，時間尚早，再前往「通安步道」走走，抵達通安步道入口時，旁邊民宅飼養兩隻大狼犬，未綁狗鍊、兇猛狂吠，雖有圍籬，但體型碩大，恐有衝出之虞，由於今日只有我單獨一人，安全起見，於步道口拍幾張照後，便速速離開，下次人多勢眾時，再來造訪。</p><p>走走日期： 2013/01/26</p><h2 id="小東河步道-行車路線" tabindex="-1"><a class="header-anchor" href="#小東河步道-行車路線" aria-hidden="true">#</a> 小東河步道 行車路線</h2><p>國道1號下頭份交流道，接上124縣道往三灣、南庄方向，經過南庄大橋後續直行124縣道，於124縣道30.3Km處(飯盆頭餐廳)左轉進入，約行1.1Km到達百香農場，停車於路旁較寬處。</p><h2 id="小東河步道-走走路線" tabindex="-1"><a class="header-anchor" href="#小東河步道-走走路線" aria-hidden="true">#</a> 小東河步道 走走路線</h2><p>→124縣道30.3Km處，由此左轉進入<br><img src="https://1013399.github.io/image-3/168/248268585_l.jpg" alt="小東河步道" loading="lazy"></p><p>→百香農場大門，往前約10公尺即為北登山口<br><img src="https://1013399.github.io/image-3/168/248268628_l.jpg" alt="小東河步道" loading="lazy"></p><p>→北登山口<br><img src="https://1013399.github.io/image-3/168/248268633_l.jpg" alt="小東河步道" loading="lazy"></p><p>→登山口指標<br><img src="https://1013399.github.io/image-3/168/248268642_l.jpg" alt="小東河步道" loading="lazy"></p><p>→陡上枕木階梯<br><img src="https://1013399.github.io/image-3/168/248268649_l.jpg" alt="小東河步道" loading="lazy"></p><p>→沿途生態豐富、綠林夾道<br><img src="https://1013399.github.io/image-3/168/248268655_l.jpg" alt="小東河步道" loading="lazy"></p><p>→繼續陡上<br><img src="https://1013399.github.io/image-3/168/248268665_l.jpg" alt="小東河步道" loading="lazy"></p><p>→林間稍有展望<br><img src="https://1013399.github.io/image-3/168/248268670_l.jpg" alt="小東河步道" loading="lazy"></p><p>→即將到達高點觀景台，旁有山棕解說牌<br><img src="https://1013399.github.io/image-3/168/248268676_l.jpg" alt="小東河步道" loading="lazy"></p><p>→枕木階梯結束，步道轉為土石小徑，於右側觀景台休息<br><img src="https://1013399.github.io/image-3/168/248268686_l.jpg" alt="小東河步道" loading="lazy"></p><p>→續行，步道極為平緩，經過一座小橋<br><img src="https://1013399.github.io/image-3/168/248268691_l.jpg" alt="小東河步道" loading="lazy"></p><p>→觀音座蓮，蕨類的一種，旁有解說牌<br><img src="https://1013399.github.io/image-3/168/248268698_l.jpg" alt="小東河步道" loading="lazy"></p><p>→第二處觀景台<br><img src="https://1013399.github.io/image-3/168/248268703_l.jpg" alt="小東河步道" loading="lazy"></p><p>→觀景台展望<br><img src="https://1013399.github.io/image-3/168/248268708_l.jpg" alt="小東河步道" loading="lazy"></p><p>→進入杉木林<br><img src="https://1013399.github.io/image-3/168/248268714_l.jpg" alt="小東河步道" loading="lazy"></p><p>→出杉木林，路旁兩棵頗為特殊的樹<br><img src="https://1013399.github.io/image-3/168/248268720_l.jpg" alt="小東河步道" loading="lazy"></p><p>→快到達南登山口了，俯瞰春谷農場住宿區<br><img src="https://1013399.github.io/image-3/168/248268725_l.jpg" alt="小東河步道" loading="lazy"></p><p>→接上石板步道，開始往下行<br><img src="https://1013399.github.io/image-3/168/248268730_l.jpg" alt="小東河步道" loading="lazy"></p><p>→風景優美，有進入私人渡假村的感覺<br><img src="https://1013399.github.io/image-3/168/248268736_l.jpg" alt="小東河步道" loading="lazy"></p><p>→叉路，直下接上道路，取右往南登山口<br><img src="https://1013399.github.io/image-3/168/248268740_l.jpg" alt="小東河步道" loading="lazy"></p><p>→梯狀排列的飼養池，養殖高冷性「虹鱒」<br><img src="https://1013399.github.io/image-3/168/248268745_l.jpg" alt="虹鱒飼養池" loading="lazy"></p><p>→小東河步道南登山口，取左回<br><img src="https://1013399.github.io/image-3/168/248268749_l.jpg" alt="小東河步道南登山口" loading="lazy"></p><p>→春谷農場住宿區<br><img src="https://1013399.github.io/image-3/168/248268754_l.jpg" alt="春谷農場住宿區" loading="lazy"></p><p>→路旁農場簡介圖<br><img src="https://1013399.github.io/image-3/168/248268760_l.jpg" alt="春谷農場簡介圖" loading="lazy"></p><p>→錦鯉觀賞池<br><img src="https://1013399.github.io/image-3/168/248268764_l.jpg" alt="小東河步道" loading="lazy"></p><p>→春谷養鱒場入口意象(往回拍攝)，這些山友要從南登山口起登<br><img src="https://1013399.github.io/image-3/168/248268775_l.jpg" alt="春谷養鱒場入口" loading="lazy"></p><p>→春谷虹鱒石碑(往回拍攝)<br><img src="https://1013399.github.io/image-3/168/248268782_l.jpg" alt="春谷虹鱒石碑" loading="lazy"></p><p>→回到北登山口，取車前往「通安步道」</p><h2 id="小東河步道-走走資訊" tabindex="-1"><a class="header-anchor" href="#小東河步道-走走資訊" aria-hidden="true">#</a> 小東河步道 走走資訊</h2><div class="hint-container info"><p class="hint-container-title">相關信息</p><p>走走時間：約1小時</p><p>高低落差：約150公尺</p></div><h2 id="小東河步道-參考地圖" tabindex="-1"><a class="header-anchor" href="#小東河步道-參考地圖" aria-hidden="true">#</a> 小東河步道 參考地圖</h2>',36),b={href:"http://www.keepon.com.tw/DiscussLoad.aspx?code=314B5CF9AEC3A19170A9A7E294A7989AB82F5A5EF6AA79BF",target:"_blank",rel:"noopener noreferrer"},_=t("br",null,null,-1),u=t("img",{src:"https://1013399.github.io/image-3/168/248268933_l.jpg",alt:"小東河步道 參考地圖",loading:"lazy"},null,-1),y={href:"http://blog.xuite.net/joey_yeh/blog/64123142",target:"_blank",rel:"noopener noreferrer"},j=t("br",null,null,-1),z=t("img",{src:"https://1013399.github.io/image-3/168/248268928_l.jpg",alt:"小東河步道 參考地圖",loading:"lazy"},null,-1),f=p('<h2 id="通安步道-行車路線" tabindex="-1"><a class="header-anchor" href="#通安步道-行車路線" aria-hidden="true">#</a> 通安步道 行車路線</h2><p>回到124縣道左轉往仙山方向，於124縣道31.3Km處右轉，過通安橋再右轉，沿主線行駛約500公尺，即可到達觀景平台停車場。</p><p>→觀景平台停車場<br><img src="https://1013399.github.io/image-3/168/248268789_l.jpg" alt="通安步道" loading="lazy"></p><p>→通安步道導覽圖<br><img src="https://1013399.github.io/image-3/168/248268794_l.jpg" alt="通安步道" loading="lazy"></p><p>→眺望陡峭的通安步道<br><img src="https://1013399.github.io/image-3/168/248268799_l.jpg" alt="通安步道" loading="lazy"></p><p>→猛犬狂吠，速速離開</p><h2 id="通安步道-走走資訊" tabindex="-1"><a class="header-anchor" href="#通安步道-走走資訊" aria-hidden="true">#</a> 通安步道 走走資訊</h2><div class="hint-container info"><p class="hint-container-title">相關信息</p><p>走走時間：預估約1.5小時</p><p>高低落差：約310公尺</p></div>',8);function x(v,A){const i=g("ExternalLinkIcon");return r(),o("div",null,[h,t("p",null,[a("(資料參考："),t("a",c,[a("參山國家風景區管理處"),l(i)]),a("、"),t("a",d,[a("udn旅遊休閒網"),l(i)]),a(")")]),s(" more "),m,t("p",null,[a("原圖連結→"),t("a",b,[a("登山補給站-祥馬圖集(十四96)"),l(i)]),_,u]),t("p",null,[a("原圖連結→"),t("a",y,[a("我的山林印象"),l(i)]),j,z]),f])}const B=e(n,[["render",x],["__file","post-168-2013-01-29.html.vue"]]);export{B as default};
