import{_ as l}from"./plugin-vue_export-helper-c27b6911.js";import{r as e,o,c as p,a as g,b as t,f as a,d as r,e as s}from"./app-a9847bbb.js";const n={},h=t("p",null,[t("strong",null,"北隘勇古道"),a("，位於苗栗頭屋鄉與獅潭鄉交界稜線上，與南隘勇古道是昔日的隘勇線，自登山口至蕃子寮山僅約600公尺，前段已舖設寬大石板步道，末段100公尺則為陡峭木棧階梯(雨後超級濕滑、必須小心行走)，爬完階梯即登上蕃子寮山。探訪北隘勇步道可順訪附近的鳴鳳古道、八達嶺古道、錫隘古道、夢幻桐花步道等。")],-1),c=t("p",null,[t("strong",null,"蕃子寮山"),a("，海拔492公尺，行政區屬頭屋鄉鳴鳳村，山頂有顆三等三角點No.7，山頂視野有限。")],-1),m=s('<p>走走日期：2013/01/06</p><h2 id="行車路線" tabindex="-1"><a class="header-anchor" href="#行車路線" aria-hidden="true">#</a> 行車路線</h2><p>台13線自苗栗市往北行，於22.5Km處(網路資料23K有誤)右轉苗22鄉道(鳴鳳道路)，經過雲洞宮後，於苗22鄉道9.1Km一大彎道處停車，此即北隘勇古道的入口。</p><p>註：續行苗22鄉道往獅潭方向，可以接上台3線112.4Km處(距離約2.8Km)，但道路施工封閉中，故<strong>目前造訪北隘勇古道，只能從台13線進入</strong>。</p><h2 id="走走路線" tabindex="-1"><a class="header-anchor" href="#走走路線" aria-hidden="true">#</a> 走走路線</h2><p>→苗22鄉道9.1Km北隘勇古道入口指標<br><img src="https://1013399.github.io/image-3/169/246317596_l.jpg" alt="苗22鄉道9.1Km北隘勇古道入口指標" loading="lazy"></p><p>→往獅潭(台3線)方向，目前道路施工封閉中<br><img src="https://1013399.github.io/image-3/169/246317611_l.jpg" alt="北隘勇古道、蕃子寮山" loading="lazy"></p><p>→出發進入產道<br><img src="https://1013399.github.io/image-3/169/246317605_l.jpg" alt="北隘勇古道、蕃子寮山" loading="lazy"></p><p>→北隘勇古道入口，取右進入<br><img src="https://1013399.github.io/image-3/169/246317615_l.jpg" alt="北隘勇古道入口" loading="lazy"></p><p>→北隘勇古道里程碑，每100公尺設一座<br><img src="https://1013399.github.io/image-3/169/246317624_l.jpg" alt="北隘勇古道、蕃子寮山" loading="lazy"></p><p>→護欄觀景點<br><img src="https://1013399.github.io/image-3/169/246317629_l.jpg" alt="北隘勇古道、蕃子寮山" loading="lazy"></p><p>→觀景點視野還不錯<br><img src="https://1013399.github.io/image-3/169/246317634_l.jpg" alt="北隘勇古道、蕃子寮山" loading="lazy"></p><p>→叉路，左下可往浮雲洞、夢幻桐花步道，續直行<br><img src="https://1013399.github.io/image-3/169/246317641_l.jpg" alt="北隘勇古道、蕃子寮山" loading="lazy"></p><p>→叉路，右上往夢幻台(觀景點)，續直行<br><img src="https://1013399.github.io/image-3/169/246317647_l.jpg" alt="北隘勇古道、蕃子寮山" loading="lazy"></p><p>→十字叉路，左下山路往謝家伙房，右前產道可往獅潭，前方為「嬲崠茶亭」<br><img src="https://1013399.github.io/image-3/169/246317654_l.jpg" alt="嬲崠茶亭" loading="lazy"></p><p>→十字叉路旁設有「嬲崠茶亭」及「浮雲洞」解說牌<br><img src="https://1013399.github.io/image-3/169/246317662_l.jpg" alt="「嬲崠茶亭」及「浮雲洞」解說牌" loading="lazy"></p><p>→嬲崠茶亭，為昔日往來頭屋與獅潭間商旅人們歇腳喝茶的涼亭<br><img src="https://1013399.github.io/image-3/169/246317667_l.jpg" alt="嬲崠茶亭" loading="lazy"></p><p>→茶亭內牆上掛有昔日古道懷舊相片<br><img src="https://1013399.github.io/image-3/169/246317673_l.jpg" alt="嬲崠茶亭 昔日古道懷舊相片" loading="lazy"></p><p>→續行遇叉路，左右皆可登上蕃子寮山，左路似乎雜草稍多，取右步道<br><img src="https://1013399.github.io/image-3/169/246317680_l.jpg" alt="北隘勇古道、蕃子寮山" loading="lazy"></p><p>→陡上木棧階梯，非常濕滑<br><img src="https://1013399.github.io/image-3/169/246317687_l.jpg" alt="北隘勇古道、蕃子寮山" loading="lazy"></p><p>→爬完階梯即登上蕃子寮山山頂<br><img src="https://1013399.github.io/image-3/169/246317697_l.jpg" alt="往蕃子寮山山頂的階梯" loading="lazy"></p><p>→蕃子寮山三等三角點No.7<br><img src="https://1013399.github.io/image-3/169/246317706_l.jpg" alt="蕃子寮山三等三角點No.7" loading="lazy"></p><p>→左側往下路徑可回前一叉路，或續行北隘勇古道(路況不佳)，原路折返<br> →回到嬲崠茶亭，休息<br> →回到苗22鄉道</p><h2 id="走走資訊" tabindex="-1"><a class="header-anchor" href="#走走資訊" aria-hidden="true">#</a> 走走資訊</h2><div class="hint-container info"><p class="hint-container-title">相關信息</p><p>走走時間：約40分鐘</p><p>高低落差：約60公尺</p></div><h2 id="參考地圖" tabindex="-1"><a class="header-anchor" href="#參考地圖" aria-hidden="true">#</a> 參考地圖</h2>',26),d={href:"http://tw.myblog.yahoo.com/fuli1961/article?mid=18930",target:"_blank",rel:"noopener noreferrer"},b=t("br",null,null,-1),_=t("img",{src:"https://1013399.github.io/image-3/169/246317748_l.jpg",alt:"北隘勇古道、蕃子寮山",loading:"lazy"},null,-1);function u(y,j){const i=e("ExternalLinkIcon");return o(),p("div",null,[h,c,g(" more "),m,t("p",null,[a("原圖連結→"),t("a",d,[a("山影狐蹤部落格"),r(i)]),b,_])])}const x=l(n,[["render",u],["__file","post-169-2013-01-09.html.vue"]]);export{x as default};
