import{_ as t}from"./plugin-vue_export-helper-c27b6911.js";import{o as g,c as l,a as p,b as i,f as a,e as o}from"./app-7f62a6ff.js";const r={},s=i("p",null,[i("strong",null,"墨硯山"),a("，位於苗栗公館與頭屋交界，行政區屬公館鄉尖山村，山頂平坦呈方形，遠望頗似墨硯而得名，又山形似端菜之托盤，亦稱"),i("strong",null,"托盤山"),a("("),i("strong",null,"托皿山"),a(")，為苗栗八景「三台疊翠」勝景的第一台(第二台為八角崠山脈，第三台為加里山脈)。")],-1),h=i("p",null,"墨硯山有兩顆三角點分別位於南北二處，三等三角點No.144，位於海拔153公尺的北邊山頭，二等三角點No.1048，則位於海拔157公尺的南邊，有山友稱北峰為墨硯山、南峰為托皿山，也有山友稱南峰為墨硯山，似乎有些混淆不清，乾脆分別將其稱為墨硯山北峰、墨硯山南峰。",-1),e=o('<p>走走日期： 2013/01/06</p><h2 id="行車路線" tabindex="-1"><a class="header-anchor" href="#行車路線" aria-hidden="true">#</a> 行車路線</h2><p>國道1號下苗栗交流道，右轉台6線往苗栗市方向，遇台72線快速道路右轉往後龍方向，於頭屋下交流道(10號交流道)，接台13線往頭屋方向，於台13線25Km處遇加油站右轉苗25鄉道，約行1.1Km左轉過高速公路涵洞往曲洞村，過活動中心後三叉路右轉，不久即可抵達墨硯山登山口，停車於登山口路旁較寬處。</p><h2 id="走走路線" tabindex="-1"><a class="header-anchor" href="#走走路線" aria-hidden="true">#</a> 走走路線</h2><p>→墨硯山登山口<br><img src="https://1013399.github.io/image-3/170/246316567_l.jpg" alt="墨硯山登山口" loading="lazy"></p><p>→登山口旁設有墨硯山解說牌<br><img src="https://1013399.github.io/image-3/170/246316597_l.jpg" alt="墨硯山解說牌" loading="lazy"></p><p>→墨硯古道解說牌<br><img src="https://1013399.github.io/image-3/170/246316606_l.jpg" alt="墨硯古道解說牌" loading="lazy"></p><p>→登山口右側有顆中油基石<br><img src="https://1013399.github.io/image-3/170/246316836_l.jpg" alt="墨硯山北峰 中油基石" loading="lazy"></p><p>→經過一小段鋪石步道後，轉為寬大平緩山徑<br><img src="https://1013399.github.io/image-3/170/246316615_l.jpg" alt="墨硯山北峰" loading="lazy"></p><p>→進入竹林後，轉為略陡石板階梯<br><img src="https://1013399.github.io/image-3/170/246316624_l.jpg" alt="墨硯山北峰" loading="lazy"></p><p>→看見輪胎階梯就上達稜線了<br><img src="https://1013399.github.io/image-3/170/246316632_l.jpg" alt="墨硯山北峰" loading="lazy"></p><p>→稜線叉路，取右約十公尺便可見北峰三角點<br><img src="https://1013399.github.io/image-3/170/246316635_l.jpg" alt="墨硯山北峰" loading="lazy"></p><p>→墨硯山北峰三等三角點No.144<br><img src="https://1013399.github.io/image-3/170/246316641_l.jpg" alt="墨硯山北峰三等三角點No.144" loading="lazy"></p><p>→北峰略有展望，俯瞰苗栗市<br><img src="https://1013399.github.io/image-3/170/246316648_l.jpg" alt="墨硯山北峰 俯瞰苗栗市" loading="lazy"></p><p>→續行陡下，但不長<br><img src="https://1013399.github.io/image-3/170/246316653_l.jpg" alt="墨硯山北峰" loading="lazy"></p><p>→再陡上南峰<br><img src="https://1013399.github.io/image-3/170/246316661_l.jpg" alt="墨硯山北峰 陡上墨硯山南峰" loading="lazy"></p><p>→到達南峰山頂，有座鐵塔<br><img src="https://1013399.github.io/image-3/170/246316666_l.jpg" alt="墨硯山南峰山頂 有座鐵塔" loading="lazy"></p><p>→此鐵塔為一等衛星控制點<br><img src="https://1013399.github.io/image-3/170/246316672_l.jpg" alt="墨硯山南峰 鐵塔為一等衛星控制點" loading="lazy"></p><p>→山頂種植楓樹已成林，南峰三角點在左側楓樹林內<br><img src="https://1013399.github.io/image-3/170/246316681_l.jpg" alt="墨硯山南峰" loading="lazy"></p><p>→墨硯山南峰二等三角點No.1048<br><img src="https://1013399.github.io/image-3/170/246316688_l.jpg" alt="墨硯山南峰二等三角點No.1048" loading="lazy"></p><p>→山頂為私人土地，開闢成小型公園<br><img src="https://1013399.github.io/image-3/170/246316695_l.jpg" alt="墨硯山南峰山頂" loading="lazy"></p><p>→續行步道，巧遇黃姓地主<br><img src="https://1013399.github.io/image-3/170/246316700_l.jpg" alt="墨硯山南峰" loading="lazy"></p><p>→黃老先生熱心的解說正在興建中的巨龍(左掌金印、右執天筆)<br><img src="https://1013399.github.io/image-3/170/246316708_l.jpg" alt="墨硯山南峰" loading="lazy"></p><p>→還有雪山山脈群山(黃老先生請人書寫，自行刻字)<br><img src="https://1013399.github.io/image-3/170/246316712_l.jpg" alt="墨硯山南峰 雪山山脈群山" loading="lazy"></p><p>→續行，前副總統李元簇先生曾坐在此樹根上休息<br><img src="https://1013399.github.io/image-3/170/246316720_l.jpg" alt="墨硯山南峰 樹根" loading="lazy"></p><p>→與黃老先生告別後，續行寬大土路下山<br><img src="https://1013399.github.io/image-3/170/246316735_l.jpg" alt="墨硯古道" loading="lazy"></p><p>→接上水泥產道<br><img src="https://1013399.github.io/image-3/170/246316743_l.jpg" alt="墨硯古道" loading="lazy"></p><p>→經過此民宅，五條惡犬狂吠，不予理會，慢慢通過<br><img src="https://1013399.github.io/image-3/170/246316751_l.jpg" alt="墨硯古道" loading="lazy"></p><p>→墨硯古道出口<br><img src="https://1013399.github.io/image-3/170/246316758_l.jpg" alt="墨硯古道出口" loading="lazy"></p><p>→叉路，取左<br><img src="https://1013399.github.io/image-3/170/246316767_l.jpg" alt="墨硯古道" loading="lazy"></p><p>→民宅59號，取左彎<br><img src="https://1013399.github.io/image-3/170/246316775_l.jpg" alt="墨硯古道" loading="lazy"></p><p>→民宅左側有座古老土地公廟<br><img src="https://1013399.github.io/image-3/170/246316783_l.jpg" alt="墨硯古道 古老土地公廟" loading="lazy"></p><p>→民宅自設環山步道，女主人說歡迎大家來走走<br><img src="https://1013399.github.io/image-3/170/246316792_l.jpg" alt="墨硯古道" loading="lazy"></p><p>→叉路，先取右探訪百年「貓閣土地公廟」<br><img src="https://1013399.github.io/image-3/170/246316816_l.jpg" alt="貓閣土地公廟" loading="lazy"></p><p>→叉路口「原住民頭目遺址」石刻，清乾隆時，道卡斯族的貓閣社移居到此<br><img src="https://1013399.github.io/image-3/170/246316808_l.jpg" alt="原住民頭目遺址 道卡斯族的貓閣社移居到此" loading="lazy"></p><p>→貓閣土地公廟，道光三十年建(1850年)<br><img src="https://1013399.github.io/image-3/170/246316799_l.jpg" alt="貓閣土地公廟" loading="lazy"></p><p>→續行產道，看見對面的「曲洞宮」了<br><img src="https://1013399.github.io/image-3/170/246316822_l.jpg" alt="墨硯山北峰、墨硯山南峰" loading="lazy"></p><p>→接上車道，右往曲洞宮，取左回登山口<br><img src="https://1013399.github.io/image-3/170/246316831_l.jpg" alt="墨硯山北峰、墨硯山南峰" loading="lazy"></p><p>→回到登山口，時間尚早，再往「北隘勇古道」走走</p><h2 id="走走資訊" tabindex="-1"><a class="header-anchor" href="#走走資訊" aria-hidden="true">#</a> 走走資訊</h2><div class="hint-container info"><p class="hint-container-title">相關信息</p><p>走走時間：約1.5小時(含休息、聊天)</p><p>高低落差：約90公尺</p></div><h2 id="參考地圖" tabindex="-1"><a class="header-anchor" href="#參考地圖" aria-hidden="true">#</a> 參考地圖</h2><p>取自奇摩地圖自行描繪</p><figure><img src="https://1013399.github.io/image-3/170/246316887_l.jpg" alt="墨硯山北峰、墨硯山南峰" tabindex="0" loading="lazy"><figcaption>墨硯山北峰、墨硯山南峰</figcaption></figure>',44);function m(n,b){return g(),l("div",null,[s,h,p(" more "),e])}const _=t(r,[["render",m],["__file","post-170-2013-01-08.html.vue"]]);export{_ as default};
