import{_ as o}from"./plugin-vue_export-helper-c27b6911.js";import{r as l,o as s,c as r,a as h,b as i,f as t,d as a,w as e,e as n}from"./app-a9847bbb.js";const m={},b=i("p",null,[i("strong",null,"桃源谷步道"),t("，位於宜蘭縣頭城鎮，轄屬東北角海岸風景特定區，為一片斜坡式山頂大草原，沿途設有多處涼亭和觀景台，依山傍海，風景十分迷人，稜線可俯瞰蜿蜒的海岸線，眺望浩瀚的太平洋及龜山島等美景，令人心曠神怡。")],-1),c=i("p",null,"桃源谷-草嶺線步道，係串聯草嶺古道與桃源谷兩景點之間的步道，步道全長約4.5公里，其中灣坑頭山標高617公尺為本區最高峰，步道沿線設有觀景涼亭、平台，且沿途均可俯瞰龜山島、福隆金黃色沙岸、大里與大溪間海蝕海岸。",-1),d=i("p",null,"桃源谷-大溪線步道，係串聯桃源谷與大溪間步道，步道全長約5公里，其中蕃薯寮山標高456公尺，該步道目前尚為自然林間小徑，沿途綠蔭蒼蒼，蟲鳴鳥叫，是從事森林浴的最佳選擇。(參考資料：步道解說牌)",-1),_=i("strong",null,"草嶺線",-1),u=i("strong",null,"石觀音線",-1),y=i("strong",null,"大溪線",-1),j=i("strong",null,"內寮線",-1),z=n('<p>今日為今年入冬以來最強一波強烈大陸冷氣團，位於迎風面的東北角感受更加強烈，尤其到達埡口時，風勢強勁，幾乎站不住腳，加上雨勢未歇，每個人都走得狼狽不堪，也算是難得的回憶。</p><p>走走日期： 2012/12/23</p><h2 id="行車路線" tabindex="-1"><a class="header-anchor" href="#行車路線" aria-hidden="true">#</a> 行車路線</h2><p>國道3號轉國道5號下頭城交流道，北上接台2線往貢寮方向，至頭城大里國小側門下車。</p><h2 id="走走路線" tabindex="-1"><a class="header-anchor" href="#走走路線" aria-hidden="true">#</a> 走走路線</h2><p>→大里國小側門，循柏油路往上行<br><img src="https://1013399.github.io/image-3/173/244894160_l.jpg" alt="桃源谷步道(草嶺線、大溪線)" loading="lazy"></p><p>→路旁設有草嶺古道路線圖<br><img src="https://1013399.github.io/image-3/173/244894172_l.jpg" alt="草嶺古道路線圖" loading="lazy"></p><p>→叉路，左往大里天公廟，右往草嶺古道<br><img src="https://1013399.github.io/image-3/173/244894175_l.jpg" alt="桃源谷步道(草嶺線、大溪線)" loading="lazy"></p><p>→先取左往天公廟上廁所，兩側有許多石碑石柱<br><img src="https://1013399.github.io/image-3/173/244894182_l.jpg" alt="桃源谷步道(草嶺線、大溪線)" loading="lazy"></p><p>→大里天公廟<br><img src="https://1013399.github.io/image-3/173/244894186_l.jpg" alt="大里天公廟" loading="lazy"></p><p>→上完廁所回往草嶺古道行<br><img src="https://1013399.github.io/image-3/173/244894189_l.jpg" alt="草嶺古道行" loading="lazy"></p><p>→叉路，左可回大里天公廟，續直行<br><img src="https://1013399.github.io/image-3/173/244894193_l.jpg" alt="桃源谷步道(草嶺線、大溪線)" loading="lazy"></p><p>→叉路，左為草嶺古道入口，取左取直皆可，續直行<br><img src="https://1013399.github.io/image-3/173/244894197_l.jpg" alt="桃源谷步道(草嶺線、大溪線)" loading="lazy"></p><p>→石階多處穿越道路，取右改走階梯<br><img src="https://1013399.github.io/image-3/173/244894200_l.jpg" alt="桃源谷步道(草嶺線、大溪線)" loading="lazy"></p><p>→叉路，續走階梯，因此錯過了護管所<br><img src="https://1013399.github.io/image-3/173/244894205_l.jpg" alt="桃源谷步道(草嶺線、大溪線)" loading="lazy"></p><p>→觀景涼亭，可俯瞰大里地區<br><img src="https://1013399.github.io/image-3/173/244894208_l.jpg" alt="桃源谷步道(草嶺線、大溪線)" loading="lazy"></p><p>→客棧遺址(盧宅)，設有解說牌<br><img src="https://1013399.github.io/image-3/173/244894212_l.jpg" alt="桃源谷步道(草嶺線、大溪線) 客棧遺址(盧宅)" loading="lazy"></p><p>→進入較原始的古道<br><img src="https://1013399.github.io/image-3/173/244894218_l.jpg" alt="桃源谷步道(草嶺線、大溪線)" loading="lazy"></p><p>→抵達草嶺古道埡口，風勢強勁<br><img src="https://1013399.github.io/image-3/173/244894220_l.jpg" alt="桃源谷步道(草嶺線、大溪線)" loading="lazy"></p><p>→叉路，直行為草嶺古道往虎字碑、貢寮，取左往桃源谷(草嶺線步道)<br><img src="https://1013399.github.io/image-3/173/244894226_l.jpg" alt="桃源谷步道(草嶺線、大溪線)" loading="lazy"></p><p>→埡口觀景涼亭，天氣晴朗時展望極佳，但今日風雨交加又寒冷，不休息續行<br><img src="https://1013399.github.io/image-3/173/244894230_l.jpg" alt="桃源谷步道(草嶺線、大溪線)" loading="lazy"></p><p>→第一處涼亭<br><img src="https://1013399.github.io/image-3/173/244894234_l.jpg" alt="桃源谷步道(草嶺線、大溪線)" loading="lazy"></p><p>→觀景平台，設有導覽牌<br><img src="https://1013399.github.io/image-3/173/244894239_l.jpg" alt="桃源谷步道(草嶺線、大溪線)" loading="lazy"></p><p>→第二處涼亭，位於草嶺線步道里程1K處<br><img src="https://1013399.github.io/image-3/173/244894241_l.jpg" alt="桃源谷步道(草嶺線、大溪線)" loading="lazy"></p><p>→沿途多處設有擋牛石<br><img src="https://1013399.github.io/image-3/173/244894244_l.jpg" alt="桃源谷步道(草嶺線、大溪線)" loading="lazy"></p><p>→抵達草嶺山，海拔572公尺，山頂為小平台<br><img src="https://1013399.github.io/image-3/173/244894247_l.jpg" alt="草嶺山山頂小平台" loading="lazy"></p><p>→草嶺山，「站在高崗上」解說牌及山字水泥柱，續行<br><img src="https://1013399.github.io/image-3/173/244894253_l.jpg" alt="草嶺山，「站在高崗上」解說牌及山字水泥柱" loading="lazy"></p><p>→第三處涼亭，位於里程2K附近<br><img src="https://1013399.github.io/image-3/173/244894259_l.jpg" alt="桃源谷步道(草嶺線、大溪線)" loading="lazy"></p><p>→叉路，左下山徑往大里簡山，此處已距離灣坑頭山頂不遠<br><img src="https://1013399.github.io/image-3/173/244894264_l.jpg" alt="桃源谷步道(草嶺線、大溪線)" loading="lazy"></p><p>→灣坑頭山，海拔616公尺，二等三角點No.1181，名列台灣小百岳No.082<br><img src="https://1013399.github.io/image-3/173/244894270_l.jpg" alt="灣坑頭山，海拔616公尺，二等三角點No.1181，名列台灣小百岳No.082" loading="lazy"></p><p>→續行，步道開始明顯往下走<br><img src="https://1013399.github.io/image-3/173/244894274_l.jpg" alt="桃源谷步道(草嶺線、大溪線)" loading="lazy"></p><p>→叉路，左往觀景平台，取右往桃源谷<br><img src="https://1013399.github.io/image-3/173/244894278_l.jpg" alt="桃源谷步道(草嶺線、大溪線)" loading="lazy"></p><p>→第四處涼亭，位於石觀音路線叉路前<br><img src="https://1013399.github.io/image-3/173/244894282_l.jpg" alt="桃源谷步道(草嶺線、大溪線)" loading="lazy"></p><p>→石觀音線叉路，左下往石觀音寺，右側有小停車場，下經吉林產道可往貢寮，在此午餐<br><img src="https://1013399.github.io/image-3/173/244894288_l.jpg" alt="桃源谷步道(草嶺線、大溪線)" loading="lazy"></p><p>→餐畢取直行往桃源谷，路旁有顆山字水泥樁<br><img src="https://1013399.github.io/image-3/173/244894292_l.jpg" alt="桃源谷步道(草嶺線、大溪線)" loading="lazy"></p><p>→抵達桃源谷(福德山)，海拔542公尺，山頂渾圓，中間有顆雙溪水源水泥樁No.05<br><img src="https://1013399.github.io/image-3/173/244894295_l.jpg" alt="桃源谷步道(草嶺線、大溪線)" loading="lazy"></p><p>→叉路，右可往蕭家莊(內寮線)，續直行<br><img src="https://1013399.github.io/image-3/173/244894302_l.jpg" alt="桃源谷步道(草嶺線、大溪線)" loading="lazy"></p><p>→叉路，右也可往蕭家莊(內寮線)，直行進入大溪線步道<br><img src="https://1013399.github.io/image-3/173/244894312_l.jpg" alt="桃源谷步道(草嶺線、大溪線)" loading="lazy"></p><p>→大溪線第一處涼亭，設有「單面山」解說牌<br><img src="https://1013399.github.io/image-3/173/244894315_l.jpg" alt="單面山解說牌" loading="lazy"></p><p>→濃霧漸散、雨勢稍歇，漸漸出現視野<br><img src="https://1013399.github.io/image-3/173/244894319_l.jpg" alt="桃源谷步道(草嶺線、大溪線)" loading="lazy"></p><p>→往回望單面山解說牌涼亭<br><img src="https://1013399.github.io/image-3/173/244894326_l.jpg" alt="桃源谷步道(草嶺線、大溪線)" loading="lazy"></p><p>→觀景平台，往右彎續行步道<br><img src="https://1013399.github.io/image-3/173/244894332_l.jpg" alt="桃源谷步道(草嶺線、大溪線)" loading="lazy"></p><p>→土地公廟叉路，位於大溪線步道里程4.1K處<br><img src="https://1013399.github.io/image-3/173/244894338_l.jpg" alt="桃源谷步道(草嶺線、大溪線)" loading="lazy"></p><p>→叉路口設有桃源谷步道草嶺線及大溪線解說牌<br><img src="https://1013399.github.io/image-3/173/244894358_l.jpg" alt="桃源谷步道(草嶺線、大溪線)" loading="lazy"></p><p>→先取右過擋牛石往土地公廟參觀<br><img src="https://1013399.github.io/image-3/173/244894345_l.jpg" alt="桃源谷步道(草嶺線、大溪線) 土地公廟" loading="lazy"></p><p>→土地公廟對面有路可往鹿窟尾尖<br><img src="https://1013399.github.io/image-3/173/244894351_l.jpg" alt="桃源谷步道(草嶺線、大溪線) 土地公廟" loading="lazy"></p><p>→回叉路續往下行<br><img src="https://1013399.github.io/image-3/173/244894363_l.jpg" alt="桃源谷步道(草嶺線、大溪線)" loading="lazy"></p><p>→公廁，位於步道里程3.6K處<br><img src="https://1013399.github.io/image-3/173/244894371_l.jpg" alt="桃源谷步道(草嶺線、大溪線) 公廁" loading="lazy"></p><p>→叉路，位於步道3.4K處，左側泥土山徑應為繞腰路，取右上往蕃薯寮山<br><img src="https://1013399.github.io/image-3/173/244894375_l.jpg" alt="桃源谷步道(草嶺線、大溪線)" loading="lazy"></p><p>→一路陡上<br><img src="https://1013399.github.io/image-3/173/244894382_l.jpg" alt="桃源谷步道(草嶺線、大溪線)" loading="lazy"></p><p>→蕃薯寮山，海拔456公尺，三等三角點No.1120<br><img src="https://1013399.github.io/image-3/173/244894387_l.jpg" alt="蕃薯寮山，海拔456公尺，三等三角點No.1120" loading="lazy"></p><p>→俯瞰大溪仁澤社區，此社區為龜山島移民處，旁有解說牌<br><img src="https://1013399.github.io/image-3/173/244894393_l.jpg" alt="大溪仁澤社區，此社區為龜山島移民處" loading="lazy"></p><p>→續行，一路陡下<br><img src="https://1013399.github.io/image-3/173/244894401_l.jpg" alt="桃源谷步道(草嶺線、大溪線)" loading="lazy"></p><p>→叉路，位於步道約2K處，左側為繞腰路古道，取右續行步道<br><img src="https://1013399.github.io/image-3/173/244894406_l.jpg" alt="桃源谷步道(草嶺線、大溪線)" loading="lazy"></p><p>→第二處涼亭，視野不錯<br><img src="https://1013399.github.io/image-3/173/244894415_l.jpg" alt="桃源谷步道(草嶺線、大溪線)" loading="lazy"></p><p>→俯瞰大溪漁港<br><img src="https://1013399.github.io/image-3/173/244894420_l.jpg" alt="桃源谷步道(草嶺線、大溪線) 俯瞰大溪漁港" loading="lazy"></p><p>→續行，繼續陡下<br><img src="https://1013399.github.io/image-3/173/244894427_l.jpg" alt="桃源谷步道(草嶺線、大溪線)" loading="lazy"></p><p>→拱橋叉路，步道0.2K處，直行接台2線可往大溪漁港，取右過拱橋往大溪河濱公園<br><img src="https://1013399.github.io/image-3/173/244894441_l.jpg" alt="桃源谷步道(草嶺線、大溪線)" loading="lazy"></p><p>→叉路口有顆造型特殊的樹<br><img src="https://1013399.github.io/image-3/173/244894447_l.jpg" alt="桃源谷步道(草嶺線、大溪線)" loading="lazy"></p><p>→到達大溪河濱公園，上車<br><img src="https://1013399.github.io/image-3/173/244894453_l.jpg" alt="到達大溪河濱公園" loading="lazy"></p><h2 id="走走資訊" tabindex="-1"><a class="header-anchor" href="#走走資訊" aria-hidden="true">#</a> 走走資訊</h2><div class="hint-container info"><p class="hint-container-title">相關信息</p><p>走走時間：約6.5小時(含休息、午餐)</p><p>高低落差：(總合計約700公尺)</p><ul><li>大里國小              →草嶺古道埡口          約上升320公尺</li><li>草嶺古道埡口          →草嶺山                約上升230公尺</li><li>草嶺山                →灣坑頭山              約上升40公尺</li><li>灣坑頭山              →桃源谷                約下降70公尺</li><li>桃源谷                →大溪線步道終點(5K處)  約下降60公尺</li><li>大溪線步道終點(5K處)  →觀景平台(4.1K處)      約上升50公尺</li><li>觀景平台(4.1K處)      →大溪線步道3.4K處      約下降130公尺</li><li>大溪線步道3.4K處      →蕃薯寮山              約上升50公尺</li><li>蕃薯寮山              →大溪河濱公園          約下降440公尺</li></ul></div><h2 id="參考地圖" tabindex="-1"><a class="header-anchor" href="#參考地圖" aria-hidden="true">#</a> 參考地圖</h2>',63),f={href:"http://www.yougoipay.com/kenny/w1119/index.htm",target:"_blank",rel:"noopener noreferrer"},K=i("br",null,null,-1),x=i("img",{src:"https://1013399.github.io/image-3/173/244894636_l.jpg",alt:"桃源谷步道(草嶺線、大溪線) 參考地圖",loading:"lazy"},null,-1);function N(k,w){const g=l("RouterLink"),p=l("ExternalLinkIcon");return s(),r("div",null,[b,c,h(" more "),d,i("p",null,[t("桃源谷步道主要有"),_,t("、"),a(g,{to:"/posts/post-224-2011-12-27.html"},{default:e(()=>[u]),_:1}),t("、"),y,t("及"),j,t("四條路線，這次參加新豐鄉鄉野健行隊活動，由大里天公廟先走草嶺古道南段至埡口，再走桃源谷草嶺線步道，沿途經過草嶺山、灣坑頭山後，抵達桃源谷再接大溪線步道，經過蕃薯寮山下至大溪河濱公園，全程約12.3公里。")]),z,i("p",null,[t("原圖連結→"),i("a",f,[t("偶而獨步山林間(蕭郎)"),a(p)]),K,x])])}const L=o(m,[["render",N],["__file","post-173-2012-12-25.html.vue"]]);export{L as default};
