import{_ as g}from"./plugin-vue_export-helper-c27b6911.js";import{r as p,o as e,c as o,b as t,f as i,d as l,a as r,e as n}from"./app-6d4d6805.js";const s={},h=t("p",null,[t("strong",null,"大棟山"),i("，海拔1241公尺，位於台南市白河區關仔嶺，與嘉義縣大埔鄉交界，為台南第一高峰，山頂有一等三角點、一等衛星控制點、自動雨量站、觀景平台與雙層涼亭，涼亭上層有森林火災觀測儀，該山名列台灣小百岳No.063。")],-1),d={href:"http://gohiking.myweb.hinet.net/h7/TNAdadong.htm",target:"_blank",rel:"noopener noreferrer"},c=t("p",null,[t("strong",null,"大棟山步道"),i("，全長約3Km，沿途為低海拔闊葉林，溫暖潮濕的氣候使植物高大茂盛。一路可遠眺白河、鹿寮水庫及嘉義縣仁義潭、蘭潭水庫...等，山頂視野廣闊，關仔嶺溫泉區及廣大的嘉南平原一覽無遺。")],-1),m={href:"http://recreation.forest.gov.tw/RT/RT_2_1.aspx?TR_ID=105",target:"_blank",rel:"noopener noreferrer"},b=n('<p>這次利用年度剩餘休假，邀同事計畫以兩天遠征台南附近五座小百岳(Day 1：No.63大棟山、No.64崁頭山、No.67竹子尖山；Day 2：No.65三腳南山、No.66西阿里關山)，順利完成第一天行程後，夜宿跳跳農場(兩人房$1680，老闆給我們住4人房小木屋)，隔天06:00起床，竟下起大雨，只好聽從農場老闆建議，無奈地放棄了第二天行程，用過早餐後直接踏上歸途，也許是老天爺不希望我們太累吧！</p><p>大棟山為台南地區熱門山岳，假日登山人潮多，一般山友停車於仙祖廟起登，今日非假日，可直接開到土地公廟(大棟山步道起點)停車，少走20~30分鐘路程(距離約2.1Km、落差約130M)，來回約3小時完成。</p><p>走走日期：2012/12/04</p><h2 id="行車路線" tabindex="-1"><a class="header-anchor" href="#行車路線" aria-hidden="true">#</a> 行車路線</h2><p>國道3號下白河交流道，往關仔嶺方向行駛(172縣道35K處)，於172縣道43K處右轉過紅葉隧道接175縣道，經關仔嶺溫泉區後在175縣道1.5K處左轉產道往大棟山，經仙草國小關嶺分校、仙祖廟續行至土地公廟(休閒小棧)前空地停車(假日人潮多，建議停車於仙祖廟，不宜再往上開)。</p><h2 id="走走路線" tabindex="-1"><a class="header-anchor" href="#走走路線" aria-hidden="true">#</a> 走走路線</h2><p>→土地公廟(休閒小棧)，設有涼亭及廁所<br><img src="https://1013399.github.io/image-3/176/243055135_l.jpg" alt="大棟山 土地公廟" loading="lazy"></p><p>→路旁設有附近路線導覽圖<br><img src="https://1013399.github.io/image-3/176/243055145_l.jpg" alt="大棟山 路線導覽圖" loading="lazy"></p><p>→叉路，左為仙祖廟上來的路，取右上<br><img src="https://1013399.github.io/image-3/176/243055147_l.jpg" alt="大棟山" loading="lazy"></p><p>→此叉路口即為大棟山步道起點<br><img src="https://1013399.github.io/image-3/176/243055148_l.jpg" alt="大棟山步道起點" loading="lazy"></p><p>→續行約10公尺為塩粿山休息站，有攤販賣農產品<br><img src="https://1013399.github.io/image-3/176/243055151_l.jpg" alt="大棟山 塩粿山休息站" loading="lazy"></p><p>→仰望大棟山<br><img src="https://1013399.github.io/image-3/176/243055152_l.jpg" alt="大棟山" loading="lazy"></p><p>→叉路，左往雞籠山(也可往大棟山)，取直往大棟山<br><img src="https://1013399.github.io/image-3/176/243055153_l.jpg" alt="大棟山" loading="lazy"></p><p>→叉路，直行可往福安宮，取左上往大棟山<br><img src="https://1013399.github.io/image-3/176/243055158_l.jpg" alt="大棟山" loading="lazy"></p><p>→水泥路結束接土石路<br><img src="https://1013399.github.io/image-3/176/243055160_l.jpg" alt="大棟山" loading="lazy"></p><p>→叉路，旁有木椅，左為雞籠山叉路過來的路，取右往大棟山<br><img src="https://1013399.github.io/image-3/176/243055162_l.jpg" alt="大棟山" loading="lazy"></p><p>→路旁有許多咬人貓<br><img src="https://1013399.github.io/image-3/176/243055165_l.jpg" alt="大棟山" loading="lazy"></p><p>→叉路，左往雞籠山，續直行<br><img src="https://1013399.github.io/image-3/176/243055166_l.jpg" alt="大棟山" loading="lazy"></p><p>→大棟山步道1K里程木樁<br><img src="https://1013399.github.io/image-3/176/243055168_l.jpg" alt="大棟山" loading="lazy"></p><p>→今日有時濃霧，有時艷陽高照<br><img src="https://1013399.github.io/image-3/176/243055170_l.jpg" alt="大棟山" loading="lazy"></p><p>→觀景平台，設有解說牌<br><img src="https://1013399.github.io/image-3/176/243055173_l.jpg" alt="大棟山" loading="lazy"></p><p>→仰望大棟山<br><img src="https://1013399.github.io/image-3/176/243055175_l.jpg" alt="大棟山" loading="lazy"></p><p>→俯瞰關子嶺地區，休息後續行<br><img src="https://1013399.github.io/image-3/176/243055178_l.jpg" alt="大棟山 俯瞰關子嶺地區" loading="lazy"></p><p>→路右一顆巨石<br><img src="https://1013399.github.io/image-3/176/243055182_l.jpg" alt="大棟山" loading="lazy"></p><p>→巨石對面(路左)即是橫石亭<br><img src="https://1013399.github.io/image-3/176/243055183_l.jpg" alt="大棟山 橫石亭" loading="lazy"></p><p>→不休息續行土石路<br><img src="https://1013399.github.io/image-3/176/243055189_l.jpg" alt="大棟山" loading="lazy"></p><p>→大棟山步道2K里程木樁<br><img src="https://1013399.github.io/image-3/176/243055190_l.jpg" alt="大棟山步道2K里程木樁" loading="lazy"></p><p>→土石路有些崎嶇難行<br><img src="https://1013399.github.io/image-3/176/243055191_l.jpg" alt="大棟山" loading="lazy"></p><p>→叉路，直行往大棟山，右往曉風亭<br><img src="https://1013399.github.io/image-3/176/243055196_l.jpg" alt="大棟山" loading="lazy"></p><p>→先取右木棧道往曉風亭，但設有施工中警告標示，退回續行<br><img src="https://1013399.github.io/image-3/176/243055200_l.jpg" alt="大棟山" loading="lazy"></p><p>→叉路，取右取直皆可，取直行枕木階梯<br><img src="https://1013399.github.io/image-3/176/243055203_l.jpg" alt="大棟山" loading="lazy"></p><p>→大棟山鞍部涼亭，為一處大型休憩廣場<br><img src="https://1013399.github.io/image-3/176/243055206_l.jpg" alt="大棟山" loading="lazy"></p><p>→不休息，續行陡上枕木階梯<br><img src="https://1013399.github.io/image-3/176/243055208_l.jpg" alt="大棟山" loading="lazy"></p><p>→抵達大棟山山頂<br><img src="https://1013399.github.io/image-3/176/243055212_l.jpg" alt="大棟山山頂" loading="lazy"></p><p>→此處也是大棟山步道3K處<br><img src="https://1013399.github.io/image-3/176/243055218_l.jpg" alt="大棟山步道3K處" loading="lazy"></p><p>→山頂有一等三角點、一等衛星控制點、自動雨量站與雙層涼亭<br><img src="https://1013399.github.io/image-3/176/243055215_l.jpg" alt="大棟山山頂 一等三角點、一等衛星控制點、自動雨量站與雙層涼亭" loading="lazy"></p><p>→續行可往九龍山，休息20分鐘後原路折返<br> →回到土地公廟停車處，於涼亭享用便當後，前往孚佑宮(崁頭山登山口)</p><h2 id="走走資訊" tabindex="-1"><a class="header-anchor" href="#走走資訊" aria-hidden="true">#</a> 走走資訊</h2><div class="hint-container info"><p class="hint-container-title">相關信息</p><p>走走時間：約3小時(含休息)</p><p>高低落差：約610公尺</p></div><h2 id="交通路線-參考地圖" tabindex="-1"><a class="header-anchor" href="#交通路線-參考地圖" aria-hidden="true">#</a> 交通路線 參考地圖</h2>',40),_={href:"http://tour.tainan.gov.tw/view.aspx?sn=258",target:"_blank",rel:"noopener noreferrer"},u=t("br",null,null,-1),y=t("img",{src:"https://1013399.github.io/image-3/176/243055264_l.jpg",alt:"大棟山 交通路線 參考地圖",loading:"lazy"},null,-1),z=t("h2",{id:"登山路線-參考地圖",tabindex:"-1"},[t("a",{class:"header-anchor",href:"#登山路線-參考地圖","aria-hidden":"true"},"#"),i(" 登山路線 參考地圖")],-1),j={href:"http://blog.xuite.net/laoweiz/blog/17177360",target:"_blank",rel:"noopener noreferrer"},f=t("br",null,null,-1),x=t("img",{src:"https://1013399.github.io/image-3/176/243055263_l.jpg",alt:"大棟山 登山路線 參考地圖",loading:"lazy"},null,-1);function N(K,k){const a=p("ExternalLinkIcon");return e(),o("div",null,[h,t("p",null,[i("(參考資料："),t("a",d,[i("HiSpring 優遊山林溪"),l(a)]),i(")")]),c,t("p",null,[i("(參考資料："),t("a",m,[i("台灣山林悠遊網"),l(a)]),i(")")]),r(" more "),b,t("p",null,[i("原圖連結→"),t("a",_,[i("台南市政府觀光旅遊局"),l(a)]),u,y]),z,t("p",null,[i("原圖連結→"),t("a",j,[i("老尉子的部落格"),l(a)]),f,x])])}const T=g(s,[["render",N],["__file","post-176-2012-12-07.html.vue"]]);export{T as default};
