import{_ as l}from"./plugin-vue_export-helper-c27b6911.js";import{r as p,o as r,c as o,a as e,b as i,f as t,d as g,e as s}from"./app-7f62a6ff.js";const h={},n=i("p",null,[i("strong",null,"林口頂福巖森林步道"),t("，位於新北市林口區頂福村，是一條由林口登山健行協會開闢而成的森林步道，全程3450公尺，行走於海拔200至250公尺的丘陵地，上下起伏略陡，但落差不超過50公尺，沿途生態豐富多樣，還有數個休憩點及頗富趣味的步橋，是條林相豐富且兼具有趣味性的休閒森林步道，值得來此一遊。")],-1),m={href:"http://emmm.tw/L3_content.php?L3_id=37510",target:"_blank",rel:"noopener noreferrer"},b=s('<p><strong>雪花步道</strong>，為林口森林步道擴充支線，顧名思義沿途有不少的油桐樹，每年四、五月油桐花開，雪花片片，山頂休憩平台設有茅草涼亭(富貴居)，涼亭前視野佳，繞行一圈15~20分鐘，可接回林口森林步道，算是林口森林步道的加碼行程。</p><p>走走日期： 2012/10/21</p><h2 id="行車路線" tabindex="-1"><a class="header-anchor" href="#行車路線" aria-hidden="true">#</a> 行車路線</h2><p>國道1號北上，出林口交流道B(南端)出口，左轉文化北路往林口方向，在接上中山路前數公尺左轉進入鄉間道路(路口有「頂福巖顯應祖師廟」指示牌)，約2km後抵達頂福巖停車場停車。</p><h2 id="走走路線" tabindex="-1"><a class="header-anchor" href="#走走路線" aria-hidden="true">#</a> 走走路線</h2><p>→「頂福巖顯應祖師廟」停車場<br><img src="https://1013399.github.io/image-3/179/237640261_l.jpg" alt="頂福巖顯應祖師廟 停車場" loading="lazy"></p><p>→上階梯往祖師廟參觀<br><img src="https://1013399.github.io/image-3/179/237640276_l.jpg" alt="頂福巖顯應祖師廟" loading="lazy"></p><p>→頂福巖顯應祖師廟<br><img src="https://1013399.github.io/image-3/179/237640291_l.jpg" alt="頂福巖顯應祖師廟" loading="lazy"></p><p>→廟旁設有步道路線圖<br><img src="https://1013399.github.io/image-3/179/237640304_l.jpg" alt="頂福巖森林步道路線圖" loading="lazy"></p><p>→往廟的右方前行<br><img src="https://1013399.github.io/image-3/179/237640308_l.jpg" alt="頂福巖顯應祖師廟" loading="lazy"></p><p>→林口森林步道口，左為入口，右為出口(左去右回)<br><img src="https://1013399.github.io/image-3/179/237640317_l.jpg" alt="頂福巖森林步道、雪花步道" loading="lazy"></p><p>→取左進入廢輪胎階梯陡上<br><img src="https://1013399.github.io/image-3/179/237640320_l.jpg" alt="頂福巖森林步道、雪花步道" loading="lazy"></p><p>→上完階梯，右彎進入平緩步道<br><img src="https://1013399.github.io/image-3/179/237640324_l.jpg" alt="頂福巖森林步道、雪花步道" loading="lazy"></p><p>→叉路，左上有鐵皮屋，取右進入茶園<br><img src="https://1013399.github.io/image-3/179/237640328_l.jpg" alt="頂福巖森林步道、雪花步道" loading="lazy"></p><p>→路旁「開發林088」水泥基石<br><img src="https://1013399.github.io/image-3/179/237640334_l.jpg" alt="開發林088水泥基石" loading="lazy"></p><p>→進入尤加利林<br><img src="https://1013399.github.io/image-3/179/237640343_l.jpg" alt="尤加利林" loading="lazy"></p><p>→虎山<br><img src="https://1013399.github.io/image-3/179/237640349_l.jpg" alt="虎山" loading="lazy"></p><p>→叉路，左右皆可、殊途同歸<br><img src="https://1013399.github.io/image-3/179/237640359_l.jpg" alt="頂福巖森林步道、雪花步道" loading="lazy"></p><p>→右路線也有一支虎山標示牌<br><img src="https://1013399.github.io/image-3/179/237640364_l.jpg" alt="頂福巖森林步道、雪花步道" loading="lazy"></p><p>→左右兩線會合後，開始陡下<br><img src="https://1013399.github.io/image-3/179/237640370_l.jpg" alt="頂福巖森林步道、雪花步道" loading="lazy"></p><p>→叉路，右往「神木群」，取左往「泉園」<br><img src="https://1013399.github.io/image-3/179/237640377_l.jpg" alt="頂福巖森林步道、雪花步道" loading="lazy"></p><p>→大板根<br><img src="https://1013399.github.io/image-3/179/237640382_l.jpg" alt="頂福巖森林步道 大板根" loading="lazy"></p><p>→泉園叉路，右往戲水區(若只想走半程可由此回)，取左往「蜜蜂湖」<br><img src="https://1013399.github.io/image-3/179/237640389_l.jpg" alt="頂福巖森林步道、雪花步道" loading="lazy"></p><p>→峇里島風的「泉園休息區」，左側有涼亭，右側有貴妃池及步步高昇拱橋<br><img src="https://1013399.github.io/image-3/179/237640393_l.jpg" alt="頂福巖森林步道、雪花步道 泉園休息區" loading="lazy"></p><p>→休息後，過步步高昇橋，每塊橋板下方裝置彈簧，走起來軟軟的<br><img src="https://1013399.github.io/image-3/179/237640405_l.jpg" alt="頂福巖森林步道、雪花步道 步步高昇橋" loading="lazy"></p><p>→「蜜蜂湖」，為一處山中谷地，未見蜜蜂、未見湖<br><img src="https://1013399.github.io/image-3/179/237640413_l.jpg" alt="頂福巖森林步道、雪花步道 蜜蜂湖" loading="lazy"></p><p>→過了蜜蜂湖後，開始陡上<br><img src="https://1013399.github.io/image-3/179/237640419_l.jpg" alt="頂福巖森林步道、雪花步道" loading="lazy"></p><p>→觀景台<br><img src="https://1013399.github.io/image-3/179/237640424_l.jpg" alt="頂福巖森林步道、雪花步道" loading="lazy"></p><p>→續往前不遠處叉路，左可往「吳家古厝」，取直續行<br><img src="https://1013399.github.io/image-3/179/237640429_l.jpg" alt="頂福巖森林步道、雪花步道" loading="lazy"></p><p>→「中清」民宅區，穿過絲瓜棚<br><img src="https://1013399.github.io/image-3/179/237640431_l.jpg" alt="頂福巖森林步道、雪花步道" loading="lazy"></p><p>→叉路，右不知去向，續直行<br><img src="https://1013399.github.io/image-3/179/237640440_l.jpg" alt="頂福巖森林步道、雪花步道" loading="lazy"></p><p>→直行「滑行橋」過「中清壩」<br><img src="https://1013399.github.io/image-3/179/237640449_l.jpg" alt="頂福巖森林步道、雪花步道" loading="lazy"></p><p>→陡下<br><img src="https://1013399.github.io/image-3/179/237640455_l.jpg" alt="頂福巖森林步道、雪花步道" loading="lazy"></p><p>→再陡上<br><img src="https://1013399.github.io/image-3/179/237640462_l.jpg" alt="頂福巖森林步道、雪花步道" loading="lazy"></p><p>→休憩涼亭，在此休息<br><img src="https://1013399.github.io/image-3/179/237640475_l.jpg" alt="頂福巖森林步道、雪花步道" loading="lazy"></p><p>→休息後，續行往下<br><img src="https://1013399.github.io/image-3/179/237640488_l.jpg" alt="頂福巖森林步道、雪花步道" loading="lazy"></p><p>→「金燦坑」<br><img src="https://1013399.github.io/image-3/179/237640492_l.jpg" alt="頂福巖森林步道、雪花步道" loading="lazy"></p><p>→「天搖地動橋」，橋頭兩端下方裝置彈簧，走起來搖搖晃晃<br><img src="https://1013399.github.io/image-3/179/237640503_l.jpg" alt="頂福巖森林步道、雪花步道" loading="lazy"></p><p>→「雙口溪」，再陡上階梯<br><img src="https://1013399.github.io/image-3/179/237640510_l.jpg" alt="頂福巖森林步道、雪花步道" loading="lazy"></p><p>→叉路，直行步道回祖師廟，取左上往「雪花步道」<br><img src="https://1013399.github.io/image-3/179/237640533_l.jpg" alt="頂福巖森林步道、雪花步道" loading="lazy"></p><p>→雪花步道口<br><img src="https://1013399.github.io/image-3/179/237640538_l.jpg" alt="頂福巖森林步道、雪花步道" loading="lazy"></p><p>→叉路，直行可往桐花區及「一路發」(168階梯)，取右捷徑<br><img src="https://1013399.github.io/image-3/179/237640556_l.jpg" alt="頂福巖森林步道、雪花步道" loading="lazy"></p><p>→空曠休息區，前一叉路在此接回，附近種植許多金針花及其他植物<br><img src="https://1013399.github.io/image-3/179/237640563_l.jpg" alt="頂福巖森林步道、雪花步道" loading="lazy"></p><p>→休息區旁有顆無名水泥基石<br><img src="https://1013399.github.io/image-3/179/237640571_l.jpg" alt="頂福巖森林步道、雪花步道" loading="lazy"></p><p>→往前不遠有座茅草涼亭<br><img src="https://1013399.github.io/image-3/179/237640579_l.jpg" alt="頂福巖森林步道、雪花步道" loading="lazy"></p><p>→涼亭前視野佳，俯瞰興福社區及頂福巖祖師廟<br><img src="https://1013399.github.io/image-3/179/237640596_l.jpg" alt="俯瞰興福社區及頂福巖祖師廟" loading="lazy"></p><p>→此涼亭名曰「富貴居」，涼亭上掛有拓荒者及贊助者芳名祿<br><img src="https://1013399.github.io/image-3/179/237640602_l.jpg" alt="頂福巖森林步道、雪花步道 富貴居" loading="lazy"></p><p>→續行，接回林口森林步道，取左回<br><img src="https://1013399.github.io/image-3/179/237640613_l.jpg" alt="頂福巖森林步道、雪花步道" loading="lazy"></p><p>→急陡下<br><img src="https://1013399.github.io/image-3/179/237640619_l.jpg" alt="頂福巖森林步道、雪花步道" loading="lazy"></p><p>→接上產業道路，取左回<br><img src="https://1013399.github.io/image-3/179/237640623_l.jpg" alt="頂福巖森林步道、雪花步道" loading="lazy"></p><p>→戲水區叉路，往右過橋可往泉園，續行產道<br><img src="https://1013399.github.io/image-3/179/237640627_l.jpg" alt="頂福巖森林步道、雪花步道" loading="lazy"></p><p>→「宛峰大橋」，正在重建中，小心翼翼過橋<br><img src="https://1013399.github.io/image-3/179/237640634_l.jpg" alt="頂福巖森林步道、雪花步道" loading="lazy"></p><p>→過橋後接回步道，此處名為「蝴蝶谷」，取左回<br><img src="https://1013399.github.io/image-3/179/237640640_l.jpg" alt="頂福巖森林步道、雪花步道 蝴蝶谷" loading="lazy"></p><p>→叉路，直行回祖師廟，先取右往神木群<br><img src="https://1013399.github.io/image-3/179/237640661_l.jpg" alt="頂福巖森林步道、雪花步道" loading="lazy"></p><p>→神木群只是樹木較高，並非神木<br><img src="https://1013399.github.io/image-3/179/237640666_l.jpg" alt="頂福巖森林步道、雪花步道" loading="lazy"></p><p>→退回叉路續行，經過隘口<br><img src="https://1013399.github.io/image-3/179/237640671_l.jpg" alt="頂福巖森林步道、雪花步道" loading="lazy"></p><p>→「東倒西歪橋」，走起來也是左右搖晃<br><img src="https://1013399.github.io/image-3/179/237640680_l.jpg" alt="頂福巖森林步道、雪花步道" loading="lazy"></p><p>→「婆娑之洋美麗之島」，台灣造型，稍嫌瘦了點<br><img src="https://1013399.github.io/image-3/179/237640698_l.jpg" alt="頂福巖森林步道、雪花步道" loading="lazy"></p><p>→回到步道口<br><img src="https://1013399.github.io/image-3/179/237640703_l.jpg" alt="頂福巖森林步道、雪花步道" loading="lazy"></p><p>→回到停車場</p><h2 id="走走資訊" tabindex="-1"><a class="header-anchor" href="#走走資訊" aria-hidden="true">#</a> 走走資訊</h2><div class="hint-container info"><p class="hint-container-title">相關信息</p><p>走走時間：約2.5小時(含休息)</p><p>高低落差：約60公尺(不含上下起伏)</p></div><h2 id="參考地圖" tabindex="-1"><a class="header-anchor" href="#參考地圖" aria-hidden="true">#</a> 參考地圖</h2><h3 id="不含雪花步道" tabindex="-1"><a class="header-anchor" href="#不含雪花步道" aria-hidden="true">#</a> 不含雪花步道</h3>',64),d={href:"http://www.tonyhuang39.com/tony0689/tony0689.html",target:"_blank",rel:"noopener noreferrer"},c=i("br",null,null,-1),_=i("img",{src:"https://1013399.github.io/image-3/179/237641478_l.jpg",alt:"頂福巖森林步道 參考地圖",loading:"lazy"},null,-1),u=i("h3",{id:"含雪花步道",tabindex:"-1"},[i("a",{class:"header-anchor",href:"#含雪花步道","aria-hidden":"true"},"#"),t(" 含雪花步道")],-1),y={href:"http://www.keepon.com.tw/DiscussLoad.aspx?code=314B5CF9AEC3A19113F6CAA6F539A6622427129351A1A9B6",target:"_blank",rel:"noopener noreferrer"},j=i("br",null,null,-1),z=i("img",{src:"https://1013399.github.io/image-3/179/237641483_l.jpg",alt:"頂福巖森林步道、雪花步道 參考地圖",loading:"lazy"},null,-1);function f(x,k){const a=p("ExternalLinkIcon");return r(),o("div",null,[n,e(" more "),i("p",null,[t("(資料摘自："),i("a",m,[t("美美美旅遊網"),g(a)]),t(")")]),b,i("p",null,[t("原圖連結→"),i("a",d,[t("Tony的自然人文旅記"),g(a)]),c,_]),u,i("p",null,[t("原圖連結→"),i("a",y,[t("登山補給站-brownlee(小李)"),g(a)]),j,z])])}const B=l(h,[["render",f],["__file","post-179-2012-10-23.html.vue"]]);export{B as default};
