import{_ as l}from"./plugin-vue_export-helper-c27b6911.js";import{r as p,o,c as r,a as s,b as i,e as t,d as g,f as e}from"./app-e90ae243.js";const h={},n=i("p",null,[i("strong",null,"鳳凰山"),t("，海拔1698公尺，位於南投縣鹿谷鄉的溪頭森林遊樂區東北方，山頂竹林圍繞、視野有限，有顆一等三角點。鳳凰山為中部四大名山(鳳凰山、守城大山、水社大山、集集大山)之一，在溪頭四珠(鹿屈山、金柑樹山、鳳凰山、樟空崙山)中排行第三，同時也名列台灣小百岳No.53。")],-1),m=e('<p>鳳凰山有數個登山口，今日選擇較大眾化的路線，由第一停車場登山口起登，出溪頭露營區登山口，沿途穿梭在竹林與杉木林間，林相優美，稜線叉路至鳳凰山頂，上下起伏頗大，走來並不輕鬆。</p><p>走走日期：2012/10/07</p><h2 id="行車路線" tabindex="-1"><a class="header-anchor" href="#行車路線" aria-hidden="true">#</a> 行車路線</h2><p>國道三號下竹山交流道，接集山路二段(台3線)右轉往竹山，再轉151縣道往鹿谷、溪頭方向，於151縣道18.3Km叉路左上(商店街的舊側門)路旁免費停車。</p><h2 id="走走路線" tabindex="-1"><a class="header-anchor" href="#走走路線" aria-hidden="true">#</a> 走走路線</h2><p>→151縣道18.3Km叉路，直行往溪頭、杉林溪，取左上路旁停車<br><img src="https://1013399.github.io/image-3/180/236403121_l.jpg" alt="151縣道18.3Km叉路" loading="lazy"></p><p>→整裝後出發，往商店街的舊側門，至木造大平台進入溪頭第一停車場<br><img src="https://1013399.github.io/image-3/180/236403154_l.jpg" alt="溪頭第一停車場" loading="lazy"></p><p>→平台左右兩側為商店街，左往米緹街，右可往停車場收費大門、溪頭遊樂區<br><img src="https://1013399.github.io/image-3/180/236403166_l.jpg" alt="鳳凰山" loading="lazy"></p><p>→取直行柏油路往停車場最上一排<br><img src="https://1013399.github.io/image-3/180/236403169_l.jpg" alt="鳳凰山" loading="lazy"></p><p>→停車場最上排叉路，左往露營區，取右往溪頭遊樂區<br><img src="https://1013399.github.io/image-3/180/236403172_l.jpg" alt="鳳凰山" loading="lazy"></p><p>→停車格「1-26」旁產業道路，登山口就在「樹番茄」牌子旁<br><img src="https://1013399.github.io/image-3/180/236403175_l.jpg" alt="停車格「1-26」鳳凰山登山口就在「樹番茄」牌子" loading="lazy"></p><p>→於登山口仰望鳳凰山<br><img src="https://1013399.github.io/image-3/180/236403181_l.jpg" alt="鳳凰山" loading="lazy"></p><p>→進入孟宗竹林<br><img src="https://1013399.github.io/image-3/180/236403184_l.jpg" alt="鳳凰山 孟宗竹林" loading="lazy"></p><p>→山徑漸漸趨陡<br><img src="https://1013399.github.io/image-3/180/236403188_l.jpg" alt="鳳凰山" loading="lazy"></p><p>→簡易小木橋<br><img src="https://1013399.github.io/image-3/180/236403193_l.jpg" alt="鳳凰山 小木橋" loading="lazy"></p><p>→進入杉木林<br><img src="https://1013399.github.io/image-3/180/236403206_l.jpg" alt="鳳凰山 杉木林" loading="lazy"></p><p>→叉路，左下往露營區登山口，旁有土地公，在此休息<br><img src="https://1013399.github.io/image-3/180/236403211_l.jpg" alt="鳳凰山 土地公" loading="lazy"></p><p>→休息後，直行石板路往信義方向<br><img src="https://1013399.github.io/image-3/180/236403214_l.jpg" alt="鳳凰山" loading="lazy"></p><p>→崩塌處，拉繩小心通過<br><img src="https://1013399.github.io/image-3/180/236403219_l.jpg" alt="鳳凰山 崩塌處" loading="lazy"></p><p>→展望點俯瞰<br><img src="https://1013399.github.io/image-3/180/236403229_l.jpg" alt="鳳凰山" loading="lazy"></p><p>→水源處，水流量不大<br><img src="https://1013399.github.io/image-3/180/236403234_l.jpg" alt="鳳凰山 水源處" loading="lazy"></p><p>→拉繩急陡上<br><img src="https://1013399.github.io/image-3/180/236403237_l.jpg" alt="鳳凰山" loading="lazy"></p><p>→稜線叉路，有指示牌及小涼亭，直行可往信義，右可往南鳳凰山、溪頭天文台，在此休息<br><img src="https://1013399.github.io/image-3/180/236403239_l.jpg" alt="鳳凰山" loading="lazy"></p><p>→指示牌旁有顆字跡不明的基石<br><img src="https://1013399.github.io/image-3/180/236403242_l.jpg" alt="鳳凰山" loading="lazy"></p><p>→休息後，取左陡上往鳳凰山<br><img src="https://1013399.github.io/image-3/180/236403246_l.jpg" alt="鳳凰山" loading="lazy"></p><p>→拉繩陡上<br><img src="https://1013399.github.io/image-3/180/236403248_l.jpg" alt="鳳凰山" loading="lazy"></p><p>→上至小山頭<br><img src="https://1013399.github.io/image-3/180/236403249_l.jpg" alt="鳳凰山" loading="lazy"></p><p>→緩下後又緩上<br><img src="https://1013399.github.io/image-3/180/236403252_l.jpg" alt="鳳凰山" loading="lazy"></p><p>→路旁有根「台大(國)566」水泥柱<br><img src="https://1013399.github.io/image-3/180/236403260_l.jpg" alt="鳳凰山 「台大(國)566」水泥柱" loading="lazy"></p><p>→第二座小涼亭，再緩下後緩上<br><img src="https://1013399.github.io/image-3/180/236403264_l.jpg" alt="鳳凰山" loading="lazy"></p><p>→大石壁，此處可遠眺玉山，但今日視野不佳<br><img src="https://1013399.github.io/image-3/180/236403267_l.jpg" alt="鳳凰山 大石壁，此處可遠眺玉山" loading="lazy"></p><p>→第三座小涼亭<br><img src="https://1013399.github.io/image-3/180/236403282_l.jpg" alt="鳳凰山" loading="lazy"></p><p>→開始陡下，落差約90公尺<br><img src="https://1013399.github.io/image-3/180/236403285_l.jpg" alt="鳳凰山" loading="lazy"></p><p>→下抵鞍部，旁有廢棄流籠頭<br><img src="https://1013399.github.io/image-3/180/236403287_l.jpg" alt="鳳凰山" loading="lazy"></p><p>→過了鞍部開始陡上<br><img src="https://1013399.github.io/image-3/180/236403291_l.jpg" alt="鳳凰山" loading="lazy"></p><p>→樹中竹，一根竹子長在空心的木頭中<br><img src="https://1013399.github.io/image-3/180/236403292_l.jpg" alt="鳳凰山 樹中竹" loading="lazy"></p><p>→即將抵達三角點<br><img src="https://1013399.github.io/image-3/180/236403299_l.jpg" alt="鳳凰山一等三角點" loading="lazy"></p><p>→鳳凰山山頂，中壢山友已在此泡茶、休息<br><img src="https://1013399.github.io/image-3/180/236403311_l.jpg" alt="鳳凰山山頂" loading="lazy"></p><p>→鳳凰山一等三角點，在此午餐<br><img src="https://1013399.github.io/image-3/180/236403314_l.jpg" alt="鳳凰山一等三角點" loading="lazy"></p><p>→休息後，原路下山，已起濃霧，飄起雨來<br><img src="https://1013399.github.io/image-3/180/236403318_l.jpg" alt="鳳凰山" loading="lazy"></p><p>→回到稜線叉路，取右回<br><img src="https://1013399.github.io/image-3/180/236403326_l.jpg" alt="鳳凰山" loading="lazy"></p><p>→回到露營區叉路，取右改往露營區登山口<br><img src="https://1013399.github.io/image-3/180/236403331_l.jpg" alt="鳳凰山" loading="lazy"></p><p>→一路陡下<br><img src="https://1013399.github.io/image-3/180/236403337_l.jpg" alt="鳳凰山" loading="lazy"></p><p>→接上廢棄土石產道，取左下<br><img src="https://1013399.github.io/image-3/180/236403339_l.jpg" alt="鳳凰山" loading="lazy"></p><p>→由此往右下切，離開土石產道<br><img src="https://1013399.github.io/image-3/180/236403342_l.jpg" alt="鳳凰山" loading="lazy"></p><p>→到達露營區登山口<br><img src="https://1013399.github.io/image-3/180/236403344_l.jpg" alt="鳳凰山 露營區登山口" loading="lazy"></p><p>→露營區登山口位於露營區最高處的C33營地<br><img src="https://1013399.github.io/image-3/180/236403347_l.jpg" alt="鳳凰山 露營區登山口 C33營地" loading="lazy"></p><p>→循柏油路往下行<br><img src="https://1013399.github.io/image-3/180/236403350_l.jpg" alt="鳳凰山" loading="lazy"></p><p>→叉路，右往露營區管理中心，取左回停車場<br><img src="https://1013399.github.io/image-3/180/236403355_l.jpg" alt="鳳凰山" loading="lazy"></p><p>→穿越停車場回到停車處</p><h2 id="走走資訊" tabindex="-1"><a class="header-anchor" href="#走走資訊" aria-hidden="true">#</a> 走走資訊</h2><div class="hint-container info"><p class="hint-container-title">相關信息</p><p>走走時間：約6.5小時(含午餐、休息)</p><p>高低落差：約600公尺(不含上下起伏)</p></div><h2 id="參考地圖" tabindex="-1"><a class="header-anchor" href="#參考地圖" aria-hidden="true">#</a> 參考地圖</h2>',53),b=i("br",null,null,-1),c={href:"http://www.keepon.com.tw/DiscussLoad.aspx?code=314B5CF9AEC3A19113F6CAA6F539A66228A62E9AE60DE5E5",target:"_blank",rel:"noopener noreferrer"},d=i("br",null,null,-1),_=i("img",{src:"https://1013399.github.io/image-3/180/236403416_l.jpg",alt:"鳳凰山 參考地圖",loading:"lazy"},null,-1),u={href:"http://blog.xuite.net/laoweiz/blog/16312730",target:"_blank",rel:"noopener noreferrer"},j=i("br",null,null,-1),z=i("img",{src:"https://1013399.github.io/image-3/180/236403415_l.jpg",alt:"鳳凰山 參考地圖",loading:"lazy"},null,-1);function y(f,x){const a=p("ExternalLinkIcon");return o(),r("div",null,[n,s(" more "),m,i("p",null,[t("(修改過原圖，紫色為去程、桃紅色為回程)"),b,t(" 原圖連結→"),i("a",c,[t("登山補給站-jjlu(阿竹)"),g(a)]),d,_]),i("p",null,[t("原圖連結→"),i("a",u,[t("老尉子的部落格"),g(a)]),j,z])])}const A=l(h,[["render",y],["__file","post-180-2012-10-11.html.vue"]]);export{A as default};
