import{_ as g}from"./plugin-vue_export-helper-c27b6911.js";import{r as l,o as p,c as o,a as r,b as i,f as t,d as s,e}from"./app-a39bec1a.js";const h={},n=i("p",null,[i("strong",null,"大塔山"),t("，海拔2663公尺，位於嘉義縣阿里山森林遊樂區，塔山山系是台灣原住民鄒族人心目中的聖山，山頂曾有陸軍通信排駐守於此，為阿里山山脈中的最高峰，視野極佳，可眺望玉山11群峰與俯瞰阿里山國家森林遊樂區全貌，或欣賞變化萬千的雲瀑、雲海。該山名列台灣小百岳No.59，亦為小百岳最高峰，為小百岳五嶽之中嶽。 (參考網路資料)")],-1),m=i("p",null,[i("strong",null,"塔山步道"),t("，入口位於阿里山森林遊樂區內著名景點姊妹潭之妹潭旁，單程約3.5公里，終點為塔山頂。步道為木棧道及碎石階梯，途經為柳杉林、檜木林及鐵杉林，沿途景觀資源豐富，而塔山頂更是雲起霧湧，起伏於山與樹之間，有如仙境。")],-1),b=e('<p>凌晨4點起床，搭乘4:50森林小火車往祝山觀日出(日出時間：5:53)，來阿里山三次，每次都能欣賞到日出，還滿幸運的，賞完日出，回到商店區用過早餐後，再搭接駁車至阿里山閣飯店，開始今日的塔山之旅。</p><p>走走日期：2012/09/10</p><h2 id="走走路線" tabindex="-1"><a class="header-anchor" href="#走走路線" aria-hidden="true">#</a> 走走路線</h2><p>→於阿里山火車站前遠眺大塔山<br><img src="https://1013399.github.io/image-3/182/233696909_l.jpg" alt="大塔山" loading="lazy"></p><p>→阿里山閣飯店出發，沿鐵道旁步道而行<br><img src="https://1013399.github.io/image-3/182/233696911_l.jpg" alt="塔山步道、大塔山" loading="lazy"></p><p>→叉路，左下往姊妹潭，續直行走眠月線鐵道<br><img src="https://1013399.github.io/image-3/182/233696915_l.jpg" alt="眠月線鐵道" loading="lazy"></p><p>→天橋，為左側姊妹潭旁入口上來之塔山步道<br><img src="https://1013399.github.io/image-3/182/233696917_l.jpg" alt="塔山步道 天橋" loading="lazy"></p><p>→天橋旁設有塔山步道路線圖及解說牌，續行鐵道<br><img src="https://1013399.github.io/image-3/182/233696919_l.jpg" alt="天橋 塔山步道路線圖及解說牌" loading="lazy"></p><p>→步道與鐵道平行而設，步道上下起伏，走鐵道較平緩<br><img src="https://1013399.github.io/image-3/182/233696921_l.jpg" alt="塔山步道、大塔山" loading="lazy"></p><p>→祝山線與眠月線鐵道分岔處(眠月線2.9K處)，取左續行眠月線鐵道<br><img src="https://1013399.github.io/image-3/182/233696926_l.jpg" alt="祝山線與眠月線鐵道分岔處(眠月線2.9K處)" loading="lazy"></p><p>→大塔山登山口，為一木造大平台，由此往右切入塔山步道<br><img src="https://1013399.github.io/image-3/182/233696928_l.jpg" alt="大塔山登山口" loading="lazy"></p><p>→開始一路陡上<br><img src="https://1013399.github.io/image-3/182/233696935_l.jpg" alt="塔山步道、大塔山" loading="lazy"></p><p>→繼續陡上<br><img src="https://1013399.github.io/image-3/182/233696938_l.jpg" alt="塔山步道、大塔山" loading="lazy"></p><p>→阿里山林補No.37基石<br><img src="https://1013399.github.io/image-3/182/233696940_l.jpg" alt="阿里山林補No.37基石" loading="lazy"></p><p>→大石壁，步道稍微趨緩<br><img src="https://1013399.github.io/image-3/182/233696950_l.jpg" alt="塔山步道 大石壁" loading="lazy"></p><p>→塔山步道2.5K木樁<br><img src="https://1013399.github.io/image-3/182/233696953_l.jpg" alt="塔山步道2.5K木樁" loading="lazy"></p><p>→二代木，為超過千年的紅檜<br><img src="https://1013399.github.io/image-3/182/233696961_l.jpg" alt="塔山步道 二代木" loading="lazy"></p><p>→圓形水塔<br><img src="https://1013399.github.io/image-3/182/233696965_l.jpg" alt="塔山步道、大塔山" loading="lazy"></p><p>→進入箭竹林<br><img src="https://1013399.github.io/image-3/182/233696967_l.jpg" alt="塔山步道、大塔山" loading="lazy"></p><p>→塔山步道3.0K木樁<br><img src="https://1013399.github.io/image-3/182/233696970_l.jpg" alt="塔山步道3.0K木樁" loading="lazy"></p><p>→鐵架木板橋<br><img src="https://1013399.github.io/image-3/182/233696977_l.jpg" alt="塔山步道、大塔山" loading="lazy"></p><p>→橋上可俯瞰阿里山森林遊樂區，設有解說牌<br><img src="https://1013399.github.io/image-3/182/233696978_l.jpg" alt="阿里山森林遊樂區" loading="lazy"></p><p>→步道穿梭於玉山箭竹林中(海拔最高的竹子)<br><img src="https://1013399.github.io/image-3/182/233696982_l.jpg" alt="塔山步道 玉山箭竹林" loading="lazy"></p><p>→漂亮的鐵杉<br><img src="https://1013399.github.io/image-3/182/233696986_l.jpg" alt="鐵杉" loading="lazy"></p><p>→像盆景的漂亮鐵杉<br><img src="https://1013399.github.io/image-3/182/233696989_l.jpg" alt="鐵杉" loading="lazy"></p><p>→塔山步道3.5K木樁<br><img src="https://1013399.github.io/image-3/182/233696993_l.jpg" alt="塔山步道3.5K木樁" loading="lazy"></p><p>→繞過廢棄軍營<br><img src="https://1013399.github.io/image-3/182/233696997_l.jpg" alt="塔山步道、大塔山" loading="lazy"></p><p>→最後陡上<br><img src="https://1013399.github.io/image-3/182/233697001_l.jpg" alt="塔山步道、大塔山" loading="lazy"></p><p>→大塔山山頂木造觀景平台<br><img src="https://1013399.github.io/image-3/182/233697002_l.jpg" alt="大塔山山頂木造觀景平台" loading="lazy"></p><p>→大塔山岩石，不能到達<br><img src="https://1013399.github.io/image-3/182/233697006_l.jpg" alt="大塔山岩石" loading="lazy"></p><p>→於山頂觀景平台拍張合照<br><img src="https://1013399.github.io/image-3/182/233697009_l.jpg" alt="塔山步道、大塔山" loading="lazy"></p><p>→往前可眺望玉山群峰，往左下可俯瞰阿里山國家森林遊樂區，但已起霧<br><img src="https://1013399.github.io/image-3/182/233697013_l.jpg" alt="玉山群峰" loading="lazy"></p><p>→大塔山岩石與解說牌<br><img src="https://1013399.github.io/image-3/182/233697015_l.jpg" alt="大塔山岩石與解說牌" loading="lazy"></p><p>→俯瞰樹與石間雲霧<br><img src="https://1013399.github.io/image-3/182/233697016_l.jpg" alt="塔山步道、大塔山" loading="lazy"></p><p>→回到營房有遮蔭處泡茶、休息，休息後原路折返</p><p>→回到木造大平台，不走鐵道，改走塔山步道<br><img src="https://1013399.github.io/image-3/182/233697018_l.jpg" alt="塔山步道、大塔山" loading="lazy"></p><p>→塔山步道上上下下，不久便經過2.0K木樁<br><img src="https://1013399.github.io/image-3/182/233697021_l.jpg" alt="塔山步道、大塔山" loading="lazy"></p><p>→沿途都是杉木林，經過1.5K木樁<br><img src="https://1013399.github.io/image-3/182/233697024_l.jpg" alt="塔山步道、大塔山 杉木林" loading="lazy"></p><p>→再陸續經過1.0K木樁及0.5K木樁<br><img src="https://1013399.github.io/image-3/182/233697034_l.jpg" alt="塔山步道、大塔山" loading="lazy"></p><p>→天橋，過天橋往姊妹潭<br><img src="https://1013399.github.io/image-3/182/233697035_l.jpg" alt="塔山步道、大塔山" loading="lazy"></p><p>→往姊妹潭棧道濕滑，需小心慢走<br><img src="https://1013399.github.io/image-3/182/233697043_l.jpg" alt="塔山步道、大塔山" loading="lazy"></p><p>→叉路，直行往塔山步道口，取右往姊妹潭<br><img src="https://1013399.github.io/image-3/182/233697044_l.jpg" alt="塔山步道、大塔山" loading="lazy"></p><p>→妹潭<br><img src="https://1013399.github.io/image-3/182/233697047_l.jpg" alt="妹潭" loading="lazy"></p><p>→姊妹潭，前往潭中涼亭午餐<br><img src="https://1013399.github.io/image-3/182/233697055_l.jpg" alt="姊妹潭" loading="lazy"></p><h2 id="走走資訊" tabindex="-1"><a class="header-anchor" href="#走走資訊" aria-hidden="true">#</a> 走走資訊</h2><div class="hint-container info"><p class="hint-container-title">相關信息</p><p>走走時間：約4小時(含泡茶、休息)</p><p>高低落差：約370公尺</p></div><h2 id="參考地圖" tabindex="-1"><a class="header-anchor" href="#參考地圖" aria-hidden="true">#</a> 參考地圖</h2><p>(紫色為去程、桃紅色為回程)</p>',48),c={href:"http://www.keepon.com.tw/DiscussLoad.aspx?code=314B5CF9AEC3A19113F6CAA6F539A662CF8459BE4526487C",target:"_blank",rel:"noopener noreferrer"},d=i("br",null,null,-1),_=i("img",{src:"https://1013399.github.io/image-3/182/233697141_l.jpg",alt:"塔山步道、大塔山 參考地圖",loading:"lazy"},null,-1);function u(j,y){const a=l("ExternalLinkIcon");return p(),o("div",null,[n,m,r(" more "),b,i("p",null,[t("原圖連結→"),i("a",c,[t("登山補給站-brownlee(小李)"),s(a)]),d,_])])}const K=g(h,[["render",u],["__file","post-182-2012-09-18.html.vue"]]);export{K as default};
