import{_ as l}from"./plugin-vue_export-helper-c27b6911.js";import{r as p,o,c as r,b as i,f as t,d as g,a as e,e as s}from"./app-7f62a6ff.js";const h={},n=i("p",null,[i("strong",null,"紗帽山"),t("，海拔643公尺，山頂有顆三等三角點No.1021，位於陽明山國家公園西南隅，是七星山的寄生火山，山形鐘狀渾圓，由於形狀酷似烏紗帽而得名。")],-1),m={href:"http://www.ymsnp.gov.tw/nweb/index.php?option=com_linemap&view=linemap&id=19&Itemid=132",target:"_blank",rel:"noopener noreferrer"},b=i("p",null,"紗帽山南北各有登山口，北登山口位於陽明山前山公園陽明湖旁、南登山口則為紗帽路80號附近的大埔登山口，紗帽山可與天母古道、半嶺古道、七星山連走，今日則採楊兄(Yang5757)行程，由大埔登山口上紗帽山，再連走橫嶺古道、順登湖山的Ｏ型路線。",-1),c=s('<p><strong>橫嶺古道</strong>，位於台北市北投區陽明山國家公園境內，為頂半嶺至陽明公園之古道，古道兩端入口分別為湖山路登山口及紅檀登山口，步道沿紗帽山西側山腰而行，總長約900公尺，步道平緩寬大好走，是一條幽靜的山徑。</p><p><strong>湖山</strong>，海拔368公尺，又稱湖底嶺，山頂有北市都市計畫處四等三角點No.157，樹林中無視野，由於位於橫嶺古道紅檀登山口附近，可順道造訪。</p><p>走走日期： 2012/09/02</p><h2 id="行車路線" tabindex="-1"><a class="header-anchor" href="#行車路線" aria-hidden="true">#</a> 行車路線</h2><p>台北市中山北路往北行至天母圓環後，續行中山北路七段，於219巷左轉，接紗帽路再接陽投公路右轉，於紗帽路80-1、80-8號附近路旁小空地停車，再往前不遠即為紗帽山大埔登山口。</p><h2 id="走走路線" tabindex="-1"><a class="header-anchor" href="#走走路線" aria-hidden="true">#</a> 走走路線</h2><p>→紗帽路80-1、80-8號附近路旁小空地停車<br><img src="https://1013399.github.io/image-3/184/232744544_l.jpg" alt="紗帽路80-1、80-8號附近路旁小空地停車" loading="lazy"></p><p>→紗帽山大埔登山口<br><img src="https://1013399.github.io/image-3/184/232744620_l.jpg" alt="紗帽山大埔登山口" loading="lazy"></p><p>→登山口旁解說牌，步道右側另設有里程樁(標示海拔、距離，每200公尺設一支)<br><img src="https://1013399.github.io/image-3/184/232744631_l.jpg" alt="登山口旁解說牌" loading="lazy"></p><p>→進入石階步道，由此至紗帽山頂約1Km<br><img src="https://1013399.github.io/image-3/184/232744633_l.jpg" alt="紗帽山、橫嶺古道、湖山" loading="lazy"></p><p>→步道一路往上<br><img src="https://1013399.github.io/image-3/184/232744638_l.jpg" alt="紗帽山、橫嶺古道、湖山" loading="lazy"></p><p>→太子亭遺址<br><img src="https://1013399.github.io/image-3/184/232744642_l.jpg" alt="紗帽山 太子亭遺址" loading="lazy"></p><p>→太子亭解說牌，續行步道即可見三角點<br><img src="https://1013399.github.io/image-3/184/232744645_l.jpg" alt="紗帽山 太子亭解說牌" loading="lazy"></p><p>→紗帽山，三等三角點No.1021<br><img src="https://1013399.github.io/image-3/184/232744647_l.jpg" alt="紗帽山，三等三角點No.1021" loading="lazy"></p><p>→沿途拍攝的里程樁，由里程樁可看出每200公尺約爬升60多公尺，相當平均<br><img src="https://1013399.github.io/image-3/184/232744626_l.jpg" alt="紗帽山、橫嶺古道、湖山" loading="lazy"></p><p>→觀景台叉路，先取左往觀景台<br><img src="https://1013399.github.io/image-3/184/232744649_l.jpg" alt="紗帽山、橫嶺古道、湖山" loading="lazy"></p><p>→觀景台，正前方就是七星山及七星山東峰<br><img src="https://1013399.github.io/image-3/184/232744661_l.jpg" alt="紗帽山、橫嶺古道、湖山" loading="lazy"></p><p>→觀景台上導覽圖<br><img src="https://1013399.github.io/image-3/184/232744669_l.jpg" alt="紗帽山、橫嶺古道、湖山" loading="lazy"></p><p>→眺望大屯山<br><img src="https://1013399.github.io/image-3/184/232744666_l.jpg" alt="大屯山" loading="lazy"></p><p>→俯瞰中山樓<br><img src="https://1013399.github.io/image-3/184/232744673_l.jpg" alt="中山樓" loading="lazy"></p><p>→續行步道往下，路左有石砌造景<br><img src="https://1013399.github.io/image-3/184/232744676_l.jpg" alt="紗帽山、橫嶺古道、湖山" loading="lazy"></p><p>→往陽明湖登山口約1.2Km，較為平緩<br><img src="https://1013399.github.io/image-3/184/232744681_l.jpg" alt="陽明湖登山口" loading="lazy"></p><p>→旗桿座，為清代咸豐年間陳姓舉人所立，續往內有古墓<br><img src="https://1013399.github.io/image-3/184/232744690_l.jpg" alt="紗帽山、橫嶺古道、湖山" loading="lazy"></p><p>→往陽明湖沿途每200公尺也設有里程樁<br><img src="https://1013399.github.io/image-3/184/232744695_l.jpg" alt="紗帽山、橫嶺古道、湖山" loading="lazy"></p><p>→陽明湖登山口，接紗帽路<br><img src="https://1013399.github.io/image-3/184/232744701_l.jpg" alt="陽明湖登山口" loading="lazy"></p><p>→登山口對面就是陽明湖，前往建國街午餐<br><img src="https://1013399.github.io/image-3/184/232744713_l.jpg" alt="陽明湖" loading="lazy"></p><p>→餐畢，穿越前山公園往公車總站<br><img src="https://1013399.github.io/image-3/184/232744717_l.jpg" alt="紗帽山、橫嶺古道、湖山" loading="lazy"></p><p>→於公園內仰望紗帽山<br><img src="https://1013399.github.io/image-3/184/232744720_l.jpg" alt="紗帽山" loading="lazy"></p><p>→經過勝利街後抵達公車總站<br><img src="https://1013399.github.io/image-3/184/232744722_l.jpg" alt="紗帽山、橫嶺古道、湖山" loading="lazy"></p><p>→取候車站牌旁人行道<br><img src="https://1013399.github.io/image-3/184/232744723_l.jpg" alt="紗帽山、橫嶺古道、湖山" loading="lazy"></p><p>→接湖山路人車分道，穿越馬路走人行道<br><img src="https://1013399.github.io/image-3/184/232744724_l.jpg" alt="紗帽山、橫嶺古道、湖山" loading="lazy"></p><p>→人行道上設有陽明公園路線圖<br><img src="https://1013399.github.io/image-3/184/232744728_l.jpg" alt="陽明公園路線圖" loading="lazy"></p><p>→叉路，直行往陽明公園(後山公園)，右可往遊客中心，取左過馬路進入小公園<br><img src="https://1013399.github.io/image-3/184/232744730_l.jpg" alt="陽明公園(後山公園)" loading="lazy"></p><p>→小公園路底，右側馬路為湖山路、湖底路叉路口<br><img src="https://1013399.github.io/image-3/184/232744731_l.jpg" alt="紗帽山、橫嶺古道、湖山" loading="lazy"></p><p>→小公園路底有顆77年國家水泥柱<br><img src="https://1013399.github.io/image-3/184/232744743_l.jpg" alt="紗帽山、橫嶺古道、湖山" loading="lazy"></p><p>→橫嶺古道湖山路登山口，就在湖山路與湖底路叉路口附近<br><img src="https://1013399.github.io/image-3/184/232744757_l.jpg" alt="橫嶺古道湖山路登山口" loading="lazy"></p><p>→橫嶺古道，由湖山路往紅檀登山口一路緩下，輕鬆好走<br><img src="https://1013399.github.io/image-3/184/232744768_l.jpg" alt="紗帽山、橫嶺古道、湖山" loading="lazy"></p><p>→沿途可見數顆台北市保安林界樁<br><img src="https://1013399.github.io/image-3/184/232744775_l.jpg" alt="台北市保安林界樁" loading="lazy"></p><p>→紅檀登山口，橫嶺古道結束，接湖底產道<br><img src="https://1013399.github.io/image-3/184/232744782_l.jpg" alt="紅檀登山口" loading="lazy"></p><p>→天上廟大廣場，平時大門深鎖，樹下陰涼處是休憩泡茶的好地方<br><img src="https://1013399.github.io/image-3/184/232744784_l.jpg" alt="紗帽山、橫嶺古道、湖山" loading="lazy"></p><p>→續行產道遇叉路，左回大廣場，續直行<br><img src="https://1013399.github.io/image-3/184/232744791_l.jpg" alt="紗帽山、橫嶺古道、湖山" loading="lazy"></p><p>→十字叉路，左往紗帽路，右往湖底路，先取直往湖山<br><img src="https://1013399.github.io/image-3/184/232744795_l.jpg" alt="紗帽山、橫嶺古道、湖山" loading="lazy"></p><p>→約行150公尺，抵達湖山登山口，對面路燈編號152<br><img src="https://1013399.github.io/image-3/184/232744796_l.jpg" alt="湖山登山口" loading="lazy"></p><p>→沿護網一路往上走<br><img src="https://1013399.github.io/image-3/184/232744801_l.jpg" alt="紗帽山、橫嶺古道、湖山" loading="lazy"></p><p>→護網結束後取右即可見三角點<br><img src="https://1013399.github.io/image-3/184/232744803_l.jpg" alt="紗帽山、橫嶺古道、湖山" loading="lazy"></p><p>→湖山，北市都市計畫處四等三角點No.157，拍照後原路折返<br><img src="https://1013399.github.io/image-3/184/232744809_l.jpg" alt="紗帽山、橫嶺古道、湖山" loading="lazy"></p><p>→回到十字叉路，取右下階梯往紗帽路<br><img src="https://1013399.github.io/image-3/184/232744812_l.jpg" alt="紗帽山、橫嶺古道、湖山" loading="lazy"></p><p>→回到紗帽路<br><img src="https://1013399.github.io/image-3/184/232744813_l.jpg" alt="紗帽山、橫嶺古道、湖山" loading="lazy"></p><p>→出口對面為半嶺步道可往天母，取左走紗帽路回<br><img src="https://1013399.github.io/image-3/184/232744817_l.jpg" alt="紗帽山、橫嶺古道、湖山" loading="lazy"></p><p>→約走400公尺，回到停車處</p><h2 id="走走資訊" tabindex="-1"><a class="header-anchor" href="#走走資訊" aria-hidden="true">#</a> 走走資訊</h2><div class="hint-container info"><p class="hint-container-title">相關信息</p><p>走走時間：約4小時(含休息、午餐)</p><p>高低落差：約320公尺</p></div><h2 id="參考地圖" tabindex="-1"><a class="header-anchor" href="#參考地圖" aria-hidden="true">#</a> 參考地圖</h2>',53),d={href:"http://blog.xuite.net/yang5757/blog/28056038",target:"_blank",rel:"noopener noreferrer"},_=i("br",null,null,-1),u=i("img",{src:"https://1013399.github.io/image-3/184/232744848_l.jpg",alt:"紗帽山、橫嶺古道、湖山 參考地圖",loading:"lazy"},null,-1);function y(j,z){const a=p("ExternalLinkIcon");return o(),r("div",null,[n,i("p",null,[t("(參考資料："),i("a",m,[t("陽明山國家公園管理處"),g(a)]),t(")")]),b,e(" more "),c,i("p",null,[t("原圖連結→"),i("a",d,[t("重新站起&一步一腳印(yang5757)"),g(a)]),_,u])])}const N=l(h,[["render",y],["__file","post-184-2012-09-06.html.vue"]]);export{N as default};
