import{_ as s}from"./plugin-vue_export-helper-c27b6911.js";import{r as p,o as e,c as r,a as h,b as t,f as i,d as a,w as g,e as n}from"./app-a9847bbb.js";const m={},b=t("p",null,"走走行程：午沙港→午沙北海坑道→北竿遊客中心→壁山→橋仔漁村→芹壁聚落→北竿機場→松山機場",-1),c=t("p",null,"走走日期：2012/08/12 PM",-1),d=n('<h2 id="走走路線" tabindex="-1"><a class="header-anchor" href="#走走路線" aria-hidden="true">#</a> 走走路線</h2><p>→午餐畢前往午沙港踏浪<br><img src="https://1013399.github.io/image-3/185/230828570_l.jpg" alt="馬祖 午沙港" loading="lazy"></p><p>→午沙港海岸景觀優美，沙灘沙質晶瑩細緻，清涼不會燙腳<br><img src="https://1013399.github.io/image-3/185/231561978_l.jpg" alt="馬祖 午沙港" loading="lazy"></p><p>→像兩頭豬的大石<br><img src="https://1013399.github.io/image-3/185/231561980_l.jpg" alt="馬祖 午沙港" loading="lazy"></p><p>→沙灘上的垃圾，都是大陸對岸飄過來的<br><img src="https://1013399.github.io/image-3/185/231561982_l.jpg" alt="馬祖 午沙港" loading="lazy"></p><p>→退潮時，由此可以進入北竿的北海坑道<br><img src="https://1013399.github.io/image-3/185/231561969_l.jpg" alt="馬祖 北竿北海坑道" loading="lazy"></p><p>→北竿的北海坑道是直線型的，不同於環狀的南竿北海坑道<br><img src="https://1013399.github.io/image-3/185/230828571_l.jpg" alt="馬祖 北竿北海坑道" loading="lazy"></p><p>→北竿的北海坑道砂石淤積嚴重，已經無法行船<br><img src="https://1013399.github.io/image-3/185/230828575_l.jpg" alt="馬祖 北竿北海坑道" loading="lazy"></p><p>→走出坑道，續行步道往上<br><img src="https://1013399.github.io/image-3/185/230828578_l.jpg" alt="馬祖 北竿北海坑道" loading="lazy"></p><p>→步道叉路，直行為坑道入口，取右上往廁所，沖洗腳上的細沙<br><img src="https://1013399.github.io/image-3/185/230828580_l.jpg" alt="馬祖 北竿北海坑道" loading="lazy"></p><p>→續行至坑道入口，接駁車已等候多時，上車前往北竿遊客中心<br><img src="https://1013399.github.io/image-3/185/230828582_l.jpg" alt="馬祖 北竿北海坑道" loading="lazy"></p><p>→北竿遊客中心，就在坂里國小旁邊<br><img src="https://1013399.github.io/image-3/185/230828583_l.jpg" alt="馬祖 北竿遊客中心，在坂里國小旁邊" loading="lazy"></p><p>→先參觀對面的坂里古厝<br><img src="https://1013399.github.io/image-3/185/230828593_l.jpg" alt="馬祖 坂里古厝" loading="lazy"></p><p>→昔日的坂里村民公約，頗為有趣<br><img src="https://1013399.github.io/image-3/185/230828586_l.jpg" alt="馬祖 坂里村民公約" loading="lazy"></p><p>→古厝古色古香，有數個房間準備改為民宿<br><img src="https://1013399.github.io/image-3/185/230828588_l.jpg" alt="馬祖 坂里古厝" loading="lazy"></p><p>→重修坂里古厝紀念碑，記載重修過程<br><img src="https://1013399.github.io/image-3/185/230828591_l.jpg" alt="馬祖 坂里古厝紀念碑" loading="lazy"></p><p>→前往遊客中心視聽室，欣賞以北竿聚落為主題拍攝的介紹影片<br> →看完影片，坂里沙灘看海，上車前往壁山觀景</p><p>→壁山，海拔294公尺，是馬祖地區最高山，山頂最高點為營區，不得進入、攝影<br>   遊客只可到達觀景台(海拔約250公尺)，視野極佳，俯瞰北竿機場及海上諸島<br><img src="https://1013399.github.io/image-3/185/230830561_l.jpg" alt="馬祖 壁山 馬祖地區最高山" loading="lazy"></p><p>→觀景台附近崗哨旁為壁山步道，通往塘岐村，賞景後續往橋仔漁村<br><img src="https://1013399.github.io/image-3/185/230828598_l.jpg" alt="馬祖 壁山步道" loading="lazy"></p><p>→橋仔漁村，是北竿距離大陸最近的村落，也是馬祖地區廟宇最多的村莊<br><img src="https://1013399.github.io/image-3/185/230828600_l.jpg" alt="馬祖 橋仔漁村" loading="lazy"></p><p>→先到橋仔漁村展示館參觀，由後門進入<br><img src="https://1013399.github.io/image-3/185/230828603_l.jpg" alt="馬祖 橋仔漁村展示館" loading="lazy"></p><p>→館內展示早年當地捕魚的作業方式和各式漁具<br><img src="https://1013399.github.io/image-3/185/230828604_l.jpg" alt="馬祖 橋仔漁村展示館" loading="lazy"></p><p>→橋仔漁村，是早年北竿往返大陸的轉口港<br><img src="https://1013399.github.io/image-3/185/230828605_l.jpg" alt="馬祖 橋仔漁村展示館" loading="lazy"></p><p>→由橋仔漁村展示館大門出來，品嘗馬祖道地的甜點－黃金餃<br><img src="https://1013399.github.io/image-3/185/230828607_l.jpg" alt="馬祖 橋仔漁村展示館" loading="lazy"></p><p>→眺望對岸的黃岐半島，沿岸都是風力發電風車<br><img src="https://1013399.github.io/image-3/185/230828613_l.jpg" alt="馬祖 黃岐半島" loading="lazy"></p><p>→品嘗完黃金餃，前往昨晚喝咖啡的芹壁村</p><p>→一下車，映入眼簾的就是龜島<br><img src="https://1013399.github.io/image-3/185/230828621_l.jpg" alt="馬祖 龜島" loading="lazy"></p><p>→取左芹壁步道進入芹壁村<br><img src="https://1013399.github.io/image-3/185/230828622_l.jpg" alt="馬祖 芹壁步道 芹壁村" loading="lazy"></p><p>→芹壁14號，芹壁唯一屋頂刻有石獅的房子，是當年的海盜屋<br><img src="https://1013399.github.io/image-3/185/230828628_l.jpg" alt="馬祖 芹壁唯一屋頂刻有石獅的房子 海盜屋" loading="lazy"></p><p>→海盜屋共二層樓，建於芹壁高點，視野極好，可以掌握整個海岸動靜<br><img src="https://1013399.github.io/image-3/185/230828629_l.jpg" alt="馬祖 芹壁唯一屋頂刻有石獅的房子 海盜屋" loading="lazy"></p><p>→芹壁傳統閩東聚落建築，小窗戶是特色之一<br><img src="https://1013399.github.io/image-3/185/230828630_l.jpg" alt="馬祖 傳統閩東聚落建築" loading="lazy"></p><p>→花漾樓，現為民宿，不知與電影「花漾」在芹壁拍攝有無相關<br><img src="https://1013399.github.io/image-3/185/230828632_l.jpg" alt="馬祖 芹壁村 花漾樓" loading="lazy"></p><p>→芹壁聚落，有地中海的感覺<br><img src="https://1013399.github.io/image-3/185/230828634_l.jpg" alt="馬祖 芹壁村 芹壁聚落" loading="lazy"></p><p>→芹壁聚落，依山傍水而建，質樸無華<br><img src="https://1013399.github.io/image-3/185/230828636_l.jpg" alt="馬祖 芹壁村 芹壁聚落" loading="lazy"></p><p>→即將結束馬祖三日行，上車前，拍張芹壁全貌<br><img src="https://1013399.github.io/image-3/185/230828640_l.jpg" alt="馬祖 芹壁村 芹壁聚落" loading="lazy"></p><p>→回到龍福山莊，晚餐後前往北竿機場，搭乘立榮航空19:30班機回台<br> →約於20:20回到松山機場，結束馬祖南北竿卡蹓三日行</p><h2 id="參考地圖" tabindex="-1"><a class="header-anchor" href="#參考地圖" aria-hidden="true">#</a> 參考地圖</h2>',37),_={href:"http://www.matsu-nsa.gov.tw/UserFiles/https://1013399.github.io/image-3/185/1/bigmap2.jpg",target:"_blank",rel:"noopener noreferrer"},u=t("br",null,null,-1),j=t("img",{src:"https://1013399.github.io/image-3/185/230831284_l.jpg",alt:"馬祖北竿參考地圖",loading:"lazy"},null,-1),y=t("h2",{id:"相關行程",tabindex:"-1"},[t("a",{class:"header-anchor",href:"#相關行程","aria-hidden":"true"},"#"),i(" 相關行程")],-1);function z(f,x){const o=p("ExternalLinkIcon"),l=p("RouterLink");return e(),r("div",null,[b,c,h(" more "),d,t("p",null,[i("北竿原圖連結→"),t("a",_,[i("馬祖國家風景區"),a(o)]),u,j]),y,t("ul",null,[t("li",null,[a(l,{to:"/posts/post-189-2012-08-20.html"},{default:g(()=>[i("南北竿卡蹓三日行(一)：馬祖酒廠、八八坑道、水產試驗所、馬港天后宮、媽祖巨神像朝聖步道")]),_:1})]),t("li",null,[a(l,{to:"/posts/post-188-2012-08-21.html"},{default:g(()=>[i("南北竿卡蹓三日行(二)：雲台山、津沙聚落、鐵堡、北海坑道、大漢據點、山隴蔬菜公園")]),_:1})]),t("li",null,[a(l,{to:"/posts/post-187-2012-08-22.html"},{default:g(()=>[i("南北竿卡蹓三日行(三)：摩天嶺、240大砲連、民俗文物館、福澳港(往北竿)")]),_:1})]),t("li",null,[a(l,{to:"/posts/post-186-2012-08-23.html"},{default:g(()=>[i("南北竿卡蹓三日行(四)：06據點、戰爭和平紀念館、12據點、螺山自然步道、08據點")]),_:1})]),t("li",null,[a(l,{to:"/posts/post-185-2012-08-24.html"},{default:g(()=>[i("南北竿卡蹓三日行(五)：午沙港、午沙北海坑道、北竿遊客中心、壁山、橋仔漁村、芹壁聚落")]),_:1})])])])}const N=s(m,[["render",z],["__file","post-185-2012-08-24.html.vue"]]);export{N as default};
