import{_ as s}from"./plugin-vue_export-helper-c27b6911.js";import{r as p,o as r,c as e,a as h,b as t,f as i,d as a,w as g,e as m}from"./app-a01d9b9c.js";const n={},b=t("p",null,"走走行程：06據點→戰爭和平紀念館→12據點→螺山自然步道→08據點",-1),c=t("p",null,"走走日期：2012/08/12 AM",-1),d=m('<h2 id="走走路線" tabindex="-1"><a class="header-anchor" href="#走走路線" aria-hidden="true">#</a> 走走路線</h2><p>→今日上午行程皆在塘后道沙灘右側的大澳山、螺山附近，這裡是將來馬祖賭場預定地<br>   首先前往06據點，停車場下車後，循步道往下行<br><img src="https://1013399.github.io/image-3/186/230828472_l.jpg" alt="馬祖 06據點 馬祖賭場預定地" loading="lazy"></p><p>→步道終點觀景平台<br><img src="https://1013399.github.io/image-3/186/230828475_l.jpg" alt="馬祖 觀景平台" loading="lazy"></p><p>→觀景平台設有附近島嶼解說牌<br><img src="https://1013399.github.io/image-3/186/230828480_l.jpg" alt="馬祖 觀景平台" loading="lazy"></p><p>→往右眺望三連嶼<br><img src="https://1013399.github.io/image-3/186/230828478_l.jpg" alt="馬祖 三連嶼" loading="lazy"></p><p>→往前眺望獅嶼等島嶼<br><img src="https://1013399.github.io/image-3/186/230828481_l.jpg" alt="馬祖 獅嶼" loading="lazy"></p><p>→往左眺望大陸北茭半島，剛好有班立榮航空飛機即將登陸北竿機場<br><img src="https://1013399.github.io/image-3/186/230828482_l.jpg" alt="馬祖 大陸北茭半島" loading="lazy"></p><p>→平台直下階梯或右側階梯都可往06據點，我們取直下再右回<br><img src="https://1013399.github.io/image-3/186/230828485_l.jpg" alt="馬祖 06據點" loading="lazy"></p><p>→坑道內有機槍口、副砲口、連長室、輔導長室<br><img src="https://1013399.github.io/image-3/186/230828486_l.jpg" alt="馬祖 06據點 坑道內有機槍口、副砲口、連長室、輔導長室" loading="lazy"></p><p>→還有中山室、官兵寢室、主砲口、彈藥庫<br><img src="https://1013399.github.io/image-3/186/230828488_l.jpg" alt="馬祖 06據點 有中山室、官兵寢室、主砲口、彈藥庫" loading="lazy"></p><p>→於砲口眺望附近島嶼<br><img src="https://1013399.github.io/image-3/186/230828490_l.jpg" alt="馬祖 06據點" loading="lazy"></p><p>→沿岸的岩壁上長了許多海芙蓉<br><img src="https://1013399.github.io/image-3/186/230828492_l.jpg" alt="馬祖 06據點 海芙蓉" loading="lazy"></p><p>→出坑道後，取右上回觀景平台<br><img src="https://1013399.github.io/image-3/186/230828494_l.jpg" alt="馬祖 06據點" loading="lazy"></p><p>→烏雲密佈，開始下起大雨，儘速上車往12據點<br><img src="https://1013399.github.io/image-3/186/230828501_l.jpg" alt="馬祖 北竿" loading="lazy"></p><p>→於一崗哨處下車步行<br><img src="https://1013399.github.io/image-3/186/230828503_l.jpg" alt="馬祖 一崗哨處" loading="lazy"></p><p>→叉路，取右往12據點<br><img src="https://1013399.github.io/image-3/186/230828506_l.jpg" alt="馬祖 12據點" loading="lazy"></p><p>→剛才於06據點看到的飛機，現在起飛回台灣了<br><img src="https://1013399.github.io/image-3/186/230828511_l.jpg" alt="馬祖 北竿" loading="lazy"></p><p>→先直行上至戰爭和平紀念館頂樓<br><img src="https://1013399.github.io/image-3/186/230828512_l.jpg" alt="馬祖 戰爭和平紀念館頂樓" loading="lazy"></p><p>→往左俯瞰面海的媽祖像及其後的土地公像<br><img src="https://1013399.github.io/image-3/186/230828515_l.jpg" alt="馬祖 媽祖像及其後的土地公像" loading="lazy"></p><p>→往右俯瞰仰臥的佛祖像<br><img src="https://1013399.github.io/image-3/186/230828517_l.jpg" alt="馬祖 仰臥的佛祖像" loading="lazy"></p><p>→俯瞰螺山、蚌山及螺山自然步道<br><img src="https://1013399.github.io/image-3/186/230828519_l.jpg" alt="馬祖 螺山、蚌山及螺山自然步道" loading="lazy"></p><p>→戰爭和平紀念公園主題館，位於12據點上方<br><img src="https://1013399.github.io/image-3/186/230828521_l.jpg" alt="馬祖 戰爭和平紀念公園主題館" loading="lazy"></p><p>→紀念館以「感動‧回憶」為主題<br><img src="https://1013399.github.io/image-3/186/230828523_l.jpg" alt="馬祖 戰爭和平紀念公園主題館" loading="lazy"></p><p>→緣起、軍事沿革大事紀、軍用品展示區、軍民生態文化等展區<br><img src="https://1013399.github.io/image-3/186/230828525_l.jpg" alt="馬祖 戰爭和平紀念公園主題館" loading="lazy"></p><p>→天氣放晴了，臨時決定走走螺山自然步道，由紀念館前階梯往下行<br><img src="https://1013399.github.io/image-3/186/230828528_l.jpg" alt="馬祖 螺山自然步道" loading="lazy"></p><p>→導遊帶我們從12據點進入步道，回程再由步道口回<br><img src="https://1013399.github.io/image-3/186/230828531_l.jpg" alt="馬祖 螺山自然步道" loading="lazy"></p><p>→經過12據點接上步道<br><img src="https://1013399.github.io/image-3/186/230828533_l.jpg" alt="馬祖 螺山自然步道" loading="lazy"></p><p>→螺山自然步道，早期是后澳民眾採蚵及釣客行走的小徑，步道一路往下<br><img src="https://1013399.github.io/image-3/186/230828535_l.jpg" alt="馬祖 螺山自然步道" loading="lazy"></p><p>→三連嶼，因三座島礁緊臨而命名，各島礁之外形、色彩均各具特色<br><img src="https://1013399.github.io/image-3/186/230828537_l.jpg" alt="馬祖 三連嶼" loading="lazy"></p><p>→200M里程碑(每100M設一顆，但未見第一顆)，步道全長742M<br><img src="https://1013399.github.io/image-3/186/230828540_l.jpg" alt="馬祖 螺山自然步道 200M里程碑" loading="lazy"></p><p>→沿途未見過的小花<br><img src="https://1013399.github.io/image-3/186/230828542_l.jpg" alt="馬祖 螺山自然步道" loading="lazy"></p><p>→步道趨緩，途經兩座涼亭後抵達步道終點<br><img src="https://1013399.github.io/image-3/186/230828547_l.jpg" alt="馬祖 螺山自然步道" loading="lazy"></p><p>→海上孔子像，猶如至聖孔子，靜立海上、聽濤觀海<br><img src="https://1013399.github.io/image-3/186/230828550_l.jpg" alt="馬祖 海上孔子像" loading="lazy"></p><p>→遠處奇石也宛如猩猩背海而坐，休息後原路折返<br><img src="https://1013399.github.io/image-3/186/230828552_l.jpg" alt="馬祖 螺山自然步道" loading="lazy"></p><p>→回到12據點，取直行(不經12據點)，步道旁設有導覽圖<br><img src="https://1013399.github.io/image-3/186/230828553_l.jpg" alt="馬祖 螺山自然步道導覽圖" loading="lazy"></p><p>→叉路，左不知去向，取右回<br><img src="https://1013399.github.io/image-3/186/230828556_l.jpg" alt="馬祖 螺山自然步道" loading="lazy"></p><p>→回到戰爭和平紀念館，上車往08據點</p><p>→08據點前擺放軍方除役戰車、自走砲及防空機槍<br><img src="https://1013399.github.io/image-3/186/230828557_l.jpg" alt="馬祖 08據點" loading="lazy"></p><p>→隨著12據點的主題館啟用，08據點的戰爭文物展示館，就封館了<br><img src="https://1013399.github.io/image-3/186/230828564_l.jpg" alt="馬祖 08據點" loading="lazy"></p><p>→眺望螺山、蚌山<br><img src="https://1013399.github.io/image-3/186/230828569_l.jpg" alt="馬祖 螺山、蚌山" loading="lazy"></p><p>→回龍福山莊午餐，午餐休息後，繼續下午行程</p><h2 id="參考地圖" tabindex="-1"><a class="header-anchor" href="#參考地圖" aria-hidden="true">#</a> 參考地圖</h2>',42),_={href:"http://www.matsu-nsa.gov.tw/UserFiles/https://1013399.github.io/image-3/186/1/bigmap2.jpg",target:"_blank",rel:"noopener noreferrer"},u=t("br",null,null,-1),j=t("img",{src:"https://1013399.github.io/image-3/186/230831284_l.jpg",alt:"馬祖北竿參考地圖",loading:"lazy"},null,-1),y=t("h2",{id:"相關行程",tabindex:"-1"},[t("a",{class:"header-anchor",href:"#相關行程","aria-hidden":"true"},"#"),i(" 相關行程")],-1);function z(f,x){const o=p("ExternalLinkIcon"),l=p("RouterLink");return r(),e("div",null,[b,c,h(" more "),d,t("p",null,[i("北竿原圖連結→"),t("a",_,[i("馬祖國家風景區"),a(o)]),u,j]),y,t("ul",null,[t("li",null,[a(l,{to:"/posts/post-189-2012-08-20.html"},{default:g(()=>[i("南北竿卡蹓三日行(一)：馬祖酒廠、八八坑道、水產試驗所、馬港天后宮、媽祖巨神像朝聖步道")]),_:1})]),t("li",null,[a(l,{to:"/posts/post-188-2012-08-21.html"},{default:g(()=>[i("南北竿卡蹓三日行(二)：雲台山、津沙聚落、鐵堡、北海坑道、大漢據點、山隴蔬菜公園")]),_:1})]),t("li",null,[a(l,{to:"/posts/post-187-2012-08-22.html"},{default:g(()=>[i("南北竿卡蹓三日行(三)：摩天嶺、240大砲連、民俗文物館、福澳港(往北竿)")]),_:1})]),t("li",null,[a(l,{to:"/posts/post-186-2012-08-23.html"},{default:g(()=>[i("南北竿卡蹓三日行(四)：06據點、戰爭和平紀念館、12據點、螺山自然步道、08據點")]),_:1})]),t("li",null,[a(l,{to:"/posts/post-185-2012-08-24.html"},{default:g(()=>[i("南北竿卡蹓三日行(五)：午沙港、午沙北海坑道、北竿遊客中心、壁山、橋仔漁村、芹壁聚落")]),_:1})])])])}const M=s(n,[["render",z],["__file","post-186-2012-08-23.html.vue"]]);export{M as default};
