import{_ as p}from"./plugin-vue_export-helper-c27b6911.js";import{r as g,o as s,c as r,a as n,b as t,e as l,d as a,w as o,f as h}from"./app-e9e80f6e.js";const m={},_=t("p",null,"走走行程：摩天嶺→240大砲連→民俗文物館→福澳港(往北竿)→北竿芹壁賞夜景",-1),c=t("p",null,"走走日期：2012/08/11 PM",-1),d=h('<h2 id="走走路線" tabindex="-1"><a class="header-anchor" href="#走走路線" aria-hidden="true">#</a> 走走路線</h2><p>→午餐畢前往摩天嶺，車停於一處營區前，進入摩天嶺步道，由上往下走至涼亭觀景點<br><img src="https://1013399.github.io/image-3/187/230714163_l.jpg" alt="馬祖 摩天嶺步道" loading="lazy"></p><p>→牛角聚落解說牌，南竿全島狀似一頭犀牛，牛角聚落因位於南竿東北角而得名，現為復興村<br><img src="https://1013399.github.io/image-3/187/230714171_l.jpg" alt="馬祖 牛角聚落解說牌" loading="lazy"></p><p>→俯瞰牛角聚落<br><img src="https://1013399.github.io/image-3/187/230714173_l.jpg" alt="馬祖 牛角聚落" loading="lazy"></p><p>→續行步道可往馬祖酒廠，原路折返，上車前往參觀240大砲<br><img src="https://1013399.github.io/image-3/187/230714178_l.jpg" alt="馬祖 摩天嶺步道" loading="lazy"></p><p>→大砲連營區門口，需驗身份證，導遊交代，只可拍設施，不可拍阿兵哥<br><img src="https://1013399.github.io/image-3/187/230714181_l.jpg" alt="馬祖 大砲連營區門口" loading="lazy"></p><p>→室內展示一門雷霆巨砲<br><img src="https://1013399.github.io/image-3/187/230714182_l.jpg" alt="馬祖 巨砲" loading="lazy"></p><p>→再往前涼亭內設有240榴砲簡介<br><img src="https://1013399.github.io/image-3/187/230714186_l.jpg" alt="馬祖 240榴砲簡介" loading="lazy"></p><p>→從涼亭俯瞰巨砲<br><img src="https://1013399.github.io/image-3/187/230714190_l.jpg" alt="馬祖 巨砲" loading="lazy"></p><p>→近距離欣賞，參觀完續行往民俗文物館<br><img src="https://1013399.github.io/image-3/187/230714192_l.jpg" alt="馬祖 巨砲" loading="lazy"></p><p>→馬祖民俗文物館，外形仿閩東聚落建築模式，展現馬祖傳統聚落高低錯落之美<br><img src="https://1013399.github.io/image-3/187/230714196_l.jpg" alt="馬祖 馬祖民俗文物館" loading="lazy"></p><p>→民俗文物館對面是勝利水庫<br><img src="https://1013399.github.io/image-3/187/230714200_l.jpg" alt="馬祖 勝利水庫" loading="lazy"></p><p>→牆上、地上都有這種圖案，導遊說這叫「如魚得水」<br><img src="https://1013399.github.io/image-3/187/230714203_l.jpg" alt="馬祖 馬祖民俗文物館 如魚得水圖案" loading="lazy"></p><p>→大門右側十二生肖石雕，生動可愛<br><img src="https://1013399.github.io/image-3/187/230714206_l.jpg" alt="馬祖 馬祖民俗文物館 十二生肖石雕" loading="lazy"></p><p>→旁邊陳列四座清代霧砲<br><img src="https://1013399.github.io/image-3/187/230714210_l.jpg" alt="馬祖 馬祖民俗文物館 四座清代霧砲" loading="lazy"></p><p>→進入館內，先搭電梯至四樓，由上往下參觀<br><img src="https://1013399.github.io/image-3/187/230714213_l.jpg" alt="馬祖 馬祖民俗文物館" loading="lazy"></p><p>→導遊的專業解說，充分瞭解到馬祖的文化資產與歷史沿革<br><img src="https://1013399.github.io/image-3/187/230714215_l.jpg" alt="馬祖 馬祖民俗文物館" loading="lazy"></p><p>→結束南竿的行程了，前往福澳港搭16:30的船班往北竿<br><img src="https://1013399.github.io/image-3/187/230714221_l.jpg" alt="馬祖 福澳港" loading="lazy"></p><p>→回望福山公園的「枕戈待旦」，馬祖戰地的精神代表，與金門的「母忘在莒」同享盛名<br><img src="https://1013399.github.io/image-3/187/230714218_l.jpg" alt="馬祖 福山公園的「枕戈待旦」" loading="lazy"></p><p>→約於16:50抵達北竿白沙港，由接駁車直接載往住宿處－龍福山莊</p><p>→晚餐休息後，前往芹壁村喝咖啡、賞夜景<br><img src="https://1013399.github.io/image-3/187/230714224_l.jpg" alt="馬祖 芹壁村" loading="lazy"></p><p>→傳統的閩東古厝，現已被利用經營民宿與咖啡簡餐廳<br><img src="https://1013399.github.io/image-3/187/230714228_l.jpg" alt="馬祖 閩東古厝" loading="lazy"></p><p>→21:30回龍福山莊休息</p><h2 id="參考地圖" tabindex="-1"><a class="header-anchor" href="#參考地圖" aria-hidden="true">#</a> 參考地圖</h2>',24),u={href:"http://www.matsu-nsa.gov.tw/UserFiles/image/1/bigmap3.jpg",target:"_blank",rel:"noopener noreferrer"},b=t("br",null,null,-1),j=t("img",{src:"https://1013399.github.io/image-3/187/230831811_l.jpg",alt:"馬祖南竿 參考地圖",loading:"lazy"},null,-1),y={href:"http://www.matsu-nsa.gov.tw/UserFiles/image/1/bigmap2.jpg",target:"_blank",rel:"noopener noreferrer"},z=t("br",null,null,-1),f=t("img",{src:"https://1013399.github.io/image-3/187/230831284_l.jpg",alt:"馬祖北竿參考地圖",loading:"lazy"},null,-1),w=t("h2",{id:"相關行程",tabindex:"-1"},[t("a",{class:"header-anchor",href:"#相關行程","aria-hidden":"true"},"#"),l(" 相關行程")],-1);function x(k,v){const e=g("ExternalLinkIcon"),i=g("RouterLink");return s(),r("div",null,[_,c,n(" more "),d,t("p",null,[l("南竿原圖連結→"),t("a",u,[l("馬祖國家風景區"),a(e)]),b,j]),t("p",null,[l("北竿原圖連結→"),t("a",y,[l("馬祖國家風景區"),a(e)]),z,f]),w,t("ul",null,[t("li",null,[a(i,{to:"/posts/post-189-2012-08-20.html"},{default:o(()=>[l("南北竿卡蹓三日行(一)：馬祖酒廠、八八坑道、水產試驗所、馬港天后宮、媽祖巨神像朝聖步道")]),_:1})]),t("li",null,[a(i,{to:"/posts/post-188-2012-08-21.html"},{default:o(()=>[l("南北竿卡蹓三日行(二)：雲台山、津沙聚落、鐵堡、北海坑道、大漢據點、山隴蔬菜公園")]),_:1})]),t("li",null,[a(i,{to:"/posts/post-187-2012-08-22.html"},{default:o(()=>[l("南北竿卡蹓三日行(三)：摩天嶺、240大砲連、民俗文物館、福澳港(往北竿)")]),_:1})]),t("li",null,[a(i,{to:"/posts/post-186-2012-08-23.html"},{default:o(()=>[l("南北竿卡蹓三日行(四)：06據點、戰爭和平紀念館、12據點、螺山自然步道、08據點")]),_:1})]),t("li",null,[a(i,{to:"/posts/post-185-2012-08-24.html"},{default:o(()=>[l("南北竿卡蹓三日行(五)：午沙港、午沙北海坑道、北竿遊客中心、壁山、橋仔漁村、芹壁聚落")]),_:1})])])])}const L=p(m,[["render",x],["__file","post-187-2012-08-22.html.vue"]]);export{L as default};
