import{_ as s}from"./plugin-vue_export-helper-c27b6911.js";import{r as p,o as r,c as e,a as h,b as t,f as i,d as a,w as g,e as m}from"./app-6513a715.js";const n={},b=t("p",null,"走走行程：馬港天后宮→雲台山軍情館→津沙聚落→仁愛鐵堡→南竿遊客中心→北海坑道→大漢據點→山隴蔬菜公園",-1),c=t("p",null,"走走日期：2012/08/11 AM",-1),d=m('<h2 id="走走路線" tabindex="-1"><a class="header-anchor" href="#走走路線" aria-hidden="true">#</a> 走走路線</h2><p>→第二天出發前，先於馬祖劍碑前拍合照<br><img src="https://1013399.github.io/image-3/188/230714045_l.jpg" alt="馬祖劍碑" loading="lazy"></p><p>→馬港天后宮也拍一張，再由導遊導覽天后宮，之後前往雲台山<br><img src="https://1013399.github.io/image-3/188/230714046_l.jpg" alt="馬祖 馬港天后宮" loading="lazy"></p><p>→雲台山展望解說牌<br><img src="https://1013399.github.io/image-3/188/230714051_l.jpg" alt="馬祖 雲台山展望解說牌" loading="lazy"></p><p>→雲台山軍情館，進入參觀需押證件，且不得拍照<br><img src="https://1013399.github.io/image-3/188/230714053_l.jpg" alt="馬祖 雲台山軍情館" loading="lazy"></p><p>→雲台山，海拔248公尺，南竿最高點，小百岳No.98<br><img src="https://1013399.github.io/image-3/188/230714056_l.jpg" alt="馬祖 雲台山" loading="lazy"></p><p>→眺望福澳港<br><img src="https://1013399.github.io/image-3/188/230714055_l.jpg" alt="馬祖 福澳港" loading="lazy"></p><p>→眺望馬祖港、媽祖巨神像，拍照後前往津沙聚落<br><img src="https://1013399.github.io/image-3/188/230714064_l.jpg" alt="馬祖 馬祖港、媽祖巨神像" loading="lazy"></p><p>→津沙位於雲台山下，是南竿最接近大陸第一大漁村聚落<br><img src="https://1013399.github.io/image-3/188/230714065_l.jpg" alt="馬祖 津沙" loading="lazy"></p><p>→津沙保存著很多完整的閩東式古厝，臨港依山而築<br><img src="https://1013399.github.io/image-3/188/230714067_l.jpg" alt="馬祖 津沙" loading="lazy"></p><p>→津沙港擁有如金砂般細緻的沙灘<br><img src="https://1013399.github.io/image-3/188/230714069_l.jpg" alt="馬祖 津沙港" loading="lazy"></p><p>→接著來到仁愛鐵堡<br><img src="https://1013399.github.io/image-3/188/230714078_l.jpg" alt="馬祖 仁愛鐵堡" loading="lazy"></p><p>→鐵堡是座突出於海上的獨立岩礁，循木棧道往下行，再經過小橋進入<br>   鐵堡內仍保有完整的地下石室、坑道、射口、砲台、廚房、廁所、房間、臥床等<br><img src="https://1013399.github.io/image-3/188/230714079_l.jpg" alt="馬祖 仁愛鐵堡" loading="lazy"></p><p>→出鐵堡後繞至鐵堡上方，眺望仁愛港、仁愛村<br><img src="https://1013399.github.io/image-3/188/230714081_l.jpg" alt="馬祖 仁愛港、仁愛村" loading="lazy"></p><p>→馬祖當年唯一有軍階的狼犬駐防於此，參觀完續行往北海坑道<br><img src="https://1013399.github.io/image-3/188/230714084_l.jpg" alt="馬祖 仁愛鐵堡" loading="lazy"></p><p>→於南竿遊客中心下車<br><img src="https://1013399.github.io/image-3/188/230714085_l.jpg" alt="馬祖 於南竿遊客中心" loading="lazy"></p><p>→叉路，右往大漢據點，先取直往北海坑道<br><img src="https://1013399.github.io/image-3/188/230714088_l.jpg" alt="馬祖 北海坑道" loading="lazy"></p><p>→眺望大漢據點<br><img src="https://1013399.github.io/image-3/188/230714092_l.jpg" alt="馬祖 大漢據點" loading="lazy"></p><p>→途經馬祖國家風景區管理處<br><img src="https://1013399.github.io/image-3/188/230714097_l.jpg" alt="馬祖國家風景區管理處" loading="lazy"></p><p>→北海坑道入口意象<br><img src="https://1013399.github.io/image-3/188/230714099_l.jpg" alt="馬祖 北海坑道入口" loading="lazy"></p><p>→入口擺設兩門高射砲<br><img src="https://1013399.github.io/image-3/188/230714101_l.jpg" alt="馬祖 北海坑道入口" loading="lazy"></p><p>→眺望印地安人頭岩<br><img src="https://1013399.github.io/image-3/188/230714103_l.jpg" alt="馬祖 印地安人頭岩" loading="lazy"></p><p>→北海坑道解說牌<br><img src="https://1013399.github.io/image-3/188/230714105_l.jpg" alt="馬祖 北海坑道" loading="lazy"></p><p>→由此進入北海坑道<br><img src="https://1013399.github.io/image-3/188/230714108_l.jpg" alt="馬祖 北海坑道" loading="lazy"></p><p>→北海坑道為民國57年的「北海案」作戰工程<br><img src="https://1013399.github.io/image-3/188/230714110_l.jpg" alt="馬祖 北海坑道" loading="lazy"></p><p>→北海坑道當年開鑿情形<br><img src="https://1013399.github.io/image-3/188/230714111_l.jpg" alt="馬祖 北海坑道" loading="lazy"></p><p>→當年供登陸小艇使用之坑道碼頭，現已改為遊客划船用<br><img src="https://1013399.github.io/image-3/188/230714114_l.jpg" alt="馬祖 北海坑道" loading="lazy"></p><p>→步行需戴鋼盔(塑膠製)，環繞一圈約30分鐘<br><img src="https://1013399.github.io/image-3/188/230714118_l.jpg" alt="馬祖 北海坑道" loading="lazy"></p><p>→出坑道後續往大漢據點，回望北海坑道<br><img src="https://1013399.github.io/image-3/188/230714127_l.jpg" alt="馬祖 北海坑道" loading="lazy"></p><p>→大漢據點解說牌<br><img src="https://1013399.github.io/image-3/188/230714130_l.jpg" alt="馬祖 大漢據點" loading="lazy"></p><p>→大漢據點入口<br><img src="https://1013399.github.io/image-3/188/230714133_l.jpg" alt="馬祖 大漢據點入口" loading="lazy"></p><p>→大漢據點從下到上有三層坑道，設置大大小小機槍砲射口<br><img src="https://1013399.github.io/image-3/188/230714137_l.jpg" alt="馬祖 大漢據點" loading="lazy"></p><p>→砲台旁掛有中共敵艦識別圖，以免誤擊<br><img src="https://1013399.github.io/image-3/188/230714139_l.jpg" alt="馬祖 大漢據點" loading="lazy"></p><p>→砲台天花板上消音錐<br><img src="https://1013399.github.io/image-3/188/230714142_l.jpg" alt="馬祖 大漢據點 砲台天花板上消音錐" loading="lazy"></p><p>→出坑道後回望大漢據點砲口<br><img src="https://1013399.github.io/image-3/188/230714145_l.jpg" alt="馬祖 大漢據點砲口" loading="lazy"></p><p>→取右上階梯<br><img src="https://1013399.github.io/image-3/188/230714149_l.jpg" alt="馬祖 大漢據點" loading="lazy"></p><p>→風化區，已風化的花崗岩<br><img src="https://1013399.github.io/image-3/188/230714151_l.jpg" alt="馬祖 已風化的花崗岩" loading="lazy"></p><p>→上至據點頂端，沿步道即可回到南竿遊客中心<br><img src="https://1013399.github.io/image-3/188/230714154_l.jpg" alt="馬祖 南竿遊客中心" loading="lazy"></p><p>→上車後前往介壽村午餐，於山隴蔬菜公園附近下車<br><img src="https://1013399.github.io/image-3/188/230714160_l.jpg" alt="馬祖 山隴蔬菜公園" loading="lazy"></p><p>→蔬菜公園解說牌<br><img src="https://1013399.github.io/image-3/188/230714158_l.jpg" alt="馬祖 山隴蔬菜公園 解說牌" loading="lazy"></p><p>→蔬菜公園就在連江縣政府前面<br><img src="https://1013399.github.io/image-3/188/230714162_l.jpg" alt="馬祖 山隴蔬菜公園" loading="lazy"></p><p>→午餐休息後，繼續下午行程</p><h2 id="參考地圖" tabindex="-1"><a class="header-anchor" href="#參考地圖" aria-hidden="true">#</a> 參考地圖</h2>',43),_={href:"http://www.matsu-nsa.gov.tw/UserFiles/image/1/bigmap3.jpg",target:"_blank",rel:"noopener noreferrer"},u=t("br",null,null,-1),j=t("img",{src:"https://1013399.github.io/image-3/188/230831811_l.jpg",alt:"馬祖南竿參考地圖",loading:"lazy"},null,-1),y=t("h2",{id:"相關行程",tabindex:"-1"},[t("a",{class:"header-anchor",href:"#相關行程","aria-hidden":"true"},"#"),i(" 相關行程")],-1);function z(f,x){const o=p("ExternalLinkIcon"),l=p("RouterLink");return r(),e("div",null,[b,c,h(" more "),d,t("p",null,[i("南竿原圖連結→"),t("a",_,[i("馬祖國家風景區"),a(o)]),u,j]),y,t("ul",null,[t("li",null,[a(l,{to:"/posts/post-189-2012-08-20.html"},{default:g(()=>[i("南北竿卡蹓三日行(一)：馬祖酒廠、八八坑道、水產試驗所、馬港天后宮、媽祖巨神像朝聖步道")]),_:1})]),t("li",null,[a(l,{to:"/posts/post-188-2012-08-21.html"},{default:g(()=>[i("南北竿卡蹓三日行(二)：雲台山、津沙聚落、鐵堡、北海坑道、大漢據點、山隴蔬菜公園")]),_:1})]),t("li",null,[a(l,{to:"/posts/post-187-2012-08-22.html"},{default:g(()=>[i("南北竿卡蹓三日行(三)：摩天嶺、240大砲連、民俗文物館、福澳港(往北竿)")]),_:1})]),t("li",null,[a(l,{to:"/posts/post-186-2012-08-23.html"},{default:g(()=>[i("南北竿卡蹓三日行(四)：06據點、戰爭和平紀念館、12據點、螺山自然步道、08據點")]),_:1})]),t("li",null,[a(l,{to:"/posts/post-185-2012-08-24.html"},{default:g(()=>[i("南北竿卡蹓三日行(五)：午沙港、午沙北海坑道、北竿遊客中心、壁山、橋仔漁村、芹壁聚落")]),_:1})])])])}const N=s(n,[["render",z],["__file","post-188-2012-08-21.html.vue"]]);export{N as default};
