import{_ as g}from"./plugin-vue_export-helper-c27b6911.js";import{r as p,o,c as r,b as i,f as t,d as l,a as e,e as s}from"./app-63889d57.js";const h={},n=i("p",null,[i("strong",null,"大旗棟古道"),t("(大崎棟步道)，位於新竹縣橫山鄉之大山背區域，全長約為1.5公里，入口設於樂善堂寺廟後側，步道建造完善，全程皆為石階步道與枕木步道，十分容易行走，而且此區的森林景觀相當優美，翠綠的山林中還有機會看見果樹，非常適合全家人一同前往。")],-1),m={href:"http://ezfun.coa.gov.tw/view.php?theme=route&id=F_huei_20071015020134&city=J&class=R01",target:"_blank",rel:"noopener noreferrer"},b=i("p",null,[i("strong",null,"大山背山"),t("，又稱大旗崠山，海拔705公尺，原本的三等三角點已遺失，千岳俱樂部在2007年1月6日新設三等三角點五號，山頂有座觀景平台及涼亭，視野極佳，可見上坪溪與油羅溪匯集成頭前溪的景觀。")],-1),c=s('<p>走走日期：2012/08/05</p><h2 id="行車路線" tabindex="-1"><a class="header-anchor" href="#行車路線" aria-hidden="true">#</a> 行車路線</h2><p>由竹東走台3線北上，於台3線72.9Km右轉產業道路，約150公尺後接上於於竹34鄉道，左轉往萬瑞森林遊樂園及九華山莊方向續行，過頭份林後，於竹34鄉道3.2Km叉路取右竹35-1鄉道上行，經過萬瑞森林遊樂園及九華山莊後，抵達樂善堂，續往前數十公尺第一停車場停車。</p><h2 id="走走路線" tabindex="-1"><a class="header-anchor" href="#走走路線" aria-hidden="true">#</a> 走走路線</h2><p>→樂善堂第一停車場<br><img src="https://1013399.github.io/image-3/190/229683060_l.jpg" alt="樂善堂第一停車場" loading="lazy"></p><p>→往回走往樂善堂，左側叉路可往高頂農莊、大山背山<br><img src="https://1013399.github.io/image-3/190/229683214_l.jpg" alt="樂善堂" loading="lazy"></p><p>→路旁有大山背導覽圖，此圖有誤(左右相反)<br><img src="https://1013399.github.io/image-3/190/229683229_l.jpg" alt="大山背導覽圖 此圖有誤" loading="lazy"></p><p>→樂善堂入口大崎棟步道石碑<br><img src="https://1013399.github.io/image-3/190/229683238_l.jpg" alt="樂善堂入口大崎棟步道石碑" loading="lazy"></p><p>→樂善堂為百年古剎，原名石峽廟，在竹東就可看見此觀音像<br><img src="https://1013399.github.io/image-3/190/229683247_l.jpg" alt="樂善堂" loading="lazy"></p><p>→樂善堂前廣場展望佳<br><img src="https://1013399.github.io/image-3/190/229683259_l.jpg" alt="樂善堂" loading="lazy"></p><p>→取廟右方進入步道<br><img src="https://1013399.github.io/image-3/190/229683272_l.jpg" alt="大旗棟古道、大山背山" loading="lazy"></p><p>→大崎棟步道入口指標<br><img src="https://1013399.github.io/image-3/190/229683279_l.jpg" alt="大崎棟步道入口指標" loading="lazy"></p><p>→叉路，直行回樂善堂，取右上<br><img src="https://1013399.github.io/image-3/190/229683287_l.jpg" alt="大旗棟古道、大山背山" loading="lazy"></p><p>→步道一路往上，幾乎都是石階<br><img src="https://1013399.github.io/image-3/190/229683299_l.jpg" alt="大旗棟古道、大山背山" loading="lazy"></p><p>→叉路，直行不知去向，取右續行<br><img src="https://1013399.github.io/image-3/190/229683310_l.jpg" alt="大旗棟古道、大山背山" loading="lazy"></p><p>→漫長的石階步道<br><img src="https://1013399.github.io/image-3/190/229683319_l.jpg" alt="大旗棟古道、大山背山" loading="lazy"></p><p>→抵達高頂農莊<br><img src="https://1013399.github.io/image-3/190/229683332_l.jpg" alt="高頂農莊" loading="lazy"></p><p>→高頂農莊有提供用餐、喝咖啡(需先預約)<br><img src="https://1013399.github.io/image-3/190/229683376_l.jpg" alt="高頂農莊" loading="lazy"></p><p>→接上產道，左側有停車場，可回樂善堂，穿越馬路直上階梯<br><img src="https://1013399.github.io/image-3/190/229683386_l.jpg" alt="大旗棟古道、大山背山" loading="lazy"></p><p>→仿木橋<br><img src="https://1013399.github.io/image-3/190/229683397_l.jpg" alt="大旗棟古道、大山背山" loading="lazy"></p><p>→接寬大土路，左右皆不知去向，續直行階梯<br><img src="https://1013399.github.io/image-3/190/229683409_l.jpg" alt="大旗棟古道、大山背山" loading="lazy"></p><p>→接水泥路，左下雜草叢生、似已荒廢，取右上續行<br><img src="https://1013399.github.io/image-3/190/229683423_l.jpg" alt="大旗棟古道、大山背山" loading="lazy"></p><p>→此段水泥路可供人車兩用<br><img src="https://1013399.github.io/image-3/190/229683439_l.jpg" alt="大旗棟古道、大山背山" loading="lazy"></p><p>→新建觀景平台，旁有涼亭及大水塔，在此休息<br><img src="https://1013399.github.io/image-3/190/229683448_l.jpg" alt="大旗棟古道、大山背山" loading="lazy"></p><p>→觀景平台展望佳<br><img src="https://1013399.github.io/image-3/190/229683457_l.jpg" alt="大旗棟古道、大山背山" loading="lazy"></p><p>→續行步道，時而石階、時而石板<br><img src="https://1013399.github.io/image-3/190/229683473_l.jpg" alt="大旗棟古道、大山背山" loading="lazy"></p><p>→上抵稜線，取左續行<br><img src="https://1013399.github.io/image-3/190/229683510_l.jpg" alt="大旗棟古道、大山背山" loading="lazy"></p><p>→櫻花觀景平台，視野有限<br><img src="https://1013399.github.io/image-3/190/229683516_l.jpg" alt="大山背山 櫻花觀景平台" loading="lazy"></p><p>→石塊山徑，不久又接石階步道<br><img src="https://1013399.github.io/image-3/190/229683528_l.jpg" alt="大旗棟古道、大山背山" loading="lazy"></p><p>→最後攻頂<br><img src="https://1013399.github.io/image-3/190/229683540_l.jpg" alt="大旗棟古道、大山背山" loading="lazy"></p><p>→即將攻頂<br><img src="https://1013399.github.io/image-3/190/229683547_l.jpg" alt="大旗棟古道、大山背山" loading="lazy"></p><p>→大山背山，山頂觀景台，展望極佳，但今日濛濛一片無視野<br><img src="https://1013399.github.io/image-3/190/229683554_l.jpg" alt="大旗棟古道、大山背山" loading="lazy"></p><p>→山頂涼亭<br><img src="https://1013399.github.io/image-3/190/229683560_l.jpg" alt="大山背山山頂涼亭" loading="lazy"></p><p>→大山背山，千岳俱樂部新設三等三角點五號<br><img src="https://1013399.github.io/image-3/190/229683567_l.jpg" alt="大山背山，千岳俱樂部新設三等三角點五號" loading="lazy"></p><p>→休息後續往前行產道下山<br><img src="https://1013399.github.io/image-3/190/229683573_l.jpg" alt="大旗棟古道、大山背山" loading="lazy"></p><p>→天壇寺大門，取右彎續行產道<br><img src="https://1013399.github.io/image-3/190/229683580_l.jpg" alt="天壇寺大門" loading="lazy"></p><p>→櫻花步道入口，可回櫻花觀景平台，取右彎續行產道<br><img src="https://1013399.github.io/image-3/190/229683589_l.jpg" alt="櫻花步道入口" loading="lazy"></p><p>→大山背53號民宅，似已廢棄<br><img src="https://1013399.github.io/image-3/190/229683595_l.jpg" alt="大旗棟古道、大山背山" loading="lazy"></p><p>→叉路，右往慈惠堂可接竹35-1鄉道，取左續行<br><img src="https://1013399.github.io/image-3/190/229683604_l.jpg" alt="大旗棟古道、大山背山" loading="lazy"></p><p>→大山背農園<br><img src="https://1013399.github.io/image-3/190/229683612_l.jpg" alt="大山背農園" loading="lazy"></p><p>→叉路，左往高頂農莊，取右續行<br><img src="https://1013399.github.io/image-3/190/229683619_l.jpg" alt="大旗棟古道、大山背山" loading="lazy"></p><p>→大嬸婆之家<br><img src="https://1013399.github.io/image-3/190/229683626_l.jpg" alt="大嬸婆之家" loading="lazy"></p><p>→此處也是鍾石妹開墾大山背義民兵紮營區<br><img src="https://1013399.github.io/image-3/190/229683635_l.jpg" alt="大旗棟古道、大山背山" loading="lazy"></p><p>→樂善堂第二停車場<br><img src="https://1013399.github.io/image-3/190/229683644_l.jpg" alt="樂善堂第二停車場" loading="lazy"></p><p>→木棧道叉路，直行回樂善堂，取右木棧道回第一停車場<br><img src="https://1013399.github.io/image-3/190/229683652_l.jpg" alt="樂善堂" loading="lazy"></p><p>→回到竹35-1鄉道之第一停車場<br><img src="https://1013399.github.io/image-3/190/229683659_l.jpg" alt="竹35-1鄉道之第一停車場" loading="lazy"></p><h2 id="走走資訊" tabindex="-1"><a class="header-anchor" href="#走走資訊" aria-hidden="true">#</a> 走走資訊</h2><div class="hint-container info"><p class="hint-container-title">相關信息</p><p>走走時間：約100分鐘(含休息)</p><p>高低落差：約250公尺</p></div><h2 id="參考地圖" tabindex="-1"><a class="header-anchor" href="#參考地圖" aria-hidden="true">#</a> 參考地圖</h2>',49),d={href:"http://tw.myblog.yahoo.com/kentjon106/article?mid=3631&prev=3686&next=-1",target:"_blank",rel:"noopener noreferrer"},_=i("br",null,null,-1),u=i("img",{src:"https://1013399.github.io/image-3/190/229684615_l.jpg",alt:"大旗棟古道、大山背山 參考地圖",loading:"lazy"},null,-1),y=i("br",null,null,-1),j=i("img",{src:"https://1013399.github.io/image-3/190/229684610_l.jpg",alt:"大旗棟古道、大山背山 參考地圖",loading:"lazy"},null,-1),z={href:"http://www.tonyhuang39.com/tony0876/tony0876.html",target:"_blank",rel:"noopener noreferrer"},f=i("br",null,null,-1),x=i("img",{src:"https://1013399.github.io/image-3/190/229922922_l.jpg",alt:"大旗棟古道、大山背山 參考地圖",loading:"lazy"},null,-1);function k(v,w){const a=p("ExternalLinkIcon");return o(),r("div",null,[n,i("p",null,[t("(資料摘自："),i("a",m,[t("農業易遊網"),l(a)]),t(")")]),b,e(" more "),c,i("p",null,[t("原圖連結→"),i("a",d,[t("蜜豆的家"),l(a)]),_,u,y,j]),i("p",null,[t("原圖連結→"),i("a",z,[t("Tony的自然人文旅記"),l(a)]),f,x])])}const B=g(h,[["render",k],["__file","post-190-2012-08-08.html.vue"]]);export{B as default};
