import{_ as g}from"./plugin-vue_export-helper-c27b6911.js";import{r as p,o as e,c as o,b as i,e as t,d as l,a as r,f as n}from"./app-cb5d936b.js";const s={},h=i("p",null,[i("strong",null,"蓬萊溪自然生態園區"),t("，位於苗栗南庄鄉蓬萊村，位處象鼻山與福南山之間蓬萊溪谷，海拔介於400-500M，屬亞熱帶氣候，全區範圍北起永安吊橋，南至大湳溪與八卦力溪會流處，全長2.4公里。入口毗鄰苗124線，民國98年11月園區木棧道全線沿河床修築完成，供遊客更舒適、更安全欣賞水陸動植物生態。")],-1),c={href:"http://www.nanchuang.gov.tw/nanjhuang_township/normalContent.php?forewordID=9950&secureChk=9cb09e3397c2377d60cd57bd50818928",target:"_blank",rel:"noopener noreferrer"},m=i("p",null,[i("strong",null,"護魚步道"),t("，隨著蓬萊溪岸的地形，或鋪枕木，或架棧道，或鋪架木橋越過險阻，或利用溪邊石塊砌成簡易的石階。步道全程路況佳，走在其間，有溪流淙聲悅耳，有魚群悠遊悅目，可說是一條令人心曠神怡的觀魚步道。")],-1),d={href:"http://www.ioneone.com/pic_show_6.php",target:"_blank",rel:"noopener noreferrer"},b=n('<p>走走日期：2012/07/22</p><h2 id="行車路線" tabindex="-1"><a class="header-anchor" href="#行車路線" aria-hidden="true">#</a> 行車路線</h2><p>國道1號下頭份交流道，接上124縣道往三灣、南庄方向，經過南庄大橋後續直行124縣道，約於124縣道34.5Km即可到達蓬萊溪自然生態園區停車場。</p><h2 id="走走路線" tabindex="-1"><a class="header-anchor" href="#走走路線" aria-hidden="true">#</a> 走走路線</h2><p>→蓬萊溪自然生態園區停車場(共33個停車位)<br><img src="https://1013399.github.io/image-3/192/228199840_l.jpg" alt="蓬萊溪自然生態園區、護魚步道" loading="lazy"></p><p>→護魚步道入口<br><img src="https://1013399.github.io/image-3/192/228199848_l.jpg" alt="蓬萊溪自然生態園區、護魚步道" loading="lazy"></p><p>→循木棧道往下行<br><img src="https://1013399.github.io/image-3/192/228199854_l.jpg" alt="蓬萊溪自然生態園區、護魚步道" loading="lazy"></p><p>→蓬萊溪禁止垂釣、捕魚、戲水<br><img src="https://1013399.github.io/image-3/192/228199863_l.jpg" alt="蓬萊溪自然生態園區、護魚步道" loading="lazy"></p><p>→碧綠清澈的蓬萊溪<br><img src="https://1013399.github.io/image-3/192/228199871_l.jpg" alt="蓬萊溪自然生態園區、護魚步道" loading="lazy"></p><p>→長年護魚的成果，溪中魚群數量眾多<br><img src="https://1013399.github.io/image-3/192/228199879_l.jpg" alt="蓬萊溪自然生態園區、護魚步道" loading="lazy"></p><p>→步道沿溪興建，可近距離賞魚<br><img src="https://1013399.github.io/image-3/192/228199892_l.jpg" alt="蓬萊溪自然生態園區、護魚步道" loading="lazy"></p><p>→沿途設有解說牌<br><img src="https://1013399.github.io/image-3/192/228199903_l.jpg" alt="蓬萊溪自然生態園區、護魚步道" loading="lazy"></p><p>→步道清涼舒適<br><img src="https://1013399.github.io/image-3/192/228199922_l.jpg" alt="蓬萊溪自然生態園區、護魚步道" loading="lazy"><br><img src="https://1013399.github.io/image-3/192/228199950_l.jpg" alt="蓬萊溪自然生態園區、護魚步道" loading="lazy"></p><p>→路旁水利署基石(沿途共見3顆)<br><img src="https://1013399.github.io/image-3/192/228199963_l.jpg" alt="蓬萊溪自然生態園區、護魚步道" loading="lazy"></p><p>→步道起伏不大，輕鬆好走<br><img src="https://1013399.github.io/image-3/192/228199974_l.jpg" alt="蓬萊溪自然生態園區、護魚步道" loading="lazy"></p><p>→溪中被劈成兩半的大石<br><img src="https://1013399.github.io/image-3/192/228199991_l.jpg" alt="蓬萊溪自然生態園區、護魚步道" loading="lazy"></p><p>→叉路，左往蓬萊社區，續直行<br><img src="https://1013399.github.io/image-3/192/228200000_l.jpg" alt="蓬萊溪自然生態園區、護魚步道" loading="lazy"></p><p>→流龍頭，似乎還在使用<br><img src="https://1013399.github.io/image-3/192/228200027_l.jpg" alt="蓬萊溪自然生態園區、護魚步道" loading="lazy"></p><p>→美麗的蓬萊溪，溪水或急或緩<br><img src="https://1013399.github.io/image-3/192/228200037_l.jpg" alt="蓬萊溪自然生態園區、護魚步道" loading="lazy"></p><p>→步道轉為沒有扶手的木板道<br><img src="https://1013399.github.io/image-3/192/228200047_l.jpg" alt="蓬萊溪自然生態園區、護魚步道" loading="lazy"></p><p>→木板道崁有石刻<br><img src="https://1013399.github.io/image-3/192/228200060_l.jpg" alt="蓬萊溪自然生態園區、護魚步道" loading="lazy"></p><p>→步道轉折點，取左上行往七星宮<br><img src="https://1013399.github.io/image-3/192/228200068_l.jpg" alt="蓬萊溪自然生態園區、護魚步道" loading="lazy"></p><p>→枕木步道緩上<br><img src="https://1013399.github.io/image-3/192/228200090_l.jpg" alt="蓬萊溪自然生態園區、護魚步道" loading="lazy"></p><p>→小木橋<br><img src="https://1013399.github.io/image-3/192/228200101_l.jpg" alt="蓬萊溪自然生態園區、護魚步道" loading="lazy"></p><p>→過小橋後轉為水泥產道，右側為泉田鱒魚場，取左往七星宮<br><img src="https://1013399.github.io/image-3/192/228200119_l.jpg" alt="蓬萊溪自然生態園區、護魚步道" loading="lazy"></p><p>→叉路，左下往鱒魚場，取右上<br><img src="https://1013399.github.io/image-3/192/228200128_l.jpg" alt="蓬萊溪自然生態園區、護魚步道" loading="lazy"></p><p>→叉路，右上方為七星宮，續直行<br><img src="https://1013399.github.io/image-3/192/228200141_l.jpg" alt="蓬萊溪自然生態園區、護魚步道" loading="lazy"></p><p>→七星宮<br><img src="https://1013399.github.io/image-3/192/228200153_l.jpg" alt="蓬萊溪自然生態園區、護魚步道" loading="lazy"></p><p>→接上124縣道，取左回<br><img src="https://1013399.github.io/image-3/192/228200168_l.jpg" alt="蓬萊溪自然生態園區、護魚步道" loading="lazy"></p><p>→蓬萊派出所<br><img src="https://1013399.github.io/image-3/192/228200176_l.jpg" alt="蓬萊溪自然生態園區、護魚步道" loading="lazy"></p><p>→回到停車場<br><img src="https://1013399.github.io/image-3/192/228199832_l.jpg" alt="蓬萊溪自然生態園區、護魚步道" loading="lazy"></p><h2 id="走走資訊" tabindex="-1"><a class="header-anchor" href="#走走資訊" aria-hidden="true">#</a> 走走資訊</h2><div class="hint-container info"><p class="hint-container-title">相關信息</p><p>走走時間：約1.5小時(含休息、賞魚)</p><p>高低落差：</p><ul><li>停車場    → 溪谷步道      下降約10公尺</li><li>溪谷步道  → 七星宮        上升約70公尺</li><li>七星宮    → 停車場        下降約60公尺</li></ul></div><h2 id="參考地圖" tabindex="-1"><a class="header-anchor" href="#參考地圖" aria-hidden="true">#</a> 參考地圖</h2>',34),_={href:"http://blog.xuite.net/laoweiz/blog/16948372",target:"_blank",rel:"noopener noreferrer"},u=i("br",null,null,-1),j=i("img",{src:"https://1013399.github.io/image-3/192/228201282_l.jpg",alt:"蓬萊溪自然生態園區、護魚步道 參考地圖",loading:"lazy"},null,-1);function z(y,f){const a=p("ExternalLinkIcon");return e(),o("div",null,[h,i("p",null,[t("(資料摘自："),i("a",c,[t("南庄鄉公所觀光導覽"),l(a)]),t(")")]),r(" more "),m,i("p",null,[t("(資料摘自："),i("a",d,[t("南庄民宿網"),l(a)]),t(")")]),b,i("p",null,[t("原圖連結→"),i("a",_,[t("老尉子的部落格"),l(a)]),u,j])])}const k=g(s,[["render",z],["__file","post-192-2012-07-26.html.vue"]]);export{k as default};
